INSERT INTO `tb_procedure_info` VALUES (55, '01-09-017', '飞机维修检查检验管理程序', '2018-05-20 00:00:00', '2018-06-29 09:20:21', 0, '00', '适用于维修中队、航空安全质量分部、技术支援分部。', NULL, '20180625110641_01-09-017 飞机维修检查检验管理程序.pdf', 1, '', '', '', '', '', '', '', NULL, '11', '4.1	编写依据\r\n4.1.1	《工程手册》5.12\r\n4.1.2	CCAR-121《大型飞机公共航空运输承运人运行合格审定规则》121.367\r\n4.1.3	AC-121-53《民用航空器维修方案》\r\n4.1.4	01-09-17《飞机维修检查检验管理程序》\r\n4.2	关联程序\r\n4.2.1	01-08-005《必检项目》\r\n4.2.2	01-06-004《人员资格授权管理程序》', '为规范、明确飞机维修工作中检查和检验的标准和要求，特制定本程序。');
INSERT INTO `tb_procedure_info` VALUES (56, '01-10-001', '超手册修理的处理', '2018-05-20 00:00:00', '2018-06-29 09:23:01', 0, '01', '本程序适用于维修中队、技术支援分部、生产计划控制分部、航空质量安全分部。', NULL, '20180625110839_01-10-001 超手册修理的处理R1.pdf', 1, '', '', '', '', '', '', '', NULL, '无', '4.1 编写依据：\r\n4.1.1 01-10-01《超手册修理的处理》\r\n4.2	关联程序：\r\n4.2.1	01-10-002《重要修理和重要改装的处理》', '为规范偏离制造厂家技术手册的修理流程，达到超手册维修方案的制定、批准和实施有效管理的目的，特制定本程序。');
INSERT INTO `tb_procedure_info` VALUES (57, '01-10-002', '重要修理和重要改装的处理', '2018-05-20 00:00:00', '2018-06-29 09:26:02', 0, '00', '本程序适用于维修中队、技术支援分部、生产计划控制分部、航空质量安全分部。', NULL, '20180625111108_01-10-002 重要修理和重要改装的处理.pdf', 1, '', '', '', '', '', '', '', NULL, '11', '4.1	编写依据：\r\n4.1.1	FAR43 Appendix A&B和FAR145.209\r\n4.1.2	CCAR43.5、CCAR43附录E和CCAR145.32\r\n4.1.3	EASA21A.91\r\n4.1.4	01-10-02《重要修理和重要改装的处理》\r\n4.2	关联程序：\r\n无', '为规范重要修理和重要改装的管理流程，达到重要修理和重要改装有效管理的目的，特制定本程序。');
INSERT INTO `tb_procedure_info` VALUES (58, '01-10-003', '客户的工程指令', '2018-05-20 00:00:00', '2018-06-29 09:27:56', 0, '00', '本程序适用于技术支援分部、生产计划控制分部。', NULL, '20180625111216_01-10-003客户的工程指令.pdf', 1, '', '', '', '', '', '', '', NULL, '无', '4.1	编写依据：\r\n4.1.1	FAR43.9和FAR.145.201\r\n4.1.2	01-10-03《客户的工程指令》\r\n4.2	关联程序：\r\n4.2.1	01-11-016 《标准非例行工作单（支撑工作单）管理程序》', '为规范客户指令的执行程序，达到完成客户需要的维修工作的目的，特制定本程序。');
INSERT INTO `tb_procedure_info` VALUES (59, '01-10-004', '客户的维修提示', '2018-05-20 00:00:00', '2018-06-29 09:28:46', 0, '00', '本程序适用于技术支援分部、生产计划控制分部。', NULL, '20180625111314_01-10-004 客户的维修提示.pdf', 1, '', '', '', '', '', '', '', NULL, '无', '4.1 编写依据：\r\n4.1.1	01-10-04《客户的维修提示》\r\n4.2	关联程序：\r\n无', '为规范传达三方客户的维修提示，达到按照维修提示要求执行维护工作,特制定本程序。');
INSERT INTO `tb_procedure_info` VALUES (60, '01-10-005', '线性测量数值的取舍', '2018-05-20 00:00:00', '2018-06-29 09:31:45', 0, '00', '本程序适用于维修中队、生产计划控制分部、技术支援分部、航空安全质量分部。', NULL, '20180625111403_01-10-005 线性测量数据的取舍.pdf', 1, '', '', '', '', '', '', '', NULL, '无', '4.1 编写依据：\r\n4.1.1	01-10-05《线性测量数值的取舍》\r\n4.2	关联程序：\r\n无', '为规范飞机维修工作中公差数值的取舍流程，达到测量工具/设备无任何可以影响工作结果的目的，特制定本程序。');
INSERT INTO `tb_procedure_info` VALUES (61, '01-10-007', '关键设计构型控制限制（CDCCL）要求', '2018-05-20 00:00:00', '2018-06-29 09:33:57', 0, '00', '本程序适用于维修中队、技术支援分部、生产计划控制分部、人力资源分部。', NULL, '20180625111455_01-10-007 关键设计构型控制限制（CDCCL）要求.pdf', 1, '', '', '', '', '', '', '', NULL, '无', '4.1 编写依据：\r\n4.1.1	01-10-07《关键设计构型控制限制（CDCCL）要求》\r\n4.2	关联程序：\r\n无', '为了明确关键设计构型控制限制（CDCCL）的相关要求，达到相关工作的实施满足适航当局的标准，特制定本程序。');
