INSERT INTO `tb_step_info` VALUES (10, 3, '5.2.3', '航空安全质量分部评估各部门意见，完成修改。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (11, 3, '5.2.4', '航空安全质量分部在5个工作日内完成其与规章、手册的符合性的审核，按需启动对各工作程序的风险评估工作。审核、风险评估工作完成后对不符合要求的工作程序完成修改。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (12, 3, '5.2.5', '航空安全质量分部负责将修改完成的工作程序排版后报质量经理批准。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (13, 3, '5.2.6', '建立本单位《工作程序手册》与Ameco《维修管理工作程序手册》、《航空安全管理工作程序手册》的《符合性对照表》Form No.: QM-090，《符合性对照表》上要列出Ameco《维修管理工作程序手册》、《航空安全管理工作程序手册》中的全部程序，并对全部程序进行评估，使Ameco《维修管理工作程序手册》、《航空安全管理工作程序手册》的要求完全落实到维修单位的《工作程序手册》中。使用《符合性对照表封面页》Form No.: QM-089将修改的《符合性对照表》报本单位质量经理批准。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (14, 3, '5.2.7', '航空安全质量分部发文予以发布，公布程序生效日期并提出培训和执行等要求，培训需求填写《培训需求申请表（A类）》送人力资源分部。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
