INSERT INTO `tb_step_info` VALUES (18, 3, '5.3.1', '《维修管理工作程序手册》的修订来源：\n1）有关规章发生修订使上述手册的符合性受到影响时；\n2）《维修管理手册》的修订影响上述手册的内容时；\n3）局方评审及监察中发现的手册相关的任何不符合之处时；\n4）公司的实际情况或工作的实际流程发生的变化影响上述手册的内容时；\n5）公司自我质量审核以及运行当中发现或通过其他渠道发现的上述手册的任何缺陷和不足时；\n6）能促进或改进公司管理的任何其他理由或建议。', NULL, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
