INSERT INTO `tb_step_info` VALUES (43, 3, '5.6.2', '航空安全质量分部负责对手册的现行有效性进行控制和监督。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (44, 3, '5.7', '学习和执行', NULL, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (45, 3, '5.7.1', '人力资源分部依据航空安全质量分部提出的培训需求组织相关人员进行培训，培训可采取集中学习或自学形式，并按要求反馈培训记录。', 1, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (46, 3, '5.7.2', '各部门因出差、休假等原因未能按时进行培训的人员在返岗15个工作日内应及时予以培训，并补报培训记录至人力资源分部。', 10, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (47, 3, '5.7.3', '航空安全质量分部根据手册/程序的实际情况下发执行日期予以执行。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (48, 3, '5.8', '意见反馈和解释说明', NULL, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (49, 3, '5.8.1', '在手册学习和执行过程中，如发现编写错误或有意见，任何人均有责任和义务提出，填写《手册/程序意见反馈单》经本部门领导审核后向航空安全质量分部反馈。', 10, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (50, 3, '5.8.2', '航空安全质量分部负责意见收集、解释说明和处理、反馈。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (51, 3, '5.9', '报废手册的处理                                      \r\n旧版手册或换下的修订插页由航空安全质量分部负责销毁，并以《报废手册/插页销毁清单》Form No.: QM-005予以记录。', NULL, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (52, 3, '5.10', '客户提供的手册按照以上流程完成分发控制。', 8, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (53, 3, '5.11', '《CAAC培训大纲》的制定、修订和报批流程在《CAAC培训大纲》1.2中描述。', 1, '2019-03-07 15:44:37', '2019-03-07 15:44:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (54, 4, '5.1', '页面设置', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (55, 4, '5.1.1', '页边距：上：3.5厘米、下：2.5厘米、左、右侧：2.5厘米。手册的修订后应在页面左侧，靠修订内容的边缘用竖实线标出。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (56, 4, '5.1.2', '程序页眉从左到右内容为：公司标志、分公司名称，手册名称，日期、修改号、页码、程序编号，程序编号原则XX-XX-XXX。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (57, 4, '5.1.2.1', '上级通用程序部分，如与上级程序直接对应，则直接在上级程序编号第三组编号前加“0”。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (58, 4, '5.1.2.2', '上级安全管理工作程序部分，如与上级程序直接对应，则直接将前三位“SMM”替换为“01”。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (59, 4, '5.1.2.3', '如分公司特有程序将第一编号为TJ、第二组编号按照上级手册规律编号，第三组数采用三位流水号或三位流水号加注CCA。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (60, 4, '5.2', '项目编号规则                               \r\n项目编号采用固定编号形式的层级到四级：第一级X，第二级X.X，第三级X.X.X，第四级X.X.X.X，如1.2.2.1。四级编号以下的项目，视情采用顺序编号或并列关系编号。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (61, 4, '5.3', '字体、字号、字距、行距的要求', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (62, 4, '5.3.1', '字体、字号要求                            \r\n程序编号为Arial四号；程序标题为黑体小二号；一级标题为宋体加粗小四号；程序内容为宋体小四号；程序内容编号使用阿拉伯数字，Arial小四号。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (63, 4, '5.3.2', '字距要求', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (64, 4, '5.3.2.1', '正文字距的要求为标准间距。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (65, 4, '5.3.2.2', '编号字距为标准间距。如果左侧编号出现串行的情况，则仅针对此编号采用紧缩设置，磅值0.8。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (66, 4, '5.3.3', '行距要求                                    \r\n行距要求为固定值18磅，段前0.5行。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (67, 4, '5.4', '程序内容的编写要求                           \r\n程序内容共分为：目的、适用范围、名词术语、依据、程序、存档、附件和表格、流程图等八个部分。各部分的编写要求如下：', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (68, 4, '5.4.1', '“目的”的编写                              \r\n统一格式为：为规范XXXX，达到XXXX，特制定此程序。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (69, 4, '5.4.2', '“适用范围”的编写                         \r\n明确列出本工作程序适用的具体部门名称或人员岗位名称。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (70, 4, '5.4.3', '“名词术语”的编写                               \r\n列出程序中出现的关键名词的定义/解释，有两个及以上术语时采用二级编号规则进行编号；若无术语则写“无”。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (71, 4, '5.4.4', '“依据”的编写\r\n“依据”的编写分为“编写依据”和“关联程序”两部分。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (72, 4, '5.4.4.1', '编写依据\n1）按照适航规章、咨询通告、适航文件、手册的顺序分条目列出；\n2）引用的规章写到条目如CCAR-145《民用航空器维修单位合格审定规定》145.30；\n3）引用的咨询通告写明编号和名称，如AC-145-5《维修单位手册编写指南》；\n4）引用的手册写到章节，如《维修管理手册》5.7；\n5）如没有依据写“无”。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (73, 4, '5.4.4.2', '关联程序\n1）如本程序引用了其他程序，应列出关联程序的编号和名称；\n2）如没有关联程序，写“无”。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (74, 4, '5.4.5', '“程序”的编写', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (75, 4, '5.4.5.1', '总体要求：语言简练、责任明确、接口清晰、实现闭环。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (76, 4, '5.4.5.2', '“程序”分“程序流程”和“责任者”两部分。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (77, 4, '5.4.5.3', '“程序流程”部分依据“五个W一个H”(what、when、where、who、why、how)即应该做什么，何时做，在哪做，由谁做，为什么做以及如何做等内容进行编写。工作程序的内容应围绕工作程序的题目和工作程序的目的按照工作步骤展开描述。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (78, 4, '5.4.5.4', '“责任者”栏填写“程序流程”涉及的责任部门或具体岗位，如无，则以“--”标识。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (79, 4, '5.4.5.5', '程序中提到的表格，需注明表格号和表格名称，表格名称应加书名号，如：《XXX检查单》Form No.: XX-XXX。如直接使用上级表格，编号与上级保持一致；如分公司特有表格，采用XX-TXX的形式，编号前两位与上级保持一致，后两位采用流水号;如直接采用局方表格，直接引用局方表格编号。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (80, 4, '5.4.6', '“存档”的编写\r\n“存档”内容为程序中涉及的相关记录/文件的存档要求,存档要求应包括责任单位、存档方式和期限等。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (81, 4, '5.4.7', '“表格和附件”的编写\n“表格和附件”由“表格”和“附件”两个部分组成。', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (82, 4, '5.4.7.1', '表格\r\n1）分行列出表格名称和编号（如有），多于两行时编二级编号，左侧为表格名称,右侧为表格编号，表格编号右对齐；如程序不含表格，则写“无”。\r\n2）引用关联程序中的表格无需在此列出，但应在程序描述时明确表格出处。\r\n3）表格形式要求：表格左下角写表格编号(字号五号，字体The New Romans），右下角写页码如“1/5”字号五号，后附表格填写说明（如有）。\r\n公司标志统一使用如下样式：（见原程序）\r\n4）表格文字内容均采用中英文。\r\n注：所有表格样件均放在《表格手册》中统一管理，此处仅列出表格名称及编号，例如：《xxx检查单》                         Form No.: XX-XXX', NULL, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (83, 4, '5.4.7.2', '附件\n1）列出附件名称（如有），多于两行时编二级编号；如程序不含附件，则写“无”。\n2）依次列出附件。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (84, 4, '5.4.8', '“流程图”的编写\n流程图使用Microsoft Visio软件绘制，采用“泳道图”形式，流程图必须与工作程序描述一致并形成闭环。', 8, '2019-03-07 15:44:18', '2019-03-07 15:44:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (85, 5, '5.1', '分公司《维修单位培训大纲》的制定', NULL, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (86, 5, '5.1.1', '人力资源分部负责组织编写分公司《维修单位培训大纲》，以满足适航法规、公司《CAAC培训大纲》以及员工岗位资格要求、生产和业务发展需要。编写完成后交航空安全质量分部审核。', 1, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (87, 5, '5.1.2', '航空安全质量分部审核后经分公司责任经理签署后报适航当局批准生效。', 8, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (88, 5, '5.2', '分公司《维修单位培训大纲》的修订和批准', NULL, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (89, 5, '5.2.1', '分公司培训大纲的修订实施动态管理，各部门可提出修改意见：\n1）适航法规、咨询通告发生变化；\n2）分公司维修许可范围及维修能力发生变化，需改变培训标准；\n3）审核发现问题的纠正措施；\n4）分公司内部提出的修订建议并得到培训管理部门和质量管理部门的认可；\n5）人员工作职责变化，授权类别及要求发生变化；\n6）上级《维修单位培训大纲》修订或变更；\n7）责任经理认为必要的修订。', 10, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (90, 5, '5.2.2', '属地局方、上级单位对于分公司培训大纲的修订提出时限要求的情况下，人力资源分部应在局方要求上报时限前完成修订。', 1, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (91, 5, '5.2.3', '人力资源分部负责收集、分析、汇总、评估修改意见，对于分公司和属地局方提出的涉及培训标准和培训课程变化情况，须首先报公司培训部评估，待公司培训部评估可修改后再进行大纲修订，修订后报航空安全质量分部审核。', 1, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (92, 5, '5.2.4', '对分公司培训大纲修订部分进行审核，经责任经理签署后报本地区适航当局批准。', 8, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (93, 5, '5.3', '分公司《维修单位培训大纲》的发布和报备', NULL, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (94, 5, '5.3.1', '保存获批准的分公司《维修单位培训大纲》的主本及修订记录，在培训大纲生效后的3个工作日内在公司、分公司内网同时发布并通知人力资源分部。', 8, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (95, 5, '5.3.2', '将获批准的分公司《维修单位培训大纲》在培训大纲生效后的5个工作日内上报公司培训部备案。', 1, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (96, 5, '5.4', '分公司《维修单位培训大纲》的使用          \r\n分公司培训工作应严格按照分公司培训大纲执行，同时也需要执行公司《CAAC培训大纲》的培训要求。', NULL, '2018-06-27 16:43:13', '2018-06-27 16:43:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (97, 6, '5.1', '概述', NULL, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (98, 6, '5.1.1', '本单位作为经相关适航当局批准的维修单位，应该建立一套及时体现相关适航法规要求的工作程序，以保证维修工作顺利开展，提供令客户满意的维修质量。同时，必须遵守客户制定的经批准的维修方案。', 8, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (99, 6, '5.1.2', '当客户的要求多于或不同于本单位的工作程序手册时，应根据合同，将这些额外或特殊的要求编入客户化工作程序中，本单位员工在执行工作时还应遵守客户化工作程序的要求。如客户化工作程序与本单位通用的《工作程序手册》有冲突时，应以客户化工作程序为准。', NULL, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (100, 6, '5.1.3', '客户化工作程序的编号为：序号+客户名称，其格式与通用工作程序相一致。', NULL, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (101, 6, '5.2', '客户化工作程序的编写，改版和分发', NULL, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (102, 6, '5.2.1', '在合同签署后，应将相关信息提供给各生产计划控制分部，同时提供客户相关负责人员的联系方式。', 6, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (103, 6, '5.2.2', '生产计划控制分部根据其工作计划，适时召集航空安全质量分部以及其他相关部门，启动客户化工作程序的编写事宜。', 6, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (104, 6, '5.2.3', '航空安全质量分部负责组织制定客户化程序的完成。编写和修订过程和要求参照01-01-001《质量手册的管理程序》和01-01-002《工作程序编写规定》。客户化工作程序在报批前应与客户达成一致。', 8, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (105, 6, '5.2.4', '各部门对客户化工作程序的改版意见建议应以《手册/程序意见反馈单》Form No.: QM-001提出。', 10, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (106, 6, '5.2.5', '航空安全质量分部负责收集、评估修改意见建议的可行性并按需进行客户化工作程序的改版。', 8, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (107, 6, '5.2.6', '航空安全质量分部负责将完成的客户化工作程序报分公司质量经理或其授权人员审批，然后报客户进行批准或认可。', 8, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (108, 6, '5.2.7', '航空安全质量分部负责将获得客户批准或认可的客户化工作程序公布在分公司内网，然后正式发布并通知相关部门。', 8, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (109, 6, '5.3', '客户化工作程序的培训', NULL, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (110, 6, '5.3.1', '当客户化工作程序（包括新编写和改版的程序）得到批准并正式颁发后，航空安全质量分部负责提出培训需求,填写《培训需求申请表（A类）》Form No.: TM-T01送人力资源分部。', 8, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (111, 6, '5.3.2', '人力资源分部依据培训需求组织相关人员进行培训，并保存相应的培训记录。客户化工作程序培训记录方式与工作程序培训记录保持一致。', 1, '2018-06-28 09:41:25', '2018-06-28 09:41:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (112, 7, '5.1', '客户投诉的收集', NULL, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (113, 7, '5.1.1', '天津分公司收到关于产品及服务质量的书面客户投诉（包括索赔附件）后，在确定为天津分公司责任后，应按照《航空安全管理工作程序》01-SA-002强制报告程序要求将信息通报至航空安全质量分部。', 6, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (114, 7, '5.1.2', '其他服务类投诉由生产计划控制分部控制和跟踪。', 6, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (115, 7, '5.2', '客户投诉的处理', NULL, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (116, 7, '5.2.1', '航空安全质量分部在收到关于产品及服务质量的客户投诉信息后，按照01-SA-007《航空安全管理工作程序》人为原因不安全事件调查程序要求完成调查。', 8, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (117, 7, '5.2.2', '生产计划控制分部在收到其他服务类的投诉时，启动运行调查。', 6, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (118, 7, '5.2.3', '各部门应给予配合，并有责任按航空安全质量分部或生产计划控制分部的要求提供相关情况的书面说明及附件。需各部门负责的调查，如不能按期完成调查工作，应提前说明原因及调查进展情况。', 10, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (119, 7, '5.2.4', '航空安全质量分部将根据客户要求对产品及服务质量投诉进行答复和存档，答复由质量经理负责签署。', 8, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (120, 7, '5.2.5', '生产计划控制分部负责其他服务类投诉的回复和存档，回复应交当日天津分公司值班领导或分公司主管领导审批。', 6, '2018-05-29 11:00:48', '2018-05-29 11:00:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (121, 8, '5.1', '通则要求', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (122, 8, '5.1.1', '所有办公室、生产区域、存储区域应处于清洁、照明及通风良好的工作环境。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (123, 8, '5.1.2', '存储区域应保持在一个平稳干燥的温度下以尽可能地减少冷凝和变质。若未能满足预定的温湿度极限要求，应该采取在生产厂手册存储要求章节中规定的必要措施或进行抽样检查。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (124, 8, '5.1.3', '对于具有高精密和高可靠性要求的附件如陀螺、仪表以及其它带有机械或机电特性功能的附件，在维修过程中可能需要具有特殊环境条件的生产区域。', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (125, 8, '5.1.4', '特殊环境条件的生产区域中的温度、湿度和每限定单位空气容量内尘埃微粒的环境要求是由实践标准或由相关的附件生产厂家来决定。应遵守并保持已决定的特殊环境要求。对于实施的特殊环境测试的有效性应进行监控和记录。文件应按规定进行存档。', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (126, 8, '5.2', '要求的工具设备', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (127, 8, '5.2.1', '对于有温湿度要求的区域应使用经校验的温湿度表或记录仪定期测量。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (128, 8, '5.2.2', '对于有洁净度要求的区域应使用经校验的洁净度测量装置定期测量。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (129, 8, '5.2.3', '对于有防静电要求的区域应使用经校验的防静电测量装置定期测量。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (130, 8, '5.3', '一般要求', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (131, 8, '5.3.1', '所有办公室、生产区域、存储区域应处于良好的照明和通风状态；所有办公室、机库、车间、实验室、储存区域和培训设施应保持清洁有序。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (132, 8, '5.3.2', '办公室所有区域，包括文件、维修记录以及仍在保留期内的有关适航数据，都应保持清洁和整齐。所有办公设备，如计算机、打印机和磁盘等，都应有防尘措施，使其始终处于清洁和可工作状态。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (133, 8, '5.3.3', '机库、机坪、车间、库房、器材间、工具间、资料室、维修记录的档案中心、公共通道、休息室、更衣室、厕所和浴室等均应保持清洁干净。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (134, 8, '5.3.4', '工作梯、工作台、试验台、机床、试验设备和工具，都应保持清洁，以保证其正常工作。工作结束后，应将工作现场的外溢的燃油、润滑油、清洁剂以及废料垃圾清理干净，并清除影响现场整洁的其它物品。工作中产生的有害物质和液体应立即清除，以防止污染环境。', 9, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (135, 8, '5.3.5', '润滑工作中外溢多余的油脂和润滑油应及时清理。', 9, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (136, 8, '5.3.6', '每架飞机或单独的工作场所必须配备足够的垃圾箱。用单独的容器存放空油罐以及擦油时所用的布等易燃物品。使用过的锯末也应单独存放。', 9, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (137, 8, '5.3.7', '为保证在修理、大修过程中不会玷污附件和组件，所有工作台面、存放架和存放柜都应保持清洁。工作台上不能把附件与其它物品混放。附件和组件表面不应有明显的灰尘或污迹。在整个工作过程中，敏感/精密附件/零件应更加注意防尘。', 3, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (138, 8, '5.3.8', '使用者所用工具设备在返还工具间时应清洁干净。', 9, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (139, 8, '5.3.9', '工具和设备应在规定的时间内进行清洁。', 5, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (140, 8, '5.3.10', '专为测试台设定的清洁条件应符合生产厂家或维修手册中提到的清洁要求。', 5, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (141, 8, '5.3.11', '对于不同的计量标准器具和标准物质应根据其手册建立不同的环境条件，开展的计量检测工作应同时符合所依据文件的环境条件要求。', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (142, 8, '5.3.12', '在隔离区域内进行可能会污染工作环境的工作时，应采取相应措施保护其它附件和组件不会受到玷污。', 9, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (143, 8, '5.3.13', '对有可能污染工作环境的附件和组件，应先在专设清洁间内进行清洁后，再带入工作区域。清洗槽中的清洗剂名称、型号/件号,或批次号应使用标签标示。', 9, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (144, 8, '5.3.14', '应保证车辆的内外清洁。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (145, 8, '5.3.15', '房屋清洁应包括地板、墙、门和放置在房屋内财产（办公家具、设备等）的清洁。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (146, 8, '5.3.16', '所有清洁工作都应使用符合要求的清洁剂。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (147, 8, '5.3.17', '区域责任人应负责环境管理，达到合格状态。所有在此区域的工作人员有义务按要求持续保持环境。', 10, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (148, 8, '5.3.18', '飞机机库和车间应尽量关闭减少灰尘污染。', 5, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (149, 8, '5.3.19', '维修区域应满足相应的厂家或维修手册中的清洁要求，如电子车间、喷漆车间、绝尘室、零件清洗车间等。', 3, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (150, 8, '5.4', '带有温湿度要求的存储区域', NULL, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (151, 8, '5.4.1', '存储区域中的温度应该保持在5℃至40℃之间。湿度不得超80%。应持续记录温度和湿度。每天上、下午各记录一次。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (152, 8, '5.4.2', '每当湿度接近95％或在12小时内温度上升10℃或以上时应对零件和其包装物进行冷凝现象的抽查。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (153, 8, '5.4.3', '各部门按各自制定的超温湿度及冷凝现象处理措施处理。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (154, 8, '5.4.4', '橡胶材料和类似材料应该与散热器管道保持1米的间距，防止日晒或其他发光的紫外线照射。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (155, 8, '5.4.5', '有特殊存储温度要求的化工品应存放在封闭的化工品存储区域中，温度应该保持在5℃至25℃之间。需冷藏的化工品应在冰箱中储存。任何厂家的特殊要求都应当遵守。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (156, 8, '5.4.6', '没有特殊存储要求的化工品存放在通风良好的地方避免阳光直射。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (157, 8, '5.4.7', '不能相容的化工品应保证隔离存放。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (158, 8, '5.4.8', '像瓶子，金属罐，圆桶等包装物，应封严。塑料袋物不应破损，应封好。不应发生冷凝现象。当湿度接近95％时，应在特定的区域和容器内做冷凝现象的抽查。', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (159, 8, '5.4.9', '易燃材料应配置消防警告和设备。', 7, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (160, 8, '5.4.10', '对于带有温湿度要求的器材库房存储区域，根据生产厂家对器材的存储要求，有温湿度控制要求的器材必须储藏在特定的温湿度环境中：\n1）航空化工品应存放在化工库中，库房温度在5℃－25℃之间时，湿度≤75％；\n2）库房温度在5℃－30℃之间时，湿度≤80％；\n3）规定需要冷藏的器材，需放入冰箱中存储；\n4）规定需要冷冻的器材，需放入冰柜中存储；\n5）规定需要恒温储存的器材，需放入恒温箱中存储；', 2, '2018-06-28 09:50:02', '2018-06-28 09:50:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (161, 9, '5.1', '概述', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (162, 9, '5.1.1', '在飞机维修许可证规定范围内的维修过程中，房屋与设施在设计、使用及维护方面满足适航法规及其它相关行业规范要求，可提供状态良好、功能齐全的房屋和设施，以及确保房屋和设施不受气象环境因素影响，不发生环境及工作地点污染，具备必要的安全保护措施。', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (163, 9, '5.1.2', '根据适航法规的要求，当厂房设施的变动影响到批准的维修能力时，分公司必须获得相关局方的批准，并根据风险管理程序要求进行系统和工作分析。', 8, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (164, 9, '5.1.3', '确保房屋处于受控状态以满足生产需要，明确房屋、设施正确使用流程中的相关部门的职责。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (165, 9, '5.1.4', '确保设施设备处于受控状态以满足生产需要，明确相关设施设备正确使用流程中的相关部门的职责。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (166, 9, '5.2', '使用中的维护', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (167, 9, '5.2.1', '非计划性检修', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (168, 9, '5.2.1.1', '如厂房在使用中出现问题，应及时报综合管理分部进行修理；如设施设备运行中出现故障，应及时报生产支援分部进行修理。', 10, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (169, 9, '5.2.1.2', '综合管理分部负责联系落实厂房的修理。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (170, 9, '5.2.1.3', '生产支援分部负责联系落实设施设备的修理。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (171, 9, '5.2.2', '计划性检修', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (172, 9, '5.2.2.1', '分公司所有房屋与设施都应纳入预防性维护计划，报请属地分公司批准，对维护计划实施闭环管理。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (173, 9, '5.2.2.2', '每一年的第三季度要做出下一年度房屋的维修改造计划和费用估算，填写《基建维修改造项目申请单》Form No.: CS-005, 及《基建维修改造项目汇总表》Form No.: CS-006。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (174, 9, '5.2.2.3', '每一年的第三季度要做出下一年度设施的维修改造计划和费用估算，填写《基建维修改造项目申请单》及《基建维修改造项目汇总表》，并提交综合管理分部。房屋设施的管控职责切分见本程序附件7.2.1。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (175, 9, '5.2.2.4', '将5.2.2.2和5.2.2.3中的表格进行合并，并提交综合管理部进行整体评估，以确定下一年度房屋设施维修费用预算。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (176, 9, '5.3', '特种设备', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (177, 9, '5.3.1', '特种设备的安装、检验、使用、大修和报废等必须符合国家相关法规和标准并由政府质量技术监督部门认可。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (178, 9, '5.3.2', '凡属特种设备的购买、安装、大修和报废须向运行管理部报备。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (179, 9, '5.3.3', '由符合资质的专业公司对电梯、天吊、机库大门等进行定期维护保养和检修工作。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (180, 9, '5.3.4', '特种设备，如：压力容器、起重机和电梯等，应定期报政府质量技术监督部门进行技术安全检测和取证。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (181, 9, '5.3.5', '只有具备合格证且在有效期内的特种设备才能被使用，并要严格执行安全操作规程。', 9, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (182, 9, '5.3.6', '负责起吊所用吊具（包括吊架和吊索）的定检和维修，并按规范要求检查和测试允许承载的负荷。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (183, 9, '5.3.7', '若特种设备严重腐蚀变形、有裂纹、丧失整体稳定性，无法修复或无修复价值时，应及时报废。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (184, 9, '5.3.8', '对报废特种设备负责执行强制解体或委托处理。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (185, 9, '5.4', '消防设施', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (186, 9, '5.4.1', '不得擅自拆改消防设施，变动消防器材位置，禁止封、挡、盖、遮、压消防设施。', 10, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (187, 9, '5.4.2', '按相关要求实施定期消防检查及检测。', 7, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (188, 9, '5.5', '电气系统', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (189, 9, '5.5.1', '变（配）电所高压配电装置应根据设备环境情况、负荷重要程度及负载运行情况等条件安排设备的清扫检查工作。一般情况下，至少每年一次。若需停电，应提前通知生产计划控制分部。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (190, 9, '5.5.2', '不得私自连接或更改供电线路, 用电设备严禁超负荷或带故障运行。', 10, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (191, 9, '5.6', '环境保护', NULL, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (192, 9, '5.6.1', '严格执行国家和地方政府的有关环保法令法规。', 10, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (193, 9, '5.6.2', '禁止向下水道、地面及垃圾箱倾倒有毒、有害废弃物和危险品，包括：油类、漆类、酸碱液、清洗液、稀料、褪漆液、褪漆渣、废水站污泥、荧光液、废电池、医用废弃物、过期化学药品等，应放入专用危险废物收集容器中，保证无漏洒，无污染。生产支援分部负责后续处理。', 10, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (194, 9, '5.6.3', '禁止进行室外喷漆、砂磨及焚烧有毒、有害废弃物。', 10, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (195, 9, '5.6.4', '负责各部门有毒有害废液和废弃物的合规清运和处置。', 5, '2018-07-02 14:56:15', '2018-07-02 14:56:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (196, 10, '5.1', '等效工具设备的选择', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (197, 10, '5.1.1', '当分公司不具备飞机、发动机或附件制造厂家手册推荐使用的工具/设备或有必要采用其它工具/设备时，生产支援分部应根据《Ameco等效替代工具/设备清单》Form No.: EM-003，查找是否有经批准的等效替代工具/设备。', 5, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (198, 10, '5.1.2', '当《Ameco等效替代工具/设备清单》未列出适合的等效工具/设备时，应遵守本程序的要求对申请的等效替代工具/设备进行申请，并获得相关部门的批准。', 5, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (199, 10, '5.2', '等效工具设备的评估', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (200, 10, '5.2.1', '已有替代关系的评估\n当技术支援分部收到厂家技术手册的改版时，评估此次手册改版内容是否涉及按照先前版次手册批准的工具/设备替代关系。\n1）如涉及，修改等效工具/设备清单 \n2）如不涉及，则基于先前版次批准的替代关系仍然有效，无需修改。', 4, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (201, 10, '5.2.2', '新申请替代关系的评估', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (202, 10, '5.2.2.1', '生产支援分部填写《工具/设备等效替代评估表》Form No.: EM-001R1中相应栏目。\r\n注1：对于表格中不适用于CAAC放行要求的栏目填写N/A。\r\n注2：当EDOC系统无法正常使用时，生产支援分部需按照现行有效的《工具/设备等效替代评估表》提交纸张版申请，并按照以下流程进行评估，审批。当EDOC系统重新启用时，生产支援分部需按照已批准的《工具/设备等效替代评估表》在系统中重新申请，并将其作为申请资料添加在附件中，同时系统中提交的《工具/设备等效替代评估表》中的信息必须与批准的纸张版表格的信息保持一致（包括附加的申请材料）。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (203, 10, '5.2.2.2', '生产支援分部将完成评估内容的《工具/设备等效替代评估表》报技术支援分部进行证实。\n注：技术支援分部具有Ⅰ类、Ⅱ类和Ⅲ类等效替代的批准权利。', 5, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (204, 10, '5.2.2.3', '当等效替代工具/设备影响施工程序和步骤时，应将客户化施工指南或工卡与等效替代工具/设备评估文件一并进行报批。', 5, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (205, 10, '5.2.2.4', 'II类和III类等效替代工具/设备制造完毕后，应使用《工具/设备技术验收单》Form No.: EM-002R1对其执行技术验收，以确保图纸/指南和相关技术文件中的各项要求可以得到满足。同时对等效工具/设备进行验证，并且将验证结果和OEM要求的结果进行对比，验证过程中的数据资料作为等效结论的支持材料一并存档。\r\n注：如《工具/设备技术验收单》要求对工具/设备进行实物功能测试，且实物验证发现与当初审批技术要求标准有差异，对工具/设备的修复改变了该工具/设备等效替代报批文件中的任何技术指标、参数和图纸等，需要取消已批准的工具/设备等效替代申请，并对修复后的工具/设备重新办理等效替代审批手续。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (206, 10, '5.3', '等效工具设备的审批', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (207, 10, '5.3.1', '对于专门用于确认航空器及其部件最终放行的Ⅰ类、Ⅱ类、Ⅲ等效替代工具/设备：', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (208, 10, '5.3.1.1', '技术支援分部检查《工具/设备等效替代评估表》及根据所提供的等效性相关技术文件，确认替代工具/设备与厂家推荐的工具/设备在完成特定维修任务时，在功能上是否是等效或高于的，对所涉及的维修产品的质量和安全是否有不利影响。\r\n如同意等效替代，在“等效性证实结论”栏内填写评估结果和答复/决定记录，在“证实”栏内签字。并提交分部经理审核。\r\n如不同意等效替代，系统工程师将《工具/设备等效替代评估表》注明意见并退回到申请人。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (209, 10, '5.3.1.2', '分部经理审核未通过则退回申请部门；审核通过则提交分公司主管副总经理批准，批准完后报航空安全质量分部。', 4, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (210, 10, '5.3.1.3', '航空安全质量分部将收到的《工具/设备等效替代评估表》报局方批准或认可。\n如不批准，则在《工具/设备等效替代评估表》上注明意见，并退回给技术支援分部。', 8, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (211, 10, '5.3.2', '对于非专门用于确认航空器及其部件最终放行的Ⅰ类和Ⅱ、Ⅲ等效替代工具/设备：', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (212, 10, '5.3.2.1', '技术支援分部检查《工具/设备等效替代评估表》及根据所提供的等效性相关技术文件，确认替代工具/设备与厂家推荐的工具/设备在完成特定维修任务时，在功能上是否是等效或高于的，对所涉及的维修产品的质量和安全是否有不利影响。\r\n如同意等效替代，在“等效性证实结论”栏内填写评估结果和答复/决定记录，在“证实”栏内签字。并提交分部经理审核。\r\n如不同意等效替代，系统工程师将《工具/设备等效替代评估表》注明意见并退回到申请人。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (213, 10, '5.3.2.2', '分部经理审核未通过则退回申请部门；审核通过则提交分公司主管副总经理。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (214, 10, '5.3.2.3', '分公司主管副总经理在“批准”栏内签署批准。\r\n如不批准，则在《工具/设备等效替代评估表》上注明意见，并退回给技术支援分部，再由技术支援分部退回到申请人。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (215, 10, '5.4', '等效工具设备的清单', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (216, 10, '5.4.1', '批准后的工具/设备替代加入《Ameco等效替代工具/设备清单》Form No.：EM-003并给予发布。同时通知到工具/设备替代的申请人。', 4, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (217, 10, '5.4.2', '等效替代工具/设备得到批准后，技术支援分部修改受影响的维修工作文件，在维修工作文件（工卡、施工指南等）中应列出厂家手册推荐的工具/设备和经过批准的等效替代工具/设备。', 4, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (218, 10, '5.4.3', '如采用等效替代工具/设备对厂家手册施工程序有影响，技术支援分部将得到批准的客户化施工指南附在相关的维修工作文件（工卡、施工指南等）上面，替代工卡、施工指南中厂家手册中的相应内容。', 4, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (219, 10, '5.5', '等效工具设备的取消', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (220, 10, '5.5.1', '在维修生产活动中，如发现：\r\n1）由于参考技术手册的改版、工具/设备等效替代适用的飞机或附件不存在。或\r\n2）其它原因导致已批准的等效替代工具/设备应被取消时。\r\n生产支援分部应通知技术支援分部将已批准的等效替代工具/设备取消，在内部发文中应明确取消的具体原因，内部发文经生产支援分部经理签署后提交给技术支援分部。', NULL, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (221, 10, '5.5.2', '技术支援分部按照批准的内部发文的要求更新“工具/设备等效替代清单”，在清单中标识出取消的等效替代工具/设备和取消的日期，例如：根据xxxx内部发文，自YYYY-MM-DD起失效。该内部发文同原有《工具/设备等效替代评估表》一同存档。', 4, '2018-06-28 10:08:41', '2018-06-28 10:08:41', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (222, 11, '5.1', '通则要求', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (223, 11, '5.1.1', '负责将本单位最新的计量器具清单公布在分公司内网。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (224, 11, '5.1.2', '为保证计量器具达到所要求的准确度等级，应对其进行定期校验,使之可用并达到相应的标准。无论是通过内部校验或外部校验，最终将计量器具溯源至适航当局接受的国家计量院。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (225, 11, '5.1.3', '彩色标记系统（黄色标记除外）应向使用者明确地表明计量器具的校验状态：该计量器具是否可用或不可用，并且对限用的计量器具应注明限制范围。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (226, 11, '5.1.4', 'C类计量器具只做初次校验不做重复校验。如有故障，应及时送校。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (227, 11, '5.1.5', '对于长期（通常指1年以上）不用的计量器具，经校验合格后，可进行封存处理。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (228, 11, '5.1.6', '应建立监控系统以保障计量器具不会超过规定的校验周期。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (229, 11, '5.1.7', 'Ameco内部校验工作应由获得Ameco质量经理或其授权的人员授权计量检测人员或在授权的计量检测人员监督下进行；Ameco外委校验工作应由授权的供应商执行。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (230, 11, '5.2', '新计量器具的采购及首次校验', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (231, 11, '5.2.1', '按照生产实际需求制定计量器具的采购计划并完成获批的采购项目。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (232, 11, '5.2.2', '接收检查新采购的计量器具，确保符合订单要求及是否有因运输或其它原因造成的明显损坏。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (233, 11, '5.2.3', '新计量器具必须进行首次校验，如一项或全部不符合要求的出具校验数据并注明不合格项目交送校单位办理退货。购于国外的计量器具也应进行首次校验，如果该计量器具只有生产厂家有校验能力并且出厂校验证书是在有效期内的则不需首次校验。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (234, 11, '5.2.4', '填写《计量器具技术要求表》Form No.: CT-002，对该计量器具的类别、校验周期、校验参数、测量范围和准确度要求进行评估并提供原始校验证书和现行有效的计量器具生产厂家技术文件。同时，将该计量器具随《校验委托单》Form No.: CT-003送计量检测单位校验。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (235, 11, '5.2.5', '负责接受和维护计量器具生产厂家手册，通告和说明书等相关技术文件，将新增计量器具信息通知航空安全质量分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (236, 11, '5.2.6', '建立新增计量器具《计量器具监控卡》Form No.: CT-005，在网上更新计量器具清单信息。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (237, 11, '5.3', '计量器具的控制', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (238, 11, '5.3.1', '建立计量器具监控卡系统, 并每月检查监控卡，根据批准的技术文件对计量器具执行到期校验，确保即将到期的计量器具能够及时地送到计量检测单位。计算机监控系统是计量器具监控的辅助工具。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (239, 11, '5.3.2', '负责维护计量器具生产厂家手册，通告和说明书等相关技术文件，同时确保这些资料是现行有效版本。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (240, 11, '5.3.3', '计量器具周检', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (241, 11, '5.3.3.1', '每月20日之前制定《计量器具周检控制单》Form No.: CT-T01发生产支援分部；《计量器具周检控制单》的内容包括：序号，类别，名称，出厂编号，津航编号，使用人，有效期，上缴者，上缴日期，检定结果，检定日期，领用者，建议有效期等。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (242, 11, '5.3.3.2', '接到《计量器具周检控制单》后，在保证不影响日常工作的前提下，制定送检计划并将送检信息反馈生产计划控制分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (243, 11, '5.3.3.3', '按照《计量器具周检控制单》Form No.: CT-T02，对到期设备按照计量器具送检清单核对，按照送检计划在计量器具有效期之前完成到期计量器具收缴和出库下架工作，并及时将器具送往有资质的计量检测单位完成检定工作。工具房架位上的计量器具送检后在架位上贴上送检标签，并通知当日值班人员。\n有资质的计量检测单位包括：\n计量器具生产厂\n得到中国合格评定国家认可委员会（CNAS）或国际实验室合作认可组织中的认可机构（ILAC）认可的校验机构\n由政府计量行政主管部门授权的检定机构', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (244, 11, '5.3.3.4', '到期未送检的计量器具应隔离存放，存放地点有明显可见的待检标志。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (245, 11, '5.3.3.5', '所有送到计量检测单位校验的计量器具都应加以适当保护，以防止计量器具在运输过程中的损坏。例如：管路接头应用堵头或包装进行保护、氧气压力表应单独包装防止被油污染。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (246, 11, '5.3.3.6', '在计量器具送检后的20个工作日内完成收回，查验计量器具的校验记录。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (247, 11, '5.3.3.7', '收回后3个工作日内通知航空安全质量分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (248, 11, '5.3.3.8', '按照计量器具的校验记录并存档，于5个工作日内依据5.6完成色标粘贴工作，色标应粘贴于计量器具明显位置，且不易损毁、脱落。校验记录至少包括以下内容：\n1）计量器具识别信息；\n2）完成校验工作的人员姓名；\n3）校验日期；\n4）有效期；\n5）校验中使用的计量标准器具；\n6）校验中依据的方法；\n7）校验结果；\n8）发现的问题；\n9）溯源声明。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (249, 11, '5.3.3.9', '填写《计量器具监控卡》。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (250, 11, '5.3.3.10', '负责将校验好的计量器具分发或入库上架，放回的架位上撤掉送检标签，并通知当日机库值班人员；将信息反馈生产计划控制分部。填写《计量器具周检控制单》反馈航空安全质量分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (251, 11, '5.3.3.11', '填写《计量器具周检控制单》并存档。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (252, 11, '5.4', '校验周期的制定和评估', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (253, 11, '5.4.1', '校验周期确定原则\n1）计量器具生产厂家或其原始校验证书的建议作为首选的校验周期；\n2）如果没有上述建议，根据计量检测系统中同型或同类针对同种工作类别的计量器具的校验周期而定；\n3）根据统计记录调整校验周期要得到航空安全质量分部的批准。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (254, 11, '5.4.2', '对于新的或降级使用的计量器具，根据厂家要求及实际使用频度、用途和稳定性，确定计量器具的校验周期、准确度要求和分类等级。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (255, 11, '5.4.3', '为保证计量器具在校验周期中的可用性和准确性，应对历次校验结果进行评估，以确定校验周期的适宜性。统计记录应包括：该计量器具或该类计量器具的校验合格情况，超差的说明以及超差的数据记录。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (256, 11, '5.4.4', '依据生产部门的经验及前3次的校验结果，校验周期是可以更改的。校验周期的改变要经部门经理批准。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (257, 11, '5.4.5', '如果发现某计量器具连续2次超差，应缩短该计量器具的校验周期并找出其超差原因。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (258, 11, '5.5', '计量器具的使用和管理', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (259, 11, '5.5.1', '工作者在使用计量器具前应检查是否有明显的损坏，封印是否完好，以及是否在校验有效期内。', 9, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (260, 11, '5.5.2', '使用贴有限用证的计量器具时要特别小心，不能违背相关技术文件的要求，如AMM、CMM等。', 9, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (261, 11, '5.5.3', '临时性借用的计量器具应先检查其校验类别、有效期及准确度等级，Ameco内部相互借用的计量器具类别不能低于所开展工作要求的类别。', 9, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (262, 11, '5.5.4', '不合格的计量器具', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (263, 11, '5.5.4.1', '发现不合格计量器具应停用，并立即报送生产支援分部。不合格计量器具指：\r\n1）被损坏；\r\n 2）过载或误操作；  \r\n3）显示不正常；\r\n4）功能是否正常受到怀疑；\r\n5）超过了规定的校验周期；\r\n6）彩色标记完整性已被损坏。', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (264, 11, '5.5.4.2', '不合格计量器具应与可用的计量器具隔离存放，在适当的位置悬挂工具设备不可用挂签，注明不可用原因，需修理的还应注明故障原因，并及时送计量检测单位作相应处理。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (265, 11, '5.5.4.3.1', '彩色标记完整性损坏的处理\n1）发现人员立即通知生产支援分部；', 9, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (266, 11, '5.5.4.3.2', '2）生产支援分部确认为可用计量器具后粘贴临时绿色标记，并于下一个工作日内通知航空安全质量分部。临时绿色标记应注明计量器具序号、粘贴人和粘贴日期；', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (267, 11, '5.5.4.3.3', '3）航空安全质量分部接到信息后3个工作日内按5.6相关规定粘贴新的彩色标记。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (268, 11, '5.5.4.4', '任何到期校验、调试、修理后的计量器具，其技术指标仍无法满足相关校验要求的，填写《计量器具报废评估单》Form No.: CT-010，经部门经理确认后，按照工具设备报废程序执行，并将报废处置文件反馈航空安全质量分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (269, 11, '5.5.5', '计量器具的封存和启封', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (270, 11, '5.5.5.1', '长时间（通常指一年以上）不用的计量器具，经部门经理确认后可申请封存，填写《计量器具封存申请表》交航空安全质量分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (271, 11, '5.5.5.2', '对相应计量器具进行封存处理，按5.6粘贴色标。封存的计量器具必须是经校验合格的。在有效期内未使用过的无需进行校验可直接进行封存。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (272, 11, '5.5.5.3', '封存后的计量器具由生产支援分部负责保存，在启封前仍需进行校验以保证计量器具的安全和质量。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (273, 11, '5.5.6', '计量器具的调拨\n如需对在用的计量器具进行调拨，完成调拨后须填写《计量器具调拨通知单》Form No.: CT-T03通知航空安全质量分部。', 5, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (274, 11, '5.5.7', '接到报废处置文件、《计量器具封存申请单》或《计量器具调拨通知单》后更新《计量器具监控卡》和计量器具清单信息并保存相关记录。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (275, 11, '5.6', '彩色标记', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (276, 11, '5.6.1', '计量器具管理人员按照检测单位的校验记录及标签贴如下的不干胶标记：\r\n1）绿色标记（合格证）\r\nA、B类绿色标记：校验合格的计量器具。\r\nC类绿色标记：这种标记适用于C类计量器具，表示该计量器具满足校验要求，但上面不填写校验日期。\r\n2）绿色计量标准器标记：用于计量行政部门颁发的计量标准考核证书中主计量标准器。\r\n3）蓝色标记（限用证）\r\n表示该计量器具的使用范围是受限制的。限用范围由计量检测单位的相关人员在记录（中英文）和限用证（中文）上标注限制范围（应清楚地标注可使用范围或不可使用范围）。使用贴有限用证的计量器具要特别小心不能违背依据文件的要求。\r\n4）紫色标记（封存证）\r\n用于暂时不用的计量器具。\r\n5）红色标记（禁用证）\r\n因各种原因不允许使用的计量器具。\r\n6）红色封印\r\n为防止任意调节，在调节钮及从计量器具外容易接触的点上应贴上封印标记，任何非法调节可使标记损坏，表明计量器具的不可用。\r\n7）黄色标记（可用性检查）\r\n提醒使用者此类计量器具在使用前必须进行可用性检查。', NULL, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (277, 11, '5.6.2', '标记的填写\n除计量标准器标记、C类标记、红色封印和黄色标记，其它标记应按适用性填写下列内容：\n1）设备号；                                                  \n2）到期日；\n3）封存或禁用计量器具或标准器的日期；\n4）责任人签字；\n5）在圆圈中填写计量器具的类别。', 8, '2018-06-28 10:25:35', '2018-06-28 10:25:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (278, 12, '5.1', '概述\n航材与采购部天津分部的责任就是保证航班运行和机务维修生产所需的航材库存，所有器材应有计划的订购，保持合理库存资金。本程序涉及Ameco产权消耗件的计划工作，周转件和可修件计划可参考客户化手册。', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (279, 12, '5.2', '器材', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (280, 12, '5.2.1', '器材——分类', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (281, 12, '5.2.1.1', '周转件\n周转件是复杂附件具有以下特征：\n1）通常无限次数的修理；\n2）通常无报废可能；\n3）有唯一对应序号可以控制；\n4）维护中更换。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (282, 12, '5.2.1.2', '可修件\n可修件是从技术和经济上讲可以进行修理的附件。\n1）在通常情况下，没有必要对具体序号进行跟踪；\n2）有修理次数极限，可能报废。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (283, 12, '5.2.1.3', '消耗件\n消耗性器材是不能修理的，并且在拆下和检查结果为是不可用之后便报废。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (284, 12, '5.2.2', '器材——物料主数据', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (285, 12, '5.2.2.1', '物料主数据\n器材以物料主数据的形式在SAP系统中表示。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (286, 12, '5.2.2.2', '物料主数据信息\n物料主数据至少应明确如下信息：\n1）件号；\n（1）内部件号；\n（2）外部制造厂商的件号，即列于制造厂商文件中的件号。\n2）样式、型号、序号 (如适用)；\n3）描述：制造厂商文件中所给出的器材名称；\n4）制造厂商；\n5）工厂；\n6）物料组；\n7）MRP 控制员；\n8）MRP 类型；\n9）数量和度量单位；\n10）计划参数；\n（1）再订货点；\n（2）安全库存点；\n（3）最大库存点。\n11）是否需要检验。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (287, 12, '5.2.3', '器材 - 计划参数与公式', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (288, 12, '5.2.3.1', '周转件和可修件的计划参考客户化手册。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (289, 12, '5.2.3.2', '消耗件的计划', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (290, 12, '5.2.3.2.1', '1）消耗件计划参数\r\n消耗性器材备用量的计划方法基于以下的参数：\r\n（1）年需求量 (YD)\r\n预计的一年的需求量。数据来自于前几年的需求量并考虑到可能发生的变化。\r\n（2）月消耗量 (MC)\r\nA.过去某一时间段的平均消耗量。\r\nB.参考平均消耗量、特殊情况和未来的工作量的增加来预测消耗量。\r\n（3）现库存量 (CSL)\r\nSAP 提供现库存量CSL。\r\n（4）安全量 (SL)\r\nSL 由紧急情况下的再供应时间体现\r\n（5）订货点 (ROP)\r\nROP 由正常状态下的供货时间决定并应满足在正常供货时间内的消耗量加上安全量。\r\n（6）重新订货量 (ROQ)\r\nROQ 由预计需求量、再提供时间和库存量决定。\r\n（7）库存峰值点 (MSL)\r\n库存峰值点是SL 与ROQ 之和。\r\n（8）最低订货量 (MOQ)\r\nMOQ 由供应商定义，当定义MSL 和计算ROQ 时必须被考虑。\r\n（9）供货时间 (RT) [按月计算]\r\n供货时间被定义为从发布采购申请到获得所订购的器材之间所需的时间。包括：\r\nA.计划、采购和批准的内部管理时间\r\nB.厂商 / 供应商的交货期\r\nC.运输时间和报关验收工作时间\r\nD.内部器材处理\r\n（10）订货合同成本（POC）[以每份合同计量]\r\n（11）源自订货合同的成本包括：\r\nA.人工成本\r\nB.订货附加成本\r\n（12）存储成本率（SCR）[以百分比计量]\r\n存储成本率是一个固定的比率，由计算出的成本计算得出。\r\n（13）价格（P）\r\n此价格指的是器材的单价。', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (291, 12, '5.2.3.2.2', '2）消耗件的计划方法和公式\r\n总的备件数量是根据此后所描述的各因素复杂的相互关系来计算的。\r\n当定义库存峰值点（MSL）、安全量（SL）、订货点（ROP）和重新订货量（ROQ）时这些因素应被考虑，针对每一个件号都应被定义的要素：\r\n（1）供应商信息\r\nA.交货时间\r\nB.最低订货量\r\nC.更高数量的总额折扣\r\nD.器材价格\r\n（2）订货\r\nA.人工成本\r\nB.订货附加成本 (SAP, 通信等等)\r\n（3）接收和运输\r\nA.接收成本\r\nB.操作成本\r\nC.报关成本\r\nD.运输成本\r\n（4）生产\r\nA.月消耗量\r\nB.缺件对于生产的影响 (最坏状况：停产)\r\n（5）存储\r\nA.存储成本\r\nB.存储空间限制\r\n（6）停场计划中的季节依赖或改变\r\nA.供应商市场\r\nB.生产\r\n这些因素可能随时间而改变，例如生产中的生产程序的改变，增加的工作量，致使停产的生产能力的损失或缺乏国际市场份额等。这影响到计划数据的有效性并导致每次发布采购申请时都必须核实这些数据。\r\n考虑所有因素和他们的复杂关系和定义公式一样难，总的备件数量可以通过一个简化线性计算模式的基本公式来计算。这些公式应被应用于获得根据前面所提及的因素能够被调整的默认值。\r\n从简化模式的图表可以获得一个对于计划方法的粗略认识。（图标参见程序）\r\n标准计算规则如下：\r\n（7）安全量SL \r\nSL应覆盖未预料需求和到货延迟的紧急情况下的需求。因此供应商的RT应被考虑在内。可运用以下基本规则：\r\nSL = 1/2 x MC x RT\r\n（8）订货点 ROP \r\nROP 应满足以覆盖被估算的RT和SL期间的消耗量。\r\nROP = SL + (MC x RT)\r\n基础计算规则是：\r\nROP = 3/2 x (MC x RT)\r\n（9）重新订货量 ROQ\r\nROQ取决于供应商的MOQ，也就是MSL。\r\n应用如下基础规则，ROQ可以计算得出：\r\nROQ = MSL - CSL + (MC x RT)\r\n（10）库存峰值点 MSL\r\nMSL取决于库存成本、MOQ、高购买量的折扣和RT。\r\n根据上面的线形计算图形可以得出MSL是ROQ与SL的和：\r\nMSL = ROQ + SL\r\n使用一个更基础的规则，MSL可以计算得出：\r\nMSL = SL + (2 x MC x RT) = 5/2 x (MC x RT)\r\n下面图表为：ROP、SL、MSL与RT之间的关系基准。（图标参见程序）', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (292, 12, '5.3', '计划程序', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (293, 12, '5.3.1', '责任分工\n航材与采购部天津分部按照航材计划与采购部制定的航材计划业务标准落实本区域内航材计划的相关工作。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (294, 12, '5.3.2', '器材需求', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (295, 12, '5.3.2.1', '天津航材分部根据例行工作的器材需求，在系统中设立、修改再订货起订点，以满足器材需要。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (296, 12, '5.3.2.2', '计划性需求\n飞机计划性改装、排故所需器材信息，由生产计划控制分部以《维修指令器材设备需求单》Form No.: MM-T01形式将需求信息通知航材与采购部天津分部。', 6, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (297, 12, '5.3.2.3', '维修中缺料需求\n维修工作中的缺料需求，维修中队以《Ameco天津分公司航材需求单》Form No.: MM-T02形式由维修中队经理批准后将提出需求信息通知航材与采购天津分部。', 9, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (298, 12, '5.3.2.3.1', '1）航材与采购天津分部，依据《Ameco天津分公司航材需求单》紧急程度中“紧急”、“补充库存”，实施器材采购。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (299, 12, '5.3.2.3.2', '2）如为“后续工作”，维修中队需在“缺陷描述”栏中填写具体缺陷部位、内容等信息，并将《Ameco天津分公司航材需求单》交生产计划控制分部。', 9, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (300, 12, '5.3.2.3.3', '3）将《Ameco天津分公司航材需求单》按要求分别交：第一联航材与采购天津分部、第二联自留、第三联给技术支援分部。', 6, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (301, 12, '5.3.2.3.4', '4）技术支援分部在10个工作日内，依据“缺陷描述”，下发相应NR给生产计划控制分部。', 4, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (302, 12, '5.3.2.3.5', '5）生产计划控制分部，根据飞机维修计划，安排维修中队实施NR，以完成“后续工作”。', 6, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (303, 12, '5.3.2.4', '航材与采购部天津分部依据以上需求，制定器材采购计划。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (304, 12, '5.3.3', '如果系统中不存在该器材的物料主数据，由航材与采购部天津分部人员先维护物料主数据。\n根据备件情况和航材缺件的急缓程度，航材与采购部天津分部选择催货、内部调拨、租借、采购等措施。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (305, 12, '5.3.4', '剩余器材', NULL, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (306, 12, '5.3.4.1', '剩余器材是指3年内没有发生过移动（收料、发料、退料）的消耗件。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (307, 12, '5.3.4.2', '以下原因潜在剩余器材的库存应被继续保持：\n1）MEL 清单中的“不放行项目”；\n2）稀少的备件；\n3）维修单位有其特殊需求的器材。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (308, 12, '5.3.4.3', '由于下列原因器材应被考虑为剩余：\n1）不再被消耗的器材 （例如：用于被淘汰型号的飞机部件）；\n2）对于器材的供应合同期满。', 2, '2018-07-03 14:00:50', '2018-07-03 14:00:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (309, 13, '5.1', '概述\n航材与采购部天津分部的责任是确保在最佳条件下及时购买需求的器材，最佳条件包括：器材价格、质量、交货时间、售后服务、索赔与保障等。', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (310, 13, '5.2', '供应商选择\n对于飞机器材来说，航材与采购部天津分部只允许向列在经 Ameco批准的供货商清单中的供货商或有效的技术手册中所述的厂家购买。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (311, 13, '5.3', '需要供应商提供的重要信息和文件', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (312, 13, '5.3.1', '1）生产日期，库寿时间和储存要求；\n2）有关延长库寿的试验数据；\n3）包装说明；\n4）时寿件的时寿信息。', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (313, 13, '5.3.2', '符合性文件\n适航放行表格，如AAC-038，FAA 8130-3 或 EASA FORM 1；\n标准件，原材料，化工品最低需要符合性声明。', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (314, 13, '5.3.3', '当采购化工品时，应要求供货商提供化工品安全数据单（MSDS）。并且应要求每批到货均附有MSDS（由于MSDS 的版本有可能更新）。', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (315, 13, '5.3.4', '订购使用过的件, 要求供货商根据适用情况，提供下列资料和信息：\r\n1）使用经历和执行过的维修、零部件更换的记录；\r\n2）适航指令状况；\r\n3）服务通告的执行状态；\r\n4）时限/循环寿命（如使用时间、翻修后的使用时间、循环，及能证实其历史状况的记录文件）；\r\n5）库存寿命数据限制，包括制造日期或硫化日期；\r\n6）返回使用日期；\r\n7）组件或器材包的缺件状况；\r\n8）重要修理和重要改装记录；\r\n9）以往出现过的不正常情况,如：过载、意外终止使用、过热、重大的故障或事故；\r\n10）声明不是从可能不明确损伤的事故航空器上拆下的部件；\r\n11）特殊要求。', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (316, 13, '5.4', '采购流程', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (317, 13, '5.4.1', '询价\n询价基于公平、公正原则，询价时对供应商采用一次性、限时报价。\n航材与采购部天津分部依据采购需求，每项航材订货需同时向不少于三家供货商发出询价；对于决定直接向原生产厂（OEM）或者其独家代理商订货的项目，有该原生产厂家的有效价格目录时，不再询价；没有该原生产厂家的有效价格目录时，应向该原生产厂家询价后再订货。\nAOG情况下，航材与采购部天津分部可以不经过询价和报价直接创建合同。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (318, 13, '5.4.2', '报价的选择\n择优选择价格合理、交货期满足需求、航材订货文件符合适航规定的供货商。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (319, 13, '5.4.3', '采购合同\n当比价完成以后，订货员在SAP 系统中创建采购合同。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (320, 13, '5.4.4', '合同审批\n合同创建后，航材与采购部天津分部应根据“Ameco 内部审批权限”申请合同审批，审批后将订货合同发送给供应商。如合同申请被拒绝，需要修改合同再申请审批或调整采购需求。在紧急订货情况下，航材与采购部天津分部可以不经过批准直接发出，但发出后航材与采购部天津分部要尽快申报相关经理进行审批。\n如合同申请被拒绝，需要修改合同再申请审批或调整采购需求。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (321, 13, '5.4.5', '采购合同的跟踪\n航材与采购部天津分部应与供货商或AIE随时保持联系跟踪订货合同的状况，如果没有供货商的确认信息或其它相关的有效信息，航材与采购部天津分部应与供货商联络，以便供货商可以更新合同的状态并按申请部门要求的交货期限及时交货。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (322, 13, '5.4.6', '差异处理\n在订货器材和到货器材存在任何差异的情况下，航材与采购部天津分部应证实相关问题，并应立即采取措施解决差异。', 2, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (323, 13, '5.5', '采购可用件\r\n当采购部门需要采购可用件时，航材与采购部天津分部需要提前了解器材的状态，包括器材的来源等(具体见合同要求的资料和信息)。如需要确认器材的件号及构型等要求时,向技术支援分部的工程师咨询。\r\n采购可用件时，在订货合同中应明确要求供应商提供下列资料及信息：\r\n1）有相关资料证明此件不是从发生事故或非正常事件的航空器或发动机上拆下的零附件，未出现不正常情况，例如:过载，意外终止使用，过热,重大的故障和事故等。\r\n2）拆件人提供的器材的拆件挂签或其它能够证明器材来源的等效凭证（证明器材从哪架航空器或哪台发动机拆下，包括件号，序号，数量，日期）。\r\n3）时限/循环（仅适用于时寿件，包括总使用时间、循环数，翻修后的使用时间，循环数及能证明其历史状况的记录文件—BTOB）。\r\n4）如果供应商未提供上述资料及信息中的任何一项,则不允许采购。', NULL, '2018-06-28 10:34:11', '2018-06-28 10:34:11', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (324, 14, '5.1', '器材入库', NULL, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (325, 14, '5.1.1', '航材与采购部天津分部接收经验收合格的器材。应根据伴随文件检查器材的件号、序号（如适用）、批次、数量，是否在运输过程中有损坏，同时确认其附件标识挂签盖有器材检验员的印章，确保该附件已验收合格。危险化学品应附上对应的安全数据单。如有差异，将器材及其文件退回相关部门处理。附件应与其附件标识挂签一起放入库房架位。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (326, 14, '5.1.2', '航材与采购部天津分部检查随附的文件是否齐备、与实物一致，库寿件是否符合库存要求。若发现差异，将器材及其文件退回相关部门处理。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (327, 14, '5.1.3', '防静电器材应包装在防静电袋中。在需要打开防静电器材包装检查件号等时，必须使用腕带在防静电工作台上进行操作。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (328, 14, '5.1.4', '航材与采购部天津分部应即时将器材收料入库信息录入航材管理系统中，并完成登帐工作。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (329, 14, '5.1.5', '当日未完成上架的入库器材应统一存放在规定的区域，其入库单据也应该统一存放在规定位置。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (330, 14, '5.1.6', '每批器材应粘贴或附带可用器材标签，以保证器材的追溯性。粘贴时应尽量不覆盖或遮挡厂家信息。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (331, 14, '5.2', '器材出库', NULL, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (332, 14, '5.2.1', '生产领料', NULL, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (333, 14, '5.2.1.1', '各相关部门根据生产需求，由本部门经授权的人员在器材管理系统中做预留和确认，依据相关凭证到航材与采购部天津分部领料。', 6, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (334, 14, '5.2.1.2', '航材与采购部天津分部应遵照先进先出的原则发放器材，同时核对器材库存寿命、保管封存期是否符合使用要求，其外观、器材标识挂签等是否符合出库要求。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (335, 14, '5.2.1.3', '航材与采购部天津分部应即时将器材发料信息录入器材管理系统中，打印发料单据并核实单据信息与出库器材可用器材标签的批次是否一致。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (336, 14, '5.2.1.4', '附件器材出库时应附有“附件标识挂签”和可用SAP条码签,消耗件发出应有SAP条码标签.', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (337, 14, '5.2.1.5', '对于发出的原材料，在无法配备包装的情况下，将可用器材标签贴在原材料的一端或一角。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (338, 14, '5.2.1.6', '对于发出的化工品，需确保每个独立包装上贴有可用器材标签或打码标签。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (339, 14, '5.2.1.7', '对于发出的带有保质期的油料、化工品、胶带等物资，需确保每个胶带上或独立包装上贴有库寿标签或打码标签。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (340, 14, '5.2.1.8', '航材与采购部天津分部与领料人交接器材，核对器材外观是否完好，发料单与实物数量、件号、序号是否一致，领料人在打印的发料单据上签字。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (341, 14, '5.2.1.9', '对于还没有经过航材检验的紧急发料，正班时间航材库房管理员要通知航材检验人员，立即安排对该航材进行航材检验，检验合格后，可以从验收库直接发出。非正班时间航材人员要通知被授权的人员实施航材检验。航材发料人员系统外做发料并做好记录，下一个工作日由航材检验人员在系统内录入检验结果，航材发料人员在系统内补办发料手续。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (342, 14, '5.2.2', '国航附件发料', NULL, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (343, 14, '5.2.2.1', '发出的国航产权附件，应带有《附件标识挂签》Form No.: QM-053和发料单。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (344, 14, '5.2.2.2', '在AOG情况下，原始合格证复印件可代替发料单随附发出的器材。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (345, 14, '5.2.2.3', '航材与采购部天津分部在发改装包时，如果改装包库存信息里标有BOM标识（存在高价件），航材与采购部天津分部需查找器材包中器材包清单交给领料人。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (346, 14, '5.2.3', '指令性出库\n库房管理员凭航材计划人员通过系统打印的《器材出库指令单》Form No.: MM-001发料。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (347, 14, '5.3', '东区设备间器材的发放', NULL, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (348, 14, '5.3.1', '航线中队在领取东区设备间的器材时，生产支援分部监督航线中队人员填写领料单。', 5, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (349, 14, '5.3.2', '航线中队根据领料单在论坛上回复预留情况。', 9, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (350, 14, '5.3.3', '航材与采购部天津分部根据预留在航材系统发料。', 2, '2018-06-28 10:36:27', '2018-06-28 10:36:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (351, 15, '5.1', '计划性布局调配', NULL, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (352, 15, '5.1.1', '器材配置方案\n1）根据天津分公司机队规模、分布情况、飞机引进计划以及航线布局，器材的库存情况、维修周期、器材使用情况以及附件平均拆换间隔时间等数据制定配置方案。\n2）航材与采购部天津分部负责制定天津分公司器材配置方案以及调配的控制和监督。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (353, 15, '5.1.2', '创建调配订单\n确定器材配置方案后，航材与采购部天津分部通过航材管理系统创建调配订单。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (354, 15, '5.1.3', '航材与采购部天津分部将调配订单发给航材与采购部或航材与采购部各分部计划员，通知其安排调配出库。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (355, 15, '5.1.4', '器材的出库和发运\n库房依据《器材出库指令单》Form No.: MM-001办理器材的出库和发运手续，调配高价件须随附《附件标识挂签》。《器材出库指令单》一式两份，一份随调配器材发运，一份库房存档。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (356, 15, '5.1.5', '器材接收\n航材库房接到调入器材的发运信息后，安排器材的提运，根据调配订单验收入库，检查实物外观，并核对《附件标识挂签》等文件和实物是否相符。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (357, 15, '5.1.6', '器材验收\n按照01-08-001《器材检验程序》完成器材验收。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (358, 15, '5.2', '缺料情况下器材调配', NULL, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (359, 15, '5.2.1', '创建调拨申请\n航材与采购部天津分部接到生产计划控制分部缺件需求，需要调配器材，正班时间与航材计划部门（或航材与采购部指定部门）电话沟通；非正班时间与航材与采购部AOG部门联系得到同意后，通过航材管理系统创建调配申请。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (360, 15, '5.2.2', '创建调配订单\n航材计划部门（或航材与采购部指定部门）或AOG部门按照就近就快原则在航材管理系统中创建调配订单。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (361, 15, '5.2.3', '调出方办理器材出库\n调出方航材计划部门（或指定部门）或AOG部门，打印《器材出库指令单》到发出库房，由库房办理器材的出库（拣配、装箱、过账、运输）。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (362, 15, '5.2.4', '器材的出库、发运和器材接收、反馈同本程序5.1.4和5.1.5计划性布局调配。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (363, 15, '5.3', '器材调配的差异处理\n调入方接收到器材后，若发现差异，及时通报航材计划部门或AOG部门。', 8, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (364, 15, '5.4', '航材与采购部天津分部与北京分部间消耗器材调配', NULL, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (365, 15, '5.4.1', '航材与采购部天津分部与北京航材部门之间发生消耗器材调配需求时，各自通过各自的航材管理系统创建器材调配需求，并通过两套系统的接口传输到对方系统中。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (366, 15, '5.4.2', '航材与采购部天津分部与北京航材与采购部门接收来自对方的调配需求，进行审批，审批同意后，创建销售订单及出库指令。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (367, 15, '5.4.3', '航材与采购部天津分部与北京航材库房按照销售订单及出库指令，办理器材的出库（拣配、过账、打印装箱单/发票、运输）。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (368, 15, '5.4.4', '器材的出库、发运和器材接收、反馈同本程序5.1.4和5.1.5计划性布局调配。', 2, '2018-06-28 10:38:09', '2018-06-28 10:38:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (369, 16, '5.1', '概述', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (370, 16, '5.1.1', '航材与采购部天津分部依据采购战略管理分部制定的规范，对起落架、发动机反推、单次送修器材等合同的呈报要求，负责本单位航材送修合同管理和业务操作。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (371, 16, '5.1.2', '器材附件送修采取定向、试修、非定向送修的方式', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (372, 16, '5.1.2.1', '定向送修是指对于同一件号发生送修业务时，原则上送系统中指定厂家进行附件维修工作，其收费模式主要包括固定价、小时保修等。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (373, 16, '5.1.2.2', '试修是指对于同一产品的试修厂家原则上可选择一家或一家以上，其收费模式主要包括固定价、工时加器材等。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (374, 16, '5.1.2.3', '非定向送修是指对于系统中未定向的航材附件，送修时，直接送至原生产厂或原生产厂指定的授权厂家或通过询比价送至较优的承修商进行维修工作，其收费模式主要包括工时加器材、固定价等。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (375, 16, '5.2', '器材送修', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (376, 16, '5.2.1', '接收航材待修信息。\n针对飞机拆下不可用器材，维修单位在完成飞机维护工作后，生产人员应按照01-05-009《可用件/不可用件退库工作程序》填写《附件标识挂签》Form No.: QM-053 （见01-08-002《器材检验标准》）中相关信息，打印故障信息代码PFR/TSD（如有）,随不可用件在1个工作日内退回到航材待修库房，或者指定器材接收地点。如果涉及发动机短舱类器材，应尽量提供故障位置的相关照片。', 9, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (377, 16, '5.2.2', '针对指令性出库送修器材，库房人员依据01-05-003《器材的入库与发放》中相关规定将器材移动到航材待修库房。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (378, 16, '5.2.3', '针对验收差异器材需重新送修时，库房人员依据01-05-009《可用件/不可用件退库工作程序》将器材移动到航材待修库房。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (379, 16, '5.2.4', '待修库人员须核对《附件标识挂签》（见01-08-002《器材检验标准》）信息与实物信息一致后，将相关信息录入航材管理系统，并将《附件标识挂签》一联传递至航材附控部门，由附件控制工程师根据TJ-05-001CCA《附件控制工作程序》在航材管理系统中完成附件拆装工作及附件使用履历的确认。\n航材计划/送修/索赔人员每个工作日在航材管理系统中查询待修器材。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (380, 16, '5.2.5', '确定送修合同类型', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (381, 16, '5.2.5.1', '送修合同类型包括索赔送修合同、改装送修合同、专项送修合同、正常送修合同。\n1）索赔送修合同：根据上一次维修记录、PSAA/SSC中索赔期条款、修理协议中索赔期条款、采购协议中索赔期条款与待修件附控信息匹配，如果确定符合相应的索赔条件，出具索赔送修合同。\n2）改装送修合同：根据工程指令拆下或者从可用库出库执行改装，出具改装送修合同。\n3）专项送修合同：根据公司发布的专项任务指令（包括飞机销售、飞机退租、专项改装等要求）及专项预算指标，出具专项送修合同。\n4）正常送修合同：除以上情况以外的故障送修，出具正常送', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (382, 16, '5.2.5.2', '根据待修器材的附控信息、改装信息、时控信息、上一次送修信息、索赔信息及协议信息，航材计划/送修/索赔人员确定送修合同类型。如果涉及改装，查看工程指令信息，包括是否收费及相关费用信息、是否有指定送修厂家及地址等。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (383, 16, '5.2.5.3', '如待修器材为租赁（租出和租入）器材时，航材计划/送修/索赔人员询问负责器材租赁的业务人员，确认相关信息后，再执行送修业务。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (384, 16, '5.2.6', '送修合同中承修商的确定原则', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (385, 16, '5.2.6.1', '航材计划/送修/索赔人员在航材管理系统中根据属地、件号、修理级别查询是否存在指定承修厂家。如果存在，按照指定厂家执行送修业务。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (386, 16, '5.2.6.2', '如果航材管理系统中没有指定送修厂家时，送修原生产厂或原生产厂指定的授权厂家，可以直接执行送修业务。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (387, 16, '5.2.6.3', '如果航材管理系统中没有指定送修厂家或因业务需求要调整指定厂家时，航材与采购部天津分部的航材计划/送修/索赔人员参考中国民航维修网的维修能力清单、依据“运营人供应商清单”、送修总协议确定承修商的维修能力，向航材送修管理分部提交《送修申请单》Form No.: MM-011；《送修申请单》业务评估说明栏必须详细填写评估内容，内容至少包括：SAP系统定价项目的价格、周期、索赔期（定向偏离项目）、有能力附件送修厂家报价（价格、周期、索赔期，报价原则上不少于两家）等信息，综合考虑评估修理成本、质量、周期和服务等因素并参考历史送修数据（如有）后，择优拟定送修厂家。《送修申请单》经航材与采购部批准后，执行送修业务，并将送修厂家的价格（包修费）录入SAP航材管理系统（创建FD行）。各航材中心的航材计划/送修/索赔人员将相关记录及文件作为《送修申请单》的附件存档、备查。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (388, 16, '5.2.6.4', '在特殊情况下，如果选择的承修商未包含在“运营人供应商清单”范围内，航材与采购部天津分部的航材计划/送修/索赔人员按照01-09-05《供应商资格评估程序》填写《供应商资格评估表》ESFM-QM-004，并随附相关资料，报航材送修管理分部。经航材送修管理分部完成业务需求评估及采购战略管理分部完成承修商管理评估后，由采购战略管理分部报公司质量部审批。经批准后，按照01-05-13《航材与采购部协议/业务信函管理程序》的规定，进行单项合同审批及签署。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (389, 16, '5.2.7', '出具送修合同', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (390, 16, '5.2.7.1', '航材计划/送修/索赔人员根据器材附控信息、相关服务通告、指令及其他送修要求，在正常情况下，1个工作日内，以订单方式在航材管理系统中出具相应送修类型的送修合同，并在合同中明确故障信息、服务通告信息等工作范围要求。如果服务通告属于运营人客户化指令，应将服务通告的工作内容随同送修合同一并提供给承修商。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (391, 16, '5.2.7.2', '送修合同应当至少包括以下内容：\n1）合同号、合同联系人及联系方式；\n2）送修件的名称、件号或型号、序号或批次、数量；\n3）本次送修前使用的航空器注册号、机型；\n4）拆下日期、原因或故障描述、使用时间；\n5）维修要求，包括维修工作类别和其他指定维修工作（如改装、执行服务通告或适航指令等）和维修工作依据文件；\n6）合格证件和维修记录要求。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (392, 16, '5.2.8', '发运送修器材', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (393, 16, '5.2.8.1', '航材计划/送修/索赔人员在航材管理系统中根据送修合同创建外向交货单，打印送修合同后，经本部门经理审核及签署。送修合同签署后，将合同及相关维修工作依据文件交航材待修库房人员办理包装业务，并在航材管理系统中制作装箱单。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (394, 16, '5.2.8.2', '器材包装完毕后，在1个工作日内航材待修库房人员将送修器材、送修合同、相关维修工作依据文件及装箱单交航材发运人员办理发运业务。对于急件送修，发运部门在收到器材后24小时内发出发运部门，其它器材送修应在2个工作日内发出发运部门。如果为国内送修业务，发运业务由指定运输代理完成。如果为国外送修业务，器材先发至AIE，由AIE负责国外运输的航班订舱、出口报关，并将器材运输到货场。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (395, 16, '5.2.8.3', '送修器材发出后，航材发运人员或指定人员在2个工作日内将运单信息、或自提信息录入航材管理系统。如涉及AIE订舱业务，AIE应在2个工作日内将运单信息反馈到相关各航材中心的航材与采购部门，由各航材中心的指定人员负责将运单信息录入航材管理系统。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (396, 16, '5.2.9', '送修合同的跟踪', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (397, 16, '5.2.9.1', '送修器材发出后，如果承修地点为国内，航材计划/送修/索赔人员或相关指定部门将发运信息通知承修商。如果承修地点为国外，AIE负责将发运信息通知承修商、指定运输代理。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (398, 16, '5.2.9.2', '在确认承修商收到器材后，根据PSAA/SSC协议中修理周期要求、或运营人送修总协议中修理周期要求，各航材中心的航材计划/送修/索赔人员负责跟踪器材的修理状态。对于超过指定修理周期未回的送修合同，应及时与承修商沟通，督促加快修理进度，将相关跟踪信息录入到航材管理系统中，包括承修商收到日期、承修商发出日期、发出、返回的运单信息、航班号、航班日期，以及在送修状态信息,直至送修器材返回验收入库。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (399, 16, '5.2.9.3', '航材送修过程中，应当至少对下述事项进行控制。承修商负责向各航材中心的航材计划/送修/索赔人员提出，航材计划/送修/索赔人员对于以下的2、3条（包括但不限于）的情况需报航材送修管理分部评估批准(根据相关规定必要时报工程部门批准)后反馈，航材计划/送修/索赔人员根据批准负责提供书面确认反馈承修商。具体内容如下：\n1）检查发现送修实物信息、故障与送修合同描述不一致；\n2）超手册维修；\n3）使用串件、自制件或PMA件；\n4）外委送修或转包维修；\n5）器材修理换件情况；\n6）其他任何非正常情况。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (400, 16, '5.2.10', '送修报价审批原则', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (401, 16, '5.2.10.1', '航材计划/送修/索赔人员收到修理厂家的修理器材报价后，根据Ameco有关批准权限规定进行审核和报批，并按照以下要求审核：\r\n1）核实修理换件的器材明细，如有疑问，应向工程技术部门发出书面询问。同时，核实换件的器材价格，如有疑问，应询价。\r\n2）核实器材使用时间是否符合相关索赔要求，如果是，应与承修商沟通，按照索赔执行。如果索赔不成立，应确认索赔不成立的原因，并核对上次修理报告中的更换器材。同时，在航材管理系统中记录相关索赔信息，以便进行索赔成功率的统计、分析。\r\n3）核对承修商修理报价与协议价格是否相符。\r\n4）如果涉及送修合同内要求的改装，应核实更换器材与改装指令、索赔信息和协议信息是否相符，如有疑问，应向工程技术部门发出书面询问。\r\n5）若器材修理涉及送修合同要求外的改装，应向工程技术部门发出书面询问，并按照其书面确认信息执行。\r\n6）若修理涉及到超经济修理或器材报废，则按照ESM LD 005《国航产权高价件航材报废工作程序》执行。\r\n7）若航材维修过程中发生器材交换，且交换件为旧件时，要求承修商提供具有AAC-038适航批准标签/维修放行证书和CCAR-145部所要求的维修记录（包括但不限于适航指令和服务通告的执行记录、重要修理及改装记录、时限/循环寿命、库存寿命数据限制等），国外修理器材还应有FAA 8130-3或EASA FORM 1，同时，要求承修商根据使用情况提供有助于确定其适航性的资料和信息：\r\n最近一次部件拆下时飞机的运营人/国籍登记注册号/机型，或发动机的使用人/型号/序号。\r\n最近一次部件拆下的时间/单位。\r\n最近一次部件拆下的原因/状况。\r\n非事故声明。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (402, 16, '5.2.10.2', '送修报价的批准权限\n依据送修合同的报价，按照Ameco航材业务审批权限规定报批：\n1）修理报价20万人民币以下的由航材与采购部门经理批准；\n2）修理报价20万人民币以上（含）、50万人民币以下的由航材与采购部部门经理级领导批准；\n3）修理报价50万人民币以上（含）、200万人民币以下的由航材与采购部总经理批准；\n4）修理报价200万人民币以上（含）、500万人民币以下的由公司副总经理批准；\n5）修理报价500万人民币（含）以上的由Ameco总经理批准。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (403, 16, '5.2.10.3', '送修报价批准\n1）收到承修商提供的维修报价后，按照Ameco相应审批权限审批及签署报价。当超出签署权限时，需填写《大额报价申请单》Form No.:MM-016，经本单位相关权限人员审批后，交航材与采购部审批。\n2）送修报价批准后，航材计划/送修/索赔工程师将报价信息（工时费、材料费，或包修费）及时录入到航材管理系统中，并书面通知承修厂家。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (404, 16, '5.2.11.1', '1）根据货运部门/运输代理的运输信息，各航材中心负责将器材提运至航材验收库，并在1个工作日内将提运信息录入到航材管理系统中。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (405, 16, '5.2.11.2', '2）航空安全质量分部航材检验员在3个工作日内按照01-08-001《器材检验程序》完成器材检验。', 8, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (406, 16, '5.2.11.3', '3）对于生产急需的送修器材到货后，航材计划/送修/索赔人员应及时通知器材需求申请部门。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (407, 16, '5.2.12.1', '1）各航材中心的航材计划/送修/索赔人员每天查看航材管理系统中送修差异情况，并与厂家联系处理。\r\n2）经与厂家沟通后，将处理结果录入到航材管理系统中。', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (408, 16, '5.2.12.2', '3）航空安全质量分部航材检验员根据航材计划/送修/索赔人员的反馈结果，按照01-08-001《器材检验程序》完成器材检验。', 8, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (409, 16, '5.2.13', '送修合同付款\n1）航材送修管理分部、各航材中心或指定的费用审核人员接到送修合同结算单据后，根据送修合同内容，确认到货情况、修理工作范围、修理费用、报价批准情况、SAP航材管理系统中协议模块中的内容等信息后签署意见，并在航材管理系统中确认修理费用。\n2）根据财务分部提供的付款通知单，填写报销单，并按照Ameco费用报销规定报批后，交财务分部处理。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (410, 16, '5.2.14', '修后使用时间监控\n航材与采购部天津分部负责监控本单位附件修理后使用时间，发现异常数据，分析原因，并通报到航材送修管理分部。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (411, 16, '5.2.15', '高价件送修成本监控及分析', NULL, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (412, 16, '5.2.15.1', '根据财务分部每月发布的入账明细数据，负责核实本单位入账明细，分析本单位成本结构及变化趋势。针对异常数据，需通报航材送修管理分部。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (413, 16, '5.2.15.2', '根据送修成本分析结果与高价件送修预算匹配，对于异常数据进行分析，必要时通报相关部门。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (414, 16, '5.2.15.3', '航材送修管理分部、各航材中心根据历史送修费用预算执行及趋势变化及可预见的送修成本的发生，预估未来发生的费用，并采取相应的管控措施，完成预算目标。', 2, '2018-06-28 10:57:55', '2018-06-28 10:57:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (415, 17, '5.1', '器材仓库\n器材仓库应满足下列要求：\n1）按照国家、中国民用航空行业标准的有关规定选址和设置器材库房。\n2）仓库要坚固、清洁、通风；并防尘、防潮、防火、防盗。仓库的设计和建造应满足存放飞机发动机、APU、机载设备、零部件和航空材料的要求。\n3）仓库应配置消防设备。\n4）仓库应配置温度、湿度调节设备和检测设备，并有相应的温度、湿度记录。\n5）仓库内部的货柜要排放整齐并具有一定空间，区域、架位隔档上须有明显标识，便于计算机管理和机械化作业。\n6）危险化工品仓库须按照危险品分类单独建库。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (416, 17, '5.2', '器材的合理储存', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (417, 17, '5.2.1', '器材存放的要求', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (418, 17, '5.2.1.1', '可用器材要与不可用器材分开存放。有害物品要与无害物品分开仓库存放，保证航空器材质量不受损害和不对环境造成污染。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (419, 17, '5.2.1.2', '器材的包装和储存必须符合生产厂家的规定与要求。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (420, 17, '5.2.1.3', '包装箱或器材随带文件上提到的保护和储存要求、放置方向标志、注意及警告说明必须遵守。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (421, 17, '5.2.1.4', '器材应尽可能保留其原包装，直至发付使用，这样可以最小程度的减少储存当中发生器材损坏或变形。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (422, 17, '5.2.1.5', '对于有特殊储存要求的器材，如：磁性器材，放射性器材，静电敏感元件，危险品等，和有特殊储存温度、湿度要求的器材应按照相关生产厂的要求进行储存。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (423, 17, '5.2.1.6', '器材应存放在固定的架子上，尽量不要罗列堆放，除非器材的包装或包装箱具有极强的保护作用可使器材免受损坏。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (424, 17, '5.2.1.7', '禁止未经授权的人员进入库房。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (425, 17, '5.2.1.8', '按照程序“零部件的自制”生产的器材应单独存放并严禁出售。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (426, 17, '5.2.2', '需用特殊包装箱的附件类别\r\n为了避免有害环境的影响（如振动、冲击、磁化、放射性和重量等原因），在运输和存储期间需要使用特殊包装箱的附件类别有：\r\n1）	陀螺仪；\r\n2）指示器（例如：特高频全向无线电导航（VOR），雷达微波中继装置（RMI），测距机（DME），水平位置指示器（HIS），图像位置指示器（PPI），姿态指引仪（ADI））；\r\n3）计算机（例如：大气数据，自动驾驶－俯仰倾斜，偏航无线电收发机，配重与平衡，多卜勒雷达，工作失效监控）；\r\n4）火警环路；\r\n5）天线（多卜勒雷达）；\r\n6）发电机组（电源）；\r\n7）加速表；\r\n8）液压泵；\r\n9）燃油控制组件/恒速传动装置；\r\n10）救援信标（包含电瓶）。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (427, 17, '5.2.3', '电器附件\n电路板和电器组件按照厂家要求的温度和湿度进行存放，应确保潮气不会凝结在附件上。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (428, 17, '5.2.4', '静电敏感元件', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (429, 17, '5.2.4.1', '静电敏感元件（ESDS）应包装在防静电箱或防静电袋中。防静电袋不应有破损。静电敏感元件的包装应有特殊的防静电标记和说明。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (430, 17, '5.2.4.2', '对于静电敏感元件需打开其防静电包装检查件号时，必须使用腕带在防静电工作台上进行操作。防静电工作台应按照规定定期检查、测试和维护，建立记录档案。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (431, 17, '5.2.5', '燃油液压和气动部件\n所有入口和出口都应封闭，这种部件都应包装在塑料袋内（如果可行），尤其是部件内注入有液体时，所有孔口要堵盖并用塑料袋包装。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (432, 17, '5.2.6', '客舱玻璃窗\r\n客舱玻璃窗要用单独包装箱包装，不得靠近热源。要使用充足的填料加以保护。包装箱必须平放，不得堆置。存储区域必须清洁、干燥。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (433, 17, '5.2.7', '救生滑梯\n充气救生滑梯应储存在光线暗淡而且干燥的房间内，按照厂家要求的温度和湿度进行存放。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (434, 17, '5.2.8', '氧气瓶\n原则上，氧气瓶应用软材料专用包装袋包装，以防止放电和擦伤。要严格与任何油脂或用油脂和润滑油油封的部件分开存放。任何情况下活门出口要用新的干净的堵盖封闭，防止水气进入。充气后的氧气瓶要置于阴凉处，且与易燃材料分开存放。氧气瓶应放在离开地面的架子上，且应防止滑油和油脂洒漏在上面。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (435, 17, '5.2.9', '航空机轮和轮胎', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (436, 17, '5.2.9.1', '航空机轮和轮胎应储存在凉爽、干燥的地方，避免阳光直射。应尽可能竖立存放在轮子架上，每3个月变换一次支撑位置。注意避免使轮子接触到油、汽油、航空煤油等。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (437, 17, '5.2.9.2', '机轮和轮胎不应堆垛挤压存放，不允许产生刻痕、压痕、鼓包和其它的变形。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (438, 17, '5.2.10', '橡胶和类似材料\n不同种类的橡胶材料不应彼此接触。远离蒸汽管、散热器或其它热源，防止日晒，避免接触水、油类或其它污染。温度和湿度按照厂家的要求进行控制。软管口应使用堵盖封好，储存时不应有扭结或死弯儿。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (439, 17, '5.2.11', '化工类材料', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (440, 17, '5.2.11.1', '需冷藏的化工品应根据其存储温度要求在冰箱或冰柜中存储。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (441, 17, '5.2.11.2', '没有特殊存储要求的化工品应存放在通风良好的地方避免阳光直射。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (442, 17, '5.2.11.3', '瓶子、金属罐、桶等容器应封严；塑料袋等不应破损，应封好。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (443, 17, '5.2.11.4', '所有危险化学品应有安全数据单。储存要求参考GB15603-1995《常用危险化学品储存通则》。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (444, 17, '5.2.12', '原材料如板材、钢管等应平放不能竖放以防变形。板材或电镀材料之间要有隔离材料保护防止划伤。如在同一架位上存放不同种类的原材料时，应使用包装盒进行区分。原材料发料后，剩余部分应重新粘贴标识。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (445, 17, '5.2.13', '复合材料修理用需冷藏的原材料', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (446, 17, '5.2.13.1', '概述\n1）对于复合材料修理用的需冷藏的原材料，例如预浸布、胶膜、发泡胶条，存储的方法必须能够保证材料的可用性，以及加工之后材料能够提供预期的性能，不正确的存储和处理方法会损伤材料的性能。\n2）预浸布和胶膜应该根据生产厂家的规范要求来存储。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (447, 17, '5.2.13.2', '冰柜\n1）冰柜必须进行除霜检查，按需进行除霜，以便在无霜状态下存放材料。\n2）冰柜的温度必须低于－18℃ （0℉）。\n3）冰柜的温度必须每日进行检查和记录，此记录应该至少保存2年。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (448, 17, '5.2.13.3', '冷藏材料包装\n1）为了防止吸收水分，冰柜中的材料必须保存在防潮的袋子中。\n2）如果将材料取出冰柜，在包装袋表面的冷凝物消失之前不要打开袋子。否则凝结在材料表面的水汽将影响材料的状况。如果把材料放回冰柜，必须要保证包装袋还是防潮的。\n3）为了防止纤维断裂或胶移动，材料卷应该在不受力的状态下存储。也就是说，存放的各卷材料之间彼此不应该接触。其它平板型的材料需水平放置，并且不受其它材料的压挤。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (449, 17, '5.2.13.4', '接触材料\n接触材料时，必须配戴未经洗涤的干净的棉质手套。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (450, 17, '5.2.13.5', '报废材料\n超过存储寿命或暴露时间限制的材料必须报废，并从存放可用材料的冰柜内取出。\n标记清楚的失效材料可用于除飞机以外的其他用途，如：培训目的。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (451, 17, '5.3', '器材在飞机维护中队/车间中的保存', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (452, 17, '5.3.1', '在维修的各个阶段中，应对可用及不可用器材提供必要的保护。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (453, 17, '5.3.2', '在飞机维修部门或车间必须有单独的存放区域或存放架位。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (454, 17, '5.3.3', '要遵守注意或警告说明。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (455, 17, '5.3.4', '在可行的情况下，从库房领出的器材在安装前要保存在它原有的保护包装内。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (456, 17, '5.3.5', '将要安装的可用附件以及已经部分组装的附件要防止灰尘和其他污染，不允许有看得见的表面污染。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (457, 17, '5.3.6', '不可用附件拆下后，为了防止对其进一步的损坏，应具有合适的包装，并尽可能将其包装在原可用附件（替换件）的包装箱内。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (458, 17, '5.3.7', '对附件、函道、管子的开口和零部件的敏感区域如驱动花键、安装和配合面等必须采取保护措施。附件、函道和管子在拆下后，对其开口部位要进行封堵直到再次安装。上述要求只有在这两种情况下例外：处于检查和清洁的目的时以及对一些外来物或污迹能在安装是很容易发现的短直管。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (459, 17, '5.3.8', '维修放行之前检查对器材进行的保护和包装工作是否符合生产厂家相关手册中的规定。如果相关手册中规定将注意或警告说明附在器材上，则必须予以遵守，以保证对其采取相应的保护措施。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (460, 17, '5.3.9', '化工类材料的储存参见本程序5.2.11。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (461, 17, '5.3.10', '复合材料修理用的需冷藏的原材料的储存参见本程序5.2.13。', 9, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (462, 17, '5.4', 'Ameco产权器材库寿控制', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (463, 17, '5.4.1', '依据器材生产厂家规定或CAMP信息对器材的库存寿命期限进行控制。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (464, 17, '5.4.2', '有库寿的消耗器材包括：橡胶封圈、爆炸帽、救生包、不干胶铭牌、胶带、壁纸、化工品（例如：漆、胶、油、除冰液等）、用于复合材料修理的原材料（例如：预浸布、胶膜、发泡胶条）等。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (465, 17, '5.4.3', '入库的每一项库寿器材都必须标有库寿数据。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (466, 17, '5.4.4', '器材库房要设置专人对库寿器材进行及时、有效的控制，严防超寿领出装机使用。', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (467, 17, '5.4.5', '库寿器材的日常监控', NULL, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (468, 17, '5.4.5.1', '航材与采购部天津分部根据CAMP要求，设立专人负责CAMP时控件的库寿控制。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (469, 17, '5.4.5.2', '非CAMP航材的库寿控制', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (470, 17, '5.4.5.2.1', '1）航材与采购部天津分部每月10日前通过航材管理系统中查看下一个月到期的器材到期报表。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (471, 17, '5.4.5.2.2', '2）航材与采购部天津分部每次完成器材到期报表后，需有完成时间及人员的记录，并保存一年。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (472, 17, '5.4.5.2.3', '3）对于不可延寿的库寿器材（例如：橡胶封圈、爆炸帽、救生包、某些化工品等），航材与采购部天津分部负责库寿件控制的人员在航材到寿日期前五天，办理航材从可用库区移到隔离库区。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (473, 17, '5.4.5.2.4', '4）航材与采购部天津分部负责库寿件控制的人员通过系统打印航材报废单，报航材与采购部天津分部经理批准，在航材管理系统内办理航材出库报废。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (474, 17, '5.4.5.2.5', '5）对于可延寿的库寿器材，航材与采购部天津分部依据每月的非CAMP库寿航材到期报表，综合评估后确定航材是否需进行延寿申请。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (475, 17, '5.4.5.2.6', '6）对于可延寿的航材，在到期前一个月，将航材样品送合格的检测中心检测。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (476, 17, '5.4.5.2.7', '7）航材与采购部天津分部负责非CAMP库寿件的到寿送检，根据检测中心提供的检测合格报告进行检验：\n（1）到期航材样品检测不合格，航材与采购部天津分部按照01-05-011《器材的报废》办理报批和航材报废。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (477, 17, '5.4.5.2.8', '（2）到期航材样品检测合格后，航材检验人员在航材管理系统中修改该物料批次的到寿日期。', 8, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (478, 17, '5.4.5.2.9', '（3）检测合格报告应上传系统服务器。', 2, '2018-06-28 11:04:07', '2018-06-28 11:04:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (479, 18, '5.1', '器材的运输', NULL, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (480, 18, '5.1.1', '运输通则 \r\n器材运输时应有包装或运输保护装置，遵守器材随附的注意或警告说明，以防对它们造成损坏或对不可用件造成进一步损坏。\r\n搬运时应遵照包装箱上的标识或说明进行。 \r\n在任何可能的情况下，器材（可用或不可用）在运输过程中都要保持其包装完好以防止损坏，大件要保留其支撑物，以防变形。 \r\n零部件在吊装时必须在规定的部位使用规定的设备进行操作。\r\n运输速度要控制在防止零部件相互碰撞和损坏的范围内。\r\n器材运输时，要将重物放在下面，轻物放在上面。无外部包装的 机械件、电子件、化工品不得混合运输。 \r\n所有相关部门应将待运输器材放置在包装箱内或运输专用箱/盘 上，要符合运输要求。\r\n器材运输前，各部门相关人员需与航材运输人员对包装箱外观及数量进行核对、确认。', NULL, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (481, 18, '5.1.2', '安全使用车辆运输', NULL, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (482, 18, '5.1.2.1', '车辆使用通则 \n运输人员须爱护车辆，严格遵守驾驶安全操作规程。行车前，确保车辆状况正常。 \n离开车辆时，需检查车门车窗是否关闭，锁好车辆。 \n车上装有器材时，驾驶员不可随意离开车辆，防止器材丢失。', 2, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (483, 18, '5.1.2.2', '集装板运输 \n集装板装卸及对其上货物进行捆绑、罩网时要有专业人员在场进行指挥。\n使用集装板拖运货物时，20英尺板一次只能托运1个、P6板不得超过4个，托运集装箱（无论有无货物）时，每次托运的集装箱车数量不得超过6个。拖运时速不得超过当地管理部门限定的时速。', 2, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (484, 18, '5.1.2.3', '叉车运输 \n铲运器材时，通常情况下，器材只能码放1层。必需多层重叠铲运时要使用专用运输箱、柜进行铲运。 \n所有铲运的器材必须有包装箱或专用托盘，器材要固定牢固。 \n叉车时速不得超过当地管理部门限定的时速。', 2, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (485, 18, '5.1.2.4', '卡车运输 \n大件器材装卸要有专用设备（如：叉车、吊装设备），器材码放要整齐。 \n小件器材运输时要有包装，装卸时轻拿轻放码放整齐。 \n当器材超出车厢多层码放时要捆绑牢固，防止器材滑落丢失。', 2, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (486, 18, '5.1.2.5', '封闭式货车运输 \r\n器材装卸时要轻拿轻放，码放整齐。必须多层次码放时包装要符合码放要求，轻物放上、重物放下。防止将下层器材挤压变形损坏。 \r\n行车时要将门窗关闭、锁好防止器材丢失。', NULL, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (487, 18, '5.1.3', '特殊器材运输', NULL, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (488, 18, '5.1.3.1', '危险品器材运输\n除上述要求外，危险品器材运输还必须满足危险品航材管理规定01-05-21中对于运输的要求。', 2, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (489, 18, '5.2', '损坏的处理\n掉落或遭到损坏的器材要视为不可用件，当事人应将事件及时 向本部门及航材与采购部天津分部报告，同时航材与采购部天津分部通知相关部门启动保险理赔程序。', 2, '2018-06-28 11:06:50', '2018-06-28 11:06:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (490, 19, '5.1', '概述\r\n为保证器材在运输、储存过程中的安全，对必要的器材，应在其包装箱内加装支撑结构或加入必须的填充材料。包装工作应做到外包装坚固，内包装使用专用填充物并保证六面都有充足垫料。\r\n原则上包装应一件一箱，但对于较小的附件也可以多件一箱。 如：手柄，喷咀等。但必须对每一件都要使用包装材料进行包裹 处理，如果需多层码放，在每层间用垫料隔离方可接受。 \r\n对于器材的包装，原则上，应采用原始包装来进行；如特殊情况，器材不具有原始包装，则应当采取等效替代包装。\r\n等效替代包装是指包装要有足够的强度和支撑且垫料充分，以使得器材在仓储和运输过程中不被损坏。', NULL, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (491, 19, '5.2', '包装箱的分类\n按使用次数包装箱可以分为三类：\nⅠ类：可重复使用多次。 \nⅡ类：可重复使用次数最小为10个来回程。 \nⅢ类：可重复使用次数最小为1个来回程。 \n按照材质包装箱可以分为： \nⅠ类：金属、塑料、玻璃纤维、胶合板。 \nⅡ类：木-可能有进出口的限制、纤维板、纸板、任一种Ⅰ类材料。', 2, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (492, 19, '5.3', '器材准备 \n当器材从飞机/发动机上被拆下，应采取必要的防护措施进行保护。\n对于需使用专用包装箱的，应使用专用包装箱。\n对有支撑架/连接点的（如进气道、起落架等），要确保牢固安全。\n如果器材含有油类或污物，应尽可能排放并确保排放口被封堵，液体不会流出。避免在运输途中泄露，造成污染。', 2, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (493, 19, '5.4', '器材交接 \n当需包装的器材被运至包装部门时，包装人员要和运送货物人员核对器材的箱数，进行交接，写明到达的时间并签字确认。器材应按普通货物或危险品、可用不可用分别存放。AOG件要告知包装人员并被优先处理。', 2, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (494, 19, '5.5', '器材包装 \r\n首先要检查合同等文件是否齐全，件号、序号、数量是否与实物相符，有无损坏。\r\n发现有差异的应立即通知相关人员，器材放入差异区隔离存放。\r\n检查包装箱是否适用，如果是木制包装箱有无熏蒸标志。\r\n包装时要保证垫料充分，包装箱结实坚固。对可能存有液体的部件，要着重检查排油口是否被有效封堵。\r\n创建装箱单，箱单创建后应检查箱单信息是否正确完整。箱单一式4 份，其中一份放入箱中。封箱时要再次确认箱单和实物是否一致。包装完成后把包装箱放入待发区，通知发运人员提取。 \r\nESDS器材在包装时，要由具备操作资质的人员使用防静电工作台单独操作。 \r\n包装危险品时，还需参看01-05-015《危险品管理规定》并按其要求操作。', NULL, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (495, 19, '5.6', '防护措施 \n在任何可能的情况下，飞机器材（可用或不可用）在处理和运输过程中都要保留其包装防止损坏。大件要保留其支撑物，以防变形。对于函道、插口、接头都应有堵头堵盖，以防止异物和灰尘进入。', 2, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (496, 19, '5.7', '标识标签 \r\n标识需清晰、可读、不褪色、耐用且对比度高。永久性标识必须用热印、雕版或其他类似的压花方法标记到表面。对于木质、纤维板、玻璃纤维或其他无法进行标记压花、印制或打印的材料，可以使用能够长期曝露并能承受正常装运和输送的标识。 \r\n标签不得被用作永久性标识，除非客户特别指定的标签。标签应贴在包装箱的右上角。按规定使用包装的标识、标签。\r\n主要包括： 向上标识、小心轻放标识、发货标签（如：收货人、发货人及地址等）、其它特殊标识、已经印有“玻璃制品，不得投掷或乱扔”或“玻璃制品，小心搬运”等类似永久性标识的货箱无须再粘贴易碎物品标签。\r\n非一次性使用的包装箱在重复使用时，需将不适用的标签撕除、 覆盖或用黑粗笔划叉去除。危险品应按相关规定使用标识、标签。', NULL, '2018-06-28 11:13:51', '2018-06-28 11:13:51', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (497, 20, '5.1', '可用件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (498, 20, '5.1.1', '消耗件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (499, 20, '5.1.1.1', '各维修单位从库房领出的消耗件，在没有使用和损坏的前提下，可以退回航材库房。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (500, 20, '5.1.1.2', '领出未用消耗件在退库时应满足以下条件：\n1）贴有可用标签的原发料包装，外表完好、清洁；\n2）随附器材的发料单或复印件，以保证其追溯性；\n3）发料单上记录的数据应与退回的器材相符,当退回器材数量不一致时，应在发料单上注明实际退回数量。\n4）维修单位相关人员在发料单上签字确认可用性，飞机维修检验员签字/盖章确认；\n5）对于有真空包装的橡胶封圈须未开包拆封方可退库；\n6）各维修单位需及时退料，否则领用器材工单关闭10天后系统不能接收退料。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (501, 20, '5.1.1.3', '航材与采购部天津分部接到退库的领出未用消耗件时，按照上述要求进行检查并输入SAP系统。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (502, 20, '5.1.1.4', '如包装玷污或破损，应重新包装；如可用标签损坏，但可识别，可以重新打印并粘贴可用标签。可用标签丢失不可以重新打印，应按报废件处理。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (503, 20, '5.1.1.5', '如保税器材退料时已征税，SAP系统会产生新的批次号，需重新打印并粘贴可用标签。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (504, 20, '5.1.1.6', '对于库寿器材，在退库时，应检查器材是否在库寿期内。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (505, 20, '5.1.1.7', '维修部门相关人员领出器材后，如发现器材实物不是可用标签标识的器材，且在SAP系统中没有其它领退料记录，退回库房隔离存放，经确认后如需要由航材与采购部天津分部与供应商联系解决。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (506, 20, '5.1.2', '附件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (507, 20, '5.1.2.1', '领出未用附件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (508, 20, '5.1.2.1.1', '1）应使用原包装，外表完好、清洁。发料单上记录的数据（件号、序号、数量等）应与实物相符。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (509, 20, '5.1.2.1.2', '2）维修单位相关人员在挂签上注明领出未用、飞机号或发动机号，签字或盖章并注日期。飞机维修检验员签字或盖章确认。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (510, 20, '5.1.2.1.3', '3）航材检验人员接到退库的领出未用附件时，按照上述要求进行检查并输入SAP系统。', 8, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (511, 20, '5.1.2.2', '飞机拆下可用附件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (512, 20, '5.1.2.2.1', '1）维修单位相关人员填写挂签并签字或盖章，使用适合的包装箱及充分的垫料退库。《附件标识挂签》中“拆件情况说明”部分或备注栏上应有机械员和飞机维修检验员签署的判断结论并签字或盖章。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (513, 20, '5.1.2.2.2', '2）航材检验人员检查挂签上的信息与实物相符后输入SAP系统。', 8, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (514, 20, '5.1.2.2.3', '3）航材附控部门需要在系统做拆装。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (515, 20, '5.2', '不可用件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (516, 20, '5.2.1', '不可用消耗件的处置', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (517, 20, '5.2.1.1', '维修过程中更换下的标准件（如封圈垫圈螺钉等），由工作者直接报废妥善处理并置入报废箱，无需退料。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (518, 20, '5.2.1.2', '对于既无发料单，也无可用标签的消耗件退库，为使器材的追溯性得到保证，必须报废以避免错误使用。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (519, 20, '5.2.2', '不可用附件的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (520, 20, '5.2.2.1', '从飞机、发动机或组件上拆除不可用附件后，维修单位相关人员应填写挂签上的不可用部分的相关内容。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (521, 20, '5.2.2.2', '如果可以打印故障信息代码PFR/TSD，维修单位相关人员应打印后随挂签与拆下件放在一起。如果涉及发动机短舱类器材，应尽量提供故障位置的相关照片。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (522, 20, '5.2.2.3', '对于装机前或装机后发现器材故障的情况，维修单位相关人员应填写挂签的不可用部分的相关内容，按不可用件处理。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (523, 20, '5.2.2.4', '将器材分解，使用其中的一部分用于维修，而器材的剩余部分因此成为不可用件。维修单位相关人员应填写挂签上的不可用部分的相关内容并且签字。在拆下的串件的位置上挂上红色警告牌，并注明被串件的件号。航材与采购部天津分部根据挂签和红色警告牌对该附件进行检查。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (524, 20, '5.2.2.5', '为了防止对拆下的不可用附件的进一步损坏，拆除的附件应有适合的包装，如有可能应将其装在原可用附件（替换件）的包装箱内；拆除附件的进出口部分和电子连接件部分应附有堵盖。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (525, 20, '5.2.2.6', '在飞机维修时或车间中拆下的不可用附件应运送至指定地点， 排列整齐，保持清洁，与可用件隔离存放。不同类别的附件（如液压、氧气、电子等附件） 应隔离存放。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (526, 20, '5.2.2.7', '接到退库的不可用附件后，航材与采购部天津分部应检查包装箱完好，实物上的件号和序号与挂签上的信息相符，在SAP系统中录入拆件日期和退料日期。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (527, 20, '5.2.2.8', '航材与采购部天津分部将器材移交至待修库房，后续处理由客户决定就地送修或者发运回修理厂家。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (528, 20, '5.2.2.9', '对于国航产权附件，将所需数据从拆件挂签上转入SAP系统。为保证数据的准确性，应对输入的数据进行重复检查。挂签第二联交与时寿控制部门,挂签第三联应与不可用附件一起存放。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (529, 20, '5.2.2.10', '对于国航产权库寿到期附件的出库，航材与采购部天津分部根据指令单将器材出库。同时，在挂签中注明“库寿到期出库”并签字、注明日期。挂签第一联，应作销毁处理，后两联应随附器材运至待修库房。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (530, 20, '5.2.3', '拆下的危险品器材的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (531, 20, '5.2.3.1', '对于拆下的危险品器材，包括：灭火瓶，氧气瓶，滑梯气瓶，电池等，在空运前，维修单位相关人员应先将此器材送往车间作减压或数据测试工作，然后将器材，数据测试工作单以及专用的包装箱退回待修库房。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (532, 20, '5.2.3.2', '航材与采购部天津分部对客户决定就地报废或返回本部的器材，需经客户代表签字认可。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (533, 20, '5.2.4', 'EO器材包改装后的剩余器材的退库', NULL, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (534, 20, '5.2.4.1', '退回EO器材包改装后的剩余器材时，维修中队相关人员在退料单上应一一注明剩余项目的数量及EO号，也要注明飞机号和SB（服务通告）号（如需），将器材退回待修库房，放入待处理区域。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (535, 20, '5.2.4.2', '航材与采购部天津分部对客户决定就地报废或返回本部的器材，需经客户代表签字认可。', 2, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (536, 20, '5.2.5', '观察件退库\n维修中队负责观察件后续手续的办理，3日内确认是否可用并完成退库工作。', 9, '2018-06-28 11:16:17', '2018-06-28 11:16:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (537, 21, '5.1', '概述\n原则上，报废件应在放行之前进行损毁或进行改装、永久性标识后用于合法的非飞行使用（例如用于培训、教育、研究、发展或其它非航空使用），以防止再被当作可用件使用或销售。', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (538, 21, '5.2', '器材报废的主要原因包括：\n1）器材不具有追溯性；\n2）器材具有不可修理的故障，包括视觉可见和不可见故障；\n3）器材不在批准设计的规范之内，即不符合适用的规范；\n4）按照合格证持有人系统的要求，进一步加工或返工的故障器材仍不能成为合格产品；\n5）故障器材无法进行改装或其他途径修复；\n6）时寿器材已经到期、超期或者其时寿记录不完整或丢失；\n7）器材由于受到超过设计极限的外力、过热等损伤，而不能返回到适航状态；\n8）按照工程指令的要求和厂家信息需报废的器材以及不经济修理器材等。', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (539, 21, '5.3', '航材与采购部天津分部的库存器材报废流程', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (540, 21, '5.3.1', '原则上，航材与采购部天津分部的库存器材仅拥有消耗件，而且这些器材在特殊的环境中才能报废，如库寿器材。', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (541, 21, '5.3.1.1', '非库寿原因的器材报废可能的原因：器材损坏、依据工程文件要求报废等。', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (542, 21, '5.3.1.2', '器材报废时，应按以下权限批准：\n报废器材审批权限：单项原值1000人民币（含）以下，由航材与采购部天津分部经理批准；单项原值1000人民币以上、5万人民币以下（含），由航材与采购部华北航材部经理批准，单项原值5万人民币以上、25万人民币以下（含），由航材与采购部副总经理批准。以上遵循Ameco授权规定执行。', 2, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (543, 21, '5.3.1.3', '对于经批准后才能报废的器材，应填写《航材报废单》，说明器材报废原因，由相关经理签字批准。', 2, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (544, 21, '5.3.2', '一般消耗件的报废：\n对于消耗件器材的报废，航材与采购部天津分部做报废预留打印《航材报废单》签字报批后做实物出库。《航材报废单》备注字段中要录入器材报原因。', 2, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (545, 21, '5.3.3', '高价周转件的报废：', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (546, 21, '5.3.3.1', '航材与采购部天津分部将器材报废信息输入SAP系统，并打印《航材报废单》按器材单项原值及权限报批，收到批准的《航材报废单》后，在SAP中做器材移库，实物附填写的红色报废标识签发回航材与采购部中心库房保存。《航材报废单》备注字段中要录入器材报原因。', 2, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (547, 21, '5.3.3.2', '《航材报废单》和附属文件扫描发至相关责任部门。', 2, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (548, 21, '5.3.3.3', '报废器材必须与其它器材隔离存放，并应及时移交给报废库。确认报废器材附有《航材报废单》。', 2, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (549, 21, '5.3.4', '危险品和化学品的报废处理：\r\n处理对环境或人身健康有影响的报废危险品，如电池，化工品等，应严格遵守国家相关法律、法规。对于化工品应单独存放。电池、电瓶、气瓶等危险品应有相应的警示标识，进行专门存放。交给具有报废处理资质的单位进行处理。', NULL, '2018-06-28 11:18:21', '2018-06-28 11:18:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (550, 22, '5.1', '发动机的入库', NULL, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (551, 22, '5.1.1', '在发动机入库前要对发动机进行外包装检查，没有外包装的必须包装好后才能入库。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (552, 22, '5.1.2', '如发现外包装破损，应对其破损部位的发动机进行外观检查，检查有无磕碰损坏。如只是外包装破损，应对发动机进行重新包装。如发动机有磕碰损坏，应立即通知相关部门。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (553, 22, '5.1.3', '入库前要检查发动机是否随附可用或不可用挂签。如没有随附挂签的发动机，视为待处理发动机。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (554, 22, '5.2', '发动机的运输', NULL, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (555, 22, '5.2.1', '发动机的场内运输通则', NULL, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (556, 22, '5.2.1.1', '发动机（可用或不可用）要使用托架小心运输。运输时速度必须在允许的最大速度范围内。托架要符合相关发动机生产厂手册的有关要求。发动机的托架和发动机的安全安装要符合维修放行的要求。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (557, 22, '5.2.1.2', '当使用尼龙轮运输工具（运输架或拖车）移动发动机时，只允许短距离移动。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (558, 22, '5.2.1.3', '带有减震装置的移动发动机的设备，只允许慢速移动发动机。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (559, 22, '5.2.1.4', '在长距离移动发动机时，需使用带有空气悬挂装置或者同等柔韧装置的拖车进行拖运。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (560, 22, '5.2.1.5', '充气轮胎的发动机拖车无防震系统，不能用于长距离移动发动机。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (561, 22, '5.2.1.6', '移动发动机时，应在平滑、坚固的路面上慢速行驶。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (562, 22, '5.2.2', '送修或调拨发动机的运输\n对于国内运输，安排指定运输代理用专用气垫减震车操作，航材与采购部天津分部配合装卸工作。', 2, '2018-06-28 11:19:40', '2018-06-28 11:19:40', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (563, 23, '5.1', '清点原则及清点方式', NULL, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (564, 23, '5.1.1', '遵照以实物为准的原则，对库存实物与非库存实物，如在途（调配、在修、借出、领出未退）、借入、外站器材、发动机进行清点。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (565, 23, '5.1.2', '清点的实物包括所有消耗件、可修件、周转件、发动机、APU等有关飞机及发动机的所有部件，以及在库的非飞机器材，以下统称“器材”。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (566, 23, '5.1.3', '清点必须由两人组成一组共同参与实物清点，不能单独做。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (567, 23, '5.1.4', '实物清点人员不允许查看保管账记录；不允许使用涂改液进行涂改。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (568, 23, '5.1.5', '清点方式包括：抽样清点、定期清点、大规模清点（即循环清点）。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (569, 23, '5.2', '清点计划\n制定清点计划，须首先确定清点方式，清点计划包括清点开始日期和结束日期（工作日天数）、清点范围、待清点的项数（批次数或序号数）、平均每日清点项数、责任人等，每年至少要完成一次器材的清点工作。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (570, 23, '5.3', '清点的实施', NULL, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (571, 23, '5.3.1', '在库器材实物清点', NULL, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (572, 23, '5.3.1.1', '对在库的所有器材实物（可用、不可用、外站）进行清点，准备并如实填写清点单、清点差异单（内容要包括架位，件号，批次号，数量、序号、状态、到寿日期等）。清点必须由两人组成一组共同参与实物清点，其中一人称为“ 宣读者” 负责大声说出他所发现的件号,批次号, 数量/序号和它的状况(可用或不可用)。另外一个人称为“ 记录者”负责将以上所有记录如实记录下来。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (573, 23, '5.3.1.2', '清点过程中，对器材伴随的挂签或标签进行检查，如发现缺失，记录到清点单中，向上级主管汇报，并及时纠正。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (574, 23, '5.3.2', '非在库器材（调配、在修、借出、领出未退器材）实物清点', NULL, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (575, 23, '5.3.2.1', '对所有调配、在修、借出、领出未退器材进行清点，准备并如实填写清点单、清点差异单（内容要包括合同号，件号，批次号，数量、序号、器材物理状态等）。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (576, 23, '5.3.2.2', '合同的出具部门负责清点未闭合的合同器材。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (577, 23, '5.3.3', '差异项目复查\n对差异项目须进行复查，至少复查一次；需要复查的清点单要立即交给实物清点人员进行复查；复查与正常清点流程相同。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (578, 23, '5.3.4', '差异项目调整', NULL, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (579, 23, '5.3.4.1', '对于按非固定资产管理的差异项目，经过复查核实后，可直接进行盘盈盘亏差异过账。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (580, 23, '5.3.4.2', '对于按固定资产管理的高价周转件差异项目，不能直接进行账面调整，经过复查核实后，列出差异器材清单，国航产权差异报国航，货航产权差异报货航。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (581, 23, '5.3.5', '准备清点报告', NULL, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (582, 23, '5.3.5.1', '制定航材与采购部天津分部清点报告，清点报告须按产权或客户分别准备，国航产权清点报告交国航，货航产权器材清点报告交货航。若按固定资产管理的器材有差异项目，须附差异器材清单，差异器材清单包括件号、批次号、序号、差异数量、地点等。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (583, 23, '5.3.5.2', '国航器材差异项目盘盈盘亏过账\n公司财务部对航材与采购部天津分部提交的清点报告进行复核，将清点差异报国航股份批准。经国航股份批准后，进行固定资产类器材清点差异的账务处理工作，并及时反馈各责任部门。', 2, '2018-06-28 11:23:39', '2018-06-28 11:23:39', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (584, 24, '5.1', '岗位资质要求', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (585, 24, '5.1.1', '应加强危化品管理和使用人员的资质控制，明确危化品仓储管理、装卸/运输、物料使用、危废处理等岗位的资质要求，确保相关人员在危化品管理与使用过程中能够落实相关规定要求、保障生产安全。', 1, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (586, 24, '5.1.2', '相关人员通过培训应了解危化品基本概念、识别方法和本单位危化品相关管理程序，并掌握其工作范围所涉及危化品的相关物理化学特性、危害特性、安全防护注意事项、储运/使用要求以及应急情况下的处理方法。', 1, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (587, 24, '5.2', '采购要求', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (588, 24, '5.2.1', '采购化工品时，须要求化工品的供应商提供产品的材料安全数据单（MSDS），否则不能采购。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (589, 24, '5.2.2', 'MSDS版本有可能更新，应要求化工品的供应商确保每批到货均附有相应批次的MSDS并明确指定化工品型号和牌号。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (590, 24, '5.2.3', '对于相关技术文件提供多个规范或相同规范规格型号危化品可选的情况，应结合危化品材料安全数据单（MSDS）对其通用性、安全性、使用效果和经济性进行综合评估，择优采购。MSDS查阅网址：http://amemobileapp.ameco.com.cn/msds/index.htm，用户名：ameco\\cbt，密码：cbt', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (591, 24, '5.2.4', '技术支援分部应负责对航材与采购部天津分部提出的化工品替代进行工程评估，并对首次使用的替代化工品有效性、安全性进行现场评估并进行有效监控。', 4, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (592, 24, '5.3', '危险品的入库', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (593, 24, '5.3.1', '航材检验员在收货时核对危险品的相关文件、标识、标签信息与系统是否一致，对于系统信息缺失或与文件不一致的情况，需生成系统差异或以书面、邮件形式通知航材计划部门。', 8, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (594, 24, '5.3.2', '进行危险品装卸、移动时，需根据其危险性佩戴相应的个人防护用品并使用相应的操作工具、设备。', 10, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (595, 24, '5.3.3', '有关危险品的证件和资料如MSDS（Material Safety Data Sheet）等需至少以纸张形式存档。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (596, 24, '5.4', '危险品的储存', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (597, 24, '5.4.1', '危险品库的要求', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (598, 24, '5.4.1.1', '贮存的危险品的地点应设置明显的标志，标志应符合《危险货物包装标志》（GB190-1990）的规定。同一区域贮存两种或两种以上不同级别的危险品时，应按照最高等级危险物品的性能设置标志。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (599, 24, '5.4.1.2', '危险品库门口应设置醒目的防火标志 ，进入甲、乙类物品库房的人员必须登记并交出携带的火种。危险品的储存区域应设置应急事故照明及疏散指示标志。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (600, 24, '5.4.1.3', '消防器材应当设置在明显和便于取用的地点，周围不准堆放物品和杂物。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (601, 24, '5.4.1.4', '库区的消防车道和仓库的安全出口、消防通道，严禁堆放物品。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (602, 24, '5.4.1.5', '危险品储存货架应设置静电接地，其接地阻值不应大于30欧姆。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (603, 24, '5.4.1.6', '在临时储存危险品的区域内，要有防爆柜或密闭柜的设备配置，并分类、单独存放，在醒目处标明储存物品的名称、性质及灭火方法。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (604, 24, '5.4.1.7', '在储存危险品时，应将其放置在所适应的消防设施内。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (605, 24, '5.4.2', '危险品的储存管理', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (606, 24, '5.4.2.1', '危险品应存放于危险物品专用的仓库或是局方认可的指定区域，有明显的分隔，与普通航材分开存放。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (607, 24, '5.4.2.2', '危险品应按危险性的不同的类别、项别分别放置在不同的区域中。满足MSDS及使用说明书、其他相关的国家法规及DGR中有关危险品存储、分离与隔离的要求。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (608, 24, '5.4.2.3', '库存物品应当分类、分垛储存，每垛占地面积不宜大于100平方米，垛与垛间距不小于1米，垛与墙间距不小于0.5米，垛与梁、柱间距不小于0.3米，主要通道的宽度不小于2米。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (609, 24, '5.4.2.4', '危化品甲、乙类物品和一般物品以及容易相互发生化学反应或者灭火方法不同的物品，必须分间、分库、存放在专用库房内，或存放在专用储存柜中。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (610, 24, '5.4.2.5', '易自燃或者遇水分解的物品，必须在湿度较低、通风良好和空气干燥的场所储存，并安装专用仪器定时检测，严格控制湿度与温度。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (611, 24, '5.4.2.6', '危化品甲、乙类物品的包装容器应当牢固、密封，发现破损、残缺，变形和物品变质，等情况时，应当及时进行安全处理，严防跑、冒、滴、漏。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (612, 24, '5.4.2.7', '储存甲、乙、丙类物品的库房布局、储存类别不得擅自改变。各类危险品不得与禁忌物料混合贮存，禁忌物料配置见《常用化学危险品贮存通则》（GB 15603-1995）。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (613, 24, '5.4.2.8', '压缩气体和液化气体必须与爆炸物品、氧化剂、易燃物品、腐蚀性物品隔离贮存。易燃气体不得与助燃气体、剧毒气体同贮；氧气不得与油脂混合贮存。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (614, 24, '5.4.2.9', '易燃液体、遇湿易燃物品、易燃固体不得与氧化剂混合贮存，具有还原性氧化剂应单独存放。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (615, 24, '5.4.2.10', '腐蚀性物品，包装必须严密，不允许泄漏，严禁与液化气体和其它物品共存。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (616, 24, '5.4.2.11', '有毒物品应贮存在阴凉、通风、干燥的场所，注意避免阳光直晒，不要接近酸类物质。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (617, 24, '5.4.2.12', '危险品库内储存的危险品的总量不能超过库房设计批准的量。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (618, 24, '5.4.2.13', '领出的化工品一经开封，库房一律不再回收。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (619, 24, '5.4.2.14', '管制类物品的存放需满足当地公安部门的要求。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (620, 24, '5.4.2.15', '所有的危险品在存储时都必须进行记录，该记录需可被随时查阅。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (621, 24, '5.5', '危险品的运输', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (622, 24, '5.5.1', '内部运输', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (623, 24, '5.5.1.1', '除安装了防爆装置的电瓶车、铲车外，任何机动车辆不得进入危险品库区。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (624, 24, '5.5.1.2', '不得使用同一车辆运输互为禁忌的危险品。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (625, 24, '5.5.1.3', '在运输时，应确认被运输的危险品已采取有效的包装措施，以防止因包装破裂并致发生泄漏、起火爆炸等伤及作业人员的事件。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (626, 24, '5.5.1.4', '当运输的航材属于危险品货物时，必须由通过公司危险品操作培训，具备危险品操作资质的人员进行操作。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (627, 24, '5.5.2', '外部运输', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (628, 24, '5.5.2.1', '总则：危险品的外部运输过程要满足MSDS及使用说明书的相关运输要求，符合DGR、国家危化品运输的相关标准。如需使用运输代理，则需选择和委托符合上述标准，具备危化品运输资质的代理公司实施运输。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (629, 24, '5.5.2.2', '社会道路运输：危险品在社会道路上的运输，需符合国家、所处地区的法律、法规要求。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (630, 24, '5.5.2.3', '航空运输：危险品航材的包装及交运应符合IATA《危险品规则》要求，并粘贴好相应的标签，交给运输代理时如实告知危险品航材相关信息。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (631, 24, '5.6', '危险品的使用', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (632, 24, '5.6.1', '针对所有危化品，除使用阶段外严禁分装、配制。', 9, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (633, 24, '5.6.2', '在危险品使用过程中，操作人员须根据所使用危化品的危险特性（爆炸性、燃烧性、有毒有害性、氧化性、腐蚀性等）和使用条件进行分类管理。确保相关人员在危化品管理过程中按照规章、行业标准、持续适航性资料、MSDS及使用说明书的相关要求落实管理和使用要求及安全注意事项。', 10, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (634, 24, '5.6.3', '在执行清洁、润滑等技术手册中没有具体操作步骤的危化品使用作业时，应当充分做好生产准备，明确危化品使用型号、工作要求和安全注意事项等。', 10, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (635, 24, '5.6.4', '应当规范作业现场允许携带的危化品用量，在维修工作现场配备安全的危化品盛放容器，同时规范现场作业时危化品的密闭防洒。同时，应当严格作业前的安全条件确认，做好作业现场的安全警示，安排专人监护（若需），落实防护措施，确保作业安全。', 10, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (636, 24, '5.6.5', '可燃危化品使用过程中，应当明确现场的火源隔离和施工环境的控制：作业现场不应有任何明火和热作业，不应使用可产生火花的工具、设备和车辆；确保维修工作现场的充分通风；在可燃蒸汽危险区域，不应对飞机进行通电，应使用相应等级的防爆电气设备和灯具；明确规定工作现场应急器材的配备要求并有效执行。', 10, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (637, 24, '5.6.6', '当委托维修涉及危化品管理和使用时要对其工作进行监督检测。', 9, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (638, 24, '5.7', '危化品监督：建立多层次、全方位的危化品监督机制和发放、归还管理机制，明确各级生产管理人员的安全监督职能以及质量安全系统在日常监督检查、安全措施落实、综合安全管理等方面的相关要求。', 7, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (639, 24, '5.8', '安全防护', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (640, 24, '5.8.1', '航材与采购部天津分部订货应要求化工品供应商或生产厂家提供MSDS和相关使用说明书（如有）。', 2, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (641, 24, '5.8.2', '1）根据生产厂家的MSDS和相关使用说明书（如有）以及现场使用情况评估、制定、修订存储、使用、回收、应急处理等环节的安全操作规程，配备合适的防护设备，并对相关人员进行有针对性的培训。\n2）修订或更新内容后对相关人员进行有针对性的培训。', 7, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (642, 24, '5.8.3', '应确保MSDS信息和使用说明书持续有效、并对其修订或更新进行评估，及向相关人员通报修订或更新内容，告知最新的安全防护措施要求，同时通知人力资源分部。', 7, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (643, 24, '5.8.4', '应当根据各类危化品的防护要求在工作现场配备相应的个人防护设备，明确危化品使用过程中个人安全的防护要求，对相关人员进行培训，确保相关防护要求得到有效执行。', 5, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (644, 24, '5.9', '应急处理', NULL, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (645, 24, '5.9.1', '应严格贯彻落实安全管理体系应急管理要求，根据所使用危化品的范围和环境，建立危化品泄漏、火灾、爆炸、人员受伤等事件的应急处置预案。', 9, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (646, 24, '5.9.2', '应对消防设备等应急设施进行定期检查和维护，保持状态完好使用，并对相关人员进行操作训练，确保在紧急情况下正确使用应急设施。', 7, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (647, 24, '5.9.3', '应对危化品存储、运输、使用等环节的工作人员进行应急预案培训，并制定应急演练计划，定期组织危化品相关事件的应急演练，确保工作人员掌握应急处置流程，提高应急响应能力。', 9, '2018-06-28 11:30:06', '2018-06-28 11:30:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (648, 25, '5.1', '机轮的控制管理', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (649, 25, '5.1.1', '新采购轮毂送定检中队装配', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (650, 25, '5.1.1.1', '1）航材计划部门根据新采购轮毂的入库信息，通过航材管理系统打印《器材移库指令单》交库房，通知库房将轮毂由可用库移至待修库。\n2）航材库房根据《器材移库指令单》在航材管理系统中办理轮毂移库，并将轮毂实物交到待修库，将一份《器材移库指令单》转交航材送修部门。\n3）航材送修部门根据《器材移库指令单》，通过航材管理系统创建送修合同，将轮毂送到定检中队进行机轮组装。', 2, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (651, 25, '5.1.1.2', '4）生产计划控制分部2个工作日内按《航材送修合同》下发生产指令至定检中队。', 6, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (652, 25, '5.1.1.3', '5）定检中队按照生产指令执行器材修理。如果定检中队没有修理能力，反馈航材送修计划员，并将实物退回航材待修库。', 3, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (653, 25, '5.1.2', '装配好的机轮\n1）机轮在定检中队组装完毕后，返回航材库房验收区，需定检中队提供AAC-038证件，作为航材检验的依据。航材检验员按照01-08-001《器材检验程序》进行入库检验，并在航材管理系统中录入检验结果，检验合格后入机轮可用区。\n2）机轮实物也可以存放在定检中队保管，航材检验人员在定检中队完成，并在航材管理系统中录入检验结果，打印《附件标识挂签》在航材管理系统中录入入库信息，检验合格后入机轮可用区。', 8, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (654, 25, '5.1.3', '机轮的领用和控制', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (655, 25, '5.1.3.1', '可用机轮存的领用：维修中队要领用机轮，由本部门经授权的人员在器材管理系统中做预留和确认，依据相关凭证到库房领料。', 9, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (656, 25, '5.1.3.2', '维修人员在实施机轮的更换工作时，须妥善保管和存放可用机轮的护盖，防止丢失，机轮拆下后须将机轮护盖装回拆下机轮，防止机轮轴承接触异物而损伤，将拆下后的机轮退回定检中队待修库之前，须确保退库机轮的护盖完整。', 9, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (657, 25, '5.1.3.3', '考虑到实际操作的便捷性，维修人员可以将拆下后的不可用机轮按要求直接退回到定检中队，同时须确保退库机轮护盖的完整，定检中队需检查机轮护盖是否在位，如不在位要求退料人补齐。定检中队将更换机轮的《附件标识挂签》传递到航材与采购部天津分部。', 9, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (658, 25, '5.1.4', '拆下机轮的送修', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (659, 25, '5.1.4.1', '1）航材待修库房人员根据《附件标识挂签》拆下器材的信息，在航材管理系统将拆下机轮信息录入，并将挂签传递给航材附控部门。\n2）航材附控部门通过航材管理系统查询待修机轮的历史记录以及CAMP报表确定修理级别；航材送修计划员依据修理级别，打印《航材送修合同》并将《航材送修合同》送交生产计划控制分部。', 2, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (660, 25, '5.1.4.2', '生产计划控制分部按《航材送修合同》下发生产指令至定检中队。', 6, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (661, 25, '5.1.4.3', '定检中队按照生产指令执行器材修理。如果定检中队没有修理能力，反馈航材送修计划员，并将实物退回航材待修库。', 3, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (662, 25, '5.1.4.4', '定检中队完成工作后，应于1个工作日内按手续把器材返还航材与采购部天津分部，送修合同日交回生产计划控制分部。', 3, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (663, 25, '5.1.4.5', '生产计划控制分部2日内反馈航材与采购部天津分部。', 6, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (664, 25, '5.2', '电瓶的控制管理', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (665, 25, '5.2.1', '新采购电瓶送定检中队装配', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (666, 25, '5.2.1.1', '1）航材计划部门根据新采购电瓶的入库信息，通过航材管理系统打印《器材移库指令单》交库房，通知库房将电瓶由可用库移至待修库。\n2）航材库房根据《器材移库指令单》在航材管理系统中办理电瓶移库，并将电瓶实物交到待修库，将一份《器材移库指令单》转交航材送修部门。\n3）航材送修部门根据《器材移库指令单》，通过航材管理系统创建送修合同，将电瓶送到定检中队进行组装。', 2, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (667, 25, '5.2.1.2', '生产计划控制分部2个工作日内按《航材送修合同》下发生产指令至定检中队。', 6, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (668, 25, '5.2.1.3', '定检中队按照生产指令执行器材修理。如果定检中队没有修理能力，反馈航材送修计划员，并将实物退回航材待修库。', 3, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (669, 25, '5.2.2', '拆下电瓶的送修', NULL, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (670, 25, '5.2.2.1', '1）航材待修库房人员根据《附件标识挂签》拆下器材的信息，在航材管理系统将拆下电瓶信息录入，并将挂签传递给航材附控部门。\n2）航材附控部门通过航材管理系统查询待修电瓶的历史记录以及CAMP报表确定修理级别；航材送修计划员依据修理级别，打印《航材送修合同》并将《航材送修合同》送交生产计划控制分部。', 2, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (671, 25, '5.2.2.2', '生产计划控制分部2个工作日内按《航材送修合同》下发生产指令至定检中队。', 6, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (672, 25, '5.2.2.3', '定检中队按照生产指令执行器材修理。如果定检中队没有修理能力，反馈航材送修计划员，并将实物退回航材待修库。', 3, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (673, 25, '5.2.2.4', '定检中队完成工作后，应于1个工作日内按手续把器材返还航材与采购部天津分部，送修合同日交回生产计划控制分部。', 3, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (674, 25, '5.2.2.5', '生产计划控制分部2日内反馈航材与采购部天津分部。', 6, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (675, 25, '5.2.3', '内场修复后的电瓶\n电瓶在定检中队修复后，返回航材库房验收区，需定检中队提供AAC-038证件，作为航材检验的依据。航材检验员按照01-08-001《器材检验程序》进行入库检验，并在航材管理系统中录入检验结果，检验合格后入电瓶可用区。', 8, '2018-06-28 11:32:56', '2018-06-28 11:32:56', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (676, 26, '5.1', '年度培训计划制定', NULL, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (677, 26, '5.1.1', '培训需求收集', NULL, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (678, 26, '5.1.1.1', '各部门根据分公司培训大纲要求和人员发展提升需要在每年9月15日前，提交《年度培训计划申请表》Form No.: TM-001R1。年度培训需求的分析要点如下：\r\n1）公司发展战略及人力资源规划；\r\n2）适航法规的要求和质量管理的要求；\r\n3）新机型的引进，新工艺、新技术、新设备的使用；\r\n4）人岗匹配，满足大纲中授权人员培训标准的要求。', NULL, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (679, 26, '5.1.1.2', '航空安全质量分部等运行支持部门除上报本单位培训需求外还需根据公司总体运行情况、不安全事件的发生情况、审核结果提出相应的培训需求报人力资源分部。', 8, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (680, 26, '5.1.2', '人力资源分部结合各部门所报需求，根据适航法规、工作程序、培训大纲要求、岗位资格要求对分公司员工的培训需求进行全面客观的分析、评估，制定本单位年度培训计划及经费预算。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (681, 26, '5.1.3', '人力资源分部将分公司《年度培训计划申请表》上报分公司总经理审批，并于每年9月30日前上报公司培训部。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (682, 26, '5.1.4', '人力资源分部根据公司批准的年度培训经费预算对年度培训计划进行调整确认，经分公司总经理审批后在5个工作日内上报公司培训部审批。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (683, 26, '5.1.5', '年度培训计划在获得公司批准前，分公司可按已上报的年度培训需求计划实施，待年度培训计划获得公司批准后，对培训计划进行相应调整。', NULL, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (684, 26, '5.2', '临时培训计划管理', NULL, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (685, 26, '5.2.1', '各部门对因实际生产需要确需增加，但未列入年度培训计划的培训需求，若需实施或委托外部培训机构实施（产生费用的），需填写《临时培训计划申请表》Form No.: TM-002R1；能够在分公司内部实施且不产生费用的航空安全质量分部填写《培训需求申请表（A类）》Form No.: TM-T01、其他部门填写《培训需求申请表（B类）》Form No.: TM-T02，报人力资源分部。', 10, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (686, 26, '5.2.2', '批准后，组织实施；其他的临时培训，只需在月报表中向培训部报备。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (687, 26, '5.3', '培训计划完成情况报表\n人力资源分部应于每月28日前（遇节假日顺延）根据上月完成情况及确认的后一个月培训实施计划，填写《年度培训完成情况月报表》Form No.: TM-003R1、《年度培训后（）月动态计划表》Form No.: TM-004R1上报公司培训部。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (688, 26, '5.4', '年度培训计划调整与监控', NULL, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (689, 26, '5.4.1', '每年7月中旬，根据分公司培训计划项目履行情况、适航法规及生产需要等变化情况，在培训部组织下调整年度培训计划及培训预算。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (690, 26, '5.4.2', '人力资源分部应于下一年度1月15日前完成对上一年度培训计划完成情况评估，对未完成的培训项目进行分析，提出评估意见。对仍需要完成的培训项目，将其纳入下一年度培训计划，填写《年度培训计划完成情况评估表》Form No.: TM-005R1报公司培训部。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (691, 26, '5.4.3', '培训情况评估结果纳入到下一年度计划监控。', 1, '2018-07-04 09:45:58', '2018-07-04 09:45:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (692, 27, '5.1', '培训计划实施管理', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (693, 27, '5.1.1', '人力资源分部按照公司培训部批准的年度培训计划及临时培训计划实施培训。培训方式包括课堂培训、自学培训和委托培训。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (694, 27, '5.1.2', '培训计划项目若属于公司《CAAC培训大纲》课程，由公司培训部安排实施；其它培训项目由人力资源分部自行实施。', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (695, 27, '5.1.3', '由公司培训部安排实施的培训课程分为如下两种处理方式：', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (696, 27, '5.1.3.1', '公司培训部结合自身能力及资源情况制定的统一培训实施计划，下发分公司报名参加的，组织分公司相关人员报名参加。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (697, 27, '5.1.3.2', '分公司选用兼职教员实施的内训课程：人力资源分部结合本单位兼职教员情况，提出培训项目实施具体时间意向，以月报形式报培训部备案。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (698, 27, '5.2', '委托培训', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (699, 27, '5.2.1', '委托培训由人力资源分部组织实施。委托培训外部培训机构选择原则：\n1）本着降本增效原则，优先选择本区域具备相关培训资质能力、质量高、成本低的培训机构实施培训。\n2）优先选择国航系内部培训机构，争取内部协议价格。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (700, 27, '5.2.2', '基于上述原则，建立并管理《委托培训供应商清单》Form No.: TM-010R1，或直接使用公司《委托培训供应商清单》。《CAAC培训大纲》培训标准中的委托培训课程应从《委托培训供应商清单》中选择培训供应商。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (701, 27, '5.2.3', '人力资源分部应至少提前一个月与委托培训实施机构沟通，确定培训意向后报培训部批准；需要签署培训协议的培训项目，由人力资源分部组织参训人员签署培训协议。协议完成签署后，分公司按照培训机构的培训计划完成培训。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (702, 27, '5.2.4', '学员培训完成后，应将培训证书复印件（若有）交人力资源分部。', 10, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (703, 27, '5.2.5', '人力资源分部将学员培训证书复印件（若有）存入个人技术档案，将培训记录录入培训管理系统/SAP系统。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (704, 27, '5.3', '公司内部培训实施形式\n内部培训实施形式包括课堂培训、实习培训、自学培训。', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (705, 27, '5.3.1', '课堂培训', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (706, 27, '5.3.1.1', '人力资源分部在各项培训资源满足开课条件后，下发正式的培训通告，将开课信息通知培训对象所在部门。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (707, 27, '5.3.1.2', '各部门负责协调培训对象的工作和培训时间，使其能够按照计划参加课堂培训。', 10, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (708, 27, '5.3.1.3', '班级管理员应严格考勤记录，对于学员缺勤学时数超过该课程教学大纲规定学时数20%，视为培训不合格。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (709, 27, '5.3.1.4', '培训结束后，人力资源分部应将培训记录录入培训管理系统/SAP系统，并妥善保存培训记录。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (710, 27, '5.3.2', '实习培训', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (711, 27, '5.3.2.1', '分公司在组织完成相应理论培训后，将开课信息通知培训对象，按要求组织培训。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (712, 27, '5.3.2.2', '各部门负责协调培训对象的工作和培训时间，使其能够按照计划参加实习培训。', 10, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (713, 27, '5.3.2.3', '按照该课程《教学大纲》及工作条目要求，指导教员实施培训。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (714, 27, '5.3.2.4', '实习人员达到培训目标后，由指导教员填写《实习操作培训记录表》Form No.: TM-007R1，完成签署评估，并将记录表作为培训记录保存。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (715, 27, '5.3.2.5', '对于多名学员参加培训的，可填写《实习培训记录审核汇总表》Form No.: TM-008R1，并作为培训记录保存。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (716, 27, '5.3.3', '自学培训', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (717, 27, '5.3.3.1', '人力资源分部在组织自学培训时应作好答疑准备工作，指定相关人员对疑难问题进行解答，确保自学效果。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (718, 27, '5.3.3.2', '选择自学形式的培训对象应当在以前经历过类似项目的课堂培训并且能正确理解培训教材所使用的语言。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (719, 27, '5.3.3.3', '自学培训结束后，各部门应组织参加自学的学员填写《自学记录表》Form No.: TM-006R1，交人力资源分部保存。', 10, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (720, 27, '5.3.3.4', '人力资源分部保存各部门完成的《自学记录表》，将培训记录录入培训管理系统/SAP系统。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (721, 27, '5.3.4', '临时培训', NULL, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (722, 27, '5.3.4.1', '在培训计划之外按照实际工作需要展开培训时，航空安全质量分部填写《培训需求申请表（A类）》Form No.: TM-T01、其他部门填写《培训需求申请表（B类）》Form No.: TM-T02后向人力资源分部提出培训需求。培训需求须经本部门经理批准。', 10, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (723, 27, '5.3.4.2', '人力资源分部接到各部门的培训需求后，按照上述程序要求组织培训。', 1, '2018-06-28 13:19:02', '2018-06-28 13:19:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (724, 28, '5.1', '指定授权签署人员', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (725, 28, '5.1.1', '公司授权', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (726, 28, '5.1.1.1', '公司一级授权：公司责任经理，或由责任经理授权的质量经理。责任经理或授权的质量经理有权对公司体系下的各类人员进行授权。', 8, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (727, 28, '5.1.1.2', '公司二级授权：公司质量经理或其授权的航空安全与质量管理部/工程部/航材与采购部/运行管理部/航空技术培训部总经理、各维修单位质量副总经理。获得质量经理授权的人员有权对公司体系下部分人员进行授权。', 8, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (728, 28, '5.1.2', '分公司授权\n分公司责任经理，或由责任经理授权的质量经理。责任经理或获得授权的质量经理授权有权对分公司体系下的各类人员进行授权。', 8, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (729, 28, '5.1.3', '具体划分参见《各类人员授权资质分类表》。', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (730, 28, '5.2', '各类人员授权资质分类', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (731, 28, '5.2.1', '具体划分参见7.2.2《各类人员授权资质分类表》。', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (732, 28, '5.2.2', '教员授权和管理按照《CAAC培训大纲》4.4要求执行。', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (733, 28, '5.3', '各类人员授权资格要求', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (734, 28, '5.3.1', '各类人员授权资格要求见本程序7.2.1《人员授权项目资格要求》，各类人员授权培训要求见《CAAC培训大纲》。', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (735, 28, '5.3.2', '兼职人员的资格要求必须满足相应授权项目的人员资格要求。', NULL, '2018-06-28 13:29:42', '2018-06-28 13:29:42', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
