INSERT INTO `tb_step_info` VALUES (2331, 90, '5.1', '飞机运行应急指挥总则：AMC、MCC共同构成了Ameco的飞机运行的应急指挥系统，AMC为总控中心，具体事件的应急指挥以事件发生地的属地MCC为中心。生产计划控制分部参照本管理程序的基本原则和天津机场的特殊要求，制定相应的应急处理工作流程。', 6, '2018-06-29 14:13:28', '2018-06-29 14:13:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
