INSERT INTO `tb_step_info` VALUES (2463, 94, '5.3.2.2', '技术支援分部负责根据技术标准确定需采取的抢修方案。', 4, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
