INSERT INTO `tb_step_info` VALUES (2467, 94, '5.3.2.6', '运行管理部航站维修管理项目负责了解外站维修资源（含外委协议的谈判和办理协议签署手续），协调营业部落实证件、国外代垫款事宜。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2468, 94, '5.3.2.7', '由AMC通知财务部，由财务部判断损伤是否涉及保险内容，如涉及保险内容财务部跟进后续工作。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2469, 94, '5.3.2.8', '确认是否成立调查组赴现场。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2470, 94, '5.3.2.9', '确定执行损伤抢修部门。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
