INSERT INTO `tb_step_info` VALUES (2472, 94, '5.4.1', '抢修方案、工卡、手册的准备。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2473, 94, '5.4.2', '实施结构损伤抢修方案所需的器材、工具设备及厂房设施。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2474, 94, '5.4.3', '实施结构损伤抢修工作的人员名单及负责人。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2475, 94, '5.4.4', '实施结构损伤抢修工作的人员前往事发地点的交通方式。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2476, 94, '5.4.5', '实施结构损伤抢修工作所需器材、工具设备的运输方式。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2477, 94, '5.4.6', '落实结构损伤抢修所需器材的缺件情况。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2478, 94, '5.5', '抢修方案的制定与准备', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2479, 94, '5.5.1', '应急小组配合分公司制定抢修方案及器材、工具、设备的运输方式，并将紧急抢修方案中所涉及的抢修周期、人员、器材、设备设施及当地需提供服务的明细向AMC通报。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2480, 94, '5.5.1.1', '技术支援分部负责依据抢修方案出具相应的工作单及器材、工具设备清单，并下发生产计划控制分部。', 4, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2481, 94, '5.5.1.2', '生产计划控制分部按照技术支援下发工作单并结合常规控制条目进行工作组包，进行器材预留。同时将工作包中的器材清单、工具设备清单及预留号下发相关部门。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2482, 94, '5.5.1.3', '生产支援分部按工具设备清单完成准备。', 5, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2483, 94, '5.5.1.4', '航材与采购部天津分部按器材清单完成准备。', 2, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2484, 94, '5.5.1.5', '定检中队排故人员分别进行器材、工具设备清点及领取工作，进行打包并将器材、工具设备清单通知MCC。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2485, 94, '5.5.1.6', '生产支援分部负责人员的运输。', 5, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2486, 94, '5.5.1.7', '如器材、工具设备需单独运输，则由航材与采购部天津分部负责。', 2, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2487, 94, '5.5.2', 'MCC根据预计的结构抢修周期负责72小时内的运力调整，生产计划控制分部进行72小时以外的运力调整。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2488, 94, '5.5.3', '结构抢修小组负责人确定后，由该负责人根据实施方案指定人员准备器材、工具、设备和办理乘机手续，并定时向AMC通报准备情况。在准备过程中发生的任何问题均要向AMC或MCC通报，由AMC或MCC指定人员或部门解决和落实发生的问题。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2489, 94, '5.5.4', '抢修组成员、工具设备、器材由分公司派出直接前往事发地点的，由MCC全权负责人员、工具设备、器材的组织运输工作，并及时向AMC通报准备情况。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2490, 94, '5.5.5', '抢修组由执行结构抢修基地派出经北京前往境内事发地点的，由MCC负责抢修组人员到达北京的组织运输工作。AMC负责指定人员为抢修组人员办理由北京出发的乘机手续，MCC根据外站紧急抢修名单向AMC提供派出人员清单、身份证号等。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2491, 94, '5.5.6', '中国境外的结构抢修，抢修人员派出经北京前往事发地点的，按照01-11-030《境外排故人员派遣手续办理工作程序》为抢修人员办理乘机手续。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2492, 94, '5.5.7', '对于器材的需求由航材与采购部天津分部根据抢修方案进行准备，视情向航材与采购部AOG提出支援需求，由航材与采购部AOG协同天津分部共同解决。AOG负责将器材运达目的地（含中转），对报关和运输过程全程监控和跟踪，将结果向AMC通报。', 2, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2493, 94, '5.5.8', '航材与采购部天津分部协同航材与采购部AOG负责组织工具设备、器材的运输工作。涉及境外抢修的基地需向AOG提供需托运的工具设备、器材的中英文清单、包装数量、体积、重量及发运地址。', 2, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2494, 94, '5.6', '紧急抢修小组的组成', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2495, 94, '5.6.1', '分公司执管飞机，优先由分公司派出外站结构抢修人员，并指定带队负责人或组长，并将指定组长的联系方式报AMC，费用由分公司支付（国内）。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2496, 94, '5.6.2', '若分公司无能力或无法派出抢修组时，或不能第一时间到达事发地点时，应急小组应综合考虑人力、物力、快速就近支援等因素，指定最合适的维修基地派出抢修小组，由分公司对人员派出基地以《外站结构损伤紧急抢修工作委托书》Form No.：PM-082的形式进行书面委托。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2497, 94, '5.6.3', '非分公司执管飞机，被应急小组指定由分公司执行结构抢修工作，应积极配合抢修工作，制订抢修方案，组织合格的人员组成结构抢修小组并指定带队负责人或组长完成抢修工作，同时垫付相关费用（国内），指定组长的联系方式应及时报AMC。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2498, 94, '5.7', '抢修方案的实施', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2499, 94, '5.7.1', '营业部、驻外机务工程师、航线协议维修单位负责办理器材和工具设备的提取。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2500, 94, '5.7.2', '抢修人员到达后立即对结构损伤情况进行详细检查，及时将损伤的检查状况向AMC通报。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2501, 94, '5.7.3', '根据对损伤情况的进一步检查，AMC视情组织工程部、质量部、航材与采购部等部门召开会议，进一步细化修理方案，并将细化后的方案通知到抢修小组。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2502, 94, '5.7.4', '生产计划控制分部、AMC对修理工作进行全程监控和跟踪，及时协调解决抢修过程中发生的问题。抢修组负责人定时或随时向AMC汇报抢修进度、突发情况、缺件情况、及计划采取的措施，AMC定时或随时组织召开由工程部、航空安全与质量管理部、航材与采购部、运行管理部及飞机执管维修基地参加的支持小组会议，对抢修组提出的问题进行研讨，视情修改解决方案。', 6, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2503, 94, '5.7.5', '对于更新后的抢修方案，抢修组应按新方案制定实施措施，超手册修理和重大修理由工程部报局方批准。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2504, 94, '5.8', '抢修后的处理', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2505, 94, '5.8.1', '根据抢修工作进度，抢修组对结束时间进行预测，并提前48小时（超48小时停场）向AMC通报，AMC将信息向运控、总队、客舱等部门发布，以确保各保证各单位做好相关准备，如安排调机或执行航班的机组人员等，以保证飞机和抢修人员回到基地站。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2506, 94, '5.8.2', '抢修工作全部完成后，抢修小组组长负责填写《飞机结构抢修报告》Form No.:PM-083，并与飞机执管维修基地生产计划控制分部进行交接。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2507, 94, '5.8.3', '根据抢修工作的后续要求AMC配合AOC各部门安排完成后续工作所需的72小时内停场，运行管理部维修计划项目配合商务委员会完成72小时外的停场安排。', NULL, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2508, 94, '5.8.4', '抢修小组负责抢修后的工具、设备、航材的清点、整理及清单准备工作并提交营业部，航材与采购部负责协调营业部完成抢修后工具、设备、航材的回程运输工作。', 3, '2018-06-29 14:20:17', '2018-06-29 14:20:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2509, 95, '5.1', '起落架重大故障信息的获得\n参见01-11-023《工程机务原因航班不正常管理程序》。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2510, 95, '5.2', '成立应急小组', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2511, 95, '5.2.1', '组长：Ameco值班领导', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2512, 95, '5.2.2', '副组长：运行管理部领导、航空安全与质量管理部领导、工程部领导、航材与采购部领导、分公司领导。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2513, 95, '5.2.3', '组员：运行管理部AMC值班经理、运行管理部航站维修管理项目、工程部系统工程师、航空安全与质量管理部值班人员、航材与采购部AOG项目、生产计划控制经理。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2514, 95, '5.2.4', 'AMC负责通知应急小组成员到AMC进行集结；分公司领导及生产计划控制经理到本单位集结。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2515, 95, '5.3', '会议制度', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2516, 95, '5.3.1', 'AMC负责组织应急小组召开起落架重大故障紧急抢修会议，并使用《运行管理部外站起落架重大故障紧急会议记录单》Form No.: PM-084进行记录。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
