INSERT INTO `tb_step_info` VALUES (2519, 95, '5.3.2.2', '技术支援分部负责根据技术标准确定需采取的抢修方案。', 4, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2520, 95, '5.3.2.3', '工程部负责与厂家联系制订超手册修理方案和紧急支援。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
