INSERT INTO `tb_step_info` VALUES (2523, 95, '5.3.2.6', '运行管理部航站维修管理项目负责了解外站维修资源（含外委协议的谈判和办理协议签署手续），协调营业部落实证件、国外代垫款事宜。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2524, 95, '5.3.2.7', '由AMC通知财务部，由财务部判断损伤是否涉及保险内容及跟进后续工作。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2525, 95, '5.3.2.8', '确认是否成立调查组赴现场。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2526, 95, '5.3.2.9', '确定执行起落架重大故障抢修部门。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2527, 95, '5.4', '预计抢修周期', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2528, 95, '5.4.1', '抢修方案、工卡、手册的准备。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2529, 95, '5.4.2', '实施起落架重大故障抢修方案所需的器材、工具设备及厂房设施。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2530, 95, '5.4.3', '实施起落架重大故障抢修工作的人员名单及负责人。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2531, 95, '5.4.4', '实施起落架重大故障抢修工作的人员前往事发地点的交通方式。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2532, 95, '5.4.5', '实施起落架重大故障抢修工作所需器材、工具设备的运输方式。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2533, 95, '5.4.6', '落实起落架重大故障抢修所需器材的缺件情况。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2534, 95, '5.5', '抢修方案的制定与准备', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2535, 95, '5.5.1', '应急小组配合执行起落架重大故障抢修单位制定抢修方案及器材、工具、设备的运输方式，并将紧急抢修方案中所涉及的抢修周期、人员、器材、设备设施及当地需提供服务的明细向AMC通报。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2536, 95, '5.5.1.1', '技术支援分部负责依据抢修方案出具相应的工作单及器材、工具设备清单，并下发生产计划控制分部。', 4, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2537, 95, '5.5.1.2', '生产计划控制分部按照技术支援分部下发工作单并结合常规控制条目进行工作组包，进行器材预留。同时将工作包中的器材清单、工具设备清单及预留号下发相关部门。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2538, 95, '5.5.1.3', '生产支援分部按工具设备清单完成准备。', 5, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2539, 95, '5.5.1.4', '航材与采购部天津分部按器材清单完成准备。', 2, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2540, 95, '5.5.1.5', '定检中队排故人员分别进行器材、工具设备清点及领取工作，进行打包并将器材、工具设备清单通知MCC。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2541, 95, '5.5.1.6', '生产支援分部负责人员的运输。', 5, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2542, 95, '5.5.1.7', '如器材、工具设备需单独运输，则由航材与采购部天津分部负责。', 2, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2543, 95, '5.5.2', 'MCC根据预计的结构抢修周期负责72小时内的运力调整，生产计划控制分部进行72小时以外的运力调整。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2544, 95, '5.5.3', '抢修小组负责人确定后，由该负责人根据实施方案指定人员准备器材、工具、设备和办理乘机手续，并定时向AMC通报准备情况。在准备过程中发生的任何问题均要向AMC或MCC通报，由AMC或MCC指定人员或部门解决和落实发生的问题。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2545, 95, '5.5.4', '抢修组成员、工具设备、器材由执行起落架重大故障抢修单位派出直接前往事发地点的，由MCC全权负责人员、工具设备、器材的组织运输工作，并及时向AMC通报准备情况。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2546, 95, '5.5.5', '抢修组由执行起落架重大故障抢修单位派出经北京前往境内事发地点的，由MCC负责抢修组人员到达北京的组织运输工作。AMC负责指定人员为抢修组人员办理由北京出发的乘机手续，执行起落架重大故障抢修的单位根据外站紧急抢修名单向AMC提供派出人员清单、身份证号等。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2547, 95, '5.5.6', '中国境外的起落架重大故障抢修，抢修人员派出经北京前往事发地点的，按照01-11-030《境外排故人员派遣手续办理工作程序》为抢修人员办理乘机手续。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2548, 95, '5.5.7', '方案中对于器材的需求由分公司根据抢修方案进行准备，视情向航材与采购部AOG提出支援需求，由航材与采购部AOG协同航材与采购部天津分部共同解决，AOG负责将器材运达目的地（含中转），对报关和运输过程全程监控和跟踪，将结果向AMC通报。', 2, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2549, 95, '5.5.8', '航材与采购部天津分部负责组织工具设备、器材的运输工作。涉及境外抢修的单位需向AOG提供需托运的工具设备、器材的中英文清单、包装数量、体积、重量及发运地址。', 2, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2550, 95, '5.6', '外站起落架故障抢修小组的组成', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2551, 95, '5.6.1', '分公司执管飞机，优先由分公司派出外站起落架故障抢修小组，并指定带队负责人或组长，并将指定组长的联系方式报AMC，费用由分公司支付（国内）。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2552, 95, '5.6.2', '若分公司无能力或无法派出抢修组，或不能第一时间到达事发地点时，应急小组应综合考虑人力、物力、快速就近支援等因素，指定最合适的执管维修单位派出抢修小组，由分公司对人员派出单位以《起落架紧急抢修工作委托书》Form No.: PM-085的形式进行书面委托。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2553, 95, '5.6.3', '非分公司执管飞机，被应急小组指定由分公司派出抢修组，应积极配合抢修工作，组织合格的人员组成起落架重大故障抢修小组并指定带队负责人或组长完成抢修工作，同时垫付相关费用（国内），指定组长的联系方式及时报AMC。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2554, 95, '5.7', '抢修方案的实施', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2555, 95, '5.7.1', '营业部、驻外机务工程师、航线协议维修单位负责办理器材和工具设备的提取。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2556, 95, '5.7.2', '抢修人员到达事发地点后立即对起落架损伤情况进行详细检查，及时将损伤的检查状况向AMC通报。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2557, 95, '5.7.3', '根据对起落架损伤的进一步检查情况，AMC视情组织工程部、航空安全与质量管理部、航材与采购部等部门召开会议，进一步细化抢修方案，落实补充器材，并将细化后的方案通知到抢修组。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2558, 95, '5.7.4', '生产计划控制分部、AMC对抢修工作进行全程监控和跟踪，及时协调解决抢修过程中发生的问题。抢修组负责人定时或随时向AMC汇报抢修进度、突发情况、缺件情况、及计划采取的措施，AMC定时或随时组织召开由工程部、航空安全与质量管理部、航材与采购部、运行管理部及飞机执管维修单位参加的支持小组会议，对抢修组提出的问题进行研讨，制定解决方案。', 6, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2559, 95, '5.7.5', '对于更新后的抢修方案，抢修组应按新方案制定实施措施，超手册修理和重大修理由工程部报局方批准。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2560, 95, '5.8', '抢修后的处理', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2561, 95, '5.8.1', '根据抢修工作进度，抢修组对结束时间进行预测，并提前48小时（超48小时停场）向AMC通报，AMC将信息向运控、总队、客舱等部门发布，以确保各保证部门做好相关准备，如安排调机或执行航班的机组人员等，以保证飞机和抢修人员顺利、及时回到单位站。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2562, 95, '5.8.2', '抢修工作全部完成后，抢修小组组长负责填写《起落架抢修报告》Form No.: PM-086，并与飞机执管维修单位生产计划控制分部进行交接。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2563, 95, '5.8.3', '根据抢修工作的后续要求，AMC配合AOC各部门安排完成后续工作的72小时内停场需求，运行管理部维修计划项目配合商务委员会完成72小时外的停场安排。', NULL, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2564, 95, '5.8.4', '抢修小组负责抢修后的工具、设备、航材的清点、整理及清单准备工作并提交营业部，航材与采购部负责协调营业部完成抢修后工具、设备、航材的回程运输工作。', 3, '2018-06-29 14:21:16', '2018-06-29 14:21:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2565, 96, '5.1', '启动与关闭应急程序', NULL, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2566, 96, '5.1.1', 'MCC接到AMC通知公司启动应急程序后，立即通过短信群发的形式发布信息，并电话通知分公司值班领导、生产经理。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2567, 96, '5.1.2', 'MCC启动应急程序后应执行以下工作：', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2568, 96, '5.1.2.1', '掌握分公司当日投入运营飞机的状况，提供备份运力。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2569, 96, '5.1.2.2', '了解当日分公司计划工作中的运力状况。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2570, 96, '5.1.2.3', '当AMC宣布解除应急后，MCC立即以短信群发的形式通知应急结束。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2571, 96, '5.2', '应急处置小组：分公司处置小组办公室设在所在MCC，负责人为分公司值班领导', 10, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2572, 96, '5.2.1', '应急小组组长：分公司总经理或主管生产的副总经理或值班领导', NULL, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2573, 96, '5.2.2', '应急小组副组长：分公司值班领导，维修中队值班经理（按需）。', 10, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2574, 96, '5.2.3', '应急小组组员：生产计划控制分部值班人员。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2575, 96, '5.2.4', '联系方式：\r\nAMC主控：010-64595885\r\nAMC技术支援工程师：010-64537141\r\nAMC航班调整工程师：010-64537132/64537136\r\n传真：010-64596642/64537144\r\nEMAIL：amc@ airchina.com /airchinaamc@sina.com\r\n重庆分公司 MCC: 023-67152612/3023\r\n杭州分公司 MCC: 0571- 86663836\r\n天津分公司 MCC: 022- 24907653/7655\r\n内蒙分公司 MCC:0471-4942521/2573\r\n武汉分公司 MCC:027-65687157\r\n上海分公司虹桥MCC: 021-22380837	\r\n上海分公司浦东MCC: 021-68354072	\r\n广州分公司 MCC: 020-86138696\r\n贵阳分公司 MCC: 0851-5498267/269\r\n华北航线中心MCC: 010-64599541/64561122转6704\r\n西南航线中心 MCC: 028-85703365/3293', NULL, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2576, 96, '5.2.5', 'MCC接到运行管理部AMC发布启动应急程序后，在20分钟之内将当日和次日分公司执管飞机的备份运力、停场飞机、可用时段及地点等进行统计，按《北京飞机维修工程有限公司备份运力和停场飞机信息表》Form No.:PM-087形式传真至运行管理部AMC。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2577, 96, '5.2.5.1', '在航班调整后，MCC的维修计划人员及时在系统中标注维修工作或完成维修工作的调整。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2578, 96, '5.2.5.2', '在航班调整后涉及跟班计划调整的，MCC及时核实航班调整后的跟班信息并安排人员实施；涉及跨单位调整航班，其跟班人员仍由原计划跟班人员跟班放行；当日取消但次日补班的航班，由执行航班的飞机执管维修基地派人执行跟班任务；当调整单中因机型变更增加跟班任务时，由执行航班的飞机执管维修基地安排跟班人员；涉及飞机驻其它单位的航班在当地临时增加跟机任务的，由当地MCC安排人员实施。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2579, 96, '5.3', '对于备降飞机在备降场发生飞机故障的处理参见01-11-031 《飞机外站故障处理及快速抢修管理程序》。', 6, '2018-06-29 14:34:34', '2018-06-29 14:34:34', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2580, 97, '5.1', '基地站信息报告与处置', NULL, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2581, 97, '5.1.1', '航线中队值班工程师应通过AHM/飞机健康管理系统监控运行中的飞机技术状况，及时发现对运行有影响的故障信息，提前制定排除故障方案和计划排故的时间并通知MCC。', 9, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2582, 97, '5.1.2', 'MCC负责将上述信息通知AMC和区域分公司生产指挥中心。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2583, 97, '5.1.3', '飞机在天津基地站出港前（含航前和过站）机务维修和放行人员要严格按照工卡要求完成维修工作和放行工作，及时发现和排除飞机故障。如果不能在预计的航班执行时刻前完成维修工作或排故工作，机务现场负责人员应在第一时间将信息向MCC报告，如果执行航班任务的机组已到飞机，需要向机组报告。', 9, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2584, 97, '5.1.4', 'MCC应立即组织维修工作，同时将飞机故障或工作信息、处置措施和预计时间（处置措施和预计时间可以续报）向属地分公司生产指挥中心（HCC）和AMC报告，如遇飞机或部件损伤，可第一时间使用微信（13810198747）形式由分公司MCC向AMC传递照片。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2585, 97, '5.1.5', 'MCC接到故障或工作报告信息后，对于预计导致航班延误超过4小时的故障，应报告分公司当日值班领导。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2586, 97, '5.1.6', '发生故障的30分钟内MCC向AMC通报排除故障所需时间及航班预计延误时间，如无法给出排故时间或排故时间预计将超出2小时，MCC应向AMC提出航班调整。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2587, 97, '5.1.7', 'MCC值班人员收到飞机故障报告和排除故障所需时间要求后，应根据机务现场负责人员报告的故障情况并综合考虑当地机场的设备设施保障情况、航材保障情况和试车保障情况等其它相关因素，综合考虑确定航班推迟时间（机务完成排除故障工作时间）。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2588, 97, '5.1.8', 'MCC值班人员将确定的完成排除故障工作时间向AMC报告，报告的内容包括（但不限于）如下：航班号、飞机号、故障情况、排除故障的主要方案、预计完成排除故障工作时间、飞机停机位以及是否有备份运力可以提供。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2589, 97, '5.2', '外站发生飞机故障的报告与处置：参见01-11-031《飞机外站故障处理及快速抢修管理程序》。', NULL, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2590, 97, '5.3', 'MCC信息和故障处置流程', NULL, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2591, 97, '5.3.1', 'MCC应按照信息报告要求，及时上报各类信息，并积极组织故障处置。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2592, 97, '5.3.2', 'MCC应及时组织制定排除故障的技术方案，同时组织确定飞机故障排故的实施方案。如果在飞机故障出现1小时内仍未确定飞机最终排故方案，应及时通报AMC。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2593, 97, '5.3.3', 'MCC应按照本程序要求持续处置、监控故障排除进度和航班调整，直至完成维修排故工作；当飞机故障彻底排除、可以预计排除故障时间、出现排故进程与计划排故进程不符时，第一时间以电话方式通报AMC。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2594, 97, '5.4', '报告机械故障航班推迟时间应遵循的原则', NULL, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2595, 97, '5.4.1', '飞机故障推迟时间，应一次报告准确，杜绝多次变更时间导致的航班延误时刻不准确。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2596, 97, '5.4.2', '在排除故障过程中，因特殊原因发生时间变更，应按照5.1和5.2的流程通报。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2597, 97, '5.5', '因飞机故障航班推迟的处置', NULL, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2598, 97, '5.5.1', 'MCC值班人员接收报告的情况，具体记录航班号、飞机号、故障情况、排除故障的主要方案、预计完成排除故障工作时间、飞机停机位以及是否有备份运力可以提供。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2599, 97, '5.5.2', '航线中队值班工程师接收外站报告的情况，具体记录航班号、飞机号、故障情况按照01-11-031《飞机外站故障处理及快速抢修管理程序》执行外站故障处理流程。', 9, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2600, 97, '5.6', '航班长时间延误须遵守的原则\n预计航班延误超过2小时以上时或无法预测排故所需时间，首先要考虑通过航班调整的办法缩短延误。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2601, 97, '5.7', '飞机故障排除的持续监控\nMCC应对飞机故障排除的进度持续监控，及时向AMC报告，以利于后续航班运行的安排。', 6, '2018-06-29 14:36:16', '2018-06-29 14:36:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2602, 98, '5.1', '系统和工作分析\n系统和工作分析是通过主动对组成系统的硬件、软件、人员和环境及其相互之间的影响进行系统和持续地分析，梳理系统的组织结构、运行过程、程序及资源（人员、设备、设施），识别系统和工作运行中存在的主要问题和障碍因素，提出危险源识别要求。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2603, 98, '5.1.1', '启动时机\r\n1）新生产运行系统建立或现行系统变更,如生产流程或管理模式变化，新设备使用，新增生产线，厂房设施（机库等）布局及其功能的变更等；\r\n2）变更或产生新的作业和程序时, 如维修方案（大纲），新建程序、涉及流程变更程序等；\r\n3）系统的组织机构发生变化时, 如成立新的部门，设立分公司、基地等； \r\n4）内外部运行环境发生重大变化时,如业务扩张或缩减、提供新的服务、消减现行服务等；\r\n5）建立或变更维修能力时，如新机型/新发动机/新航站、零部件能力开发或变更等；\r\n6）系统评价识别出新的危险源和无效的风险控制措施。', NULL, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2604, 98, '5.1.2', '系统划分\n公司将运行系统划分为若干子系统，子系统领域内所涉及的工作流程又分为一级流程，对于复杂的子系统可以分解到二级工作流程（见附录）。分公司系统划分与公司保持一致。', 8, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2605, 98, '5.1.3', '分析层级', NULL, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2606, 98, '5.1.3.1', '系统和工作分析按逐级分解的方式进行，直至分解到基本的作业流程，找出关键环节和薄弱环节，也可按照各部门所负责的工作流程进行分析。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2607, 98, '5.1.3.2', '系统和工作分析层级应至少开展至部门/中队级，以确保详细到足以识别潜在危险源的程度。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2608, 98, '5.1.3.3', '分公司SMS办公室负责分公司级风险评估和管控工作，各部门负责本部门内风险评估和管控工作。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2609, 98, '5.1.4', '分析及结果', NULL, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2610, 98, '5.1.4.1', '分析要求\n系统和工作分析应该考虑下述各方面内容：本系统与相关方的相互作用；各个分系统的功能；为完成各分系统功能所需的工作；考虑必要的人为因素；系统的硬件要求；系统的软件要求；相关程序；维修运行和外界环境；以上各项的相互影响；对系统、系统之间的影响和现有风险控制措施的预估。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2611, 98, '5.1.4.2', '工作流程分析\n1）绘制工作流程图\n根据标准工作程序画出工作流程图。对于没有程序的，根据实际工作情况建立工作流程图。\n工作流程图包括该工作流程完成的主要工作、主要步骤、信息的输入和输出内容以及与其他部门或外单位的接口。\n2）流程要素分析\n从人、人与硬件、人与软件、人与环境和人与人五个角度，对工作流程中的要素进行分析，列出流程中可能存在的危险源，以及危险源存在的条件和触发因素，尤其要重点考虑人的因素。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2612, 98, '5.1.4.3', '系统和工作分析结果\n在完成系统和工作分析后，应编写《系统和工作分析记录表》Form No.: RM-001。\n《系统和工作分析记录表》由分析部门经理或分公司主管副总经理签字生效。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2613, 98, '5.2', '危险源识别\n危险源识别是根据系统和工作分析结果以及安全保证提出的要求识别生产运行过程中存在的危险源、原因及其潜在后果的过程。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2614, 98, '5.2.1', '危险源识别启动时机\n1）实施系统和工作分析后；\n2）安全保证、系统评价和管理评审的输出；\n3）当安全性能和绩效出现异常时；\n4）当法律规章发生变化时。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2615, 98, '5.2.2', '危险源识别要求\n1）从人、机、环分析可能对运行安全产生影响的危险源。\n2）分析所收集的安全信息，进行危险源识别。\n3）对于超出自身管辖范围的危险源，需向上一级SMS办公室/航空安全质量分部上报。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2616, 98, '5.2.3', '危险源识别结果\n在完成危险源识别工作后，应将识别出的危险源填入《危险源清单》Form No.: RM-002。\n危险源清单由分析部门经理或分公司主管副总经理签字生效。\n新增危险源应加入危险源数据库。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2617, 98, '5.3', '风险分析和评价\n风险分析和评价是通过对照定性和定量标准对危险源后果的严重性及其发生的可能性进行评判，确定风险等级和可接受程度。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2618, 98, '5.3.1', '风险分析\n公司将可能性、严重性各划分为8个等级，并分别赋予分值。\n严重性标准见下表：（表见原程序）\n可能性标准见下表：（表见原程序）\n1）根据“可能性标准”确定每个危险源潜在后果发生的可能性。\n2）根据“严重性标准”确定每个危险源潜在后果的严重性。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2619, 98, '5.3.2', '风险评价\n风险评价是根据风险分析得出的结论，依据风险评估矩阵或风险值计算等方法评定风险的等级和可接受程度。\n依据严重度分值及可能性分值的乘积确定风险值。\n风险评价采用风险矩阵和风险值计算两种方法。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2620, 98, '5.3.2.1', '1）风险矩阵\n以后果的严重性级别和可能性级别为坐标轴组成风险评估矩阵，对应危险源的严重性级别和可能性级别，可以在矩阵中得到该危险源的风险等级。\n2）风险值计算\n根据风险分析评定出的风险严重性和可能性的量化分值，计算出风险值，由风险值大小划分风险等级。\n风险值＝严重性分值×可能性分值\n依据风险矩阵图确定此危险源的风险等级，风险矩阵和风险值计算如下表：（表见原程序）', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2621, 98, '5.3.2.2', '风险等级标准\n公司将风险划分为五个等级，等级越高，风险越大。\n一级：白色区域，风险值≤10；\n二级：绿色区域，风险值=（11～59）；\n三级：黄色区域，风险值=（60～299）；\n四级：橙色区域，风险值=（300～800）；\n五级：红色区域，风险值≥800；\n公司风险等级与国航股份风险等级关系对照表见下图。（表见原程序）', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2622, 98, '5.3.2.3', '风险可接受程度标准（表见原程序）\n依据风险可接受程度标准确定后续行动。\n经风险评价，存在四级（含）以上风险等级的危险源时，不得实施以下内容：\n1）新的系统设计，如成立基地、新机型/新设施/设备的采用等。\n2）现有系统设计的更改，如组织结构调整、硬件或网络系统升级；\n3）新的作业和程序，如维修方案、各种工作的操作程序或管理程序（办法）等；\n4）更改后的作业或程序，如改变或调整运行管理程序，改造重要设施、设备等。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2623, 98, '5.4', '风险控制是针对识别出的三级（含）以上风险制定风险控制措施并组织实施的过程。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2624, 98, '5.4.1', '风险控制措施制定\n与危险源相关的单位负责制定风险控制措施，措施应可测量/可操作，内容包括：\n1）措施具体内容；\n2）风险控制目标；\n3）责任部门/责任人；\n4）完成期限。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2625, 98, '5.4.2', '风险控制方法\n1）改进系统，消除硬件、软件系统和组织系统上的危险源，如完善管理和监控机制、调整人员；\n2）增加防护装置或屏障，减少在危险源中的暴露或降低后果的严重性；\n3）在制定出更加有效的危险源消除方法之前，应先进行警告提示；\n4）修改相关作业程序以避开危险源或降低相关风险的可能性或后果的严重性；\n5）对相关人员进行教育培训以避开危险源或降低相关风险的可能性。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2626, 98, '5.4.3', '衍生风险评价\n在制定风险控制措施时，须对风险控制措施可能带来的衍生风险进行评价，确保衍生风险在可接受的范围内。如衍生风险在不可接受范围，重新对危险源进行根原因分析，并制定新的风险控制措施。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2627, 98, '5.4.4', '风险评估报告批准', NULL, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2628, 98, '5.4.4.1', '在完成风险分析和评价工作后，针对三级(含)以上风险应编写《Ameco风险评价报告》（Form No.: RM-004）。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2629, 98, '5.4.4.2', '分公司安全风险评估报告批准\n1）当风险等级为三级时，由安全质量分部经理批准并报公司SMS办公室。\n2）当风险等级为四级时，由分公司副总经理对其分管领域风险项目批准并报公司SMS办公室认可；分公司安全总监对本单位综合风险项目批准并报公司SMS办公室认可。\n3）当风险等级为五级时，上报公司航空安全委员会批准。', 8, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2630, 98, '5.4.4.3', '部门级风险评估报告批准\n1）当风险等级为三级时，由分部/中队经理批准并报分公司SMS办公室。\n2）当风险等级为四级时，报分公司SMS办公室，上升为分公司级风险进行批准和管控。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2631, 98, '5.4.5', '风险发布', NULL, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2632, 98, '5.4.5.1', '1）分公司SMS办公室完成的风险评价报告在分公司范围内发布，并报公司SMS办公室备案。', 8, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2633, 98, '5.4.5.2', '2）各部门按管理权限在本部门范围内发布风险评估报告，并报分公司SMS办公室备案。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2634, 98, '5.4.5.3', '3）对于可能的突发恶劣环境、行业教训或通过趋势分析发现的具有普遍借鉴意义的危险状况，以及在其它有必要的情况应发布风险提示或警示。', NULL, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2635, 98, '5.4.6', '风险控制措施的实施\n相关责任部门应按照制定的措施进行落实，并按要求向风险发布单位进行反馈。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2636, 98, '5.4.7', '风险控制措施评价\n1）风险控制措施通过安全检查、质量监督等方式进行验证措施落实的符合性。\n2）对风险控制措施是否持续有效进行分析，如果风险控制措施不能达到控制风险的目的，分析制定的风险控制措施是否有效，如果存在问题，重新分析和评价该危险源，并制定新的风险控制措施。', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2637, 98, '5.4.8', '风险项目状态监控', 10, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2638, 98, '5.4.8.1', '各级风险发布部门应建立《安全风险项目动态控制表》（Form No.: RM-003），对风险项目的状态进行监控：\n1）跟踪措施的执行情况；\n2）评估验证风险的改进情况，如风险等级发生变化，应以《安全风险项目动态评估表》（Form No.: RM-005）的形式实施发布；\n3）如通过采取措施风险达到控制目标，则关闭风险项目；\n4）如控制措施无效，应重新启动危险源识别、风险分析和评价工作，评估制定新的控制措施。', 8, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2639, 98, '5.4.8.2', '分公司级风险应通过月度安全生产讲评会等形式对风险控制项目的控制状态进行分析、讲评。', 8, '2018-06-29 14:51:03', '2018-06-29 14:51:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2640, 99, '5.1', '使用困难报告的范围', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2641, 99, '5.1.1', '运行部分CCAR-121.707（a）：\r\n1）飞行中的失火以及有关火警系统工作不正常；\r\n2）飞行中的假火警信号；\r\n3）在飞行中引起发动机、相邻结构、设备和部件损坏的排气系统故障或失效；\r\n4）飞行中由于飞机部件的故障或失效引起烟、蒸气、有毒或有害烟雾在驾驶舱或客舱积聚或流通；\r\n5）飞行中或地面发动机熄火或停车；\r\n6）飞行中燃油系统或应急放油系统故障或渗漏；\r\n7）飞行中非正常的起落架收放或起落架舱门的开启和关闭；\r\n8）刹车系统的失效或故障；\r\n9）飞机系统及其部件的故障或失效导致中断起飞或在飞行中采取紧急措施的情况；  \r\n10）在实际撤离、培训、测试、维修、演示或无意使用时，任何应急撤离系统或其部件（包括应急出口、旅客应急撤离灯系统、撤离设备）的缺陷或不能完成预定的功能；\r\n11）自动油门、自动飞行或飞行操纵系统或其部件的故障或不能完成预定的功能；\r\n12）已经危及或可能危及飞机的安全运行的故障或缺陷。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2642, 99, '5.1.2', '结构部分CCAR-121.708（a）：\n1）腐蚀、裂纹或开裂导致要求更换有关的零部件；\n2）腐蚀、裂纹或开裂因超出制造厂家规定的允许损伤限度导致要求修理或打磨；\n3）在复合材料结构中，制造厂家指定作为主要结构或关键结构件的裂纹、破裂或开裂； \n4）根据未包含在制造厂家的维修手册中，但经过批准资料进行修理的情况；\n5）已经或可能危及飞机安全运行的其他飞机结构的失效或缺陷。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2643, 99, '5.1.3', '运行中出现的涉及飞机维修的人为差错。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2644, 99, '5.1.4', '补充与说明：', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2645, 99, '5.1.4.1', '飞机在运行中，采取了以下措施的需要上报使用困难报告：\n1）飞行中采取的“紧急措施”包括中断起飞、返航、改航/备降、紧急下降高度、发动机关车、释放灭火瓶、释放氧气面罩、抛油、中止进近、重力放起落架、复飞等；\n2）滑回；\n3）落地后，由于飞机故障，造成飞机无法滑行或无法脱离跑道。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2646, 99, '5.1.4.2', '外界环境对飞机的影响，包括遭雷击、冰击、鸟击、遇风切变、严重颠簸、重着陆和外物撞击等导致飞机受损；飞行中因结冰造成飞机操纵特性的明显降低。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2647, 99, '5.1.4.3', '在飞机运行或维修中发生或发现的已经危及或可能危及飞机安全运行的，包括但不限于以下故障、失效或缺陷：\r\n1）氧气管路、氧气瓶和/或氧气发生器与电气线路摩擦；\r\n2）在燃油箱中导致或可能导致火源产生的故障或状态；\r\n3）任何多于一个液压系统的失效；\r\n4）在飞行中，两个姿态或两个空速或两个高度仪表的失效或故障；\r\n5）水系统和污水系统的渗漏导致电气故障，如前厕所或厨房的水漏到电子舱或电气接头、部件或线路上；\r\n6）电子设备冷却系统失效；\r\n7）导致一个以上电源系统失效的故障；\r\n8）导致所有VHF通信系统失效的故障；\r\n9）轮胎爆胎、分层、机轮破裂；\r\n10）非指令前轮转弯输入，小的左右的偏移除外；\r\n11）在紧急情况或对飞机进行测试期间，任何氧气系统的故障或缺陷；\r\n12）需要紧急下降的机舱增压故障；\r\n13）任何飞机、发动机的振动或抖动导致在飞行中采取紧急措施的；\r\n14）任何飞机、发动机部件、盖板的脱落或丢失；\r\n15）导致飞行机组座椅（不包括观察员座椅）非指令移动的缺陷；\r\n16）应急撤离滑梯的意外（非指令性）打开；\r\n17）导致非包容性发动机故障的事件，如叶片断裂飞出事件，包括但不限于此类事件；\r\n18）非指令推力改变或者在结束运行时发动机非正常关车；\r\n19）由于货物固定设施失效导致飞机损伤的故障；\r\n20）飞行中或地面移动过程中发动机反推异常打开或失效；\r\n21）飞行中或地面移动过程中发动机失速或喘振；\r\n22）导致更换发动机的故障。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2648, 99, '5.1.5', '延程运行（ETOPS）飞机补充报告内容：\n1）未指令的功率或者推力的变化或者喘振；\n2）发动机失控或无法获得要求的动力或推力；\n3）因疏忽产生的燃油损耗或无燃油可用，或在飞行中无法纠正的燃油不平衡；\n4）与ETOPS关键系统相关的失效、故障或缺陷；\n5）任何危及实施ETOPS安全飞行和着陆的事件。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2649, 99, '5.1.6', '除报告以上范围的事件外，还应尽可能地报告涉及产品设计缺陷、修理质量问题、故障频繁出现、原因无法确定等事件，为改进产品、经验交流、提高维修水平等提供帮助。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2650, 99, '5.2', '使用困难报告填报', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2651, 99, '5.2.1', '填报时限和要求：', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2652, 99, '5.2.1.1', '非执管飞机出现或发现5.1范围内信息后，MCC应立即将信息报告AMC，由AMC报执管单位。', 6, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2653, 99, '5.2.1.2', '执管飞机出现或收到AMC符合5.1范围内的信息后，MCC须在24小时内（节假日不顺延）通过使用困难报告系统（http://fsop.caac.gov.cn）填写和提交《使用困难报告》（F121/135-6001），并及时通知航空安全质量分部和技术支援分部。', 6, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2654, 99, '5.2.2', '《使用困难报告》及填写说明见7.1.1', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2655, 99, '5.3', '使用困难调查报告的填报', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2656, 99, '5.3.1', '填报时限和要求', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2657, 99, '5.3.1.1', '航空安全质量分部需对每起使用困难报告进行核实和调查，确认使用困难报告数据准确，查明故障件或失效系统，以及失效模式，确定事件原因（如使用、维修差错、设计/制造、翻修等原因）。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2658, 99, '5.3.1.2', '航空安全质量分部需在5.1范围内信息发生或发现后的72小时内（节假日顺延）通过使用困难报告系统填写和提交调查报告，可根据需要上报图片和补充材料。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2659, 99, '5.3.1.3', '如在故障出现或发现后的72小时内（节假日顺延）事件调查的部分内容暂时无法查明，航空安全质量分部应在提交的调查报告中说明原因和后续工作计划。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2660, 99, '5.3.1.4', '航空安全质量分部在获得更进一步的事件信息后，应及时更新调查报告；对局方等上级单位要求进一步说明清楚或需要调查的内容，应在要求的答复日期前回复。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2661, 99, '5.3.1.5', '轮胎脱层/爆胎调查工作需参照附件4《航空器轮胎事件调查程序》。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2662, 99, '5.3.2', '《使用困难报告调查单》（F121/135-6002）及填写说明见7.1.2。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2663, 99, '5.4', '机械中断使用数据填报时限和要求', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2664, 99, '5.4.1', '技术支援分部汇总上月分公司执管飞机和发动机数据，在每月7日前提交工程部。', 4, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2665, 99, '5.4.2', '飞机数据应包括：航空器在用架日、营运飞行时间、空中飞行时间、空地飞行时间、营运起落次数、正常起落次数、连续起落次数、延误次数、取消次数、返航次数、改航次数、非计划更换飞机的次数、中断飞行次数、本月修后营运飞行时间、本月修后空中飞行时间、本月修后空地飞行时间、本月修后营运起落次数、本月修后正常起落次数、本月修后连续起落次数、机组报告的各系统故障次数等。', 4, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2666, 99, '5.4.3', '发动机数据应包括：发动机序号、发动机型号、本月发动机的拆换情况、当前发动机状况、装机机号、装机位置、发动机使用时间、发动机使用循环、自开始使用时间、自开始使用循环、发动机修后时间、发动机修后循环、发动机非计划换发次数、机组报告的发动机各系统故障次数等。', 4, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2667, 99, '5.4.4', '机组报告的各系统故障次数由技术支援分部提供。', 4, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2668, 99, '5.5', '报告的上报途径', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2669, 99, '5.5.1', '使用困难报告、调查报告、均通过使用困难报告系统（http://fsop.caac. gov.cn）进行网上提交。', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2670, 99, '5.5.2', '如暂时无法使用困难报告系统进行提交，可以通过传真（010-64473611）的方式进行提交，提交后需进行电话确认（010-64473312），或通过电子邮件（sdr@mail.castc.org.cn、sdr_sys@163.com）的方式提交，并在使用困难报告系统可以使用时立即通过使用困难报告系统补报。', 6, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2671, 99, '5.6', '填报人员培训', NULL, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2672, 99, '5.6.1', '使用困难报告系统填报人员应参加民航局组织开展的使用困难报告和调查人员培训，并获得培训证书。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2673, 99, '5.6.2', '已取得使用困难报告系统填报资质的人员，需每3年参加一次民航局组织的复训。', 8, '2018-06-29 15:04:13', '2018-06-29 15:04:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2674, 100, '5.1', '报告原则：', NULL, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2675, 100, '5.1.1', '在分公司范围内发生不安全事件后，各部门均有职责将信息报告分公司MCC，并保证所报告信息的内容详细且真实有效。', 10, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2676, 100, '5.1.2', '针对国航飞机发生的不安全事件，由飞机执管单位负责进行上报；针对非国航飞机发生的不安全事件，由事发相关单位负责上报。', 6, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2677, 100, '5.1.3', '航空安全质量分部负责上报信息的监督工作。', 8, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2678, 100, '5.1.4', '对延误报告、隐瞒不报或弄虚作假等情况，分公司将按“安全管理考核程序”对责任部门和个人进行处理。', NULL, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2679, 100, '5.2', '联系方式：', NULL, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
