INSERT INTO `tb_step_info` VALUES (2705, 100, '5.5.1.4', '现场维修人员负责提供《鸟击航空器信息确认表》中航空器型号、飞机号、航班号等信息，鸟击信息经双方确认以后进行填写，并签字确认；如存在争议，维修人员应报告MCC，由MCC报天津机场运行指挥中心进行协调，双方同意后签字确认。签字确认后的《鸟击航空器信息确认表》（原件或复印件）随航线工卡交MCC。', 9, '2018-06-29 15:09:05', '2018-06-29 15:09:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
