INSERT INTO `tb_step_info` VALUES (2717, 101, '5.1.2', '公司承诺对自愿报告人信息进行保密，任何单位和个人不得以任何借口压制、打击和报复报告人；', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2718, 101, '5.1.3', '公司承诺对有价值的自愿报告给予奖励；\n对安全生产运营有重大影响的自愿报告，受理单位应立即报送主管领导采取措施；', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2719, 101, '5.1.4', '公司承诺自愿报告信息仅用于持续安全改进，不以处罚为目的；对于确实适用于处罚的事项，减轻或免除处罚；', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2720, 101, '5.1.5', '公司对本人主动报告的无后果违章行为免于处罚；\n故意违章行为不适用于减免处罚原则；', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2721, 101, '5.1.6', '自愿报告信息应录入航安系统，作为风险管理和安全保证的信息源；', 8, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
