INSERT INTO `tb_step_info` VALUES (2731, 101, '5.3.2', '员工报告可以按自己意愿采用实名或匿名方式报告。', 10, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2732, 101, '5.4', '自愿报告的处理', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2733, 101, '5.4.1', '在接到自愿报告5个工作日内处理，按需将反馈信息录入航安系统。', 8, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2734, 101, '5.4.2', '自愿报告内容交责任单位处理前，应删去报告人相关信息，为报告人做好保密工作。', 8, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2735, 101, '5.5', '自愿报告的奖励', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2736, 101, '5.5.1', 'SMS办公室在收到自愿报告15个工作日内，完成对自愿报告的评估，将对安全生产运行有贡献的自愿报告按分公司《奖惩细则》申请奖励。', 8, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2737, 101, '5.5.2', '人力资源分部负责按奖惩程序兑现奖励。对于自愿报告涉及的奖励事项，按奖励范围上限执行。此项奖励因保密要求不予公示。', 1, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2738, 101, '5.5.3', '航空安全质量分部将员工自愿报告重大隐患作为年终推选安全先进的重要依据。', 8, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2739, 101, '5.6', '系统评价\n按照01-SA-004《安全信息管理程序》和01-SA-011《系统评价程序》进行数据分析和系统评价，并识别危险源。', NULL, '2018-06-29 15:10:13', '2018-06-29 15:10:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2740, 102, '5.1', '安全信息来源包括：\n1）审核信息；\n2）不安全事件及调查信息；\n3）安全检查信息；\n4）安全监察信息；\n5）员工自愿报告信息；\n6）安全自查信息；\n7）局方监察等外部信息。', NULL, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2741, 102, '5.2', '安全信息收集\n航空安全质量分部对收集到的信息进行整理、分类、存档，建立安全信息数据库。收集的安全信息应通过航安系统上报公司SMS办公室。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2742, 102, '5.3', '安全信息录入', NULL, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2743, 102, '5.3.1', '审核信息\n审核工程师负责收集审核发现问题的相关信息。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2744, 102, '5.3.2', '不安全事件及调查信息\n安全调查工程师负责收集不安全事件调查和客户投诉事件调查的相关信息。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2745, 102, '5.3.3', '安全检查信息\n安全管理工程师负责收集上级各类安全检查发现问题的相关信息。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2746, 102, '5.3.4', '安全监察信息\n安全监察工程师负责收集日常生产运行过程中的持续监查发现问题的相关信息。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2747, 102, '5.3.5', '员工自愿报告信息\n安全管理工程师负责将自愿报告信息录入航安系统，作为风险管理和安全保证的信息源。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2748, 102, '5.3.6', '安全自查信息\n安全自查信息是各生产部门在日常生产运行过程中对本部门工作的持续检查产生的信息。在实施自查工作后，各维修中队应将安全自查信息报至航空安全质量分部。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2749, 102, '5.3.7', '局方监察等外部信息\n审核工程师负责收集局方监察中发现问题的相关信息。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2750, 102, '5.3.8', '专项安全活动信息\n安全管理工程师负责收集各类安全活动中的信息。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2751, 102, '5.4', '安全信息分析\n航空安全质量分部每月对所有收集的安全信息进行分类、统计、分析、处理，将生产运行中的安全信息与危险源关联，利用风险评价模型计算风险值，找出存在的高风险危险源，对黄色等级以上（含）的危险源制定预防措施并落实。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2752, 102, '5.5', '安全信息分析结果发布\n对安全信息的分析结果，应通过安全质量月会的形式予以发布。', 8, '2018-06-25 16:12:08', '2018-06-25 16:12:08', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2753, 103, '5.1', '安全监察工作所遵循的原则\n监督各相关部门在机务维修工作中必须按民航法规、行业标准、客户手册实施，确保在维修过程中飞机、设备、人身安全，相关维修工作符合维修质量要求，监督放行标准的执行，严把安全关。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2754, 103, '5.2', '安全监察工作内容', NULL, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2755, 103, '5.2.1', '制订分公司月度安全监察工作计划下发各部门，同时在“Ameco航空安全管理系统”上报公司SMS办公室。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2756, 103, '5.2.2', '按照安全监察计划开展对维修工作现场的安全监察。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2757, 103, '5.2.3', '安全监察工程师应在航空安全质量分部的指导下对本单位所从事的维修活动现场进行监督、抽查，发现并纠正维修过程中的安全隐患、违章事件及质量漏洞，以促进本单位在维修工作中按章办事，全面、准确的执行工卡、安全检查单，确保维修工作质量，达到保证飞机安全和持续适航的目的。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2758, 103, '5.2.4', '安全监察的范围包括但不限于以下各个方面：  \n人员资格：人员资格控制，人员资格符合性，人员监管，人力安排，人员培训，检验员/技术员等人员的岗位职责履行情况等。 工具设备：工具设备管理, 工具设备使用等。 \n器材管理：器材标识，运输/保护，存储，报废，化工品使用/管理,自制件等。 \n工作文件：文件管理，技术文件，程序文件，维修记录，维修合同等。 \n工作区域：厂房设施，工作现场管理，环境因素等。 \n维修过程：技术标准执行，工作程序执行，个体防护，安全防护，信息传递等。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2759, 103, '5.3', '安全监察工作的实施', NULL, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2760, 103, '5.3.1', '依据公司季度安全监察工作计划及上级（局方、国航股份、公司）文件要求，结合分公司近期安全管理工作重点，制订分公司月度安全监察工作计划，经航空安全质量分部经理或高工审批后在分公司内发布并通过“Ameco航空安全管理系统”报备公司SMS办公室。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2761, 103, '5.3.2', '安全监察工程师根据预先制定的监察计划、监察检查单或相应要求执行有计划的检查或抽查。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2762, 103, '5.3.3', '安全监察工程师在进行维修工作现场安全监察时须做好检查工作记录，必要时采取拍照、录音等方式取证，完成检查后应及时录入数据库。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2763, 103, '5.3.4', '安全监察人员按上级要求编写安全监察报告定期上报公司航空安全与质量管理部。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2764, 103, '5.3.5', '安全监察人员按月对发现的问题进行系统分类、分析，提交维修工作现场安全监察发现问题分析报告，报告内容包括发现问题汇总、纠正措施落实情况及整改建议等，并加入本单位安全生产月报。对检查发现的典型、重复性问题，下发《质量检查发现问题通知单》（Form No.: SA-T01）到责任部门，提出整改要求，并对责任部门制定的整改措施进行评估。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2765, 103, '5.3.6', '责任部门收到《质量检查发现问题通知单》后应按要求制定整改措施报航空安全质量分部，并按照审批后的措施进行整改。', 10, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2766, 103, '5.4', '安全监察工作整改措施的落实与验证', NULL, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2767, 103, '5.4.1', '安全监察工程师在进行日常安全监察的同时，要对上一月度维修单位整改措施的落实情况进行验证，如验证不合格，将验证结果发至责任部门。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2768, 103, '5.4.2', '责任部门重新回复纠正措施，并按照新的措施进行整改。', 10, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2769, 103, '5.4.3', '安全监察部门进行新的跟踪，在问题彻底解决后，安全监察工程师关闭此项问题。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2770, 103, '5.5', '安全监察工程师的认定\n航空安全质量分部根据安全监察工程师的资格要求，向公司推荐安全监察工程师，由公司审查批准。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2771, 103, '5.6', '安全监察工程师的年度评审\n航空安全质量分部参考年度绩效考核结果，对安全监察工程师的安全监察计划的执行情况进行评估，对于不履行安全监察职责的人员取消安全监察工程师资格，评估结果需向公司SMS办公室报告备案。', 8, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2772, 103, '5.7', '系统评价\n按照01-SA-004《安全信息管理程序》和01-SA-011《系统评价程序》进行数据分析和系统评价，并识别危险源。', NULL, '2018-06-29 15:12:16', '2018-06-29 15:12:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2773, 104, '5.1', '调查基本原则', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2774, 104, '5.1.1', '调查工作应遵循独立性、客观性、深入性、全面性的原则进行。', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2775, 104, '5.1.2', '调查人员不能与被调查事件有直接关系，不得随意对外泄漏调查情况。', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2776, 104, '5.1.3', '所有的员工都有责任协助、配合调查。调查人员根据需要，可要求相关人员或部门提供对事件的书面描述，和与调查有关的文件。隐瞒事实真相或提供虚假信息的行为要予以处罚。', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2777, 104, '5.2', '调查形式和要求', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2778, 104, '5.2.1', '收到公司SMS办公室下发的《不安全事件调查通知书》后，有责任按照本程序实施调查工作并出具调查报告。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2779, 104, '5.2.2', '调查责任单位应根据事件性质和类别立即成立调查组，调查组可以由一人或多人组成。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2780, 104, '5.3', '调查启动\n当发生人为原因航空不安全事件但不限于以下情况时，应立即启动航空不安全事件调查：上级单位下发《不安全事件调查通知书》要求调查的人为原因不安全事件；人为原因维修差错事件；人为原因的航空器使用困难报告事件。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2781, 104, '5.4', '调查工作流程', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2782, 104, '5.4.1', '现场调查、封存资料和证据保护', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2783, 104, '5.4.1.1', '根据事件调查需要，指派调查员人员携带相关调查设备和调查文件及时赶赴事发现场，收集现场资料和信息。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2784, 104, '5.4.1.2', '运输航空事故征候以上事件发生后，按照国航股份《航空安全管理程序》中“航空不安全事件调查程序”要求封存并妥善保管与此次飞行有关的相关文件、样品、工具、设备。所有封存资料的启封再使用，须由发出封存通知的调查组或调查员批准。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2785, 104, '5.4.1.3', '在维修活动中发生人为原因航空不安全事件后，现场生产部门负责对现场保护并对现场各种易失证据（物体、液体、资料、痕迹）进行拍照、采样、收集，交由航空安全质量分部处理和保存。', 9, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2786, 104, '5.4.1.4', '当调查需要读取机载语音、数据记录设备记录时，按01-SA-009《机载FDR和CVR管理程序》执行。', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2787, 104, '5.4.2', '相关人员的调查', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2788, 104, '5.4.2.1', '调查人员根据需要，可对相关人员进行调查，以取得现场目击人员的陈述资料和当事人对事件过程的详细陈述材料。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2789, 104, '5.4.2.2', '调查可根据需要可采取现场录像、录音，以及调取历史录像、录音等方式。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2790, 104, '5.4.3', '相关记录文件调查\n调查人员可以根据需要提取各种维修记录和文档，如工卡、工程指令、地故单、保留单、排故指令、工卡目录、车间故障记录单、维护计划、EO执行单、飞机/发动机履历本等各种技术文件，以及零部件送修记录、零部件原始标签、零部件放行挂签、送修合同等维修相关记录文档。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2791, 104, '5.4.4', '根原因分析', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2792, 104, '5.4.4.1', '根据各种证据进行综合分析，重点进行人为因素分析。通过分析得出导致事件发生的主要原因、次要原因，以及通过事件调查发现与本事件无关但直接影响到飞行安全的问题分析，给出调查结论。详细原因分析见《原因分析表》。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2793, 104, '5.4.4.2', '人-人的因素：主要指当事人的不安全行为、导致不安全行为的前提条件，以及相应的组织管理因素。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2794, 104, '5.4.4.3', '机-机械和设备：重点分析飞机部件和事件有关的地面设备、设施的故障或失效，人为原因导致的故障或失效应按照人为因素进行分析。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2795, 104, '5.4.4.4', '环-环境因素：分析与事件有关的天气、机场、等运行环境因素，属非人为因素。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2796, 104, '5.4.5', '事件定性', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2797, 104, '5.4.5.1', '按照《北京飞机维修工程有限公司维修差错标准》提出定性建议。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2798, 104, '5.4.5.2', '对事件调查结论、定性有异议的部门，可以依次向分公司、公司和国航股份公司SMS办公室提出申诉意见，并由分公司、公司和国航股份公司SMS办公室进行裁决。', 10, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2799, 104, '5.4.6', '预防与纠正措施/安全建议', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2800, 104, '5.4.6.1', '航空安全质量分部根据事件原因分析，提出预防与纠正措施/安全建议。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2801, 104, '5.4.6.2', '事件责任部门要及时采取相应的预防与纠正措施，按照TJ-SA-001《预防与纠正措施管理程序》在收到《预防与纠正通知单》后，根据本部门实际情况制定预防与纠正措施实施方案。', 10, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2802, 104, '5.4.7', '调查报告', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2803, 104, '5.4.7.1', '《调查报告》内容应完整、准确、清晰。如果调查报告不能在规定时间内完成，可以提交阶段调查报告并申请延期。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2804, 104, '5.4.7.2', '《调查报告》经质量经理审批后报公司SMS办公室。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2805, 104, '5.4.7.3', '《调查报告》应根据需要，报局方、集团领导、国航股份领导、航空安全管理部、公司领导、客户等，分发事件责任部门。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2806, 104, '5.4.8', '预防与纠正措施/安全建议实施和验证\n按照TJ-SA-001《预防与纠正措施管理程序》执行。', NULL, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2807, 104, '5.5', '系统评价\n按照01-SA-004《安全信息管理程序》和01-SA-011《系统评价程序》进行数据分析和系统评价，并识别危险源。', 8, '2018-06-29 15:13:06', '2018-06-29 15:13:06', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2808, 105, '5.1', '调查基本原则', NULL, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2809, 105, '5.1.1', '调查工作应遵循独立性、客观性、深入性、全面性的原则进行。', NULL, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2810, 105, '5.2', '调查流程', NULL, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2811, 105, '5.2.1', '在公司安全信息周报中出现涉及分公司的机械原因不安全事件或在“Ameco航空安全管理系统”上收到公司下发的《不安全事件调查通知单》Form No.: SA-004时，启动机械原因不安全事件调查。', 8, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2812, 105, '5.2.2', '航空安全质量分部向各相关部门下发《机械事件调查报告》Form No.: SA-008模板，明确填写要求和反馈时限。', 8, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2813, 105, '5.2.3', '航线中队负责《机械事件调查报告》第一至第三项内容的初步调查填写工作，按照规定的反馈时限上报航空安全质量分部。', 9, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2814, 105, '5.2.4', '生产计划控制分部负责《机械事件调查报告》第四项内容的初步调查填写工作，按照规定的反馈时限上报航空安全质量分部。', 6, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2815, 105, '5.2.5', '技术支援分部负责《机械事件调查报告》第五、第六项内容的初步调查填写工作，按照规定的反馈时限上报航空安全质量分部。', 4, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2816, 105, '5.2.6', '航空安全质量分部汇总审核各部门上报内容，经质量经理审批后按要求上报公司SMS办公室。', 8, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2817, 105, '5.3', '措施的实施和验证\n对于公司制定的改进措施的实施和验证按照TJ-SA-001《预防与纠正措施管理程序》执行。', NULL, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2818, 105, '5.4', '机械事件讲评\n航空安全质量分部负责将机械事件调查内容加入安全质量月会进行讲评。', 8, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2819, 105, '5.5', '系统评价\n按照01-SA-004《安全信息管理程序》和01-SA-011《系统评价程序》进行数据分析和系统评价，并识别危险源。', NULL, '2018-06-26 08:17:50', '2018-06-26 08:17:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2820, 106, '5.1', '原则', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2821, 106, '5.1.1', '公司任何单位（包括所有维修协议单位）和个人均有责任保证飞机机载FDR和CVR不被非法操作，并确保其原始数据不被非正常更改。', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2822, 106, '5.1.2', 'CVR除本身故障或定期检修外的任何情况拆下和译码须得到国航股份航空安全管理部总经理或其授权人员批准。', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2823, 106, '5.2', 'FDR、CVR断电和拆下', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2824, 106, '5.2.1', '分公司MCC接到国航股份天津分公司航空安全管理部或AMC转发的国航股份航空安全管理部断电/拆下FDR、CVR书面指令后，应立即将该指令书面通知维修中队。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2825, 106, '5.2.2', '维修中队在实施拆下FDR、CVR书面指令时，应首先执行“断开FDR、CVR设备电源”程序后再实施相应工作。', 9, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2826, 106, '5.2.3', '在紧急情况下，接到国航股份天津分公司航空安全管理部或AMC转达的国航股份航空安全管理部断电/拆下FDR、CVR电话指令后，MCC应以电话的形式通知维修中队首先实施“断开FDR、CVR设备电源”程序，然后根据随后补发的书面指令安排下一步工作。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2827, 106, '5.2.4', '维修中队在按照MCC电话指令实施“断开FDR、CVR设备电源”程序后，应等待MCC书面指令再实施后续工作。', 9, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2828, 106, '5.3', 'FDR、CVR运输和保存\n拆下FDR、CVR后，由 MCC负责安排保管和运输工作，在此过程中应妥善保存该设备，不得擅自转移记录器以及下载、破译其内容。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2829, 106, '5.4', 'FDR、CVR的数据下载\n由 MCC指派人员将FDR、CVR运送到国航股份航空安全管理部或其授权人员指定的地点进行数据下载。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2830, 106, '5.5', 'FDR、CVR的恢复使用', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2831, 106, '5.5.1', 'FDR、CVR数据分析完成后，MCC根据上级航空安全管理部门的书面恢复指令，通知维修中队按照相应的维护手册和工作单的要求将FDR、CVR恢复到可用状态。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2832, 106, '5.5.2', '“切断FDR、CVR设备电源”指令发出后，如不需要拆下译码，MCC应根据上级航空安全管理部门指令，通知维修中队按相应的维护手册和工作单的要求恢复FDR、CVR到可用状态。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2833, 106, '5.6', '局方要求拆下FDR、CVR', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2834, 106, '5.6.1', '接到局方下达的拆下 FDR、CVR 指令时，MCC必须首先通知维修中队执行“断开 FDR、CVR 设备电源”的程序，同时通知航空安全质量分部。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2835, 106, '5.6.2', '航空安全质量分部负责上报国航股份航空安全管理部，并将国航股份航空安全管理部的后续处置指令反馈给MCC。', 8, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2836, 106, '5.6.3', 'MCC依据国航股份航空安全管理部的指令安排后续工作。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2837, 106, '5.7', '三方客户要求拆下FDR、CVR\n接到三方客户的拆下 FDR、CVR 书面文件时，由MCC按照三方客户要求安排执行FDR、CVR的拆下和恢复工作。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2838, 106, '5.8', '用于飞机排故拆下FDR', NULL, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2839, 106, '5.8.1', '当发生飞机、发动机系统故障，需对FDR进行译码分析时，由维修中队填写《飞行记录器拆下申请单》Form No.：SA-009报MCC值班经理批准后再实施拆下工作。', 9, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2840, 106, '5.8.2', 'MCC值班经理负责《飞行记录器拆下申请单》的批准，并将批准后的《飞行记录器拆下申请单》报AMC和航空安全质量分部。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2841, 106, '5.8.3', '航空安全质量分部负责报国航股份天津分公司航空安全管理部备案。', 8, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2842, 106, '5.8.4', 'FDR拆下后，由 MCC负责安排译码工作，译码数据交技术支援分部。', 6, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2843, 106, '5.8.5', 'FDR数据的下载、分析不得用于排故以外的其它用途，且不可对外发布。', 4, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2844, 106, '5.8.6', '如在数据分析中发现因飞行操作不当造成的机械故障、不安全事件，应立即报航空安全质量分部。', 4, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2845, 106, '5.8.7', '航空安全质量分部负责将发现的不安全事件上报国航股份航空安全管理部。', 8, '2018-06-29 15:16:19', '2018-06-29 15:16:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2846, 107, '5.1', '系统评价内容\n系统评价用于评估与适用法规、公司手册、程序和标准以及风险控制措施的符合性，从而评价运行过程与安全管理体系要求的符合性。具体包括：\n1）安全指标的实现状况和趋势；\n2）现行的风险控制措施实施的符合性和有效性；\n3）安全管理体系要求实施的符合性；\n4）是否发现新的危险源；\n5）生产运行过程和安全管理体系需要改进的区域等。', NULL, '2018-06-29 15:19:16', '2018-06-29 15:19:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2847, 107, '5.2', '系统评价实施\n1）确认符合现行的风险控制措施或安全管理体系要求，则继续监控。 \n2）确认已制定的风险控制措施无效，提出重新实施风险管理活动的要求。\n3）确认新的危险源，提出启动风险管理过程要求。\n4）确认系统运行环境发生变化，提出启动风险管理过程要求。 \n5）确认存在与风险控制措施不符合问题，提出制定纠正措施要求。\n6）确认存在与风险控制措施潜在不符合问题，提出制定预防措施要求。', 8, '2018-06-29 15:19:16', '2018-06-29 15:19:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2848, 107, '5.3', '系统评价周期\n系统评价每月进行一次。', 8, '2018-06-29 15:19:16', '2018-06-29 15:19:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2849, 107, '5.4', '系统评价结果上报\nSMS办公室将系统评价结果通过“Ameco航空安全管理系统”上报。', 8, '2018-06-29 15:19:16', '2018-06-29 15:19:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2850, 107, '5.5', '系统评价识别出新的危险源和无效的风险控制措施应进行系统和工作分析，或根据系统评价结果开展危险源辨识。', 10, '2018-06-29 15:19:16', '2018-06-29 15:19:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2851, 107, '5.6', '公司制定安全评价指数计算方法, 按月度、年度对分公司的安全管理情况以指数形式进行评价，并通过公司安全生产会进行发布。当安全评价指数进入预警区域后，分公司应采取措施，并报公司SMS办公室备案。', 8, '2018-06-29 15:19:16', '2018-06-29 15:19:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2852, 108, '5.1', '管理评审周期\n管理评审至少每年开展一次，可根据实际情况增加评审频次。在运行过程中发生重大不安全事件，或体系发生重大变化等特殊情况时，需立即启动管理评审。', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2853, 108, '5.2', '管理评审内容\n1）安全指标和安全绩效的完成情况；\n2）风险管理的实施情况；\n3）安全保证的实施情况；\n4）安全培训、教育情况；\n5）生产运行过程和安全管理体系需要改进的建议；\n6）其它涉及安全的重要事项和问题。', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2854, 108, '5.3', '管理评审方式\n通过召开航空安全委员会会议的方式进行。', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2855, 108, '5.4', '管理评审策划', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2856, 108, '5.4.1', '航空安全质量分部负责启动管理评审工作，由安全总监负责管理评审的策划工作，确定管理评审议题、时间、地点、参加评审人员。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2857, 108, '5.4.2', '航空安全质量分部负责根据策划结果，编写管理评审启动通知，经安全总监批准后下发。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2858, 108, '5.5', '管理评审准备', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2859, 108, '5.5.1', '数据收集和系统评价\n相关责任部门依据管理评审策划完成数据收集和系统评价报告，报告内容包括：系统分析报告，提交评审会议决策的事项等，按照启动要求报航空安全质量分部。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2860, 108, '5.5.2', '管理评审报告草案\n航空安全质量分部依据各责任部门上报的系统评价材料准备管理评审报告草案，对安全管理体系年度运行状况进行评估。报告草案内容应包括：安全数据分析、安全管理绩效分析、风险评估和分析、安全监督体系评估和分析及需提交评审会议决策的事项等。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2861, 108, '5.6', '管理评审会议', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2862, 108, '5.6.1', '航空安全质量分部负责会议组织工作，由安全总监主持会议。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2863, 108, '5.6.2', '会议审议相关部门提交的报告和管理评审报告草案，对需决策的事项做出决议，制定持续改进方案或重点工作计划。', NULL, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2864, 108, '5.6.3', '航空安全质量分部负责会议记录。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2865, 108, '5.7', '管理评审报告\n航空安全质量分部负责编写管理评审报告，经安全总监批准后发布。', 8, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2866, 108, '5.8', '持续改进\n各责任部门应根据管理评审会议制定的持续改进方案或重点工作计划落实持续改进要求。', 10, '2018-06-29 15:20:29', '2018-06-29 15:20:29', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2867, 109, '5.1', '安全培训内容及实施要求', NULL, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2868, 109, '5.1.1', '责任经理\n应按局方要求参加民航企事业单位主要负责人安全培训，培训满2年参加复训。', NULL, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2869, 109, '5.1.2', '质量经理和航空安全质量分部经理\n应按局方要求参加民航企事业单位中层安全管理人员安全培训，培训满2年参加复训。', NULL, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2870, 109, '5.1.3', '安全管理人员\n应按局方要求参加民航企事业单位初级安全管理人员安全培训，培训满2年参加复训。', NULL, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2871, 109, '5.1.4', '员工上岗前安全培训', NULL, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2872, 109, '5.1.4.1', '凡新进入分公司的人员必须接受上岗前安全培训。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2873, 109, '5.1.4.2', '上岗前安全培训分三级进行，即：公司级、分公司级和岗位级。三级安全培训时间不得少于24 学时，考核合格后方可上岗，安全培训内容和考核成绩记入个人安全培训记录。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2874, 109, '5.1.4.3', '考核要求：公司级安全培训需统一组织考试，分公司级和岗位级安全培训由各个岗位结合岗前培训组织考核。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2875, 109, '5.1.4.4', '培训内容，更新培训/复训按照分公司《培训大纲》要求实施。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2876, 109, '5.1.4.5', '转岗和重新上岗人员的安全培训\n调动、转岗以及重新上岗的职工应按照分公司《培训大纲》要求进行评估，接受相应安全培训。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2877, 109, '5.1.6', '专项安全培训', NULL, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2878, 109, '5.1.6.1', '航空安全质量分部根据上级要求提出培训需求。', 8, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2879, 109, '5.1.6.2', '人力资源分部负责组织实施，保存培训记录并录入公司培训系统。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2880, 109, '5.1.6.3', '航空安全质量分部负责监督培训和考核情况。', 8, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2881, 109, '5.2', '年度安全培训计划制定\n人力资源分部负责根据培训需求和《培训大纲》要求制定年度培训计划，并按要求上报，年度培训计划应包含安全培训内容。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2882, 109, '5.3', '年度安全培训计划实施\n人力资源分部根据年度培训计划，组织实施年度安全培训工作。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2883, 109, '5.4', '临时安全培训计划的申请、实施\n未列入年度安全培训计划，但因实际需要确需增加的培训项目，需要委托外部培训机构实施（产生费用的）或培训部实施的，按照01-06-001《培训计划制定程序》执行。', 10, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2884, 109, '5.5', '培训记录\n人力资源分部在组织实施培训过程中应填写并保存完整的培训记录，并按要求录入公司培训系统。', 1, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2885, 109, '5.6', '培训有效性验证\n航空安全质量分部负责安全培训有效性验证，并对安全培训制度、培训内容及安全培训实施情况进行监督检查。', 8, '2018-06-29 15:21:50', '2018-06-29 15:21:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2886, 110, '5.1', '制定安全教育计划\n航空安全质量分部负责制定安全教育工作计划，收集、整理安全经验教训内容。', 8, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2887, 110, '5.1.1', '安全教育计划的制定原则：\n1）安全教育计划要覆盖全体员工；\n2）安全教育计划要明确教育主题、责任人、实施时间及验证方法。', NULL, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2888, 110, '5.1.2', '安全教育计划的制定依据：\n1）局方和上级单位安全教育文件；\n2）不安全事件案例集；\n3）安全风险提示；\n4）其他各类安全经验交流材料。', NULL, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2889, 110, '5.1.3', '安全教育计划的制定： \n航空安全质量分部根据公司SMS办公室制定的安全教育计划，结合分公司的安全思路和特点，在每年12月制定分公司下一年度的《年度安全教育计划》（Form No.:SP-001），经SMS办公室主任审核后，报安全总监批准，上报下发。', 8, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2890, 110, '5.2', '安全教育的实施', NULL, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2891, 110, '5.2.1', '安全教育形式包括：\n1）自学；\n2）组织学习；\n3）教员授课；\n4）主题讨论；\n5）案例教育；\n6）主题活动等形式。', NULL, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2892, 110, '5.2.2', '航空安全质量分部负责月度安全教育材料的收集，确定安全教育主题，每月1日前发各部门，对于专项教育材料，按上级文件要求时限下发。', 8, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2893, 110, '5.2.3', '各部门依据航空安全质量分部下发的安全教育材料，结合本部门工作特点，确定安全教育重点。', 10, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2894, 110, '5.2.4', '各部门以部门或班组为单位组织月度安全教育，要求每月10日完成。专项安全教育按文件要求执行。', 10, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2895, 110, '5.2.5', '各部门应按《安全教育学习记录》（Form No.:SP-002）和《安全教育学习签到表》（Form No.:SP-T01）格式做好安全教育学习记录。', 10, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2896, 110, '5.2.6', '对出差、病假、休假，外派培训等原因未能按时完成安全教育的人员，应由所在部门安排其在上班一周内完成补课。', 10, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2897, 110, '5.3', '安全教育结果验证', NULL, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2898, 110, '5.3.1', '航空安全质量分部负责对各部门的安全教育情况进行监督并验证。', 8, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2899, 110, '5.3.2', '验证的方式可以采用询问安全教育的内容，检查安全教育记录等形式。', 8, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2900, 110, '5.3.3', '对验证不合格的部门，要求该部门组织重新进行安全教育学习。', 8, '2018-06-29 15:31:28', '2018-06-29 15:31:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2901, 111, '5.1', '安全奖励', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2902, 111, '5.1.1', '公司奖励范围\n1）运行过程中正确处置重大特殊情况，有效降低了公司的损失。\n2）纠正他人的差错，避免了可能发生的不安全事件。\n3）发现重大安全隐患并及时报告。\n4）其它对安全生产做出突出贡献的情况。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2903, 111, '5.1.2', '分公司奖励标准（表格见原程序）', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2904, 111, '5.1.3', '奖励申报\n各部门对符合公司奖励范围和分公司奖励标准的事迹填写奖励申请单报人力资源分部。', 10, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2905, 111, '5.1.4', '分公司奖励兑现\n人力资源分部汇总各部门上报的奖励申请单，由航空安全质量分部和技术支援分部审核，经分公司主管领导批准后兑现奖励，并进行公示。', 1, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2906, 111, '5.1.5', '申请公司奖励\n对于分公司奖励中符合公司奖励范围的事迹，由航空安全质量分部填写《北京飞机维修工程有限公司奖励兑现通知单》，经分公司质量经理批准后报公司SMS办公室。', 8, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2907, 111, '5.1.6', '公司奖励兑现\n人力资源分部负责按照公司、分公司奖励就高原则将公司批准的奖励兑现。', 1, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2908, 111, '5.2', '安全惩罚', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2909, 111, '5.2.1', '安全惩罚原则', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2910, 111, '5.2.1.1', '安全惩罚主要针对发生人为原因航空一般事故征候、维修严重差错（地面事故征候）和维修一般差错事件的直接责任人、管理责任人、领导责任人和监管责任人。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2911, 111, '5.2.1.2', '对于无后果的违章行为同样要采取安全惩罚。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2912, 111, '5.2.1.3', '安全惩罚的责任人在确定后根据分类原则确定扣罚金额。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2913, 111, '5.2.1.4', '对于员工的主动报告行为根据具体情况实行减免处罚政策。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2914, 111, '5.2.2', '安全责任分类（表格见原程序）\n各级岗位人员分类及承担的安全责任：', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2915, 111, '5.2.2.1', '直接责任：各类岗位操作人员、生产支援人员、工程立法人员、工程技术人员及质量管理人员等各级操作人员及复检人员因违章操作导致发生维修一般差错、维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，均负直接责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2916, 111, '5.2.2.2', '直接管理责任：各类岗位操作人员、生产支援人员、工程立法人员、工程技术人员及质量管理人员等各级操作人员和复检人员的直接领导，包括工段长、值班经理、中队（车间）经理/书记等，对该人员因违章操作导致发生维修一般差错、维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，均负直接管理责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2917, 111, '5.2.2.3', '管理责任：各维修单位/机关部室分管副总经理或副书记（不含北京基地、成都分公司）、北京基地处级领导、成都分公司/各中心部门高级经理和书记对分管部门发生的维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，负管理责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2918, 111, '5.2.2.4', '直接领导责任： \r\n1）各维修单位/机关部室分管副总经理或副书记（不含北京基地、成都分公司）、北京基地分管处级领导、成都分公司/各中心部门经理对分管部门发生的维修一般差错、维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，负直接领导责任。\r\n2）各维修单位/机关部室总经理和书记（不含北京基地、成都分公司）、北京基地分管部级领导、成都分公司/各中心分管副总经理或副书记对所管辖各单位发生的维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，负直接领导责任。\r\n3）北京基地总经理和书记对北京基地发生的人为原因事故征候、人为原因事故及以上事件，负直接领导责任。\r\n4）成都分公司/各中心总经理和书记对成都分公司发生的人为原因事故征候、人为原因事故及以上事件，负直接领导责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2919, 111, '5.2.2.5', '领导责任：\n1）公司分管执行层领导或副书记对公司发生的人为原因事故征候、人为原因事故及以上事件，负领导责任。\n2）公司首席执行官和书记对公司发生的人为原因事故及以上事件，负领导责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2920, 111, '5.2.2.6', '直接监管责任：\n1）各维修单位安全质量经理/科经理对各分公司/基地/中心发生的维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，负直接监管责任。\n2）北京基地分管安全/质量的处领导对北京基地发生的人为原因事故征候、人为原因事故及以上事件，负直接监管责任。\n3）成都分公司/各中心安全质量部高级经理对成都分公司发生的人为原因事故征候、人为原因事故及以上事件，负直接监管责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2921, 111, '5.2.2.7', '监管责任：\r\n1）各维修单位（不含北京基地、成都分公司）/机关部室分管安全副总经理对各单位发生的维修严重差错（地面事故征候）、人为原因事故征候、人为原因事故及以上事件，负监管责任。\r\n2）北京基地分管安全/质量的部领导对北京基地发生的人为原因事故征候、人为原因事故及以上事件，负监管责任。\r\n3）成都分公司/各中心分管安全副总经理对成都分公司发生的人为原因事故征候、人为原因事故及以上事件，负监管责任。\r\n4）公司SMS办公室主任对公司各单位发生的人为原因事故征候、人为原因事故及以上事件，负监管责任。\r\n5）公司分管安全/质量执行层领导对公司发生的人为原因事故及以上事件，负监管责任。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2922, 111, '5.2.3', '各类差错事件的最高追责岗位：', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2923, 111, '5.2.3.1', '人为原因事故及以上：\n1）领导责任（含直接领导责任）：公司首席执行官和书记；\n2）监管责任（含直接监管责任）：公司分管安全/质量执行层领导。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2924, 111, '5.2.3.2', '人为原因事故征候：\n1）领导责任（含直接领导责任）：公司分管执行层领导或副书记；\n2）监管责任（含直接监管责任）：公司SMS办公室主任；北京基地分管安全部级领导、成都分公司/各中心分管安全副总经理；各分公司/机关部室分管安全副总经理。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2925, 111, '5.2.3.3', '维修严重差错（地面事故征候）：\n1）领导责任（含直接领导责任）：各分公司/机关部室总经理和书记（不含北京基地、成都分公司）；北京基地分管部级领导、成都分公司/各中心分管副总经理或副书记；\n2）监管责任（含直接监管责任）：各单位安全质量经理/科经理（含北京基地、成都分公司、各中心）。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2926, 111, '5.2.3.4', '维修一般差错：\n1）领导责任（含直接领导责任）：各分公司/机关部室分管副总经理或副书记（不含北京基地、成都分公司）；北京基地分管处级领导；成都分公司/各中心分管部门经理和书记；\n2）监管责任（含直接监管责任）：无。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2927, 111, '5.2.4', '惩罚标准', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2928, 111, '5.2.4.1', '经济处罚标准：（见原程序）\n其中，对直接责任人的经济处罚：\n事故症候等级：不超过其3个月的岗位工资；\n维修严重差错等级：不超过其2个月的岗位工资；\n维修一般差错等级：不超过其1个月的岗位工资。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2929, 111, '5.2.4.2', '技术处罚标准\n人为责任原因造成的事故征候事件（含）及以上的事件，直接责任人取消放行资格，技术资格降低一级。\n维修严重差错（地面事故征候）事件，直接责任人暂停放行资格6个月，技术资格降低一级。\n维修一般差错事件，直接责任人暂停放行资格3个月。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2930, 111, '5.2.4.3', '相关责任部门扣罚相应的安全管理考核分数。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2931, 111, '5.2.4.4', '对于在安全管理过程考核中被定义为重复发生的不安全事件，加重经济处罚：\n1）重复发生人为责任原因事故征候，所有管理责任人（含间接管理责任人）及以上级别的相关管理人员均按上限执行经济处罚。\n2）重复发生维修严重差错（地面事故征候），按人为责任原因事故征候标准执行经济处罚。\n3）重复发生维修一般差错，按维修严重差错（地面事故征候）标准执行经济处罚。\n4）重复发生一般违章事件，按维修一般差错上限标准执行经济处罚。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2932, 111, '5.2.4.5', '对于无后果的违章行为，根据情节严重性，对相关维修人员进行经济处罚，处罚标准不高于一般违章事件标准。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2933, 111, '5.2.4.6', '同一部门连续、重复发生无后果的违章行为，要追究直接管理责任人，并根据情节严重性，对直接管理责任人进行经济处罚，处罚标准不高于一般违章事件标准。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2934, 111, '5.2.4.7', '对于被认定为主动报告的一般违章（不含）以下的不安全事件，视情减少或者免除相应处罚。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2935, 111, '5.2.5', '处罚程序', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2936, 111, '5.2.5.1', '公司对维修严重差错（含）级别以上的事件按照上述标准进行处罚，分公司对维修一般差错(含)以下的事件按照上述标准进行处罚。', NULL, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2937, 111, '5.2.5.2', '对于责任人的行政处罚按照人力资源相关手册进行处理；经济处罚按照本程序5.2.4.1标准进行处罚。', 1, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2938, 111, '5.2.5.3', '对于责任人的技术资格处罚按照5.2.4.2标准进行处理。', 8, '2018-06-29 15:46:15', '2018-06-29 15:46:15', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2939, 112, '5.1', '工作时间', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2940, 112, '5.1.1', '工作时间限制', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2941, 112, '5.1.1.1', '在满足公司人事规定中的相关要求的条件下，所有航线维修都应当合理安排维修人员的工作时间。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2942, 112, '5.1.1.2', '一般情况下，员工的工作时间每天不应当超过8小时，平均每周的工作时间累计不应当超过40小时。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2943, 112, '5.1.1.3', '直接生产人员的工作时间可以不受每天8小时的限制，但每天累计工作时间不应超过11小时。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2944, 112, '5.1.1.4', '对于从事跟机维修的人员，跟机维修时间（包括为完成维修工作任务的往返乘机时间）可以不受工作时间的限制，超出工作时间时限的，在跟机维修任务完成后不得安排工作任务。跟机维修时间计入每月工作时间。跟机维修过程中在外停留的非工作时间可以不计入每月的工作时间。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2945, 112, '5.1.1.5', '不论以何种方式加班，每月累计加班时间不得超过36小时。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2946, 112, '5.1.1.6', '如果维修人员正在实施的连续维修工作步骤在执勤期内不能完成，但由其继续实施对维修质量和安全有利，执勤期可以延长2个小时，执勤期最长不得超过15小时，同时实际加班时间应累计算入每周和每月的工作时间。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2947, 112, '5.1.1.7', '执勤期后应当安排维修人员至少12个连续小时的休息期，这个休息期应当安排在该执勤期结束时刻与下一个执勤期开始时刻之间。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2948, 112, '5.1.1.8', '每7个连续日历日应保证维修人员至少休息1日。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2949, 112, '5.1.2', '工作时间限制对象', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2950, 112, '5.1.2.1', '工作时间限制的人员为直接从事航空器或者航空器零部件维修工作的维修人员，包括：\n1）因实施维修工作的目的直接接触航空器或者航空器零部件的人员；\n2）在维修工作实施的文件上直接签署工作和维修放行的人员。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2951, 112, '5.1.2.2', '当维修管理人员、支援人员部分时间履行上述涉及的维修人员职能时，其全部的工作时间和加班时间也适用。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2952, 112, '5.1.2.3', '在工作时间内，当任何维修人员感觉身体状况不适应其所从事的维修或者维修相关的工作时，应向当班负责人提出终止工作，任何人不应强迫其继续工作。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2953, 112, '5.2', '能力工时', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2954, 112, '5.2.1', '能力工时计算标准', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2955, 112, '5.2.1.1', '理论能力工时＝人员设置数量×工作时间×80%', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2956, 112, '5.2.1.2', '生产计划控制分部编制内的直接生产人员（P岗位值）按照100%提供能力工时。中队长不计能力工时，分队长和排故工程师按照50%计算能力工时。', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2957, 112, '5.2.1.3', '对于生产计划控制分部编制内的新入职直接生产人员（一般指T0人员），如果入职不满6个月，不计工时；如果入职满6个月但不满12个月，计算50%的工时；如果入职满12个月，则按照100%计算工时。', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2958, 112, '5.2.2', '能力工时评估', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2959, 112, '5.2.2.1', '人力资源分部/生产计划控制分部以年度为单位定期评估维修人员数量和提供总工时是否能满足本单位的需求，并根据情况采取增加人员或减少工作量的措施。', 1, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2960, 112, '5.2.2.2', '生产计划控制分部每月发布能力工时状况。', 6, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2961, 112, '5.3', '计划工时管理\n计划性维护工作安排所使用的工时为标准工时，例行工作标准工时参见上级程序。', 6, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2962, 112, '5.3.1', '生产中的工时管理', NULL, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2963, 112, '5.3.1.1', '原则上发放用于生产的工卡和清单中不显示标准工时信息，各项工作标准工时以电子版形式在内部网络上公布。', 6, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2964, 112, '5.3.1.2', '维修中队应客观上报产生的实际工时，并按例行和非例行进行分类。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2965, 112, '5.3.1.3', '如果实际工时与例行工时存在较大差异，须立即进行标准工时修正。T2以上级别机械员或工程师提出申请并填写《计划工时修订申请单》Form No.: LM-012，经中队长或生产经理签字确认后报生产计划控制分部进行评估。', 9, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2966, 112, '5.3.1.4', '生产计划控制分部进行评估后，按需报运管部用于标准工时修正。', 6, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2967, 112, '5.3.1.5', '当EO实际工时与收费工时存在较大差异时，要及时上报客户。', 6, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2968, 112, '5.4', '维修生产控制\n当维修过程中发现不能按照生产计划完成维修工作时，可以在维修人员的资格和工作时间限制符合CCAR-145部要求的情况下采用增加维修人员或者延长维修人员工作时间的方法恢复生产计划；如不能则应当延长航空器的停场时间，以保证维修工作能按照经批准的标准完成和工作质量。', 6, '2018-06-28 09:53:50', '2018-06-28 09:53:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2969, 113, '5.1', '工作单/包计划', NULL, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2970, 113, '5.1.1', '航线周计划按日滚动生成3日工作计划。MCC值班经理负责3日的调整及修正；72小时以外的调整由生产计划控制分部计划工程师负责修正。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2971, 113, '5.1.2', '生产计划控制分部计划工程师至少提前1天，在SAP系统中进行单机工作组包，并对计划性工作完成航材预留。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2972, 113, '5.1.3', '航材与采购部天津分部按计划完成器材准备。', 2, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2973, 113, '5.1.4', '航线中队按计划，完成人力安排，并进行器材的领取。', 9, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2974, 113, '5.2', '工作单/包控制流程', NULL, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2975, 113, '5.2.1', '如缺少器材或工具，生产计划控制分部应提前从工作单包中撤出相应工作单并控制其期限。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2976, 113, '5.2.2', '生产计划控制分部在维修控制中心备份一部分航线工作单和常用工作单，并对有效性负责。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2977, 113, '5.2.3', 'MCC值班经理根据人力、工作内容、特殊要求、停场时间等状况，对所控制的维修工作指令进行计划和安排。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2978, 113, '5.2.4', '依据“三日工作计划”中的维修工作，MCC白班值班经理提前1天编写“工作汇总单”，MCC夜班值班经理负责审核，签字并下发。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2979, 113, '5.2.5', 'MCC助理工程师按值班经理下发的“工作汇总单”，准备工作单/包。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2980, 113, '5.2.6', '如维修计划发生变化（如工作项目增减），值班经理需对工作单包内容进行相应修改并在工作汇总单中注明。特殊情况下，值班经理可以先委托生产单位进行修改，随后核对修改内容。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2981, 113, '5.2.7', '航前、过站、航后工作单包在飞机落地30分钟之前，航线中队生产单位从MCC领取已准备好的的工作单/包，做好交接。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2982, 113, '5.2.8', '航线中队负责飞机工作文件的管理工作，在工作单/包返回MCC之前，须对完成工作单进行检查，保证签署的正确性和完整性。航后工作单包及相关工作文件须在飞机出港前2小时交回MCC。工作单包涉及的所有工作记录齐全之后才能放行飞机。\r\n在飞机起飞前在SAP系统中完成工作包关闭。', NULL, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2983, 113, '5.2.9', '特殊情况由当日值班经理通过MCC值班座机电话确认飞机已放行，维修记录完整，在现场质量检验员审核认可的情况下，可以后续完成维修记录的清点工作。', 9, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2984, 113, '5.2.10', '所有返回的飞机文件由生产计划控制分部按要求分发给相关单位以保证飞机技术信息及时反馈。生产计划控制分部对返回的工作单包进行整理并检查，再交接至航空安全质量分部存档。', 6, '2018-06-28 09:56:45', '2018-06-28 09:56:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2985, 114, '5.1', '一般规定', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2986, 114, '5.1.1', '根据批准的适航性资料以及营运人的维修方案对飞机进行勤务检查工作。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2987, 114, '5.1.2', '客户必须和Ameco签署标准航线维修协议（以下简称协议），并明确要实施的工作范围，职责和要求且不能超过天津分公司维修许可证批准的范围。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2988, 114, '5.1.3', '凡交由天津分公司维修和放行的飞机，它的不同机型和技术差别，客户必须给天津分公司人员提供必要的符合适航法规要求的培训和实习指导，并给予授权批准。', 1, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2989, 114, '5.1.4', '在航前过站和航后中，技术员需按相应机型的维修手册挡放轮档。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2990, 114, '5.1.5', '客户航空公司飞机维修和预防性维修应受到严密监督。', 8, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2991, 114, '5.1.6', '天津分公司从事维修、预防性维修和改装工作的等级范围限制应满足适航部门批准的能力清单及客户的授权与限定范围。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2992, 114, '5.1.7', '使用客户提供的适航性资料中的方法、技术和维护实践实施对客户飞机的维修和预防性维修。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2993, 114, '5.1.8', '进行客户航空公司维修的人员必须接受过客户程序的培训。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2994, 114, '5.1.9', '航线中队在人员安排上需严格遵循：只有被航空安全质量分部授权和被制定给客户培训和授权的技术人员才能执行客户飞机的维护工作。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2995, 114, '5.1.10', '飞机的航线维修规则', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2996, 114, '5.1.10.1', '航行前维修的规则\r\n1）维修人员应根据手册的规定，提前到达停机位。\r\n2）维修人员应会同监护人员在检查飞机外表和舱门封条（或其他等效安全措施）完好后，办理交接手续，签字接受飞机。\r\n3）应将各种布罩、堵塞（盖）、销子、夹板等随机设备取下，清点齐全，放在规定位置。\r\n4）低温条件下按飞机维修手册的规定对发动机进行加温。\r\n5）飞机须牵引时，应按MH/T 3011.3规定执行。\r\n6）推（拖）飞机到达指定位置后，飞机应刹车，按规定放好轮挡，视情取下拖把。\r\n7）接好耳机，与机组进行联络。\r\n8）根据需要将气源车、电源车与飞机接好，并供气、供电。\r\n9）应按该型飞机的维修工作单（卡）完成航行前维修工作并在工作单（卡）上逐项签署。\r\n10）检查飞行记录本的记录，确认飞机维修工作结束，故障已排除或已正确处理，并签署放行。\r\n11）取下起落架安全销，经机长过目后，由机组或维修人员放在飞机的指定位置或由维修人员带走。\r\n12）清点工具。\r\n13）当飞机有冰、雪、霜时，应对飞机进行除冰、防冰。\r\n14）绕飞机一周，检查确保舱门、盖板已关好，飞机周围无障碍物及无关人员。\r\n15）飞机出港推出工作应至少有两名地面人员（指挥人员和送机人员）完成。如果飞机与机坪周围建筑物和其他飞机之间的距离达不到规定的间距时，应在翼尖处增加监护员。\r\n16）推（拖）飞机到指定位置后，应通知机组刹车并在前轮放置好轮挡后，方能摘取拖把。\r\n17）发动机启动完毕后，应完成下列工作：\r\n——协助电源车、气源车司机取下接头，盖好盖板；\r\n——取走轮挡；\r\n——取下耳机，盖好盖板；\r\n——取下并高举前轮转弯销，并经机长过目；\r\n——送机人员站在机头左前方，按MH/T 3010.9中附录C的规定，指挥航空器滑出。当站在左前方有不安全因素时，可站在机头右前方指挥飞机滑出。\r\n18）飞机离岗后，送机人员应清洁、整理工作现场，将地面设备放置在规定位置。', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2997, 114, '5.1.10.2', '短停维修的规则\r\n1）接机人员应知道飞机进港时间及停机位，并提前到达停机位。\r\n2）接机人员应根据需要准备好耳机、前轮转弯销、轮挡、起落架安全销和尾撑杆等设备和工具。\r\n3）维修人员应准备好飞机维修工作单（卡）和工具。\r\n4）接机人员应检查并确保飞机机位安全区内无车辆、维修设备及其他障碍物；地面标志线清晰可见；机坪表面应无松散的石子或其他可能损坏飞机结构、发动机或飞机其他系统正常运行的物体；登机廊桥处于收回位；灭火设备到位。夜间运行时，停机坪照明灯可用并在工作状态。\r\n5）接机人员应按MH/T3010.9的规定指挥飞机进入停机位，并挡好轮挡。\r\n6）飞机发动机停车后，维修人员应：\r\n——插上耳机，与机组联络（无内话机设备时可按MH/T 3010.9的规定信号与机组联络）。\r\n——按工作单（卡）的要求插上起落架安全销；\r\n——根据需要接通电源；\r\n——根据需要装上操纵面板夹。\r\n7）维修人员应认真听取机组的故障报告并详细查阅飞行记录本。\r\n8）维修人员应按该型飞机的维修工作单（卡）完成短停维修的各项工作，正确处理故障和缺陷。\r\n9）维修人员应按MH/T 3010.7的要求逐项完成工作单（卡）的签署，并在飞行记录本上签字放行。\r\n10）维修人员应清点工具、设备，确认齐全，查看并确保飞机各部位完好。\r\n11）飞机停放期间，应至少有一名送机人员在场监督。\r\n12）飞机离港送机应按上述“航行前维修规则”的11）～18）的规定执行。', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2998, 114, '5.1.10.3', '航行后维修的规则\r\n1）进港接机按上述“短停维修的规则的”1）～6）的规定执行。\r\n2）飞机需牵引时，应按MH/T 3011.3-2006的规定执行。\r\n3）维修人员应仔细翻阅飞行记录本并尽可能详细地向机组了解飞机的技术状况，并按该飞机的维修工作单（卡）完成航行后的各项工作，排除故障、缺陷。\r\n4）遇有故障需保留时，应按保留故障的规定程序办理报批和完成相应的工作。\r\n5）应按MH/T 3010.7的要求逐项完成工作单（卡）的签署，并在飞行记录本上签字放行。\r\n6）应按飞机维修手册的规定盖好各种布罩、堵塞（盖），装上舵面夹板，作好停放系留。撤离各种勤务车辆，关断电源，关好门窗，舱门应采取上锁、铅封、封条或其他等效安全措施。\r\n7）维修人员应清点工具、设备，整理场地。工作梯及其他设备应摆放在规定位置。\r\n8）全部工作结束后，维修人员应会同监护人员办理飞机监护交接手续。', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (2999, 114, '5.2', '流程概述', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3000, 114, '5.2.1', '接收飞机前的准备\n生产计划控制分部向航线中队（客户维护团队）提供每日飞机进港和出港的航班计划表，并及时提供准确的飞机到达时间和停机位以及飞机有特殊运行要求的故障信息。MCC负责准备和提供所需的工卡和收费单。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3001, 114, '5.2.2', '接收飞机', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3002, 114, '5.2.2.1', '航前，从看守飞机的警卫或安保人员那里接收飞机前（按需），负责该飞机出港的维修人员应对飞机外部进行检查，确认飞机外表和可见区域无异常情况，才能接收飞机，如有异常情况应立即向MCC及有关领导报告。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3003, 114, '5.2.2.2', '对于过站、航后飞机，负责飞机维护的人员须在飞机到达前十五分钟在停机位等候并确认以下内容：\r\n1）各种车辆、设备等均停放在规定区域内；\r\n2）停机位地面标志线，确保其清晰可见；\r\n3）停机位区域没有影响飞机安全的外来物；\r\n 4）廊桥处于收回位置并停放在廊桥机轮停放区域内。', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3004, 114, '5.2.2.3', '在航前、航后和过站中的检查、勤务和故障排除应在合同规定下，分别参考5.2.5和5.2.6完成。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3005, 114, '5.2.3', '飞机出港', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3006, 114, '5.2.3.1', '放行技术员同时也负责保证飞机处于飞行前的适航状态。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3007, 114, '5.2.3.2', '在飞机出港前必须通知机组有关飞机的技术状态和所做排故工作，并且已完成客户工卡的工作及签署。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3008, 114, '5.2.3.3', '根据客户程序，在考虑到当地机场安全规定的情况下，飞机推出时的通话和发动机启动程序必须得到遵守。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3009, 114, '5.2.3.4', '责任技术员在推出飞机后，并将已签署工卡和相关其他客户要求的技术文件，一起送交MCC。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3010, 114, '5.2.3.5', '对于Ameco提供技术支援的客户飞机，应签署和保存技术服务记录单作为质量记录。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3011, 114, '5.2.3.6', '如客户需要，在飞机出港后，客户要求的有关飞机技术状态报告必须通过电话、传真等客户认可的通讯方式发往客户的下一个航站和它的基地。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3012, 114, '5.2.4', '航后维护', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3013, 114, '5.2.4.1', '维修技术员应完成合同规定的航后工作卡内容及分配的工作并签署，航后检查应是认真和仔细的，并尽可能扩大检查面以提高航后的维修质量。如工作中遇到会影响航后完成的情况，应及时报告分队长或中队长。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3014, 114, '5.2.4.2', '如果合同要求，冬季外界气温在低于零摄氏度时，维修中队维修人员必须放干净水系统的存水并吹净管路中的余水。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3015, 114, '5.2.4.3', '航后检查完成后，责任技术员应立即签署航后工作单、TLB 和CLB，以及客户要求的其他技术文件。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3016, 114, '5.2.4.4', '放行人员应在确保没有工具遗留在飞机上后，签署航后放行。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3017, 114, '5.2.4.5', '工作结束后，送还工具、设备、文件和不可用部件等，退还器材、设备、并清洁工作场地。并将飞机及时交给警卫或安保人员（按需）。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3018, 114, '5.2.5', '检查与勤务', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3019, 114, '5.2.5.1', '对于天津分公司只进行技术协作的客户，我们的工作必须遵照负责该飞机的客户技术代表所给的指导。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3020, 114, '5.2.5.2', '对于由天津分公司全权实施对飞机的安全技术处理和签字放行的客户，应按客户提供的工卡实施航线维护，在所需的检查及勤务工作完成并确保飞机处于飞行前可用状态后，维修技术员还应清点确认现场使用的工具，确保没有遗留在飞机上，还应在工卡相应的项目栏内逐一签署，并完成相应的维护服务记录。最后应按合同要求放行飞机。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3021, 114, '5.2.5.3', '与客户签订的协议中涉及的勤务工作如：防冰/除冰等，应按02-11-006《飞机的除冰/防冰》进行工作；而对于加油等其他勤务，应遵守01-11-005《飞机勤务》。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3022, 114, '5.2.6', '故障处理', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3023, 114, '5.2.6.1', '对于故障及在检查中发现的任何问题，应根据工作授权及协议工作范围及要求告知客户代表并采取及时有效的排故及处理措施（如果故障或发现问题发生在国航非天津机队的飞机上，应报飞机的执管基地）；若故障记录在飞机技术记录本上，须跟机组澄清故障细节。此后，排除故障的工作方可进行。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3024, 114, '5.2.6.2', '如故障超出客户的最低设备清单（MEL），则根据协议及客户程序或要求采取相应的排故措施。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3025, 114, '5.2.6.3', '如故障在最低设备清单（MEL）限制范围内，则应根据客户的程序或要求采取处理措施。飞机在地面期间，客户的成本和利益应得到考虑。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3026, 114, '5.2.6.4', '排故工作的范围是受协议及授权限制的并应确认故障是否被彻底排除；如对此没有把握，应跟飞机执管基地联系以获得故障分析和排故建议。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3027, 114, '5.2.6.5', '当客户飞机出现如鸟击、雹击等外来物损伤或发生重着陆、颠簸、雷击等情况时（包括已签字放行的飞机），应报告客户航空公司相应部门并根据客户程序进行特殊检查。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3028, 114, '5.2.6.6', '原则上，如因排故需要发动机地面试车，根据与客户的合同决定由客户代表，飞行员或Ameco的技术人员实施，具体细节按01-11-007《发动机试车和客舱增压实验程序》执行。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3029, 114, '5.2.6.7', '按合同要求，所有故障和所做的工作都必须记入客户的技术文件，如：技术记录本、客舱记录本等。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3030, 114, '5.2.6.8', '非例行工作应填写带三联的收费单并由客户代表签字认可。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3031, 114, '5.2.6.9', '航线中队应及时将航线维修生产进度、飞机故障和故障处理及其他不正常信息向MCC报告。', 9, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3032, 114, '5.2.7', '航线维修工作的指挥与协调\nMCC应本着主动、及时地完成恢复生产运行任务的原则与各单位相互协调，合理安排生产资源。MCC应服从AMC的统一指挥，按照AMC的部署和要求调整和调动本分公司的生产资源，完成航线维修工作。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3033, 114, '5.2.8', '值班领导职责\n天津分公司当日值班领导应明确掌握当日本单位的生产运行情况，支持AMC和MCC的运行需求，及时协调并解决生产运行中遇到的问题。', 10, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3034, 114, '5.2.9', '技术文件', NULL, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3035, 114, '5.2.9.1', '生产计划控制分部按合同要求各客户必须另外提供适用的技术文件给天津分公司，像维护手册、零附件目录手册、故障分析程序、飞机线路图册和系统图册，以及航站手册，放行手册（MEL）等，并及时提供最新改版。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3036, 114, '5.2.9.2', '生产计划控制分部接收客户资料后转交技术支援分部，由技术支援分部进行控制。', 4, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3037, 114, '5.2.9.3', '客户的技术文件须保存在工作者容易接近的地方，并处于实时可用状态。', 4, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3038, 114, '5.2.9.4', '对飞机所做的工作必须有清晰的记录包括处理飞机问题的来往邮件和传真等应按适航法规和客户要求的期限保存。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3039, 114, '5.2.9.5', '维修中队将客户认可的技术服务记录单/收费单送交MCC，并由生产计划控制分部输机后交财务分部。', 6, '2018-06-28 10:07:25', '2018-06-28 10:07:25', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3040, 115, '5.1', '一般规定', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3041, 115, '5.1.1', '根据AC-121-50的要求，航空器在地面起飞前要求一些关键表面不能带冰、雪、霜起飞。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3042, 115, '5.1.2', '天津分公司应根据与航空营运人正式签署的地面服务协议为航空营运人提供地面除冰/防冰勤务。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3043, 115, '5.1.3', '当航空器的关键表面区域附着有雪、霜或冰时，Ameco不能放行航空器。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3044, 115, '5.1.4', '在地面结冰条件下，飞行机组必须在起飞前通过航空器外部检查确认关键表面是否附着冰冻污染物。确定飞机是否存在冰冻污染物的最终责任人是机长，但天津分公司应满足以下要求：\n1）当起飞前航空器外部检查是由经过培训并具有检查资格的机务人员完成时，在起飞前机长应当确认其完成了检查并确定航空器的状态是否适合飞行或者需要执行除冰/防冰程序。\n2）飞行前实施航线维修任务时，维修人员应当检查飞机关键表面是否附着冰冻污染物，发现或怀疑有冰冻污染物的情况时，应当及时通报机长。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3045, 115, '5.2', '需要的工具/设备和器材', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3046, 115, '5.2.1.1', '除冰/防冰过程中使用的工具设备应当符合下述规定： \n1）除冰/防冰液的喷洒应当使用能够满足除冰/防冰液使用要求（如加热和温度控制要求等）的专用设备进行。 \n2）如航空器制造厂家规定了除冰/防冰过程中使用的其他工具，应当符合制造厂家的规定，并且不得使用可能造成航空器表面损伤的任何工具。\n3）推荐的除冰/防冰液使用方法应符合AC-121-50和AEA的规定。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3047, 115, '5.2.1.2', '4）除冰/防冰液应当使用符合航空器制造厂家指定规范的产品或者使用经民航局适航审定部门批准的等效替代产品，并且根据外界温度、气象条件和预计的保持时间正确地选择。', 2, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3048, 115, '5.2.2', '生产支援分部应使用如下特定的工具设备和除冰/防冰液\n1） 飞机除冰用的专业车辆；\n2）检测除冰液用的折射计。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3049, 115, '5.2.3', '准备有符合SAE标准的I 型除冰液和II型防冰液。', 2, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3050, 115, '5.2.4', '准备有除冰用的安全带、防护服和防护镜。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3051, 115, '5.3', '除冰/防冰液的购买与储存', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3052, 115, '5.3.1', '除冰/防冰液的定购工作\n每年使用的除冰/防冰液的型号应于每年9月15日前确定。除冰/防冰液的订购工作参见01-10-010《除冰/防冰液质量保证程序》。', 2, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3053, 115, '5.3.2', '除冰/防冰液的储存', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3054, 115, '5.3.2.1', '为避免除冰液中周围环境，只使用专用飞机除冰/防冰液（ADF）的容器储存。', 2, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3055, 115, '5.3.2.2', '确保制作该容器的材料与除冰/防冰液（ADF）不发生反应。并在容器上标注简明易读的标签，防止污染。\n确保除冰/防冰液没有降级、污染发生，需要做定期检查。详细参看01-10-010《除冰/防冰液质量保证程序》。', 2, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3056, 115, '5.4', '除冰/防冰培训', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3057, 115, '5.4.1', '除冰工作要经过专业理论和实习培训的人员完成，人力资源分部应按照除防冰培训相关程序实施并完成培训。', 1, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3058, 115, '5.4.2', '维修中队应保存一份现行有效的合格除冰人员清单。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3059, 115, '5.5', '除冰/防冰前的准备工作', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3060, 115, '5.5.1', '所有与除冰/防冰工作有关的工具/设备、除冰/防冰液、依据文件等应在每年的11月1日前准备就绪。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3061, 115, '5.5.2', '除冰车应按照维护工卡进行相关的维护检查。除冰车辆必须保证可用，包括安全带、防护镜、除冰衣等，即在此日之前除冰车应送修理车间进行检修和功能测试。要填写维护记录并保存。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3062, 115, '5.5.3', '天津分公司使用SAE标准的除冰液和防冰液。除冰/防冰液在储存和使用前的质量控制、除冰/防冰液用水稀释的配比及相关记录应参见01-10-010《除冰/防冰液质量保证程序》进行处理。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3063, 115, '5.5.4', '除冰/防冰液的保持时间的确定应遵守工程部颁发的相关EO。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3064, 115, '5.6', '除冰/防冰的实施', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3065, 115, '5.6.1', '除冰/防冰的启动', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3066, 115, '5.6.1.1', 'MCC应关注每天天津滨海国际机场飞行区的雪情气象预警信息。得到降雪天气信息通知Ameco相关单位及时做好除雪准备，通过手机短信平台、对讲机、电话发布指令信息。', 6, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3067, 115, '5.6.1.2', '一旦飞机现场出现除冰雪需求，MCC值班经理担负除冰雪的组织安排工作，负责飞机的除/防冰指挥协调工作，处理和协调除/防冰工作中出现的各种问题。', 6, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3068, 115, '5.6.2', '飞行前污染物检查', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3069, 115, '5.6.2.1', '污染物检查必须执行\n污染物检查将确定是否有必要进行除冰/防冰程序。必须是一个合格的人员才能做这个检查。在天气条件差的冬季检查，可以及时发现飞机结冰。下面所列出的所有区域和部位必须除去冰，霜和雪。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3070, 115, '5.6.2.2', '检查时，对飞机进行目视或手感检查确定飞机表面是否附着有冰冻污染物：\r\n1）发动机和APU：启动前必须检查进气道（口）无冰冻物和积雪、风扇叶片无结冰。\r\n2）起落架、起落架轮舱及其舱门：外表面、连杆/铰链、活动关节处无结冰。\r\n3）水平安定面：确保表面上和链接处没有冰、霜和雪，而且水平安定面能够在其余行程中自由运动。\r\n4）机翼：确保整个机翼表面没有冰、霜和雪〔机翼下表面允许3.2毫米（1/8英寸）以下的霜〕。\r\n5）操纵面：确保表面上没有冰、霜和雪，而且能够在其全行程内自由运动。\r\n6）	机头部分：确保机头部分风挡前方没有冰和雪。\r\n7）机身和垂尾：机身和垂尾上没有冰和雪。\r\n8）空速管/静压管、静压口、全温（TAT）探头、排水口、迎角探测器、风挡雨刷等其它区域，确保没有冰和雪。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3071, 115, '5.6.3', '除冰/防冰后的检查', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3072, 115, '5.6.3.1', '除冰/防冰工作完成后，除冰操作员应当进行目视检查，确保所有霜、冰、雪及积冰已经清除干净，并且飞机表面附着足量的防冰液，必要时还应当通过触摸检查确认。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3073, 115, '5.6.3.2', '目视检查区域包括：机翼、水平/垂直安定面、机身、以及污染物检查中要求的执行过除冰的区域。该检查应从能够为所有规定表面提供足够可视度的区域进行。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3074, 115, '5.6.3.3', '当发现有污染物时，应通过进一步的除/防冰工作去除污染物，并重新进行目视检查。除冰后检查完成后，除冰操作员应将确认信息报告机械员。机械员得到除冰操作员给出的除冰后检查确认信息后，方可放行飞机。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3075, 115, '5.6.4', '除冰/防冰的构型', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3076, 115, '5.6.4.1', '两步法除冰/防冰要求：\n在进行除冰/防冰工作前，根据天气状况以及AMM和/或AOM要求和相应的工卡的规定使用除冰液/防冰液（Ⅰ型或Ⅱ型）。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3077, 115, '5.6.4.2', '在第一步除冰过程中，除冰操作员要确保将机身机翼除冰操作表面上被冻结的各种附着物都清除干净后，再实施第二步防冰工作。\n另外，要在第一步除冰液被冻结前立即实施第二步喷洒防冰液。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3078, 115, '5.6.5', '地面除冰/防冰过程中的通信和记录', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3079, 115, '5.6.5.1', '维修中队由T1资格机械员通过耳机、步话机与机组、MCC、除冰车司机、除冰操作员建立有效的联系并履行地面指挥员职责。与机组在开始除冰/防冰前和完成除冰/防冰后都应当进行必要的通信联系，机械员和飞行机组之间的通话应包含以下信息：\n1）刹车就位、发动机关闭。\n 2）除冰开始时间、除冰液类型、混合比。\n 3）除冰完成时间、Ⅰ型除冰液使用量、Ⅱ型除冰液使用量（以上信息请自己收到后填写在TLB上）。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3080, 115, '5.6.5.2', '在机位除冰和定点关车机位除冰，由机械员使用步话机与除冰车司机、除冰操作员、MCC进行沟通；通过耳机与机组保持通讯联系。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3081, 115, '5.6.6', '除/防冰中断的处置', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3082, 115, '5.6.6.1', '以下几种情况会出现除/防冰中断：\n1）除冰车故障；\n2）除冰车需补加除冰液；\n3）飞机突发故障；\n4）除冰车与飞机发生剐蹭；\n5）飞机上突发紧急情况。\n现场指挥员应与机组建立有效沟通，确保除冰工作的连续性和实效符合除冰规范之要求。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3083, 115, '5.6.6.2', '对于除冰车问题，现场指挥员根据当时的除冰运行模式（定点或机位除冰）与MCC或现场指挥员确认第二辆除冰车到位的时间、与除冰操作员一起评估剩余作业时间并根据除冰液保持时间决定是否重新进行除冰。现场指挥员负责将除冰中断原因、恢复中断作业的方式（重新除冰或继续剩余部分）预计作业持续时间报告机组和MCC或现场指挥员。除冰操作员和除冰车司机根据现场指挥员指令恢复并完成除冰中断作业。\r\n对于除冰中断涉及的3-5项之情况，现场指挥员应与机组、现场指挥员立即建立通讯联系，根据故障的难易程度、影响飞机的适航性及飞机突发应急情况来确定恢复中断的时机。除冰车司机和除冰操作员根据指令恢复并完成除冰中断作业。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3084, 115, '5.6.6.3', '当除/防冰被终止时（如除冰车无除冰液），机械员应通知机组人员中止的原因、要采取的措施和延后的时间。当再次启动除/防冰工作时，如果中断的时间超过除冰液的保持时间，之前处理过的表面都应重新除/防冰。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3085, 115, '5.6.7', '除冰/防冰的组织方式', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3086, 115, '5.6.7.1', 'MCC根据国航天津指挥中心的指令，通知维修中队值班经理组织人员进行相应的除冰保障。', 6, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3087, 115, '5.6.7.2', '负责机场T2东区的定点除冰工作，定点除冰由经理及维修中队成员组成工作团队实施现场指挥。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3088, 115, '5.6.7.1.1', '在机位除冰运行模式', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3089, 115, '5.6.7.1.2', '1）机位除霜、启用定点除冰前的机位除冰以及定点转机位后剩余少量的机位除冰雪，由MCC助理工程师统一调度除冰车辆。', 6, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3090, 115, '5.6.7.1.3', '2）明显降雪但天津滨海国际机场没有启动定点除雪情况下的机位除冰雪，应由航线中队除冰领导团队统一部署安排人员参与机位除冰雪指挥和除冰车辆调度，MCC助理工程师配合。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3091, 115, '5.6.7.1.4', '3）送飞机机械员作为现场指挥员负责机位适应性检查、通过步话机和耳机负责与MCC、机组、除冰操作员、除冰车司机的沟通；监控和处置现场除冰作业情况；通知机组除冰的开始时间、及时从除冰车驾驶员获取除冰液型号、除冰液使用量、除冰完成时间报告机长。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3092, 115, '5.6.7.2.1', '在定点除冰运行模式\n由经理级（含）以上人员组成指挥小组，其成员：', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3093, 115, '5.6.7.2.2', '1）现场指挥员：负责统一调度本区域除冰车辆、人员和设备、慢车机位、关车机位现场的监护、与除雪指挥部保持信息沟通。关车机位由勤务部门负责引导飞机入位；机械员负责用耳机与机组建立联系、确认刹车就位、发动机关闭、可以除冰，以及应急时配合机组及时撤离地面人员和车辆设备。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3094, 115, '5.6.7.2.3', '2）除冰车司机：根据现场指挥员的指令，按照规定的除冰行车路线行驶、在慢车除冰机位严禁除冰车超过除冰作业警戒线、除冰车作业期间严密监视车辆运行情况、及时将飞机用液情况通知数据记录员、发生车辆故障或除冰液需补加液量及时报告现场指挥员。', 5, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3095, 115, '5.6.7.2.4', '3）除冰操作员：实施高空作业的除冰人员必须遵守安全规则，并用耳机或对讲机跟除冰车司机及地面机械员经常保持联系。按照除冰作业规范实施除冰作业，负责除冰后检查、及时将每架飞机除冰信息填写在除冰记录单上。\n实施飞机慢车除冰工作时，需要遵守当地机场的除防冰要求， 确保安全操作。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3096, 115, '5.6.7.2.5', '4）除冰记录员：由经理或维修中队担任，负责及时记录每架飞机飞机号、滑入滑出时间、除冰开始时间、结束时间、使用除冰液型号、使用量、并将以上信息及时报除冰指挥小组。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3097, 115, '5.6.7.3', '非Ameco代理航空公司：在除冰开始前维修中队机械员和第三方客户航空公司代表到除冰现场，在得到客户代表同意除冰后，现场指挥员下达除冰指令，并及时填写维护费用单。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3098, 115, '5.7', '除冰/防冰的要求', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3099, 115, '5.7.1', '除冰人员按照批准的程序实施除防冰工作。\n除冰操作时应参考飞机维护手册中关于除冰/防冰的特定内容。\n不要将除冰/防冰液直接喷向或喷入下述部件/区域：风挡、侧面窗户、客舱窗户、空速管/静压管、静压口、全温（TAT）探头、发动机进气口、排气口、反推、引气管、吊架上安装的探头、天线、通风口、排水口、引气和排气口、小翼、APU引气门、冷却空气入口、排气口、冷却空气出口、机轮/刹车、冲压空气入口。\n对飞机操作面进行除冰时，不要把冰、雪驱入操作面前缘及其邻近结构之间。\n不要对着应急门手柄部位直接喷洒除冰液。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3100, 115, '5.7.2', '一般情况除冰/防冰时应关闭发动机，特殊情况下：\n慢车运转时的除冰/防冰，按照经公司批准认可的慢车除冰/防冰流程和预案执行。\n在发动机工作情况下除冰/防冰时，确保关闭所有的引气源，并使进入发动机的液体最少。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3101, 115, '5.7.3', '在APU故障而且地面设备又不能保证启动发动机时，可按机身顶部划分向发动机不工作的一侧喷洒液体，喷洒完成后交替运转发动机，并进行机身另一侧的喷洒。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3102, 115, '5.7.4', '机务人员在完成飞机例行和/或排故工作后，必须对发动机进气道积雪、冰实施严格物理清扫，比如用手或不损伤发动机的工具。启动APU前必须确保APU进气道区域无积雪进入的危险。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3103, 115, '5.7.5', '给飞机除冰时，除冰员应采用先喷洒浸泡再局部除冰的方式进行，车斗与飞机的距离应控制在既安全又能得到很好除冰效果的范围内。在APU区域时，务必注意，不要让除冰液进入APU进气道，以免发生APU停车。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3104, 115, '5.8', '飞机一些部位的除冰注意事项和检查标准', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3105, 115, '5.8.1', '发动机和APU\n应在启动APU/发动机前用机械方法将APU/发动机进气口的积雪除去（如使用扫帚或刷子）。用热气或采用发动机生产厂家推荐的其他方法，将可能黏附在进气口下表面或叶片上的任何冰冻凝结物除去。\n不要将除冰/防冰液直接喷入发动机或APU进气排气区域。\n关闭空调和引气，当完成除冰后等除冰液的蒸汽散净后再打开。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3106, 115, '5.8.2', '起落架和起落架舱门', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3107, 115, '5.8.2.1', '建议采取机械的除冰，注入热气、吹雪的方法进行。\n可以使用经稀释的除冰液（不允许使用水）为飞机起落架除冰，不许采取高压喷洒，应将剂量控制在最少程度。\n清除起落架周围地面上的冰和雪。如果轮胎被冻结到地面上则不要移动飞机，为此可以用热空气或除冰液将轮胎从地面解冻或清除冻结物，但是禁止使用盐，因为他可能造成腐蚀。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3108, 115, '5.8.2.2', '确保起落架系统以下部位没有聚留的冰和/或雪：\n1） 起落架部件和位置指示电门上。\n2） 前轮转弯钢索上。\n3）备用释放系统。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3109, 115, '5.8.3', '刹车', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3110, 115, '5.8.3.1', '当除冰或防冰时，不要将除冰或防冰液直接喷到机轮或刹车毂上，必要时用合适的罩子盖好机轮和刹车毂或使用停留刹车以减小刹车毂摩擦面的意外污染。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3111, 115, '5.8.3.2', '若碳刹车毂一旦被除冰液污染，应擦去除冰液并用厂家提供的部件维护手册去除污染。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3112, 115, '5.8.3.3', '可用热空气鼓风机从机轮、刹车毂或轮胎上人工清除雪或冰的积聚物。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3113, 115, '5.8.4', '紧急出口', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3114, 115, '5.8.5', '操作面', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3115, 115, '5.8.5.1', '在结冰条件下或下雪时要收回所有操作面。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3116, 115, '5.8.5.2', '如有必要操作这些操作面，收回前要确保没被冰、雪阻塞。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3117, 115, '5.8.5.3', '在除冰和/或雪时确保冰和/或雪不被推入到飞行操作面周围的区域。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3118, 115, '5.8.5.4', '确保所有的操作面无冰、雪、霜，除冰后要确保操作面铰链，导轨和作动筒没有聚留的水分。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3119, 115, '5.8.5.5', '除冰后确保所有操作面可以全行程运动。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3120, 115, '5.8.6', '前缘襟翼或前缘缝翼下部的区域', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3121, 115, '5.8.6.1', '如果不使用除冰液体加以保护，在起飞前就可能成为受污染的临界表面。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3122, 115, '5.8.6.2', '因此需要制定附加程序以确保航空器在起飞时是在不受污染的条件下起飞的。有两种选择可以采取：\n1）延迟缝翼/襟翼的放出到起飞前，或者，\n2）在进行除冰/防冰处理以前放出缝翼/襟翼，使其下部都受到处理。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3123, 115, '5.8.7', '机翼和水平尾翼表面', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3124, 115, '5.8.7.1', '机翼和水平尾翼表面一定不能有冰、雪、霜。如果由于非常冷的燃油机翼表面（在梁区域）有一层不大于3.2毫米（1/8英寸）厚的霜是允许的。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3125, 115, '5.8.7.2', '前缘表面一定不能有冰、雪、霜，检查活动表面和不活动表面间的区域确保没有冰。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3126, 115, '5.8.7.3', '在左侧和右侧的机翼和水平安定面上必须执行相同的和全面的除冰/防冰程序。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3127, 115, '5.8.8', '机身和垂直尾翼', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3128, 115, '5.8.8.1', '机身和垂直尾翼表面必须没有冰、雪，这样可减小气动阻力和飞机重量。当从装有灯和天线的机身区域清除冰、雪时要小心，否则会损坏设备。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3129, 115, '5.8.8.2', '机身上表面允许有薄的白霜，要求可看见霜覆盖的表面上的东西，如喷涂的线、标志或字母。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3130, 115, '5.8.8.3', '除去雷达罩上的积雪，否则在起飞时雪会向后吹而影响到驾驶员的视线。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3131, 115, '5.8.9', '窗户', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3132, 115, '5.8.9.1', '在喷洒窗户和风挡时，直接对着玻璃上方喷洒直接到有足够的液体留下来以防止温度急增。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3133, 115, '5.8.9.2', '在清洁风挡时，确保机鼻处的所有雪和冰都被清除，以防止飞机起飞时雪吹到风挡上。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3134, 115, '5.8.9.3', '为了旅客服务的完整性，应努力提供一个从客舱内向外的清晰的（无冰的）视野。客舱窗户是飞机安全运行的重要方面：\n1）飞行机组可以从客舱内部有利的角度观察机翼以确定起飞     前机翼没有结冰污染物，保证飞机可以安全起飞。\n2）万一地面发生紧急情况乘客需要紧急撤离时，客舱乘务员 应向窗外瞭望，以便对情况进行判断，并确定从机身的哪一边撤离比较安全。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3135, 115, '5.8.10', '各种暴露的传感器、探头和排泄口', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3136, 115, '5.8.10.1', '检查传感器、探头和排泄口周围的表面有无结冰，清除该区域内的冰。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3137, 115, '5.8.10.2', '不要将除冰/防冰液直接喷到或喷入其上面。如其被冰堵塞了，要小心地用热空气加温直到冰融化。', NULL, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3138, 115, '5.9', '国航飞机执行除冰工作时TLB的签署\n 对于国航飞机执行除冰工作，机械员应在放行前在TLB上签署除冰的信息。信息内容包括：除冰液型号、开始和结束时间。\n注：开始和结束时间留空，在除冰结束后通知机组，由机组来填写除冰的开始和结束时间。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3139, 115, '5.10', '除冰/防冰汇总及收费记录\n当除冰结束后，除/防冰人员依照本程序应与机组联系并签署《除冰/防冰工作汇总单》 Form No.: LM-024。对于三方客户飞机在津完成除防冰工作后，应填收费单并要求客户代表签字，然后送回MCC。', 9, '2018-06-28 10:52:49', '2018-06-28 10:52:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3140, 116, '5.1', '工作卡的编写', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3141, 116, '5.1.1', '根据MS或CAMP规定的维修项目和各种合法维修文件（如：维护手册、线路图册、零附件手册等）编写工作卡。如果出现差异时，以维修文件的英文为准。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3142, 116, '5.1.2', '经授权的工卡编写人员应把维修计划（MS），维护手册（AMM），和持续适航方案（CAMP）等任务、结合人员安全及经济效益并保证是正确和有序的工作原则下变成精确的工作卡。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3143, 116, '5.1.3', '根据合同部门的合同和用户要求的工作范围，及用户提供的有效维修文件编写工作卡或检查用户提供的工作卡。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3144, 116, '5.1.4', '工作卡应覆盖参考文件列出包括重要的施工步骤、数据及附图等。即所有必须满足项目的要求都已清楚无误地描述明白。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3145, 116, '5.1.5', '工作卡要用双语编写和打印，即英文和中文。但因特殊情况，受时间限制或者用户有特殊要求可例外，但是遵守CAAC要求的必须有中文。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3146, 116, '5.1.6', '工作卡编写完后，要由另一名授权批准的经授权的工卡编写人员进行检查，保证其正确性和翻译质量。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3147, 116, '5.1.7', '工作卡的工时必须在每次维护工作中以电子数据方式在内部网上公布，工作卡及目录中不体现工时。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3148, 116, '5.1.8', '为使所有工作文件便于管理和查找，应建立一有规律的管理系统，即工作卡章节系统和工作卡编号系统。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3149, 116, '5.1.9', '当停场使用客户提供的工卡时，经授权的工卡编写人员应核实工卡适用性（飞机号以及工卡与工作包内容的一致性）并根据5.3的要求为客户工卡准备封面页。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3150, 116, '5.1.10', '当停场使用公司编写的工卡时，一旦收到客户提供的参考文件的改版通知后，应立即与客户代表沟通，并要求客户对工卡中的参考文件版次的处理问题给予明确的书面答复。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3151, 116, '5.1.11', '当停场期间由于工卡改版，需用新版工卡替换旧版工卡时，原则上已签署的旧工卡应保留在文件包中。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3152, 116, '5.1.11.1', '未开工的工卡，此时工卡可认为是工作文件，用新版的工卡直接替换旧工卡。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3153, 116, '5.1.11.2', '工卡已开工而未完工，此时工卡应属于维修记录，需要经授权的工卡编写人员就已签署的部分对新旧两版工卡进行检查：\r\n1）如新旧两版工卡已签署的部分没有差异，则在旧工卡上对未签署的步骤填写“N/A”及原因（例如，步骤4-7见J/C XX-XX-XX Issued DATE 2012-08-08）；在新工卡上已完工的步骤（指工作者已在旧工卡签署的步骤）填写“N/A”及原因（例如，签署见J/C XX-XX-XX Issued DATE 2012-08-05）。\r\n2）如新旧两版工卡已签署的部分有差异，则此项工作应根据新版工卡', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3154, 116, '5.1.11.3', '对于EO工卡，因EO改版而导致工卡改版时：\r\n1）对受影响的工卡可进行手改或按照5.1.11.1，5.1.11.2操作修改。\r\n2）其它工卡只是EO参考版次不同，工卡具体内容不受影响的：填写《技术资料改版评估记录表》Form No. :BM-003，后附涉及的工卡清单，声明这些工卡不需要改版。', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3155, 116, '5.2', '工卡封面页的编写', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3156, 116, '5.2.1', '使用客户工卡进行维修工作时，因为客户工卡要求的资格签署与天津分公司的质量体系不一致，经授权的工卡编写人员对客户的工卡附加Ameco工卡作为工卡封面页，Ameco的工卡封面页根据具体的客户工卡，显示以下信息，但不限于此：\r\n1）工作指令号；\r\n2）按照相关程序01-11-005《维修人员专业划分方案》或者相关客户程序，标注所需的资格和工种；\r\n3）主成本中心，阶段（如需）；\r\n4）所需工具和器材信息（如需）；\r\n5）当客户工卡仅为英文时，为了帮助技术员正确理解客户的工作，对客户工卡的中文翻译（按需）。当中英文不符时，应以客户工', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3157, 116, '5.2.2', '对于天津分公司封面页类的工卡的准备和检查，仅对Ameco封面页工卡上的信息和翻译工作进行检查。', 4, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3158, 116, '5.2.3', '工卡封面页准备者和检查者栏应由经授权的工卡编写人员签署。\r\n技术员无需在客户工卡的Ameco工卡封面页和翻译页上签署。', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3159, 116, '5.2.4', '当Ameco工卡作为工卡封面页使用时，Ameco工卡中非例行工作单交叉索引栏的填写要求如下：\r\n如客户工卡中有非例行工作信息记录栏，请将非例行工作信息记录到客户工卡中，Ameco工卡封面页中的非例行工作单交叉索引栏无需填写；\r\n如客户工卡中没有非例行工作信息记录栏，请将非例行工作信息写入Ameco工卡封面页中的非例行工作单交叉索引栏。', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3160, 116, '5.2.5', 'Ameco工卡封面页应随工作包一同存档。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3161, 116, '5.3', '工作卡的处理\r\n工卡的检查。\r\n所有的维修文件都要复查其正确性。技术支援分部经理可以授权相关经授权的工卡编写人员负责完成工作卡的复审并检查翻译的质量，对翻译正确的工卡签署批准。复查工作包括但不限于以下内容：\r\n1）检查工卡表头信息填写正确；\r\n2）检查工卡已进行了明显的工序分工且已填入工序号；\r\n3）检查人员资格要求；\r\n4）检查“计划信息”栏已填入对应信息；\r\n5）对照英文和中文，检查翻译正确；\r\n6）如果工卡需要记录，检查相应的空间已预留出，如：改装否，尺寸/数据记录。', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3162, 116, '5.4', '工作卡/工作指令的颁发', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3163, 116, '5.4.1', '每次都应对所需要完成的工卡和工作指令编写目录，建立索引。每个负责的计划员把编写好的工作卡连同工作卡目录一并送交项目计划员以建立此次停场中所需要的一整套工作卡文件。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3164, 116, '5.4.2', '项目计划员将整套工卡和目录下发至相关生产计划控制分部工卡控制员。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3165, 116, '5.4.3', '工卡控制员负责将工卡发放生产单位，并做记录，以确保整套工卡的完整性。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3166, 116, '5.4.4', '工卡控制员应确认所有过期的文档从颁发或使用地点移除并销毁。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3167, 116, '5.5', '工作卡/工作指令的完成及回收', NULL, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3168, 116, '5.5.1', '工作者严格按照工卡要求执行相关维修工作，并签署，如果一个班次只完成工卡一个步骤的一部分或多个人完成的同一步骤，可使用《工卡签署续页》Form No.: BM-004记录维修过程。', 3, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3169, 116, '5.5.2', '任何维修文件（质量记录）本身都要求所有的空格都应填写完整。那些与此特定工作无关的空格应以“不适用”或“——”填写。', 3, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3170, 116, '5.5.3', '如需使用工卡复印件，复印人员应在工卡复印件上进行有效性控制标识。当工作者根据工卡复印件执行工作时，开工前以及完工后应先核对工卡版次并确保复印件与原版版次一致，工作完成后在原版工卡上进行签署并销毁复印件。', 3, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3171, 116, '5.5.4', '定检中队必须将完成后的工卡最迟在飞机放行前交还给生产计划控制分部。', 3, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3172, 116, '5.5.5', '允许被Ameco授权的检验工程师对工卡中标识RII的项目进行评估，并有权对非RII项目进行现场代码更正。', 8, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3173, 116, '5.5.6', '在飞机交付客户以后，应检查整套工作文件包的完整性，并传至档案室归档。', 6, '2018-06-28 11:05:38', '2018-06-28 11:05:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3174, 117, '5.1', '基本要求', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3175, 117, '5.1.1', '所有维修单位必须依法提供适当的人员，这些人员能够实施、监督或检查与该单位有关的工作。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3176, 117, '5.1.2', '合格并持有执照人员的数量可以随工作类型、工作量的不同而有所变化，但不能少于有效完成适航工作所必需的最小数量。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3177, 117, '5.1.3', '只有合格、并且具有相应质量系统授权的人员，才能独立对飞机、飞机发动机、部件或零件实施维护/重新整修工作。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3178, 117, '5.2', '人力计划。\r\n人力计划的制定是测算相关维修部门实际能提供的生产工时的过程。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3179, 117, '5.2.1', '因培训、假期和出差而引起的人员缺勤会导致定检中队提供的人力不可能达到100%的可用率，因此，可用生产工时的计算应依据工时的利用率，并除去培训和缺勤的人员。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3180, 117, '5.2.2', '根据人事部门的统计报告测算生产人员的可用率。', 1, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3181, 117, '5.2.3', '可用工时=实际生产人员数量*可用率* 8 *工时利用率。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3182, 117, '5.2.4', '根据飞机停场计划制定相对应的人力计划。飞机停场计划包括确定的停场和其他有意向的停场。\r\n根据飞机停场计划确定每架飞机的停场时间。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3183, 117, '5.2.5', '制定计划时应核实定检中队、NDT人员是否拥有足够的持有适当资格和授权的人员来计划、执行、监督和检验维修工作，同时核实机库使用计划以及车间附件维修计划。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3184, 117, '5.2.6', '每月应对人力计划进行更新，并根据情况及时进行修正。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3185, 117, '5.2.7', '根据定检中队的生产能力状况，每周在生产计划控制分部和定检中队之间召开生产能力的会议，讨论内容包括：\r\n1）最新的飞机停场计划；\r\n2）飞机机库使用计划；\r\n3）定检中队上机工时计划；\r\n4）对非预见性维修工作（如AOG，因天气因素引起的飞机延迟）所需的人力进行预计和测算。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3186, 117, '5.2.8', '如果可用的实际人员低于计划所需的生产人员，但没有重大偏离，应采取如下必要措施：\r\n1）提高效率； \r\n2）安排加班等；\r\n3）如内部不能解决，则向外部寻求人力支援；\r\n4）重新评估维修工作，必要时对工时计划进行调整；如果上述措施不能解决问题，目前的生产能力依然无法满足要求的人力，对维修工作的停场时间作出相应的调整。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3187, 117, '5.3', '人力支援。\r\n定检中队应独自完成各自的生产任务。人力短缺时，如其他中队有剩余可用人力，可相互调配，但必须通过相关部门间的协调和书面确认。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3188, 117, '5.3.1', '所有支援人员应根据Ameco的资格体系持有适当的资格和授权。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3189, 117, '5.3.2', '如果计划工作量高于实际现有的生产能力，定检中队应提交书面的人力支援申请。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3190, 117, '5.3.3', '支援部门应检查自己的人力计划状况，确认是否可以提供要求的人力支援。涉及的部门应被告知相关情况。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3191, 117, '5.3.4', '支援人员应服从调配，完成被支援部门经理、中队长、分队长安排的工作。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3192, 117, '5.3.5', '如果分公司内部人力支援不能满足要求，生产计划控制分部应以正式文件的形式向人力资源分部提交人力支援申请。', 3, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3193, 117, '5.3.6', '人力资源分部负责从外部借入适当的人员。', 1, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3194, 117, '5.3.7', '如果生产计划控制分部要求的支援不能得到满足，则应对计划的维修工作进行调整。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3195, 117, '5.4', '飞机特殊停场和大修计划的制定。\r\n按照程序01-11-010《维修计划管理程序》执行。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3196, 117, '5.5', '项目管理。\r\n在飞机停场前，应召开全体项目组成员会议，对前期准备状况进行检查。', NULL, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3197, 117, '5.5.1', '飞机停场前应下发停场信息，项目组成员名单，工作流程，人力计划，合同摘要（按需），工卡等维护文件。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3198, 117, '5.5.2', '按需召开生产会，检查和协调工作进度。信息通过“生产报告”  通报所有相关部门。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3199, 117, '5.5.3', '全体项目组成员负责所在部门生产信息传达和工作目标的完成，并负责解决所在部门的问题。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3200, 117, '5.5.4', '召开客户会和客户定期沟通飞机生产信息。使用“客户报告”记录所有重要工作信息。', 6, '2018-06-28 13:15:22', '2018-06-28 13:15:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3201, 118, '5.1', '工作时间限制', NULL, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3202, 118, '5.1.1', '定检中队都应当按照以下限制合理安排维修人员的工作时间。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3203, 118, '5.1.2', '一般情况下，直接从事航空器或者航空器部件维修工作的维修人员工作时间每天不应超过8小时，平均每周的工作时间累计最多不应当超过40小时。不论以何种方式加班，维修人员的每月累计加班时间不得超过36小时。对于获得劳动部门批准实行综合计算工时工作制的维修单位，经民航局或民航地区管理局同意，维修人员每月平均累计加班时间不得超过36小时。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3204, 118, '5.1.3', '定检中队直接从事航空器或航空器零部件维修工作人员的工作时间可以不受每天8小时的限制，但每天累计工作时间不应超过11小时。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3205, 118, '5.1.4', '如果维修人员正在实施的连续维修工作步骤在执勤期内不能完成，但由其继续实施对维修质量和安全有利，执勤期可以延长至15小时，同时其实际加班时间应累计算入每周和每月的工作时间。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3206, 118, '5.1.5', '执勤期后应当安排维修人员至少12个连续小时的休息期，这个休息期应当安排在该执勤期结束时刻与下一个执勤期开始时刻之间。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3207, 118, '5.1.6', '每七个连续日历日应保证维修人员至少休息一日。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3208, 118, '5.2', '受5.1工作时间限制的人员为直接从事航空器或航空器零部件维修工作的维修人员，包括：\r\n1）因实施维修工作的目的直接接触航空器或航空器零部件的人员；\r\n2）在维修工作实施的文件上直接签收工作和维修放行的人员。', NULL, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3209, 118, '5.2.1', '当维修管理人员、支援人员部分时间履行上述涉及的维修人员职能时，其全部的工作时间和加班时间也适用于5.1的工作时间限制。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3210, 118, '5.2.2', '在执勤期内，当任何维修人员感觉身体状况不适应其所从事的维修或维修相关的工作时，应向维修单位提出终止工作，维修单位不应强迫其继续工作。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3211, 118, '5.3', '在维修工作中进行工时计划控制', NULL, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3212, 118, '5.3.1', '定检中队长应根据每项维修工作的工作流程和工时计划安排生产人员。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3213, 118, '5.3.2', '人员安排必须尽量贴近工时计划。在实施计划维修工作的过程中应对人力进行控制，确保他们可以及时完成工作。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3214, 118, '5.3.3', '定检中队应书面向生产计划控制分部报告任何对实际工时计划的偏离，以做必要的调整。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3215, 118, '5.3.4', '在生产过程中，任何因不可预见的原因而导致的对计划的严重偏离都应由定检中队立即向生产计划控制分部报告。工作流程应得到相应的改版/更正/修改，工时计划也应相应的调整。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3216, 118, '5.3.5', '定检中队长有责任对人力进行控制，以圆满完成维修任务。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3217, 118, '5.3.6', '每项维修工作完成后，应对计划工时和实际消耗工时进行比较。当实际消耗工时与计划工时偏差较大时（大于10%），定检中队应分析偏离计划工时的原因，并将评估结果报告且发布给相关的生产计划控制分部计划员，以便对将来的维修任务工时计划进行评估。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3218, 118, '5.3.7', '维修工时计划应根据预测的维修工作量进行制定。对于没有详细工作文件包的短期合同，维修工时计划的制定应依据保持商业竞争力所需的最少维修工作量。', 6, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3219, 118, '5.3.8', '一个日历月内，缺少所需人力的25%以上被视为对经批准的工时计划的严重偏离，应通过定检中队长报告给质量经理、相关的高级管理人员和责任经理。此种人力的短缺应经分公司高层评估并做出相应的决定。', 3, '2018-06-28 13:14:21', '2018-06-28 13:14:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3220, 119, '5.1', '工作单/包准备：', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3221, 119, '5.1.1', '定检工作单的接收：\r\n1）接收MEP改版信息。\r\n2）接收EO控制人员EO/CAD执行情况反馈。\r\n3）接收运管部工卡改版信息。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3222, 119, '5.1.2', '定检项目基础数据控制：\r\n1）根据MEP改版信息，修正机队的定检控制项目清单，按需制定过渡方案。\r\n2）根据EO控制人员EO/CAD执行情况的反馈，打开/关闭机队的定检控制项目清单。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3223, 119, '5.1.3', '综合管理分部计算机管理人员负责根据运行管理部工卡改版信息更新基地服务器数据。', 7, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3224, 119, '5.1.4', '技术支援分部提供定检工作中需要器材、工具设备、化工品清单，客户化工作单由技术支援分部工卡工程师负责编写，并对其有效性、正确性和完整性负责。', 4, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3225, 119, '5.1.5', '技术支援分部应定时更新器材、工具设备、化工品清单、EO自备器材清单，保持上述清单件号与航材库存件号的一致性。\r\n当指令编号以及对应的器材、工具设备、化工品清单，EO自备器材清单改版时，技术支援分部需邮件通知生产计划控制分部、航材与采购部天津分部、生产支援分部，并同时上传至分公司论坛。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3226, 119, '5.1.6', '生产计划控制分部按照技术支援分部提供的清单对定检工作中需要的例行器材、附加器材进行SAP中的预留工作。A检提前5个工作日将预留清单发布到分公司论坛，C检提前15个工作日将预留清单发布到分公司论坛。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3227, 119, '5.1.7', '生产支援分部对定检中的常规化工品按化工品清单进行储备，对定检中EO涉及化工品进行SAP中的预留工作。', 5, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3228, 119, '5.1.8', '航材与采购部天津分部计划人员在A检前3个工作日，C检前10个工作日负责对预留清单的红黄灯器材进行统一回复。', 2, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3229, 119, '5.1.9', '生产计划控制分部按照航材与采购部天津分部的回复修改SAP系统。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3230, 119, '5.1.10', '航材与采购部天津分部按照更改后的预留在A检前1个工作日，C检前3个工作日将器材配送到定检中队。', 2, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3231, 119, '5.1.11', '生产计划控制分部负责根据维修计划进行定检、特殊停场工作打包操作。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3232, 119, '5.1.12', '生产计划控制分部定检计划工程师负责每月10日前发布次月定检计划，并在SAP中生成且动态滚动维护一个月内相对应的定检停场，打包工程师根据次月定检计划对次月定检工作包进行初级组包。\r\n打包人员每月25日监控PPC计划组发布的次月起两个月工作计划，对第二月定检情况进行核算，按定检、停场附加工作包模板初始建包。考虑定检的执行时间不确定性，打包工作应注意留有正负5天的余量，如波动值超过该值，定检计划工程师应通过改版发布。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3233, 119, '5.1.13', '在A检开工前5个工作日、C检前15个工作日确定最终的附加工作包，并由打包人员在SAP中将附加工作通知单组入相对应的停场且将电子版附加工作包清单在论坛发布。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3234, 119, '5.1.14', '附加工作。\r\n定检打包工程师建立初始附加工作包后，各控制工程师应考虑以下项目视情加入附加工作包：\r\n1）EO项目；\r\n2）DD项目；\r\n3）时控项目；\r\n4）NR项目；\r\n5）MEP单控项目；\r\n6）其他需加入的项目。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3235, 119, '5.1.15', '涉及的AD/CAD/SB/SL需详细列明。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3236, 119, '5.2', '工作单/包的发放及回收', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3237, 119, '5.2.1', '生产计划控制分部准备的定检（A检或以上级别）工作单包包括：《飞机定检工作单包封面》Form No.: BM-013R1、《RII/CAD/安全检查单及例行条目总清单》Form No.: BM-T03、《附加工作汇总单（定检）》Form No.: BM-T04等。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3238, 119, '5.2.2', '如缺少器材或工具，责任计划员提前从工作单包中撤出相应工作单并控制其期限。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3239, 119, '5.2.3', '计划员根据人力、工作内容、特殊要求、停场时间等状况，对所控制的维修工作指令进行计划和安排。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3240, 119, '5.2.4', '生产计划控制分部人员为A检（含）以上级别定检工作单包加入包括：《工作汇总单（定检）》、《定检更换件清单》Form No.: BM-016 （1份仅适用C检，更换有序号附件时填写，定检更换件附件标识挂签首页与相应工作文件订在一起存档）、汇总单涉及工作单。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3241, 119, '5.2.5', '生产计划控制分部计划员编写完A检（含）以上的定检工作单包后，应由高一级的人员审核签署。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3242, 119, '5.2.6', '如维修计划发生变化（如工作项目增减），计划员需对工作单包内容进行相应修改并在工作汇总单中注明。特殊情况下，计划员可以先委托定检中队进行修改，随后核对修改内容。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3243, 119, '5.2.7', '定检开工前一天22:00之前，定检中队从生产计划控制分部领取准备好的工作单包，做好交接。', 3, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3244, 119, '5.2.8', '定检中队负责飞机工作文件的管理工作，在工作单/包返回生产计划控制分部之前，须对完成工作单进行检查，保证签署的正确性和完整性。由定检中队的指定人员在飞机放行前关闭相对应的SAP工作包及工作包各项工作的通知单。航后工作单包及相关工作文件须在飞机出港前2小时交回计划部门，A检（含）以上定检工作单包及相关工作文件须在飞机出港前3小时交回计划部门。工作单包涉及的所有工作记录齐全之后才能放行飞机。', 3, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3245, 119, '5.2.9', '定检中队将审核后的维修记录交生产计划控制分部后，生产计划控制分部协助定检中队再次审核维修记录，检查包括工卡份数是否齐全、单份工卡页数是否齐全等。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3246, 119, '5.2.10', '维修记录反馈。\r\n生产计划控制分部在核对接收的维修记录后，按照工作要求将维修记录转交或反馈至航空安全质量分部、技术支援分部或航材与采购部天津分部。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3247, 119, '5.3', '定检工作包拆分的处理', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3248, 119, '5.3.1', '由于维修资源或营运人需求等原因，存在少数定检工作包拆分的情况。但是在这种情况下，须确保定检项目不超出期限。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3249, 119, '5.3.2', '在定检工作包拆分的过程中，生产计划控制分部计划员负责定义、准备和拆分工卡包。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3250, 119, '5.3.3', '一般地，定检工作包拆分有三种情况：\r\n1）定检适航项目在期限前执行，其余项目延期后整体执行；\r\n2）定检项目拆分成若干部分，分批执行；\r\n3）定检执行过程中因为器材工具等原因余留小部分工卡（不含适航项目）。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3251, 119, '5.3.4', '对于第1）和2）种情况下的定检拆分\r\n执行定检拆分的每一个单元都要配发放行证书，放行证书中的日历时/飞行小时/飞行循环以执行第一拆分单元时为准。工作结束后放行人员在放行证书中进行执行状况描述。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3252, 119, '5.3.5', '对于第3）种情况下的定检拆分\r\n定检工作包封面注明余留情况，按照定检放行。\r\n定检中队填写《工作单/工作指令反馈单》Form No.: BM-012，和余留工卡一同反馈至MCC值班经理，MCC值班经理签署意见后反馈至生产计划控制分部。计划员协调解决造成工卡余留的器材、工具、停场时间及人力问题并控制。条件具备，再次下发余留工卡时附上《定检余留工卡安排通知单》Form No.: BM-T02。', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3253, 119, '5.3.6', '将余留工卡合并到定检工作单包中一起反馈航空安全质量分部。', 6, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3254, 119, '5.4', '定检中的器材需求：', NULL, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3255, 119, '5.4.1', '对于定检中队在定检中因排故所需的器材，由工作者填写非例单标明所需航材及数量，由定检中队准备人员进行预留并通知航材与采购部天津分部发料，由定检中队领取航材。', 3, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3256, 119, '5.4.2', '如在较长时间定检工作中，航材暂时无法到料，定检中队应将所需航材发布在内网缺件信息控制区。', 3, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3257, 119, '5.4.3', '航材与采购部天津分部，按定检中队内网发布的航材缺件信息完成器材调拨及订货，同时标注到货日期。', 2, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3258, 119, '5.4.4', '到货后，定检中队按预留领取器材。', 3, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3259, 119, '5.4.5', '如飞机出场前航材仍未到货，对无法关闭工作，定检中队应按需填写DD单，并在论坛缺件信息控制区关闭此项缺件单，改由以DD单的方式进行控制。', 3, '2018-06-28 13:13:55', '2018-06-28 13:13:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3260, 120, '5.1', '业务联系：生产计划控制分部在总部协议管理部门组织下的三方业务联系、协议办理等工作。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3261, 120, '5.2', '能力评估：生产计划控制分部依据分公司维修能力和实际生产运行情况对是否接收三方业务进行评估，必要时可申请组成评估小组。生产计划控制分部可协调技术支援分部和执行具体工作的定检中队、航线中队、航空安全质量分部NDT对人力、工时、工装、工作接近等方面进行综合评估，必要时由分公司生产经理牵头、包含生产计划控制分部经理、技术支援分部经理、航线中队中队长、定检中队中队长、航线高工、定检高工以及生产支援分部、航材与采购部天津分部经理组成评估小组，将记录的评估问题和客户沟通，形成评估表，生产计划控制分部负责存档。纸板存档6', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3262, 120, '5.3', '回复客户', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3263, 120, '5.3.1', '如分公司不能接收此项工作，生产计划控制分部负责将信息回复客户。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3264, 120, '5.3.2', '如果接收此项业务，生产计划控制分部负责组织与客户进行业务洽谈并启动、申报签署相关协议，以及后续的相关文件准备。协议签署后生产计划控制分部将副本分发分公司各执行部门、航材与采购部天津分部和财务分部。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3265, 120, '5.3.3', '对方提供工卡：\r\n生产计划控制分部要求客户按分公司要求，提供相应工作清单及工卡。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3266, 120, '5.3.4', '编制工卡：如客户不能提供施工工卡，生产计划控制分部要求客户提供工作清单，并发技术支援分部。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3267, 120, '5.3.4.1', '技术支援分部负责编制工卡。', 4, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3268, 120, '5.3.4.2', '编写完成的工卡，生产计划控制分部要求客户进行评估，评估单与工卡合订在一起进行存档。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3269, 120, '5.4', '授权：\r\n如客户可以提供工卡，由航空安全质量分部完成授权事宜。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3270, 120, '5.5', '制定计划：\r\n合同/协议签署后，生产计划控制分部把三方工作纳入到分公司维修计划中，各部门按计划准备。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3271, 120, '5.6', '三方公司文件管理', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3272, 120, '5.6.1', '生产计划控制分部设立专用邮箱：tjcont@ameco.com.cn，接收文件后转发至相应部门负责人：航空安全质量分部经理，技术支援分部经理，人力资源分部经理；涉及生产计划控制分部的文件则直接内部处理。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3273, 120, '5.6.2', '三方客户提供的技术资料的管理：\r\n1）技术支援分部资料工程师核对清单与实物是否准确，在清单相应位置签字，反馈给第三方客户。\r\n2）技术支援分部资料工程师技术资料在2个工作日内将资料上传到分公司内网-维修资料-相应的文件夹中。对于收到的客户颁发的维修提示,按照客户的要求，传达给维修中队,同时上传分公司内网，便于维修人员查阅。\r\n3）技术支援分部资料工程师建立三方客户技术资料接收清单，并及时更新，以确保时效性。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3274, 120, '5.6.3', '相应部门接收文件后进行解读评估，内部处理存档，或转发协同部门处理。', 10, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3275, 120, '5.6.4', '如需技术培训或安全培训，由人力资源分部负责组织。', 1, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3276, 120, '5.7', '签长期合同的三方客户，维修协议应规定：\r\n三方客户应提供有客户签字的适航性资料清单，清单中至少应列出适航性资料的名称，文件号和出版厂家，出版日期和适航性资料版次。\r\n1）客户提供相关的适航性资料和有客户签字的有效适航性资料清单，清单中至少应列出适航性资料的名称，文件号和出版厂家，出版日期和适航性资料版次。\r\n2）如果客户不能提供适航性资料，并且同意使用Ameco 正在使用的现行有效的非客户化适航性资料，必须在相关合同中写明。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3277, 120, '5.8', '各部门建立三方文件接收处理清单，登记接收日期、接收人以及处理记录。', 10, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3278, 120, '5.9', '生产计划控制分部接收人每周在论坛板块：“三方资料和相关信息-生产计划”中更新三方文件接收清单，内容包括转发接口部门以及日期，接口各部门关注并核对，同时相关业务的办理由对口部门进行陪同协调。三方客户要求的接收回执由生产计划接收人统一进行回复。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3279, 120, '5.10', '飞机交接', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3280, 120, '5.10.1', '1）进厂检验中发现的问题应记入非例行工作单，并在非例行工作单上注明“进厂检验”或“初始检查”加以标识。某些涉及到计划、工卡或其它文件的问题应填入非例行工作单，报生产计划控制分部。', 9, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3281, 120, '5.10.2', '2）生产计划控制分部应按相关工作程序的要求准备工卡或其它文件。报告的发现问题，即缺陷和不适航状况在事件确认后24小时内，由生产计划控制通知客户，必要时增补或修改合同。客户反馈的意见和要求应以书面形式交给相关工作人员完成后续处理。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3282, 120, '5.11', '执行并反馈', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3283, 120, '5.11.1', '1）维修中队按计划完成工卡规定的工作，及时将全部工作记录和相关收费单据汇总反馈生产计划控制。', NULL, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3284, 120, '5.11.2', '2）涉及的工具设备、机库类支援，设备设施负责相关收费单据每周汇总递交。', 5, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3285, 120, '5.12', '生产计划控制分部负责信息和工作记录的传递。', 6, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3286, 120, '5.13', '航空安全质量分部负责维修记录的整理和出场报告编写。', 8, '2018-06-28 14:34:03', '2018-06-28 14:34:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3287, 121, '5.1', '采购计划', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3288, 121, '5.1.1', '固定资产类', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3289, 121, '5.1.1.1', '各部门按照实际生产需求填写《Ameco天津采购申请单》Form No.: FA-T01经部门经理签字确认后交生产支援分部。', 10, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3290, 121, '5.1.1.2', '针对以下几方面对申请需求进行评估：\r\n1）使用用途及功能；\r\n2）紧急程度；\r\n3）使用频次；\r\n4）是否可以租借；\r\n5）成本控制。', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3291, 121, '5.1.1.3', '制定采购计划\n根据所规定的授权范围在每年的10月底前,将下一年本单位内工具设备预算申请向航材与采购部申报。', 5, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3292, 121, '5.1.2', '低值易耗品、非通用耗材', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3293, 121, '5.1.2.1', '由需求部门填写《Ameco天津采购申请单》经部门经理签字确认后报生产支援分部。', 10, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3294, 121, '5.1.2.2', '工程师完成评估后填写《Ameco天津采购审批单》Form No.: FA-T02报部门经理审批。', 5, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3295, 121, '5.1.3', '通用耗材：\r\n定检、航线维修类通用耗材，生产支援分部计划工程师根据日常工作需求及库存量制定采购计划并填写《Ameco天津采购申请单》经部门经理确同意后填写《Ameco天津采购审批单》。', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3296, 121, '5.2', '审批权限', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3297, 121, '5.2.1', '单次采购 5000.00元以下（含）由生产支援分部经理审批。', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3298, 121, '5.2.2', '单次采购5000.00元以上或单一供应商年度采购30000.00元以下由采购评审小组审批。采购评估小组组成参考内控管理程序。', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3299, 121, '5.2.3', '单一供应商年度采购30000.00元以上按照固定资产采购执行。', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3300, 121, '5.3', '采购实施', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3301, 121, '5.3.1', '采购实施前应对供应商进行资格审核。\r\n1）依法取得企业法人资格，具有承担民事责任的能力；\r\n2）具有国家或行业主管部门颁发的相关资质证书。', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3302, 121, '5.3.2', '负责市场调研，核实供应商资质、拟订合同内容，根据审批通过的《Ameco天津采购审批单》上所列清单询价，物料采购优先选择已签署框架协议的合格供应商，按协议价格执行。', 5, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3303, 121, '5.3.3', '依据内控管理程序，完成采购。如本程序与上级部门合同管理程序和内控管理程序存在冲突，以上级部门程序为标准执行采购。', 5, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3304, 121, '5.4', '验收', NULL, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3305, 121, '5.4.1', '新增设备，要与厂家和使用部门共同验收，要核对出厂信息，需校验设备确认是否校验合格，检查外观有无破损，是否与送货清单相符，需培训设备联系厂家进行培训，培训报人力资源分部培训部门后由其组织实施完成，使用部门和技术部门必须参加，完成培训后才可上架使用，并更新工具设备清册，入帐控制。', 5, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3306, 121, '5.4.2', '填写《工具设备验收单》，包括设备信息、验收地点、设备情况等，并签字确认验收合格。', 5, '2018-06-28 14:48:02', '2018-06-28 14:48:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3307, 122, '5.1', '工具设备管理一般原则', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3308, 122, '5.1.1', '建立《工具设备清册》Form No.: FA-T05并实施动态管理，确保账实相符，更新及时，定期对清册进行清查。工具设备清册至少包括名称、件号、规格/数量/序号、位置、状态、管理要求等。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3309, 122, '5.1.2', '当有新购置、毁损、丢失、封存、送修、报废、存放位置调整等情况时，及时更新《工具设备清册》。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3310, 122, '5.1.3', '应将工具设备存放在指定的工具间或各中队中指定的区域内，并使其免受损坏、污染和潮湿。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3311, 122, '5.1.4', '对于特殊用途或临时使用等未纳入分公司工具设备管理的工具设备，在使用前必须将此类工具设备纳入工具设备管控体系，保证其处于受控状态方可使用。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3312, 122, '5.1.5', '工具设备实物上应有标识，且标识牢固不易消除。工具设备标识应至少包括设备编号。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3313, 122, '5.2', '工具设备清点', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3314, 122, '5.2.1', '依据《工具设备清册》定期下发《工具清点及6S工作单》Form No.: FA-T06。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3315, 122, '5.2.2', '机库值班人员按照下发指令单的要求对工具设备每日进行清点，对设备表面和架位进行清洁维护。要求：按照工具设备清册进行核对保证与清单一致，工具设备无损坏现象，设备表面干净整洁。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3316, 122, '5.2.3', '主管人员对清点工作进行抽查，确保工作完成。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3317, 122, '5.3', '工具设备状态巡检', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3318, 122, '5.3.1', '巡检准备1）从车队要车，按要求填写出车记录。2）到维修组查看工作计划，带好所用的工具和配件。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3319, 122, '5.3.2', '巡视路线：机库->西区过夜坪->西区货坪->东区远机位->东区T1廊桥设备区->东区T2廊桥设备区。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3320, 122, '5.3.3', '巡检内容', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3321, 122, '5.3.3.1', '常用梯架拖把千斤顶的检查，保障日常工作使用的千斤顶拖把梯架正常可用，主要部位检查维护：用力集中点，安全维护部件，剪切销，油液渗漏等，对设施的表面清洁和润滑工作要做到位。\r\n1）工作梯的外观焊接处和轮子；\r\n2）拖把的外观，焊点，轮子和剪切销；\r\n3）尾撑的外观，焊点，轮子和压力表；\r\n4）千斤顶的外观，轮子，液压系统等。', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3322, 122, '5.3.3.2', '氮气瓶的外观及气瓶压力（如低于5MPA就要带回西区更换），储存氮气瓶的保护帽安装在位。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3323, 122, '5.3.3.3', '梯架等设施的系留检查（包括地锚的检查）。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3324, 122, '5.3.3.4', '轮胎运输车外观和轮子的检查。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3325, 122, '5.3.4', '巡检记录\n每周需至少完成2次巡检工作，检查者按要求填写好各项检查记录表，并签字确认。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3326, 122, '5.3.5', '设备维修：\r\n如发现问题检查人员不能完成修理工作需及时反馈设备工程师，由工程师完成评估，寻找供应商完成修理工作。', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3327, 122, '5.4', '重要设备设施保养维护和送修', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3328, 122, '5.4.1', '重要设备的技术管理。根据设备使用说明书要求，将需要重点维护的设备进行逐个控制，每个重要设备建立《工具设备技术档案》Form No.: FA-T04，并按说明书要求定期下发指令单对设备进行维护检查。机库值班人员按指令单对设备进行维护（包括设备的开机自检情况检查，设备的充电，更换电池）。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3329, 122, '5.4.2', '按需完成工具设备改型的监控和评估，根据实际情况实施工具设备改型工作，并保存工具设备改型记录。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3330, 122, '5.4.3', '制定重要设施设备维护保养计划：\r\n1）设备工程师根据使用说明书和使用频率，对日常使用重要设备，进行定期维护保养；\r\n2）对于孔探设备，设备工程师每年送专业厂家进行维护检查和保养并对孔探设备的性能（图像清晰度，分辨率和测量精度）进行评估或检测。如设备性能难以满足孔探工作要求，应出具评估报告，视情进行设备升级或更换。\r\n3）对于需要按国家标准检测的设备（如电源，气泵等）根据检查报告控制检测日期。', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3331, 122, '5.4.4', '设备工程师联系有相应资质的厂家对该设备进行维护保养和修理。要求厂家必须提供能证明自己有维护相关设备的资质等相关资料。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3332, 122, '5.4.5', '询价出具合同：出具合同前，必须要求厂家提供书面报价单，按规范填写合同审批单，部门经理批准后报天津分公司领导审批。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3333, 122, '5.4.6', '陪同厂家专业人员对设备进行维护保养或送修，办理相关手续。', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3334, 122, '5.4.7', '验收：\r\n送修设备完成维修后，要核对维修报告和合格证书，检查外观有无破损，零部件是否齐全与清单相符，确认后才可上架使用，并填写维修记录。', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3335, 122, '5.4.8', '维保记录填写。填写《工具设备验收单》Form No.: FA-T03并修改《工具设备清册》，做好详细的维护保养记录。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3336, 122, '5.5', '工具设备封存和启封。长期（通常指1年以上）不用，但今后仍有可能使用的工具设备，可进行封存并存放在指定区域，并可根据实际情况进行工具设备的启封。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3337, 122, '5.6', '工具设备报废。工具设备存在下列情况之一时，可向北京飞机维修工程有限公司财务部申请报废：\r\n1）损害或磨损严重，且无法修复或经评估超经济修理的工具/设备。\r\n2）飞机、发动机或其附件生产厂家通告所推荐的工具设备已被新的工具设备替代。\r\n3）北京飞机维修工程有限公司范围内不再使用的工具设备。', NULL, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3338, 122, '5.7', '工具设备的库房管理。工具设备应存放在指定的工具间或工作现场指定的区域内，并使其免受损坏、污染和潮湿。', 5, '2018-06-28 14:52:16', '2018-06-28 14:52:16', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3339, 123, '5.1', '工具设备的共享和调配原则', NULL, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3340, 123, '5.1.1', '工具设备的共享。\r\nAmeco工具设备实施分散管理、共享使用。即工具设备由资产所属单位负责管理，各维修单位间可以通过互相调配或借用的方式共享使用，工具设备使用期间，工具设备所属单位负责工具设备的管理和控制，工具设备使用单位负责工具设备的保管、使用等实物的管理。', NULL, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3341, 123, '5.1.2', '工具设备的调配。运行管理部负责制定工具设备调配和借用原则和程序，组织各维修单位之间的调配和借用。工具设备调配以各维修单位间自主调配为主；当自主调配不能满足生产需求时，生产单位向运行管理部提出详细的需求报告，运行管理部将结合维修计划，统一协调解决。', NULL, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3342, 123, '5.2', '工具设备借用', NULL, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3343, 123, '5.2.1', '生产计划控制分部下发生产指令单，提供所需设备件号和使用时间。', 6, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3344, 123, '5.2.2', '生产支援分部计划工程师根据所提供设备件号，联系其他单位进行租借。单位选择本着Ameco内部优先和就近原则。', 5, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3345, 123, '5.2.3', '派专人去外单位租借设备，并与外单位做好交接记录，交接记录应至少包括工具设备的名称、件号、序号、数量、工具设备状态、交接人、交接日期等。租借人员必须对设备进行检查，确认后，带回分公司使用。', 5, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3346, 123, '5.2.4', '租借人员将设备带到机库与值班人员进行交接，确认后由值班人员按照TJ-04-004《工具设备收发和使用程序》进行管理，工作中发放使用。', 5, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3347, 123, '5.3', '工具设备借出', NULL, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3348, 123, '5.3.1', 'MCC负责协助外单位在《维护费用单》Form No.: FA-T09上填写所需设备信息，并通知设备计划工程师。', 6, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3349, 123, '5.3.2', '设施计划工程师根据工具设备清册查询是否有所需设备并反馈给MCC。', 5, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3350, 123, '5.3.3', '需外单位租借人员到MCC签字，当日MCC值班经理确认是否可以租借，并确认签字。', 6, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3351, 123, '5.3.4', '外单位租借人员凭MCC值班经理签字的《维护费用单》，到工具值班室进行租借，设备值班人员按照TJ-04-004《工具设备收发和使用程序》完成工具设备发放，并按规范在《维护费用单》上签字确认。', 5, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3352, 123, '5.3.5', '外单位将租借设备归还并签字确认后，设备值班人员按照TJ-04-004《工具设备收发和使用程序》完成工具设备回收，并将《维护费用单》及时交到MCC。', 5, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3353, 123, '5.3.6', '将《维护费用单》交分公司财务分部。', 6, '2018-06-28 15:01:37', '2018-06-28 15:01:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3354, 124, '5.1', '工具设备收发', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3355, 124, '5.1.1', '核对设备信息。\r\n维修中队人员提出工具设备需求。', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3356, 124, '5.1.2', '值班人员在收到所需设备信息后，进入工具管理系统核对有无此设备，是否处于可用状态。若无所需设备，值班人员立刻通知计划工程师联系租借。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3357, 124, '5.1.3', '工具设备收发', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3358, 124, '5.1.3.1', '工具设备检查。工具设备值班人员，在发出工具前，要对所发出设备进行检查，确认设备完好可用，计量器具标签完好并在有效期内。如发现工具设备自身故障，填写《工具设备损坏申报单》Form No.: FA-T10。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3359, 124, '5.1.3.2', '发放工具设备', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3360, 124, '5.1.3.2.1', '1）库房管理人员用工具管理系统对设备进行扫码，维修人员确认无误后扫码员工工具管理证件，完成发放。注：对于断电或断网等原因造成的工具管理系统无法使用的情况，生产支援分部人员填写《工具设备发放记录本》Form No.: FA-T11记录。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3361, 124, '5.1.3.2.2', '2）维修人员领用工具时，必须检查工具设备的完好性和有效性。借用工具车或工具箱等成套工具时，必须按清单清点，确保工具齐全完好，无其他杂物，方可借出。如果发现缺失要立即和生产支援分部值班人员进行联系，并在领用记录上注明。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3362, 124, '5.1.3.3', '归还工具设备', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3363, 124, '5.1.3.3.1', '1）维修人员工作结束，清点工具数量无误后，应及时归还所借工具设备，并应在归还前对所借工具设备进行必要的清洁、检查。对于工具车或工具箱，要确保工具号与清单一致，工具车或工具箱内无工作剩余的废弃物及剩余的航材。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3364, 124, '5.1.3.3.2', '2）工作结束后应及时归还工具设备进行必要的清洁、检查，保证工具设备完好无损并办理归还手续在系统内或《工具设备发放记录本》上销账，放到指定架位。如发现工具设备故障，按规范填写《工具设备损坏申报单》。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3365, 124, '5.1.3.4', '维修中非分公司维护人员借用工具设备按照上述要求执行。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3366, 124, '5.1.4', '消耗品的领用和归还要求', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3367, 124, '5.1.4.1', '消耗品如：擦布、手套、砂纸、塑料布、胶带、化工品（油脂、胶、清洁剂）等的领用数量由设施设备管理控制，擦布、手套必须交旧换新，其它消耗品用过后必须交回，用过的空容器和胶带纸圈也必须交回，不得随意丢弃。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3368, 124, '5.1.4.2', '值班人员负责每日的消耗品的发放与回收工作，必须保证记录清楚、数量吻合。维修人员领用消耗品时，值班人员须完成扫码或填写领用记录《线手套发放记录》Form No.: FA-T12或《抹布发放记录》Form No.: FA-T13，注明所借消耗品的名称、编号（或使用位置）、数量、日期并由借用人和值班人员分别签名。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3369, 124, '5.1.4.3', '维修人员在开工前限领用一副手套，破损后及时更换。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3370, 124, '5.1.4.4', '化工品消耗品必须以旧换新，使用过的空滑油罐、各种油脂容器及其他空的化工品包装必须统一回收到指定地点，不得随意丢弃。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3371, 124, '5.2', '工具设备使用', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3372, 124, '5.2.1', '工具“三清点”制度。\r\n维修工作人员严格执行工具“三清点”制度，即：“工作前清点、工作场所转移时清点、工作结束后清点”。', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3373, 124, '5.2.2', '维修现场控制要求。\r\n1）维修工作前工作者应清点好工具、消耗品数量。如使用的工具数量较多，工具借出后要放入提篮中，避免在转移或使用时遗失。\r\n2）维修工作中，禁止将工具、设备、器材、工作单（卡）等放置于航空器或者航空器部件上（工作需要，有规定必须置于航空器或者航空器部件上的除外），必须放在专用工具箱或提篮内，工具随用随拿，用完即放回原位，工具设备与拆下的飞机部件要分开放置。\r\n3）维修人员使用消耗材料封堵断开的插头、管路接头和部件时，必须使用红色胶带，并挂红色警示标牌，防止遗漏。使用消耗材料封堵位置在两个（含）以上的，航线中队定检中队要使用《封堵物清单》Form No.: FA-T14，由值班经理（工段长）负责控制。《封堵物清单》内容包括飞机号、封堵位置、封堵日期、封堵人签名、拆除日期和拆除人签名。放行人员在放行飞机前要确认《封堵物清单》所列封堵物均已拆除，方可实施放行。《封堵物清单》在飞机放行后由中队保存一个月。\r\n4）工作休息期间，应保证飞机的接近区域无工具，清点工具数量无误后，可以放置到工具车或工具箱内，否则应随身携带。\r\n5）在定检中非分公司维护人员的工作由定检中队负责监管。\r\n6）每项维修工作结束后、在装配工作完成后或关闭口盖前维修人员必须检查是否有外来物，清点确认现场没有遗留使用的工具和消耗材料。\r\n7）定检维修工作结束后工作者要对拆装过的部件、盖板以及工作过的区域进行全面检查，认真清理现场，清理现场的所有杂物，特别是发动机、起落架和起落架舱、操纵系统和驾驶舱和打开盖板施工的部位，必须仔细查看，确保没有外来物遗留在航空器或航空器部件上，确认清场彻底后才能结束全面工作。\r\n8）航线维修每次放行前工作者应当清点确认现场使用的工具没有遗留在航空器上。\r\n', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3374, 124, '5.3', '工具设备损坏', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3375, 124, '5.3.1', '由于工作不慎损坏或发现工具设备自身故障，借用人应及时向工具设备值班人员和当日值班经理（或工段长）说明情况，填写《工具设备损坏申报单》，并交工具房值班人员。', 9, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3376, 124, '5.3.2', '值班人员需将损坏设备放到指定的设备待修区，并在原架位上标明设备已送修。将工具设备损坏信息做好交接班记录，保证每位值班人员都能及时了解设备的当前状况。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3377, 124, '5.3.3', '设备维修工程师根据设备损坏情况结合本部门维修能力选择自修或送修，并在《工具设备损坏申报单》上填写评估及处理意见。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3378, 124, '5.3.4', '外送修理时，计划工程师应综合考虑维修厂家的维修资质、维修质量、维修价格、维修周期以及合作历史等因素，选择送修厂家并填写评估意见。送修价格按照天津分公司通用采购管理规定执行，待完成评估并批准后执行送修。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3379, 124, '5.3.5', '送修设备完成维修后，要核对维修报告，需校验设备确认是否校验合格，检查外观有无破损，零部件是否齐全与清单相符，确认后才可上架使用，并填写维修记录。', 5, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3380, 124, '5.4', '工具设备遗失。\r\n按程序01-08-009《工具设备或零部件遗失的报告和处理》执行。', NULL, '2018-06-28 15:05:47', '2018-06-28 15:05:47', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3381, 125, '5.1', '专用工具设备命名规范', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3382, 125, '5.1.1', '已经录入主数据的专用工具设备名称以主数据中的名称为准。', 5, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3383, 125, '5.1.2', '新增专用工具设备的名称按以下规则：\r\n1）根据相关手册新增的工具设备，以手册或使用说明书的名称为准。\r\n2）根据相关手册的要求，未按手册推荐的件号、厂家购买、但其经评估可等效替代的，仍按手册或使用说明书的名称为准。\r\n3）自制或改型的工具设备，根据其首次使用情况，名称应包括机型、适用部位或区域（ATA章节），尽可能达到容量辨别其适用性；\r\n4）由厂家赠予的工具设备，名称应包括机型、适用部位或区域，尽可能做到容易辨别其适用性；', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3384, 125, '5.2', '通用工具命名规范。\r\n已经录入主数据的通用工具设备名称以主数据为准。新添置通用工具的名称由使用单位按照手册、说明书、适用机型、部位或区域（ATA章节）等命名；尽可能做到容易辨别其适用性。', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3385, 125, '5.3', '工具设备的件号编号规范。\r\n已经录入主数据的专用工具设备件号以主数据中的件号为准。对于空客、波音等公司原厂家的工具设备直接采用原厂家件号。对于自制件确定的件号编码需要体现自制、体现章节等信息，编码规则如下：\r\n按原生产厂家提供设计数据由国航或转包商完成制造、改型和改装的工具设备，按原厂家件号加前缀AT。如同件号的要以阿拉伯数字标识序号。\r\n AT-原厂家件号（Air China Tool–F70207-1）。\r\n对于原生产厂家技术资料中有要求，但没有指出特定件号，而实际生产中又不可缺少的工具设备，由国航或转包商提供设计数据制造时，编码规则为：\r\nDT XX XX XX XXXX （Design Tool，章，节，项，流水号）\r\n按国航或转包商提供设计数据制造、改型、改装的工具设备，具有等效替代性。其编码规则为：\r\nET XX XX XX XXXX （Equivalent Tool，章，节，项， 流水号）。\r\n自制拖把以拖把适用机型为件号。', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3386, 125, '5.4', '通用工具件号编号规范\r\n已经录入主数据的通用工具件号以主数据中的件号为准。\r\n1）对于有原厂家件号的通用工具直接采用原厂家件号。\r\n2）自编件号套用《实耐宝手动工具目录》中文版中的件号。\r\n3）压力表件号编为Y-量程，例如量程为300Psi的压力表件号编为：Y300PSI。', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3387, 125, '5.5', '工作梯件号编号规范', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3388, 125, '5.5.1', '各机型专用工作梯件号编号规则\r\n各机型专用工作梯件号由GZT+机型（三位数字）+飞机部位（00-99）+类型号（01-99）共3个大写字母和7位数字组成。\r\n各机型编号：\r\n机型737, 747，330，340等可以分别表示为GZT737，GZT747，GZT330，GZT340等。\r\n各机型专用工作梯飞机部位编号：\r\n电子舱专用工作梯-00，客/货舱专用工作梯-01，发动机专用工作梯-02，APU专用工作梯-03，大翼专用工作梯-04，客/货舱门专用工作梯-05，起落架专用工作梯-06，垂尾专用工作梯-07，平尾专用工作梯-08，机身专用工作梯-09，机头专用工作梯-10。\r\n各机型专用工作梯类型编号：\r\n01表示第一类型，02表示第二类型等以此类推。\r\n例如：A320（第一类）飞机电子舱专用工作梯GZT320-00-01。\r\n', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3389, 125, '5.5.2', '各机型专用工作梯件号编号规则\r\n各机型专用工作梯件号由GZT+机型（三位数字）+飞机部位（00-99）+类型号（01-99）共3个大写字母和7位数字组成。\r\n各机型编号：\r\n机型737, 747，330，340等可以分别表示为GZT737，GZT747，GZT330，GZT340等。\r\n各机型专用工作梯飞机部位编号：\r\n电子舱专用工作梯-00，客/货舱专用工作梯-01，发动机专用工作梯-02，APU专用工作梯-03，大翼专用工作梯-04，客/货舱门专用工作梯-05，起落架专用工作梯-06，垂尾专用工作梯-07，平尾专用工作梯-08，机身专用工作梯-09，机头专用工作梯-10。\r\n各机型专用工作梯类型编号：\r\n01表示第一类型，02表示第二类型等以此类推。\r\n例如：A320（第一类）飞机电子舱专用工作梯GZT320-00-01。\r\n', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3390, 125, '5.5.3', '机坞专用工作梯件号编号规范\r\n机坞专用工作梯件号由GZT200+类别（00-99）+类型号（01-99）共3个大写字母和7位数字组成。\r\n机坞专用工作梯类型编号：\r\n机头专用工作梯-01，机身专用工作梯-02，大翼专用工作梯-03，机尾专用工作梯-04。\r\n例如：机坞机头（第一类）专用工作梯件号编为GZT200-01-01。', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3391, 125, '5.6', '机坪设备序号标识规范', NULL, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3392, 125, '5.6.1', '机坪上同类设备以机坪位置加阿拉伯数字标识序号。', 5, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3393, 125, '5.6.2', '机库内同类设备要以阿拉伯数字标识序号。', 5, '2018-06-28 15:13:54', '2018-06-28 15:13:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3394, 126, '5.1', '车辆进出机库。\r\n所有车辆进入机库时限速5公里/小时，车辆进入机库前应装上防火星罩。', NULL, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3395, 126, '5.2', '飞机进出机库及机库内工作要求', NULL, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3396, 126, '5.2.1', 'MCC提前确认进机库飞机工作是否使用尾坞，如不使用尾坞飞机停放时优先选用“1拉2坐”模式。', 6, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3397, 126, '5.2.2', '拖飞机进出机库安全操作要求\r\n1）拖飞机进出机库严格执行安全检查单。\r\n2）拖飞机前确认机库地面白线区域内干净无湿滑，机库大门在全开位，天吊回原位、拉绳在收上位，主轮轮挡摆放到位，确保拖机路线区域内无障碍物。\r\n3）两米以上高梯提前移动到位。\r\n4）拖机过程中要求噤声，沟通时呼喊加手势。\r\n5）当飞机刹车系统故障时禁止使用1、2号机位。\r\n', NULL, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3398, 126, '5.2.3', '1）同时使用1、2号机位时两米以上高梯提前移动到位，其他梯子按要求推拖。\r\n2）使用中在靠近机库侧翼尖处设置警示标识，如需高空车、高梯子通过，地面至少安排3个观察员。\r\n3）建立工作隔离区及警示牌。\r\n', NULL, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3399, 126, '5.3', '机库设备摆放', NULL, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3400, 126, '5.3.1', '机库地面设备应按规定区域摆放。维修人员可直接推用机库地面摆放的设备，用后必须放回规定位置。', 9, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3401, 126, '5.3.2', '机库管理人员每日对机库设备进行巡视，确保机库内设备设施归位且符合6S标准，不占用消防通道。', 5, '2018-06-28 15:15:55', '2018-06-28 15:15:55', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3402, 127, '5.1', '维修记录的种类', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3403, 127, '5.1.1', '包括但不仅限于以下类别：                 \r\n1）	机上记录本：FLB\\TLB\\CLB；\r\n2）	航线例行和非例行维修工作文件；\r\n3）	特定的维修任务（或项目）维修工作文件；\r\n4）	发动机/APU拆装工作文件；\r\n5）	附件修理工作文件；\r\n6）	定检（A检、C检等）维修工作文件；\r\n', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3404, 127, '5.1.2', 'C检维修工作文件包应包括但不仅限于以下内容（适用时）：\r\n1）工作汇总单；\r\n2）放行证书；\r\n3）工卡清单以及填写完整工卡；\r\n4）完成的服务通告/适航指令清单及工卡；\r\n5）更换件清单及相应的适航放行挂签或局方接受的其它器材追溯性文件（如Ameco PIN单或局方接受的客户附件标识挂签）；\r\n6）非例行工作单及检查单；\r\n7）结构故障修理报告；\r\n8）客户要求的维修文件；\r\n9）测试记录；\r\n10）《重要修理及改装记录》AAC085；\r\n11）其他涉及维修工作的签署文件；\r\n12）涉及三方的客户还要提交维护费用单；\r\n', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3405, 127, '5.2', '维修记录填写和检查', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3406, 127, '5.2.1', '工作者依据工作计划和施工标准完成相关维修工作后，按要求签署相关维修记录，工作者对维修记录签署的准确性负责，完工签署人员负责核对维修记录签署的准确性和及时性，并保证齐全。', 9, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3407, 127, '5.2.2', '各类完成签署的维修记录，交由部门/班组制负责人审核，确保维修记录合格签署及不发生缺页后，登记交回生产计划控制分部。', 9, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3408, 127, '5.3', '维修记录接收', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3409, 127, '5.3.1', '生产计划控制分部根据维修中队交回的维修记录种类，提取相关维修信息完成第一链条的控制、信息上报，包括但不限于：生成和关闭的DD单、需紧急反馈的特殊工卡、涉及运行要求的相关维修信息等。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3410, 127, '5.3.2', '生产计划控制分部负责按回收清单核对工卡份数的齐全准确，协助核对已经完成完工签署。同时：\r\n1）回收的维修记录保证与回收清单一致，涉及三方的维护费用单必须对应当日涉及的航班。\r\n2）除冰/防冰工作汇总单由MCC直接留存，应存档1年。\r\n3）《串件单》由MCC登记并转发航材与采购部天津分部。\r\n', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3411, 127, '5.4', '整理反馈', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3412, 127, '5.4.1', '所有完成的维修记录统一由生产计划控制分部的生产准备进行整理反馈。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3413, 127, '5.4.2', '航线维修记录', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3414, 127, '5.4.2.1', '航线维修记录包含天津本场的航线维修记录和外站返回的航线维修记录。', 9, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3415, 127, '5.4.2.2', '生产计划控制分部的生产准备人员每日接收所有的航线维修记录，当日分类处理。除/防冰工作记录单单独反馈。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3416, 127, '5.4.3', '记录本：', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3417, 127, '5.4.3.1', '生产计划控制分部准备人员按回收单附页清单核对，由统计人员提取所需记录之后，生产计划控制分部准备人员抽取规定页分发到技术支援分部，剩余部分存档一个月。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3418, 127, '5.4.3.2', '当出现缺页情况时，统计人员按有关规定查明原因，按照TJ-07-001CCA《单机档案与维修记录管理程序》进行恢复。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3419, 127, '5.4.3.3', '对于使用过的原始记录本，生产计划控制分部准备登记后交航空安全质量分部存档。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3420, 127, '5.4.4', '工卡：', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3421, 127, '5.4.4.1', '原件抽取时控件更换指令、DD单、EO工卡给相应控制人员签收处理。', 6, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3422, 127, '5.4.4.2', '特殊反馈项目见附表。\r\n其余工卡分类、登记反馈。\r\n《在津机队每日工作计划》《Ameco北京基地二方日维修计划》交回计划控制室处理。\r\n', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3423, 127, '5.4.5', '定检维修记录。\r\n定检工作结束后的1周内（节假日顺延），完成维修记录整理及登记反馈，涉及的特殊项目见附件。', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3424, 127, '5.5', '存档反馈时间。\r\n每周一、三、五交航空安全质量分部存档。反馈记录每月汇总装文件袋存档，至少保存1年。', NULL, '2018-06-28 15:51:32', '2018-06-28 15:51:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3425, 128, '5.1', '维修许可证管理', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3426, 128, '5.1.1', '分公司维修许可证已获得CAAC批准，证号：D.101141。', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3427, 128, '5.1.2', '航空安全质量分部将旧的《维修许可证》/《许可维修项目》（F145-3（10-2001））证书交回民航华北局适航维修处/民航天津监管局适航维修处换取适航管理部门颁发的新的《维修许可证》、《许可维修项目》证书后存档。《维修许可证》、《许可维修项目》证书原件悬挂在本单位小会议室，并公布在公分司内网。同时将复印件报公司航空安全与质量管理部、发生产计划控制分部，该复印件应清晰并与原件保持一致。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3428, 128, '5.1.3', '生产计划控制分部将复印件展示在MCC办公室。', 6, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3429, 128, '5.1.4', '当单位名称、地址、维修类别发生变化时，要向局方申请变更维修许可证。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3430, 128, '5.1.5', '《维修许可证》在没有变更、吊销的情况下，长期有效。被放弃、暂停、吊销的《维修许可证》应当由航空安全质量分部在5日内交还民航华北局适航维修处/民航天津监管局适航维修处。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3431, 128, '5.2', '新增维修类别/项目申请', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3432, 128, '5.2.1', '由生产计划控制分部提出《维修许可证》的办理需求。生产计划控制分部提前90天组织各单位完成申请所需各项工作准备。', 6, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3433, 128, '5.2.2', '人力资源分部提供完成该维修项目所涉及的人员的资格证明。', 1, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3434, 128, '5.2.3', '技术支援分部提供下列清单： \n1）完成该维修项目所需的工艺/工作单卡； \n2）完成该维修项目所需要的航材清单；\n3）完成该维修项目所需要工具设备清单； \n4）涉及代用品的，得到批准的代用品清单。', 4, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3435, 128, '5.2.4', '综合管理分部提供完成该维修项目所需要环境（工作间）资料。', 7, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3436, 128, '5.2.5', '航空安全质量分部提供完成该维修项目所需要人员授权清单。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3437, 128, '5.2.6', '在10个工作日内，生产计划控制分部向航空安全质量分部提供清单所要求的资料。', 6, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3438, 128, '5.2.7', '航空安全质量分部对资料进行汇总并按01-09-003《质量审核程序》完成内审工作。完成内审后，航空安全质量分部提前60天向民航华北局适航处/民航天津监管局适航维修处提出《维修许可证》申请，申请时提供下列适用的文件：\n1）《维修许可证申请书》；\n2）《维修管理手册》；\n3）《维修管理手册》；\n4）符合性说明（包括本节涉及的支持资料及航空安全质量分部的内审报告、安全风险分析报告）。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3439, 128, '5.2.8', '审查、纠正和反馈', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3440, 128, '5.2.8.1', '航空安全质量分部在收到民航华北局适航维修处/民航天津监管局适航维修处的“受理申请通知书”后，组织各单位配合民航华北局适航维修处/民航天津监管局适航维修处完成对《维修许可证》申请项目的审查工作。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3441, 128, '5.2.8.2', '审查工作若发现问题，相关责任部门填写《改正措施报告》M2008C（2008/08/10），由航空安全质量分部提交民航华北局适航维修处/民航天津监管局适航维修处。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3442, 128, '5.3', '《维修许可证》变更', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3443, 128, '5.3.1', '分公司在机构、名称、地点发生变更时，航空安全质量分部应当至少提前60天向民航华北局适航维修处/民航天津监管局适航维修处提出变更维修许可证的书面申请。申请提交下列适用资料各一份： \n1）维修许可证申请书； \n2）单位机构、名称、地点变更原因说明（如适用）； \n3）维修能力清单的变更草稿（如适用）； \n4）涉及变更的资料； \n5）本规定的符合性声明和有关符合性的支持性资料；\n6）《维修管理手册》的变更草稿。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3444, 128, '5.3.2', '民航华北局维修处/民航天津监管局适航维修处接到分公司的完整的申请文件后，做出是否受理申请资料的答复并通知分公司航空安全质量分部。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3445, 128, '5.3.3', '民航华北局维修处/民航天津监管局适航维修处在受理分公司的正式申请材料后，以书面或会面的形式确定对分公司的变更涉及内容进行现场审查的日期，并按照双方商定的日期对分公司的变更涉及内容进行现场审查并按规定收取审查费用。', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3446, 128, '5.3.4', '民航华北局维修处/民航天津监管局适航维修处现场审查完成或收到申请人对发现问题的书面改正措施之日起20个工作日内对符合CCAR-145第四章要求的并交纳了规定审查费用的国内维修单位颁发变更后维修许可证。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3447, 128, '5.3.5', '分公司在厂房设施、工具设备、器材、适航性资料、人员、组织机构和维修单位手册等情况发生较大变化时，航空安全质量分部应当至少提前30日通知民航华北局适航维修处/民航天津监管局适航维修处。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3448, 128, '5.3.6', '接受局方审查，按需完成纠正和反馈。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3449, 128, '5.4', '维修能力清单要求', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3450, 128, '5.4.1', '能力清单使用有效页清单控制其有效性，能力清单的项目排列顺序应以ATA章节号的顺序来排列。', 8, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3451, 128, '5.4.2', '能力清单的每一页应标注页号和改版日期，每一附件应单独为一行包括以下内容：\r\n1）序号（为流水号）\r\n2）主件号和尾号（－）\r\n3）名称\r\n4）ATA章节号\r\n5）制造厂家\r\n6）维修工作类别（填写“检测”“修理”“改装”“翻修”中的任一类或其组合，但对于非时控件并且其依据文件中没有翻修依据的部件不能填写“翻修”）\r\n7）依据文件（应填写航空器部件制造厂家提供的适航资料和技术文件的名称）\r\n8）主要设备（一般是最终测试设备）\r\n', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3452, 128, '5.5', '《航线维修能力清单》要求：\r\n分公司只能在维修许可证限定的地点和有限项目下为航空运营人提供相应的航线维修工作，但还需以《航线维修能力清单》的形式得到局方的批准。', NULL, '2018-06-28 16:09:37', '2018-06-28 16:09:37', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3453, 129, '5.1', '概述\r\n维修单位年度报告每年度填写一次，报告内容自上一年度1月1日至本年度1月1日，并于每年2月1日前以《维修单位年度报告》（F145-4）的格式报中国民用航空天津安全监督管理局和公司航空安全与质量管理部。', NULL, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3454, 129, '5.2', '信息收集', NULL, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3455, 129, '5.2.1', '《维修单位年度报告》第1、3、4、5项内容由航空安全质量分部完成，同时向航材部门提出信息需求。', 8, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3456, 129, '5.2.2', '《维修单位年度报告》第2项信息由生产计划控制分部反馈航空安全质量分部。', 6, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3457, 129, '5.3', '报告填写和签批', NULL, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3458, 129, '5.3.1', '按收集的信息填写《维修单位年度报告》，并报分公司质量经理签署批准。', 8, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3459, 129, '5.3.2', '在签署好的《维修单位年度报告》首页加盖分公司公章。', 8, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3460, 129, '5.4', '于2月1日前将完成的《维修单位年度报告》上报局方。', 8, '2018-06-28 16:13:01', '2018-06-28 16:13:01', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3461, 130, '5.1', '报告', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3462, 130, '5.1.1', '任何个人都有义务对维修生产中存在的困难和问题及时进行反映，填写《维修困难报告表》Form No.: QM-T03。', 10, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3463, 130, '5.1.2', '当维修生产中存在的困难和问题时，报告人应填写《维修困难报告表》中的“问题或困难”栏，将困难信息描述清晰，并经所属部门经理对所反映问题进行审核后签批，将填写好的《维修困难报告表》交航空安全质量分部。', 10, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3464, 130, '5.2', '调查取证', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3465, 130, '5.2.1', '编号。\r\n当收到《维修困难报告表》后，航空安全质量分部向报告人了解核实相应的情况，并依据当月收到报告情况对该《维修困难报告表》进行编号。编号原则为：年号+月号+流水号如：2009年1月份第一份报告，编号为：20090101，以此类推。', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3466, 130, '5.2.2', '调查取证。\r\n1）对《维修困难报告表》中涉及问题的部门和人员进行调查。调查最好采用当面调查，不影响工作人员的正常工作。\r\n2）对《维修困难报告表》涉及的问题可以通过分公司程序、工作记录、内网系统及维修记录进行调查取证。\r\n', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3467, 130, '5.3', '原因分析', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3468, 130, '5.3.1', '依据调查取证对《维修困难报告表》问题进行原因分析，查找问题最根本原因。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3469, 130, '5.3.2', '确定相应的责任部门。责任部门可能是一个部门或是多个部门，可以是人为的，也可以是系统原因。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3470, 130, '5.3.3', '填写《维修困难报告表》质量调查一栏并签署，发相关责任部门。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3471, 130, '5.4', '整改。\r\n相关部门责任部门制定《维修困难报告表》问题的整改措施。整改措施应有相应的解决时限，并由相应责任人签署。', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3472, 130, '5.5', '《维修困难报告表》控制', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3473, 130, '5.5.1', '《维修困难报告表》进行存档，并对整改措施的进展进行跟踪。控制可以采用Excel、Word、数据库等形式。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3474, 130, '5.5.2', '对于需上级部门予以协助解决的维修困难报告问题，应立即报告上级单位并建立月度跟踪制度，并对解决进程持续监控、报告。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3475, 130, '5.6', '整改验证。\r\n当《维修困难报告表》整改完成后，对整改结果进行验证。将验证结果填写到《维修困难报告表》，如整改未达到要求，向责任部门继续提出整改。', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3476, 130, '5.7', '关闭确认', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3477, 130, '5.7.1', '当质量验证完毕后，请报告提出单位或个人确认是否满足要求。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3478, 130, '5.7.2', '如满足要求，由报告提出单位或个人签署《维修困难报告表》，确认问题关闭。', 10, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3479, 130, '5.7.3', '如不满足要求，重复执行5.3-5.6。', 8, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3480, 130, '5.8', '总结。\r\n维修困难报告工作进行总结，内容包括：问题分类、与去年的对比、问题的趋势等。', NULL, '2018-06-28 16:25:21', '2018-06-28 16:25:21', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3481, 131, '5.1', '外委单位选择', NULL, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3482, 131, '5.1.1', '对于已经纳入Ameco航空安全与质量管理部《供应商清单》Form No.: QM-018和《北京飞机维修工程有限公司天津分公司外委项目清单》（AMECO-TJ-12-01）中的外委单位，可直接选择。', 6, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3483, 131, '5.1.2', '对于未纳入5.1.1清单中的外委单位，生产计划控制分部应填写《供应商资格评估表》Form No.: QM-017报航空安全质量分部评估，在纳入《北京飞机维修工程有限公司天津分公司外委项目清单》后选择。', 6, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3484, 131, '5.2', '外委单位评估', NULL, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3485, 131, '5.2.1', '航空安全质量分部审核人员按7.2.1附件一《供应商资格要求及评估标准》对外委单位进行评估。', 8, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3486, 131, '5.2.2', '对于需要执行质量审核的外委单位，按照01-09-003《质量审核程序》实施审核。审核完成后，根据审核结论填写《供应商资格评估表》。', 8, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3487, 131, '5.3', '外委单位批准发布', NULL, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3488, 131, '5.3.1', '审核人员完成评估后，将《供应商资格评估表》提交质量经理批准。', 8, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3489, 131, '5.3.2', '航空安全质量分部将经批准的外委单位纳入《北京飞机维修工程有限公司天津分公司外委项目清单》，并在分公司内网发布。', 8, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3490, 131, '5.4', '外委单位周期评估', NULL, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3491, 131, '5.4.1', '对于已批准的外委单位，航空安全质量分部将按照《供应商资格评估表》、《供应商资格要求及评估标准》的相关周期要求启动周期评估。', 8, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3492, 131, '5.4.2', '根据周期评估结果，修订《北京飞机维修工程有限公司天津分公司外委项目清单》。', 8, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3493, 131, '5.5', '外委单位持续监控。\r\n当发生以下情况时，按照本程序启动外委单位资格评估流程。\r\n1）外委单位资格范围发生变化；\r\n2）外委单位体系发生变更；\r\n3）基于质量、安全事件的调查结果，判断外委单位可能存在体系问题。', NULL, '2018-06-28 16:28:05', '2018-06-28 16:28:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3494, 132, '5.1', '飞机外部损伤评估单的填写：', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3495, 132, '5.1.1', '发现凹坑损伤后，如果该凹坑损伤属于可允许损伤，且无需后续处理的凹坑。维修中队应填写对应的《外部损伤评估表》Form No.: EM-034，同时航线中队填写相应TLB，定检中队填写相应的TLB或非例单。\r\n注意：如果该凹坑损伤需要后续修理或需要后续监控，请维修中队按照相关手册要求和规范完成修理或办理DD进行监控，不适用于本评估表。', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3496, 132, '5.1.2', '《外部损伤评估表》的编号为请填写其对应的TLB或非例单编号,然后在编号前面加T，即：T****（T后面是TLB号或非例单号）。', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3497, 132, '5.2', '更新《飞机外部结构缺损清单》Form No.: EM-T02：', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3498, 132, '5.2.1', '由维修中队各自的授权人员根据《外部损伤评估表》，更新分公司内网电子版和飞机上纸张版的《飞机外部结构缺损清单》；', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3499, 132, '5.2.2', '登陆分公司内网/共享信息/部门/飞机外部缺陷评估；', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3500, 132, '5.2.3', '进入各飞机文件夹，根据最新信息，把当前版次的《飞机外部结构缺损清单》复制到电脑桌面上进行更新，再把完成更新的《飞机外部结构缺损清单》生成更新的版次，比如之前是R(n)版，更新生成的版次为R(n+1)版，然后上传到对应飞机文件夹内。\r\n注意：旧版暂时保留，技术支援分部结构工程师负责整理删除。', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3501, 132, '5.2.4', '对于首次执行的飞机，在飞机外部缺陷评估板块上找到737NG飞机站位图，打印生成纸张版，然后放置到飞机上。\r\n注意：放置本站位图的目的是为了方便相关人员确认外部损伤具体位置。', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3502, 132, '5.2.5', '打印完成更新的《飞机外部结构缺损清单》，生成纸张版；然后放置到飞机上，替换旧版清单。', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3503, 132, '5.2.6', '48小时内通过邮件或电话的形式通知技术支援分部结构工程师该架飞机的外部结构缺损损伤的更新情况，以便其追踪外部损伤评估表，扫描放置到分公司内网上，并完成单机结构档案的更新。', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3504, 132, '5.2.7', '如果是要删除条目（有些条目完成了永久修理）更新清单，请维修中队要把删除条目对应的外部损伤评估表也从飞机上取下，并在上面注明关闭原因，并签字，然后把修理工卡的复印件附在评估表后面，返回生产计划控制分部。', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3505, 132, '5.2.8', '生产计划控制分部反馈技术支援分部。', 6, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3506, 132, '5.3', '飞机外部损伤评估表的存放和反馈：', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3507, 132, '5.3.1', '维修中队在飞机外部损伤评估表并完成填写完成后，应将原件放置在飞机上外部损伤存放夹内，复印件或扫描件交MCC。', 9, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3508, 132, '5.3.2', '将飞机外部损伤评估表反馈技术支援分部。纸张版或电子版反馈均可，优先推荐扫描件通过邮件反馈。', 6, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3509, 132, '5.4', '技术支援分部对外部损伤信息的核对和更新\n结构工程师根据生产计划控制分部和维修中队的反馈，对外部损伤进行评估和核对，在收到反馈后的一个月内完成分公司内网《外部损伤评估表》的更新，和对《飞机外部结构缺损清单》的核对和管理，然后根据以上资料完成对单机结构档案和公司故障网飞机外部凹坑信息的更新。', 4, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3510, 132, '5.5', '定期检查清理和规范：', NULL, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3511, 132, '5.5.1', '依据飞机外部损伤评估单和飞机外部缺损清单的情况，按需编写相关维护提示，对一些常见问题进行总结、说明，确保其规范性和准确性。', 4, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3512, 132, '5.5.2', '每个C检，对飞机原有外部损伤评估单，进行梳理清查。注意：本重检工作是为了保证评估单和清单的规范和准确性，也是方便今后的飞机退租工作。', 4, '2018-06-28 16:30:43', '2018-06-28 16:30:43', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3513, 133, '5.1', '人员资格与管理', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3514, 133, '5.1.1', '1）无损检测检验人员依据中国民航行业标准MH/T3001《航空器无损检测人员资格鉴定与认证》的规定进行技术培训和资格鉴定。\r\n2）无损检测检验人员依据中国民航行业标准MH/T3001《航空器无损检测人员资格鉴定与认证》的规定进行视力检查并符合要求。\r\n3）无损检测检验人员应符合分公司有关人员资格的要求并取得相应授权。\r\n', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3515, 133, '5.1.2', '专业技术文件和资料管理', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3516, 133, '5.1.2.1', 'NDT行业标准、AMM 、NDT等手册有确定的网络地址。', 4, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3517, 133, '5.1.2.2', '1）纸张版检验记录、测试记录、NDT设备说明书等保存于资料柜或专门存放。\r\n2）现场工作所需要的纸张版手册、指南须盖章或注明打印日期，并明确有效期。\r\n', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3518, 133, '5.2', '工作信息传递', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3519, 133, '5.2.1', '查阅生产计划控制分部发布的周工作计划、三日计划，做好人力安排。', 8, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3520, 133, '5.2.2', '每月30日前将下月值班信息发MCC，当日值班人员接到MCC电话后在指定时间赶到指定工作地点，并准备好工具设备。', 8, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3521, 133, '5.2.3', '当有突发无损检测工作时，依据航空安全质量分部提供的值班人员电话与当日人员建立联系，明确工作内容、时间和地点。如在外地，负责交通和工具设备准备。', 6, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3522, 133, '5.3', '检测准备', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3523, 133, '5.3.1', '结构探伤：根据指定的规范和程序，评估检测工艺、设备，包括对孔探仪器、探伤仪器、探头、试块等工具的预评估。', 8, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3524, 133, '5.3.2', '附件探伤：相关中队将需要离位探伤的零件送至机库探伤工作间。被检零件应提前做好表面处理，如清洗、退漆、除胶等，直至达到检测要求。', 9, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3525, 133, '5.3.3', '首检的NDT检查项目', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3526, 133, '5.3.3.1', '对于首检的NDT检查项目由生产计划控制分部将工艺规范或工卡发至无损检测工程师进行评估。', 6, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3527, 133, '5.3.3.2', '当检查工艺、设备工具、工作现场不满足检查要求时，NDT检测人员应反馈至生产计划控制分部。', 8, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3528, 133, '5.4', '工作实施。\r\n1）探伤工作应按照相关有效的工程文件/工卡、工艺文件、操作指南、NDT手册执行。完成检测工作后，检测人员应在相应维修工卡和工作指令单上签署，并保存检测记录（电子版）。如发现损伤超标的，及时通知执管车间和生产计划。 \r\n2）当探伤时怀疑被检件存在缺陷时，应采取其它有效检测方法辅助评估，必要时请该方法NDT 3级检测人员予以判定。\r\n', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3529, 133, '5.5', '探伤后的处理。\r\n1）清理检测现场：清点设备工具，归位。\r\n2）与相关中队交接：告知需恢复涂层及清洗的零件或检查区。  \r\n3）签署工卡并交至相关中队。\r\n', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3530, 133, '5.6', '检测结果记录与存档。\r\n《Ameco天津分公司磁粉检验报告单》Form No.: CM-T01、\r\n《Ameco天津分公司涡流检验记录》Form No.: CM-T02、\r\n《Ameco天津分公司超声波检验记录》Form No.: CM-T03、\r\n《Ameco天津分公司渗透检验记录》Form No.: CM-T04为电子版。', NULL, '2018-06-29 07:57:45', '2018-06-29 07:57:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3531, 134, '5.1', 'B747系列飞机系留带适用范围。系留带仅用于保障B747客货机和全货机在装卸货物或加抽燃油时，因重心位置改变会造成飞机的俯仰，为防止机身尾部后坐有两种方法：一种为尾撑，另一种为使用系留带（但两种不可同时使用）。（B747全客机不需使用）', 9, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3532, 134, '5.2', '系留带的技术规格。\r\n1）系留带（包括两端卸扣）有效长度为6米（一端卸扣中心到另一端卸扣中心的有效长度）。\r\n2）系留带（包括两端卸扣）额定工作拉力为6800公斤。\r\n3）两端卸扣采用U型可拆卸方式卸扣。', NULL, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3533, 134, '5.3', '系留带的检查与存放', NULL, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3534, 134, '5.3.1', '每次使用系留带前必须对系留带进行全长检查，外层保护套是否有磨损，割破、高温的损坏，金属构件是否有变形或损坏的情况，标签是否清晰（见附图一）。\r\n系留带使用者每次使用前要确认系留带过载指示带露出在位，如果没有了，该条系留带就不能使用。如果系留带上有明显受到化学腐蚀的状况，也不能使用，需要送回生产厂家修复（见附图一）。\r\n系留带存储在分公司东区工具间，每次使用前领取，使用后归还到原处。', NULL, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3535, 134, '5.3.2', '设备部门每周要用手电筒光源检查光纤导光正常（见附图一）。每年送出检查。每周检查情况记录在表单《B7474飞机系留带检查记录》Form No.: FA-T15。', 5, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3536, 134, '5.4', '系留带操作\r\n1）在B747飞机进港后，工作者挡好轮档、摆放好安全锥桶并与机组确认刹车已经松开后才可以安装系留带工作，系留带安装完毕后才可以装卸货物和抽加燃油。\r\n2）当飞机停放时前轮偏离停机线前后30CM时不能使用系留带时，为避免使用拖车将飞机拖到停机线位置时间过长而导致航班延误，应使用尾撑替代系留带操作。\r\n3）使用前必须检查相应停机位的系留位置的地锚外观无明显损伤和破损。\r\n4）严禁在地面拖拽或碾压系留带。\r\n5）严格按B747飞机AMM07-11-09安装和拆下系留带。\r\n6）使用时应当两人配合安装：系留带穿过前起落架时应避免对前起落架本体的电器和液压元件损伤和挤压，禁止带着U型卸扣穿越前起落架结构。\r\n7）系留带的肢体在使用时不能打拧和打结。\r\n8）安装并拧紧两端的U型卸扣的固定螺栓，并确认安装牢固。\r\n9）系留带安装完毕后必须由两名人员互检安装状况，并双签签署工作单（如有工作单）。\r\n10）系留带在使用时应当始终有专门的人员监控，当发现飞机重心发生变化等意外情况时应及时通知货物装卸人员和抽加燃油人员停止作业，待情况处理完毕，飞机恢复正常重心后方可继续作业。必须等待所有货物以及行李全部装好，飞机燃油已经加完，所有货舱门关闭后方可撤离系留带。\r\n11）系留带从飞机上拆下后必须放到专用的存放箱内，待保障结束后退还到工具间。\r\n', NULL, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3537, 134, '5.5', '系留带使用过程中不正常情况处理\r\n1）如果飞机偏离（超出或距离）停机线超出30CM，飞机卸货前安装了系留带，飞机卸货后前起落架会升高，导致在系留带上存在张力，从而发生取下困难，这种情况下需要拖动飞机，使前轮和停机线对齐，然后取下系留带。\r\n2）如果飞机满足和停机线对齐30CM的误差，系留带取下时存在张力，取下困难，说明飞机存在严重的后重心，需要消除后重心，使前起落架下沉，然后才可取下系留带。\r\n3）如果系留带存在过取下困难的现象，维护人员需要检查过载指示带和光纤导通良好，否则立即采取停止使用该系留带，并通知设备部门。\r\n4）在极端不正常的装卸货过程中，如果发生飞机严重的后重心导致前轮即将离地或已经离地，前起落架的空地电门会转换到空中状态，此时会导致货仓操作断电，此时需要消除飞机严重的后重心才可再次接通货仓供电，继续进行装卸货，并检查系留带的状况。\r\n', NULL, '2018-06-29 09:18:45', '2018-06-29 09:18:45', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3538, 135, '5.1', '尾撑适用范围。\r\n尾撑仅用于保障B747客货机和全货机在装卸货物时使用（全客机不使用）。', NULL, '2018-06-29 09:21:03', '2018-06-29 09:21:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3539, 135, '5.2', '尾撑结构。\r\n1）尾撑为支柱液压伸缩结构，人工打压作动升起，设有回油开关，打开回油开关支柱缩回。\r\n2）尾撑顶部设有锁扣（用于使尾撑与飞机顶块锁上），锁扣连接绳索和拉环。', NULL, '2018-06-29 09:21:03', '2018-06-29 09:21:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3540, 135, '5.3', '尾撑操作\r\n1）在飞机进港后，工作者挡好轮档、摆放好安全锥桶并与机组确认刹车已经松开后才可以安装尾撑工作。\r\n2）使用尾撑前必须检查尾撑结构完好，液压部件无渗漏。\r\n3）尾撑必须沿着划定的机位红线以外进行车辆拖拽，车辆禁止进入机位红线以内。尾撑拖拽至飞机红线以外后，必须人工推至飞机右后尾部的顶点块下部。\r\n4）取下尾撑顶部保护套（此项工作需要执行高空作业安全检查单）。\r\n5）使用时应当两人配合：首先将尾撑对正，即将尾撑顶部的凹槽与飞机右后尾部的顶点块对正。\r\n6）缓慢打压使凹槽与顶点紧密贴合，继续打压，此时应特别注意锁扣的状态，直到锁住。（要求人员应当爬上尾撑进行检查）。\r\n7）将三个尾撑附带的小铁轮挡挡在尾撑底部的三个轮子上。\r\n8）尾撑在使用时应当始终有专门的人员监控，使得顶升力始终保持在2兆帕—4兆帕（2吨—4吨）之间。待货物以及行李全部装好，货舱门关闭后方可撤离尾撑。\r\n9）撤离尾撑时应当先打开回油开关然后拉下拉环解锁直到尾撑与飞机顶点块完全脱离。\r\n10）撤离轮挡，然后人工向飞机后部推离尾撑，直至尾撑离开飞机投影面及飞机机位红线后才能使用车辆进行拖拽。拖拽尾撑的车辆禁止进入划定的飞机机位红线，将尾撑拉至停放位。\r\n11）安装尾撑顶部保护套。', NULL, '2018-06-29 09:21:03', '2018-06-29 09:21:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3541, 135, '5.4', '安全防护。\r\n由于该尾撑用于飞机尾部（APU区域）噪音大，要求监控人员必须佩带耳机。', NULL, '2018-06-29 09:21:03', '2018-06-29 09:21:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3542, 136, '5.1', '跨区拖机计划', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3543, 136, '5.1.1', '航线中队按照维修计划或临时排故需要，提前做好跨区拖机计划，并至少提前1小时向MCC提出跨越跑道拖行飞机的申请。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3544, 136, '5.1.2', 'MCC按照维修计划或临时排故需要，至少提前一小时通过电话向天津机场塔台提出跨越跑道拖行飞机的申请和预计拖行时间。', 6, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3545, 136, '5.1.3', '塔台给定允许拖行的时间后，MCC及时通报航线中队和特种车队。', 6, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3546, 136, '5.2', '跨区拖行飞机引导', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3547, 136, '5.2.1', '跨区拖行飞机必须使用引导车引导拖行。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3548, 136, '5.2.2', '引导车由航线内场值班车担任，驾车司机为航线中队兼职内场司机，该司机应熟悉跨区拖行路线和安全规定。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3549, 136, '5.2.3', '引导车副驾驶位置由跨区拖行指挥员乘坐，其负责整体指挥跨区拖行任务。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3550, 136, '5.2.4', '引导车在进入任意路口转弯时必须使用转向灯示意。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3551, 136, '5.3', '跨区拖机之前准备', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3552, 136, '5.3.1', '跨区拖行人员中位于机上的人员必须熟悉航管用语，跨区拖行时须组成四人拖机小组，并确定指挥员。每次跨区拖机之前，所有参与拖飞机人员（包括拖车司机）必须提前在指挥员的组织下，熟悉拖机固定路线、注意事项和应急处置方法等，拖行开始前，指挥人员把参与跨区拖行飞机人员姓名和应急联系方式报MCC，以备紧急情况下使用备份联系方式保持通讯联系（手机）。所有参加跨区和拖飞机的人员要保持有效的联络。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3553, 136, '5.3.2', '跨区拖行指挥员资格：由航线中队值班经理或其指定的符合资格的人员担任，其负责与塔台和拖飞机人员进行联络和指挥飞机拖行。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3554, 136, '5.3.3', '跨区拖行指挥员需提前确认参与拖机人员应经过《民用航空行业标准》、跨区拖行等有关拖机知识的培训并获得上岗资格，之后开始组织飞机实际拖行。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3555, 136, '5.3.4', '准备工作包括但不限于飞机检查和引导车检查，确保飞机和引导车没有影响拖行的故障，并保证在拖行过程中，不会把相关零件遗漏在拖行路线上。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3556, 136, '5.3.5', '当塔台新增拖行路线时，拖行小组如对路线不熟悉，需申请机场引导。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3557, 136, '5.3.6', '当遇到恶劣天气和能见度不足的情况下，指挥员要评估跨区的风险，采取临时应急措施，必要时终止跨区拖行工作。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3558, 136, '5.4', '跨区拖行实施', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3559, 136, '5.4.1', '跨区拖行飞机时，必须要严格执行三规定：规定时间、规定路线和规定的熟悉跨区路线和程序的人员，必须严格执行塔台的指令。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3560, 136, '5.4.2', '指挥员要头脑清醒，路线明确，指令清晰，坐在拖车上的联络员有职责提醒拖车司机的行驶路线、速度，必要时要求其停止，飞机上的观察员要观察路线情况，守听甚高频，必要时给指挥员联系提醒，除非拖车与飞机脱离，否则严禁踩刹车。\r\n所有参加跨区和拖飞机的人员要建立有效的联系，不能失联。', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3561, 136, '5.5', '跨区拖行时对讲机的使用要求', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3562, 136, '5.5.1', '指挥员需持有两部对讲机：一部塔台专用频道对讲机用于与塔台管制员联络；另一部为分公司频道对讲机，用于与MCC和拖飞机人员联系，其余参与拖机人员各持一部分公司频道对讲机，用于接收指挥员指令。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3563, 136, '5.5.2', '跨区拖行开始前指挥员发布指令要求分公司其它全部对讲机暂时保持静默，保证拖机过程中通讯正常无干扰，待跨区拖行飞机结束后发布解除静默指令。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3564, 136, '5.6', '跨区拖行与塔台管制员联系', NULL, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3565, 136, '5.6.1', '所有参加跨区和拖飞机的人员要建立有效的联系，不能失联。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3566, 136, '5.6.2', '与塔台联络要求：与塔台用对讲机或甚高频通话时，必须重复管塔台制员的指令，没听清或指令模糊时必须再次询问，待指令明确后才能进行动作。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3567, 136, '5.6.3', '与塔台通话时必须使用规范通讯语言，并且清晰准确，严禁长时间占用频段。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3568, 136, '5.6.4', '在得到塔台同意后，指挥员开始指挥拖机人员开始按规定路线进行正式跨区拖行飞机，同时报告MCC。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3569, 136, '5.6.5', '拖行过程中由坐在引导车上的指挥员负责与塔台联系，机上人员用VHF频率保持常守。（天津塔台频率：白天：121.95MHZ，晚上：118.20MHZ、现场指挥中心频率121.75MHZ，如有变化，以塔台管制员通知的为准）。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3570, 136, '5.6.6', '拖机进程中必须坚持每个阶段向塔台和报告制度，不得到允许不能进入下个动作，同时要向MCC报告到达位置，尤其在引导车进入跑道前，必须等待申请批准后才能进入跑道，飞机机尾脱离跑道安全线后必须及时通报塔台。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3571, 136, '5.6.7', '拖机过程中飞机必须打开航行灯、防撞灯及LOGO灯。拖行中，跟随拖车人员指挥拖车必须跟随引导车，按滑行线和规定速度拖行飞机。跨区时，引导车要骑线行驶。引导车在进入任意路口转弯时必须使用转向灯。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3572, 136, '5.6.8', '拖行过程中如遇非正常事件，指挥员必须第一时间将事件通报塔台管制员和MCC，等待进一步指令，所有参与拖机人员必须服从指挥员指令，在保障人员、飞机和设备安全的前提下，尽量降低损失和事件的影响。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3573, 136, '5.6.9', '跨区拖行完成后，及时报告MCC。', 9, '2018-06-29 09:26:31', '2018-06-29 09:26:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3574, 137, '5.1', '外委维修项目的申请', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3575, 137, '5.1.1', '对于计划性的需外委维修项目，由生产计划控制分部直接填写《外委项目申请单》Form No.: PM-T03。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3576, 137, '5.1.2', '对于在维修过程中产生需要外委维修的项目，由维修中队填写《外委项目申请单》，经其单位经理、高级工程师或值班经理批准后，报生产计划控制分部。', 9, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3577, 137, '5.2', '外委维修项目的控制\r\n分公司的外委维修项目必须属于外委厂家（维修单位）经CAAC 批准的维修范围，并由国航与外委厂家（维修单位）签署维修合同且包含相关的外委维修项目。', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3578, 137, '5.3', '新增外委项目的申请', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3579, 137, '5.3.1', '维修中队如果需要新增外委承修商和授权项目应填写《外委项目申请单》，经提出申请部门经理签署后将以上文件报生产计划控制分部。', 9, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3580, 137, '5.3.2', '确定维修工作的外委需求后，申请部门配合生产计划控制分部寻找合适的外委厂家，如选择的外委厂家及其能力包括在国航或分公司的供应商清单、外委厂家清单中，生产计划控制分部填写《外委项目送修交接单》Form No.: PM-T04直接送外委厂家进行工作。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3581, 137, '5.3.3', '如选择的外委厂家或需要的能力不包括在国航或分公司的供应商清单、外委厂家清单中，属于新增外委项目，申请部门应先将《外委项目申请单》报航空安全质量分部，航空安全质量分部对该外委项目的适航性进行审核评估，确认该项目能否进行外委工作。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3582, 137, '5.4', '外委项目的审核', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3583, 137, '5.4.1', '航空安全质量分部接到生产计划控制分部的申请后，组织分公司技术支援分部、生产计划控制分部、申请单位（必要时邀请分公司质量部相关审核人员）等相关部门构成审核小组，对外委项目及厂家进行质量审核。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3584, 137, '5.4.2', '质量审核组应对外委供应商的质量系统进行评估并获得相关的证明材料，包括质量管理手册及程序、人员培训状况及资格、工装、设备和厂房设施状况、文件、资料、手册及维修记录管理情况。对于持有适航当局维修许可证的承修商，审核组应评估其维修许可证批准的维修类别及能力清单是否包括分公司将要外委的维修项目。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3585, 137, '5.4.3', '对于非适航当局批准的外委承修商，审核组将安排一次现场审核，由质量审核工程师根据《维修管理手册》第10 章的审核要求进行审核。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3586, 137, '5.5', '工卡和技术标准的使用', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3587, 137, '5.5.1', '原则上由技术支援分部提供工卡，如果分公司提供的工作单内容过于简单，不能体现外委工序的主要施工步骤和施工要求，外委厂家应根据分公司提供的规范编写细化工作单，此工作单要求中英文对照，并能追溯到零件的件号和序号，同时，还应得到技术支援分部的批准。', 4, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3588, 137, '5.5.2', '如外委厂家采用的技术标准与分公司外委项目要求的技术标准不一致，或外委厂家使用的器材、工具设备和施工方法与分公司提供的技术标准不同，技术支援分部（或组织申请部门一起）负责对上述偏离进行等效性评估，并出具等效性评估报告。', 4, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3589, 137, '5.5.3', '将等效替代批准提供给外委厂家，外委厂家在该施工工作单中列明其实际使用的器材、工具设备和施工方法，同时在施工工作单中附分公司的等效替代批准。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3590, 137, '5.6', '审核结果报批', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3591, 137, '5.6.1', '审核结束后，根据审核结果，分公司质量管理部门编制审核报告，对被审核厂家进行评价，并确定是否合格。如果审核合格，质量管理部门将审核结果报公司质量部，与外委厂家签署合同。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3592, 137, '5.6.2', '审核不合格，生产计划控制分部按以上程序重选外委厂家并审核。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3593, 137, '5.7', '分公司外委单位及外委项目清单的更新', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3594, 137, '5.7.1', '生产计划控制分部保留分公司与外委厂家签署合同的复印件。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3595, 137, '5.7.2', '航空安全质量分部根据合同内容，更新外委单位及外委项目清单，报属地适航当局批准后在分公司内部统一发布。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3596, 137, '5.8', '外委维修项目的执行', NULL, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3597, 137, '5.8.1', '经审核批准后的外委维修，生产计划控制分部负责按合同的要求和生产需求情况联系选择好的外委厂家，确定具体的外委维修项目的工期、要求完成时间以及方式等。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3598, 137, '5.8.2', '生产计划控制分部负责外委项目申请单、外委项目送修交接单统一编号和保存。编号按四位年+三位流水号进行编号，如：2016001。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3599, 137, '5.8.3', '送外的外委由生产计划控制分部负责与申请单位填写外委项目送修交接单，申请单位协助送、取外委维修的实物。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3600, 137, '5.8.4', '外委维修项目完成后，航空安全质量分部负责组织对外委产品的合格性、适航性进行验收。', 8, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3601, 137, '5.8.5', '外委项目完成后的证明性文件由生产计划控制分部负责向外委单位接收索取，然后交航空安全质量分部进行保存。', 6, '2018-06-29 09:33:22', '2018-06-29 09:33:22', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3602, 138, '5.1', '值班干部职责/权限', NULL, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3603, 138, '5.1.1', '值班干部是分公司当天生产运行的总负责人，是生产运行、安全管理、综合服务等方面的决策者。', NULL, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3604, 138, '5.1.2', '值班干部应了解分公司安全生产运行的各个环节，熟练掌握突发、紧急情况下的应急处置程序和相关规定，遇有突发情况能够迅速响应并依据《Ameco天津分公司应急处置程序手册》妥善处置，遇有上级布置的重要任务、重要要求和阶段性重要保障，要及时向分公司主要领导汇报处置。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3605, 138, '5.1.3', '值班干部要保持手机畅通，不得远离工作岗位，确保在发生紧急情况下能够及时达到工作现场。在值班期间要深入一线，指导和监督现场各部门的工作，检查员工遵章守纪情况和程序、规章的执行情况，检查工作中是否存在安全隐患，及时协调、处理生产过程中发生的各类重大问题，使各项工作安全有序地进行。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3606, 138, '5.1.4', '当出现飞机大面积延误、备降，或发生重大排故工作等紧急事件时，值班干部应及时到达MCC或生产现场进行指挥，必要时组织召开现场生产会，对于重大突发事件应及时报告高级经理。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3607, 138, '5.1.5', '值班干部要协调处置分公司特殊、突发事件，按程序报告上级领导或对口单位，负责其他需值班干部处置的紧急事务。值班干部有权调动本单位的一切资源处理维修生产中发生的各类问题，有权对当天维修生产中的违规、违章行为按照相关规定直接做出处罚决定。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3608, 138, '5.1.6', 'MCC代表分公司在其职责范围内行使维修生产的计划、组织、指挥和协调工作，遇重大事件或授权范围外的事情及时向值班干部请示，由值班干部协调解决。', 6, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3609, 138, '5.2', '值班干部工作要求', NULL, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3610, 138, '5.2.1', '实行分公司级值班制度，每天值班24小时。值班干部应在每天08:30进行工作交接，并向MCC报到。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3611, 138, '5.2.2', '09:15前值班干部应到达东区MCC调度室，了解当班相关信息，并与MCC值班经理一同参加AMC生产运行电话会及运行会。如不能参加，要通知MCC值班经理。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3612, 138, '5.2.3', '15:00前，MCC值班经理向值班干部汇报当天的生产运行情况，并将次日特殊停机位和跨区滑行申请信息报告给值班干部。', 6, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3613, 138, '5.2.4', '15:30前，值班干部应到达国航天津分公司运行指挥中心，参加国航天津分公司运行会，了解当日公司整体运行情况、接收来自区域内的要客信息、接收包机信息、记录次日快速过站信息及相关要求并于会后通知MCC值班经理。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3614, 138, '5.2.5', '16:00值班干部在指挥中心参加国航股份电视会议，了解相关情况，并于会后及时传达给MCC值班经理及相关人员。', 10, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3615, 138, '5.2.6', '22:00前，值班干部应使用《值班干部安全监察检查单》Form No.: PM-T01R1对生产维修现场进行至少一次安全检查以实施分公司级别的三级安全监察，并将检查结果记录在值班记录本上，并由次日值班干部在分公司内部生产运行会上进行讲评。对检查结果需要进行处罚的，值班干部填写《值班干部检查发现问题通知单》发人力资源分部，由人力资源分部依据《Ameco天津分公司奖惩规定》对责任人或部门进行处罚。对检查结果需要进行整改的，值班干部填写《值班干部检查发现问题通知单》Form No.: PM-T02发航空安全质量分部，由航空安全质量分部下发《预防与纠正通知单》Form No.: SA-T02至责任部门，审核评估责任部门反馈的整改措施，并监督其整改情况。', NULL, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3616, 138, '5.2.7', '22:30前，MCC值班经理应向值班干部详细汇报当天重要工作完成情况、运行情况、不正常事件等信息。值班干部做好值班记录。', 6, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3617, 138, '5.2.8', 'C检完工后，由定检中队C检负责人通过MCC通知值班干部，并由值班干部按照01-08-007《最终检查》组织完成C检飞机的出场检。', 3, '2018-06-29 09:39:57', '2018-06-29 09:39:57', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3618, 139, '5.1', '下发预防与纠正通知单。\r\n针对在安全检查、事件调查工作中发现的问题，由分公司SMS办公室向责任部门下发《预防与纠正通知单》（Form No.: SA-T02），明确整改要求和整改措施反馈时限。', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3619, 139, '5.2', '原因分析。\r\n责任部门收到《预防与纠正通知单》后，针对存在的问题进行根原因分析，重点查找组织和系统管理缺陷。', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3620, 139, '5.3', '措施制定', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3621, 139, '5.3.1', '根据分析结果，确定是否需要制定措施。如果不需要，则向分公司SMS办公室反馈分析结果；如果需要制定措施，则在整改要求规定时间内制定预防和纠正措施。', 10, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3622, 139, '5.3.2', '责任部门根据问题的严重性和可能导致的后果等级来制定预防和纠正措施，确定措施落实的优先级别，并确定措施落实时间，上报分公司SMS办公室。', 10, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3623, 139, '5.3.3', '措施必须具体、有效、可执行和可验证，明确措施完成时限、责任人、预期要达到的目标以及措施复核验证的要求。', 10, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3624, 139, '5.3.4', '分公司SMS办公室负责协调跨部门问题措施的制定。', 8, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3625, 139, '5.3.5', '制定措施应考虑以往安全经验教训，措施可包括：\r\n1）	建立、修改和完善相关手册和工作程序；\r\n2）	开展针对性培训和教育；\r\n3）	改进硬件、软件设备；\r\n4）	增加安全管理投入；\r\n5）	其他。\r\n', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3626, 139, '5.4', '措施审批。\r\nSMS办公室负责预防与纠正措施的有效性评估，对措施的优先级、落实期限进行审核，批准或驳回责任部门上报的措施，并协调跨部门问题措施的制定。', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3627, 139, '5.5', '措施落实。\r\n责任部门按审批后的预防与纠正措施优先级调配资源，制定落实计划，在规定的期限内完成措施的落实工作，如不能按期完成则应在到期前向航空安全质量分部提出申请延期并填报《预防与纠正措施延期申请表》（Form No.: SA-T03）。', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3628, 139, '5.6', '措施监控', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3629, 139, '5.6.1', '措施落实部门在时间期限前，应向SMS办公室报告措施落实情况。', 10, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3630, 139, '5.6.2', 'SMS办公室对逾期未回复结果的责任部门进行督办考核。', 8, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3631, 139, '5.6.3', 'SMS办公室负责对措施进行跟踪监控。', 8, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3632, 139, '5.7', '复核验证', NULL, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3633, 139, '5.7.1', '分公司SMS办公室负责对措施进行复核验证，确认预防和纠正措施落实，并达到预期效果后，关闭此事项。', 8, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3634, 139, '5.7.2', '对复核验证不通过的措施，如措施落实不到位，由责任部门重新组织实施；如措施无效，重新执行5.2-5.7。', 10, '2018-06-29 09:42:02', '2018-06-29 09:42:02', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3635, 140, '5.1', '航空安全责任书分解', NULL, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3636, 140, '5.1.1', '分公司签订航空安全责任书后，由SMS办公室在10个工作日内将航空安全责任书中的考核项目按部门进行分解。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3637, 140, '5.1.2', 'SMS办公室根据安全责任书要求，完善《安全管理过程考核实施细则》。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3638, 140, '5.2', '监控、督办与考核', NULL, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3639, 140, '5.2.1', 'SMS办公室负责按照《安全管理过程考核实施细则》对本单位安全责任书的实现水平进行持续监控，收集、整理各类相关信息，监控安全绩效指标的实现水平，监控安全管理过程专项指标的进展和完成情况，形成并完善本单位安全绩效管理数据库。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3640, 140, '5.2.2', 'SMS办公室在监控过程中，如发现某项工作的进度或质量有可能存在问题，需下发《安全工作督办事项单》（Form No.: SA-T05）对存在的问题进行督办。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3641, 140, '5.2.3', '承办部门应在规定的期限内完成督办任务。', 10, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3642, 140, '5.2.4', 'SMS办公室每月根据过程考核监控结果、各部门上报的安全过程管理信息，完成各部门的安全管理过程考核，经航空安全质量分部经理签批后下发《安全过程考核通知单》（Form No.: SA-T04），并在安全质量月会公布考核结果。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3643, 140, '5.2.5', '各部门在日常监控考核过程中应当按《奖惩细则》对安全管理过程中的作出突出贡献的单位和个人及时提出奖励申请，对各类差错和违章事件按《奖惩细则》要求及时提出处罚申请，以促进安全绩效的实现。', 10, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3644, 140, '5.3', '申诉和复议', NULL, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3645, 140, '5.3.1', '被考核部门如果对考核结果存在异议，在《安全过程考核通知单》发布10个工作日内向SMS办公室提出《航空安全考核申诉单》（Form No.: SA-T06）申诉。', 10, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3646, 140, '5.3.2', 'SMS办公室在接到《航空安全考核申诉单》5个工作日内完成调查和复议，将复议结果通报申诉单位。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3647, 140, '5.3.3', '对于复议结果仍存在异议的事项，由分公司航空安全委员会最终裁决。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3648, 140, '5.4', '考核兑现', NULL, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3649, 140, '5.4.1', '各部门安全绩效的考核结果由SMS办公室发布。', 8, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3650, 140, '5.4.2', '人力资源分部负责根据安全绩效考核结果兑现安全绩效奖。', 1, '2018-06-29 09:43:10', '2018-06-29 09:43:10', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3651, 141, '5.1', 'RVSM日常维护', NULL, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3652, 141, '5.1.1', '在飞机进行RVSM运行过程中，需要监控与RVSM有关的机身结构和系统/部件的检查和修理，使飞机始终保持RVSM能力。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3653, 141, '5.1.2', 'RVSM表面测量或蒙皮曲度检查\n为了确保符合飞机机体制造厂对RVSM允许误差的要求，维护飞机机体几何尺寸以使表面轮廓保持正常，减小高度测量系统的误差。当全静压探头（空速管）周围的蒙皮出现损伤或修理后，需要进行表面测量和蒙皮曲度检查。按飞机维护手册/结构修理手册中的规定进行测试和检查。完成了可能影响机体表面和气流的修理或改装之后也应进行这些检查。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3654, 141, '5.1.3', '快速接头静压管的维护\n每当快速接头静压管脱开，应按照飞机维护手册AMM的要求，重新连接后应进行系统漏气检查。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3655, 141, '5.1.4', 'RVSM设备的维护 \n为保证现有设备的性能满足初始RVSM批准的要求，在对以下系统/设备进行维护工作时，应确保其性能符合飞机制造厂手册的要求：\n1）高度警告；\n2）自动高度控制系统；\n3）ATC应答机；\n4）高度测量系统。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3656, 141, '5.1.5', '自检设备（BITE）的测试\n自检设备（BITE）的测试不能作为一种可接受的系统校验的方式，它只能用于故障隔离或排故。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3657, 141, '5.2', '误差超限报告', NULL, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3658, 141, '5.2.1', '在RVSM环境下，允许的高度保持误差是很小的。如果超出误差范围，维修单位/协议维修单位的技术员和检验员应及时采取措施纠正超出误差允许值的情况。并且还应在72 小时内向国航和/或适航当局报告此事件，还要同时报告对事件原因的初步分析以及防止事件重复出现的措施。应报告和进行调查的误差是：\nTVE大于或等于±300英尺（±90米）\nASE大于或等于±245英尺（±75米）\nAAD大于或等于±300英尺（±90米）\n主高度表之间的误差超过200英尺\n注：ASE是高度测量系统误差的英文缩写，它是以ISA标准地面气压设', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3659, 141, '5.2.2', '报告方式', NULL, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3660, 141, '5.2.2.1', '维护人员发现超出上述误差范围的情况，应立即书面报告MCC。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3661, 141, '5.2.2.2', 'MCC报本航空安全质量分部。', 6, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3662, 141, '5.2.2.3', '由航空安全质量分部填写《缺陷和不适航状况报告》向适航当局及质量部和工程部报告。', 8, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3663, 141, '5.3', '不符合要求的航空器的维护\n已被确认有高度保持性能误差且按照上述要求需进行调查的飞机不应在RVSM空域中运行，直到采取了下列措施：\n故障或失效已经被确认并被隔离。明确机组在航前准备时高度表校准的临界值标准（75英尺，23米），超过该标准时，必须排故后才能实施RVSM运行。当任何飞机被确认ASE误差超过±200英尺时，就应对它采取纠正措施，并确认满足已批准的RVSM的完整性。', 9, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3664, 141, '5.4', '航空安全质量分部的监督。 \n航空安全质量分部监督相关RVSM系统的维护工作。', 8, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3665, 141, '5.5', '维护培训要求\n培训部负责组织工程部及外站机务的关于RVSM机务培训工作，并负责监督相关维修单位完成RVSM机务培训。\n对于有关人员的初始培训和复训的重点是：\n1） 飞机机体几何尺寸方面的检查技术；\n2） 测试设备校验/使用技术；\n3） RVSM批准所引入的特殊文件或程序。', 1, '2018-06-26 08:50:58', '2018-06-26 08:50:58', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3666, 146, '5.1', '航线维修记录\n航线维修记录包括：技术记录本（TLB）、客舱记录本（CLB）、飞机记录本（FLB）和航线维修工作单，及航线排故、执行EO和单控等维修工作产生的所有维修文件。', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3667, 146, '5.2', '技术记录本（TLB）', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3668, 146, '5.2.1', '技术记录本使用说明', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3669, 146, '5.2.1.1', 'TLB是记录飞机航线或其它维修工作中所发现的故障、故障排除、合法保留和航线放行的文件，它主要包括以下内容：\n1）飞机注册号、航班号、日期；\n2）故障描述；\n3）排故措施；\n4）如果换件，填入拆下和装上件的序号；\n5）发动机滑油添加量；\n6）保留故障记录；\n7）机长及维护人员签名。', 8, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3670, 146, '5.2.1.2.1', 'TLB一式五份：\n1）原页（白色）留在CLB中作为单机档案，送航空安全质量分部负责保存；\n2）第二张副页（粉色）送技术支援分部；', 6, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3671, 146, '5.2.1.2.2', '3）第三至第五张副页（黄色）作为维修人员所实施工作以及各种维修检查完工签写的文件记录。在每次实施了维修或勤务工作，由放行人员进行签署，在机长接收签署后，由各相关航站依次撕下一页，与工作单同时保存。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3672, 146, '5.2.1.3', '索赔期内运行的飞机，其TLB应有W标识。航材与采购部天津分部负责制定、更新《整机索赔期内飞机一览表》，并发布在天津分公司论坛-航材管理-信息发布版块，供所有TLB领用人员查询。', 2, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3673, 146, '5.2.1.4', '维修中队在补充TLB时应依据航材公布的信息进行核对，确保索赔期内运行的飞机使用有W标识的TLB。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3674, 146, '5.2.2', '技术记录本（TLB）的使用规定', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3675, 146, '5.2.2.1', 'TLB必须配置在每架飞机的驾驶舱内，由机组及经授权的维修人员负责填写。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3676, 146, '5.2.2.2', '维修工作记录内容要严格按照工作单、记录本前页和垫板的详细填写规范准确填写（其中滑油量数值应估算到小数点后一位；如未加，则填0），填写人员必须在维修工作完成后立即进行填写，并保证填写内容的完整和准确有效。机组和乘务组对发现的影响适航性或运行安全以及旅客舒适性、娱乐性等所有故障缺陷的实际情况应做尽可能详尽的描述。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3677, 146, '5.2.2.3', '为保证字迹清楚，技术记录本必须用圆珠笔填写，并不得随意涂改。如确需修改，应当经授权人签署，将修改的部分划掉并在修改的地方签名或盖章，保证被改动部分清晰可见，予以清楚标识。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3678, 146, '5.2.2.4', '使用人必须保证技术记录本的连续性和完整性，放行人员应检查前面至少48小时内技术记录本的连续性。在任何情况下都不允许记录页的缺失，如发生记录本丢失或记录页丢失的情况，并且其中的内容尚未处理，必须联系该航班的机组和乘务组，在新的记录本或记录页中将其内容添加进去并处理。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3679, 146, '5.2.2.5', '执行国际航班时技术记录本必须用英文填写，由国内维修单位实施维修工作的，还必须填写中文；对于执行国内航班的飞机，应至少使用中文填写（经营性租赁飞机的填写要求按协议执行）。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3680, 146, '5.2.2.6', '在飞机上除正在使用的记录本，同时还要配备未使用的新记录本备用。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3681, 146, '5.2.2.7', '使用完的旧记录本，除B777-300ER机型外，持续保留在飞机上至新记录本使用5个飞行日；对于B777-300ER机型，持续保留在飞机上至新记录本使用30个飞行日。在旧记录本撤下飞机前，旧记录本中的“开项”须转到新记录本上。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3682, 146, '5.2.2.8', '飞机异地运行期间，生产计划控制分部应保证按照本程序规定及时获取记录本相关副页。', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3683, 146, '5.2.2.9', '在一天中的第一次飞行，应当从新的一页开始记录。每栏的填写说明详见“技术记录本”前页的详细填写规范。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3684, 146, '5.2.2.10', '每个航段应填写独立的记录，每栏只填一项，如果位置不够时，可依次占用下面的栏目。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3685, 146, '5.2.2.11', '机组应将所有发生的影响飞机适航性、运行安全性的故障详情尽可能详尽的填入报告栏（REP），写明原因、位置及有关问题。如无故障，在“报告”栏中填入三个字母“NIL”（无）。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3686, 146, '5.2.2.12', '下列飞行事件需由机长或一名机组成员代表机长在TLB中填写，提出要求进行特别检查：\n1）超重着陆；\n2）硬着陆；\n3）在严重颠簸中飞行；\n4）着陆时减速过大或侧向负载过大； \n5）紧急制动；\n6）在有大水或积水的跑道上着陆； \n7）雷击；\n8）鸟击；\n9）在严重沙暴中飞行。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3687, 146, '5.2.2.13', '对外站发生故障的保留，由被授权的放行人员在TLB 上写明故障概况、放行依据及使用限制日期并签署，同时应以书面形式通知运行管理部维修指挥中心（AMC）、飞机执管维修单位。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3688, 146, '5.2.2.14', '在空中/地面故障排除后，各维修单位或外站航线协议维修单位的相应维修人员应在措施栏（ACT）中写明最终排故措施，故障处理栏内各维修单位相应人员及外站航线承修人应做零部件更换等信息记载，TLB 应包括影响飞机适航性和安全运行的任何缺陷及保留状况、要求的维修项目、必要维修工作记录、任何需要机组了解的维修信息。包括有关数据及当在航线上出现问题后进行的技术分析和处理说明，及必要的DD记录。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3689, 146, '5.2.2.15', '对于每项保留故障，维修人员应在措施栏（ACT）中写明放行依据、保留故障的期限、故障最终排除详情以及批准返回使用证明等，同时应书面通知飞机执管维修单位。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3690, 146, '5.2.2.16', '维修人员在填写排除故障/保留故障时应使用下列字母代码系统：\r\n1）代码A－办理“记录本保留”时使用；\r\n2）代码D－办理“保留单保留”时使用；\r\n3）代码H－当该空白项目由于更换更高一级组件而得到排除时，必须填入此代码；\r\n4）代码F－任何项目得到正确排除后，必须填写此代码。', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3691, 146, '5.2.2.17', '航前、过站或各种计划检修工作完成后，应由具有相应资格的维修人员签字并填写执照号码予以确认。\n维修人员在执行航后+航前工作时，在完成维护工作后，被授权的放行人员应在TLB“维护检查栏”内完成航后维护的签署工作，并在下一页“航前/过站检查”栏内完成航前放行签署工作。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3692, 146, '5.2.2.18', '在每次飞行前机长应在特定栏目内签字以接收飞机。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3693, 146, '5.2.2.19', '执行ETOPS飞行，航前及过站工作应由具备ETOPS资格的机械员完成，并在TLB上签字。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3694, 146, '5.2.2.20', 'B747-400飞机不需填写APU时间/循环栏。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3696, 146, '5.3', '客舱记录本', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3697, 146, '5.3.1', '客舱记录本（CLB）', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3698, 146, '5.3.1.1', 'CLB是飞行中客舱内部设备故障的记录文件，它主要包括以下内容：\n1）飞机注册号、航班号；\n2）故障描述；\n3）排故措施；\n4）如果换件，填入拆下和装上件的序号；\n5）保留故障记录；\n6）乘务员及维护人员签名。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3699, 146, '5.3.1.2.1', 'CLB一式五份：\n1）原页（白色）留在CLB中作为单机档案，送航空安全质量分部负责保存；\n2）第二张副页（粉色）送技术支援分部；', 6, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3700, 146, '5.3.1.2.2', '3）第三至第五张副页（黄色）作为维修人员所实施工作以及各种维修检查完工签写的文件记录。在每次实施了维修或勤务工作的飞行前，由各相关航站依次撕下一页，与工作单同时保存。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3701, 146, '5.3.1.3', '索赔期内运行的飞机，其CLB应有W标识。航材与采购部天津分部负责制定、更新《整机索赔期内飞机一览表》，并发布在天津分公司论坛-航材管理-信息发布版块，供所有CLB领用人员查询。', 2, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3702, 146, '5.3.1.4', '维修中队在补充CLB时应依据航材公布的信息进行核对，确保索赔期内运行的飞机使用有W标识的CLB。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3703, 146, '5.3.2', '客舱记录本（CLB）的使用规定\n1）CLB必须配置在每架飞机的驾驶舱内，由乘务长及经授权的维修人员负责填写。\n2）CLB只记录客舱内部和厨房设备的有关故障，所有影响飞机适航性、安全性的故障必须由客舱乘务员报告驾驶舱机组，由机组填写在技术记录本内。\n3）客舱记录本的使用规定同技术记录本的使用规定。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3704, 146, '5.4', '飞行记录本（FLB）', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3705, 146, '5.4.1', 'FLB 是飞机飞行使用数据的记录文件，它主要包括以下内容：\n1）营运人名称、地址、飞机型号、注册号；\n2）起飞、降落的日期、时间和地点；\n3）飞行时间、起落次数；\n4）飞机起飞重量；\n5）燃油总油量、剩余油量、添加油量；\n6）机组成员姓名、编号。', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3706, 146, '5.4.2.1', '1）（白色）保留在记录本中，航空安全质量分部负责按单机档案保存；', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3707, 146, '5.4.2.2', '2）（粉色）送生产计划控制分部', 6, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3708, 146, '5.5', '飞机记录本（包含FLB、TLB、CLB）记录纸丢失后的恢复\r\n1）生产计划控制收到飞机记录本后，检查记录本的完整性。如果发现有缺失的情况，由生产计划控制首先用副页恢复。\r\n（1）对于FLB副页，由生产计划控制直接用所存副页或复印件恢复；\r\n（2）对于TLB、CLB副页，由生产计划控制向技术支援分部提出申请，用技术支援分部提供的副页或复印件恢复。\r\n注：如无副页，由生产计划控制分部填写《维修记录恢复申请单》Form No.: QM-022，随同记录本，交到航空安全质量分部。航空安全质量分部按01-07-003《维修记录管理程序》执行。\r\n2）航空安全质量分部在整理记录本时，如发现整本缺失或缺页的情况，立即通知生产计划控制分部，启动记录本的恢复流程，并在10个工作日内完成。\r\n', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3709, 146, '5.6', '飞机记录本的配发和回收存档', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3710, 146, '5.6.1', '由生产计划控制分部接收工程部下发的空白飞机记录本，并在MCC值班室做备份保存。', 6, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3711, 146, '5.6.2', '工作者执行航后或定检工作时，按需从MCC领取空白记录本配上飞机。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3712, 146, '5.6.3', '使用完的旧记录本由航线工作者随航线工作记录回收交MCC。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3713, 146, '5.6.4', '生产计划控制分部对使用完的旧记录本进行整理，如发现缺本情况应立即进行查找，确保交接给航空安全管理分部存档的每架飞机的记录本是连续的。', 6, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3714, 146, '5.7', '航线维修工作单', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3715, 146, '5.7.1', '航线维修工作单是实施维修工作的依据，用来记录完成的航线维修工作，是飞机维修质量和适航性的证明文件，是飞机单机档案记录的一部分。', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3716, 146, '5.7.2', '航线工作单的格式内容、填写规范及配发、回收等相关管理规定详见07-11-002CCA《航线工作单管理程序》和02-11-003《航线维修文件包的准备和处理》。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3717, 146, '5.7.3', '对于航线工作单中要求打印的航后报告（PFR），需要在打印出签字后订在相应工作单后面一起保存。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3718, 146, '5.8', '航站航线维修记录的管理', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3719, 146, '5.8.1', '签署后航线维修记录的保存', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3720, 146, '5.8.1.1', '航线维修记录：由完成航线维修工作的维修单位、航线协议维修单位保存，签署后的工作单的保存期限为工作完成后30天。', NULL, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3721, 146, '5.8.1.2', '对于含有适航指令工作的工作单或在航线维修中发生的非例行工作维修记录则由生产计划MCC负责联络并协调机组或排故组带回天津，并交于航空安全质量分部（注：不能随同本架飞机当次航班带回）。', 6, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3722, 146, '5.8.1.3', '航空安全质量分部按照TJ-07-01CCA《单机档案与维修记录管理程序》相应的保存要求进行保存。含有适航指令工作的工作单的保存要求保留到飞机退出后一年。', 8, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3723, 146, '5.8.1.4', '在没有营业部或航线协议维修单位的航站，应在维修记录完成签署后由跟班放行人员将签署的维修记录交当地地面勤务代理保存。', 9, '2018-06-29 10:13:24', '2018-06-29 10:13:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3724, 147, '5.1', '飞机备降后相关部门的职责和要求', NULL, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3725, 147, '5.1.1', '当天津分公司执管飞机因天气、故障或其他原因备降在无维修委托协议的机场时，生产计划与控制MCC应将从AMC或天津分公司生产指挥中心所获得的信息立即报告分公司值班领导，并积极协助运行管理部AMC采取各种方法与该机机长取得联系，获得详尽的飞机状况和故障情况报告；同时尽可能与当地的维修单位或地面代理取得联系，并获取联系人的相关信息，了解当地的维修能力、设备情况、航材情况，并协调各方面的力量，以便及时排除故障和放行飞机，保证人员和飞机的安全。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3726, 147, '5.1.2', '如果备降飞机有故障，值班工程师负责制定排故方案，并对飞机最终的维修放行及技术性能负责。', 9, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3727, 147, '5.1.3', 'MCC值班经理负责备降飞机的维修工作计划控制，防止工作超期。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3728, 147, '5.1.4', '对于备降时出现的任何情况，MCC要第一时间上报分公司值班领导、运行管理部AMC、国航天津分公司生产指挥中心。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3729, 147, '5.2', '飞机备降在无相应维修协议的航站时的维修和放行程序', NULL, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3730, 147, '5.2.1', '飞机无故障情况', NULL, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3731, 147, '5.2.1.1', '如果飞机在此备降站过站，则被授权的AMC主控人员代表航空安全与质量管理部授权机长在本备降站做无故障放行。AMC在与机组核实飞机状况正常后，将航空安全与质量管理部批准的《无故障放行授权书》（CCA-QM-1007）传真交机长，机长据此在飞机技术记录本上签字放行，并将《无故障放行授权书》的传真件放入技术记录本。在此过程中，如AMC有需求，MCC应积极配合AMC完成联系机组等相关工作，确保飞机正常放行。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3732, 147, '5.2.1.2', '如果飞机在此备降站过夜，MCC值班经理根据CAMP的关于各例行工作的间隔定义及说明以及临时工作的执行要求，决定是否有立即执行的维修工作项目，并将此决定书面告知AMC。\r\n1）如果不必立即执行：\r\n（1）若国航在此站签有过站协议，由航站完成过站工作，并放行飞机。\r\n（2）若国航在此站不具有过站协议，由被授权的AMC主控人员代表航空安全与质量管理部授权机长做无故障放行。AMC在与机组核实飞机状况正常后，将航空安全与质量管理部批准的《无故障放行授权书》传真交机长，机长据此在飞机技术记录本上签字放行，并将《无故障放行授权书》的传真件放入技术记录本。\r\n（3）MCC值班经理判断是否要安排在最近的有相应维修协议的下一个航站补做例行工作和其他临时工作，如需要则立即安排，并按需向AMC计划工程师申请航班调整。\r\n2）如果必须立即执行：\r\n（1）国航在此站签有过站协议，AMC给予维修人员临时授权，MCC值班经理和值班工程师指导其完成相关维修工作并由值班工程师或航线中队放行人员完成《基地传真放行单》（CCA-QM-1018）的填写，由MCC传真至航站。航站将此放行传真件放入TLB，与相关页订在一起；由于有过站协议的放行人员具有过站和航前的放行资格授权，因此航站执行航前工作并放行。\r\n（2）国航在此站不具有过站协议，MCC值班经理立即报告AMC。AMC按照特殊情况报告值班领导，根据情况决定如何派人执行。\r\n', NULL, '2018-06-29 10:14:26', '2018-06-29 10:15:31', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3733, 147, '5.2.2', '飞机有故障', NULL, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3734, 147, '5.2.2.1', '如果飞机有故障，MCC值班经理通知值班工程师，值班工程师按照TJ-11-001CCA《保留故障办理程序》的基本原则判断是否可以对故障进行保留，并与机长联系确认是否可以保留。如果可以保留，由值班工程师或航线中队放行人员填写《保留故障放行单》（CCA-QM-1008），由MCC传真至航站交机长，机长据此在飞机技术记录本上签字放行，并将《保留故障放行单》传真件放入TLB。《保留故障放行单》由执管分公司的航空安全质量分部控制、签字/盖章。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3735, 147, '5.2.2.2', '如果飞机故障不能够保留，则MCC值班经理协助AMC确认当地是否有排故能力。如果有能力，则按临时授权程序授权当地航站排除故障，并放行飞机。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3736, 147, '5.2.2.3', '如果飞机有故障，故障不能够保留，且当地无排故能力，则MCC值班经理立刻通知AMC、分公司值班领导、主管生产的分公司副总经理。MCC负责组织，航线中队、定检中队积极配合安排人员赴航站排除故障，放行飞机。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3737, 147, '5.3', '临时授权程序\n维修人员和放行人员临时授权由被授权的AMC主控人员代表航空安全与质量管理部来执行。临时工作授权由MCC值班经理签发《临时工作授权书》，该授权书由航空安全质量分部控制、盖章。临时授权应与相应工卡一起保存。', 6, '2018-06-29 10:14:26', '2018-06-29 10:14:26', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3738, 148, '5.1', '手册/插页接收', NULL, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3739, 148, '5.1.1', '手册来源\n1）国航股份运行类手册：国航天津分公司运行质量部。\n2）公司级手册：航空安全与质量管理部。', NULL, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3740, 148, '5.1.2', '接收确认和发布', NULL, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3741, 148, '5.1.2.1', '纸张版国航股份运行类手册：由分公司向属地分公司运行质量管理部领取，确认后在《资料发送单》上签字，《资料发送单》至少保存一年。\n对于需在飞机上配备的国航股份运行类手册按照01-07-001《适航性维修资料的管理程序》中机上资料配备和更新要求将手册/插页配上飞机。', 4, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3742, 148, '5.1.2.2', '电子版国航运行类手册和公司级手册：接收手册改版通知并以书面形式发布。', 8, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3743, 148, '5.2', '电子版手册的接近方式', NULL, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3744, 148, '5.2.1', '电子版国航股份运行类手册接近方式：国航股份内网（http://intranet.airchina.com.cn/PortalProject/appmanager/intraCA/indexCA／应用导航／运行标准部／运行手册）。', 10, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3745, 148, '5.2.2', '电子版公司级、分公司级手册接近方式：管理手册网的“管理手册”模块下的“手册发放管理”中。（网址为：http://pekamsapp08.ameco.com.cn:8090/QUManual/TManualAction/getIndexManual.do?language=0&manualpacksort=1）。', 10, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3746, 148, '5.2.3', '电子版分公司级手册接近方式：分公司内网（管理文件/有效的质量手册/手册）', 10, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3747, 148, '5.3', '学习和执行', NULL, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3748, 148, '5.3.1', '分公司收到手册或手册改版通知后，航空安全质量分部填写《培训需求申请表》发给人力资源分部。', 8, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3749, 148, '5.3.2', '人力资源分部按要求组织相关人员进行培训，培训可采取集中学习或自学形式，并按要求反馈培训记录，培训记录至少保存两年。', 1, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3750, 148, '5.3.3', '各部门因出差、休假等原因未能按时进行培训的人员回岗后应及时予以培训，并补报培训记录至人力资源分部。', 10, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3751, 148, '5.4', '意见反馈和解释说明', NULL, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3752, 148, '5.4.1', '在手册学习和执行过程中，如发现编写错误或有何意见，分公司任何人均有责任和义务提出，填写《手册/程序意见反馈单》（Form No.: QM-001）报航空安全质量分部。', 10, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3753, 148, '5.4.2', '航空安全质量分部审核填写的《手册/程序意见反馈单》（Form No.: QM-001）后，向公司航空安全与质量管理部反馈。', 8, '2018-06-29 10:19:13', '2018-06-29 10:19:13', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3754, 149, '5.1', '装机附件的控制与管理', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3755, 149, '5.1.1', '建立飞机动态装机清册', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3756, 149, '5.1.1.1', '在新飞机交付国航后，航材附控部门依据装机清册在10个工作日内完成数据整理并依据该机型CAMP时控部分的内容逐条核实时控件数据，如有与CAMP不一致情况，及时下发《装机附件检查单》CCA-LD-025与实物进行核实，将整理完成的数据提交航材部附件控制项目。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3757, 149, '5.1.1.2', '若提交数据尚未完成导入，新飞机已发生附件拆换，相应航材管理航材附控部门应在1个工作日内报航材部附件控制科，航材部附件控制科依据《在册机队信息表》，在2个工作日内完成新飞机机号层级功能位置的创建，并反馈相应分公司航材附控部门在航材管理系统中完成附件拆装工作。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3758, 149, '5.1.2', '维修过程中附件的拆换', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3759, 149, '5.1.2.1', '维修中队在完成飞机附件更换后，正确填写《附件标识挂签》（CCA-QM-073），并将拆下件连同《附件标识挂签》后两联一起退到航材库房（可用件退验收库房，不可用件退待修库房），航材库收料人员检查《附件标识挂签》与拆下件的一致性，完成收料后，将《附件标识挂签》第二联交至分公司航材附控部门。\r\n救生衣类器材剩余时限不足一年不允许串件。', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3760, 149, '5.1.2.2', '分公司航材附控部门依据《附件标识挂签》，在航材管理系统中完成附件的拆卸、安装操作，若是时控件，到期拆下注明维修类别，未到期注明到期时限。航材管理系统自动运行，计算出附件使用时间或循环等，为附件的送修、索赔、销售提供依据。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3761, 149, '5.1.2.3', '对于维修生产过程中出现的补装飞机缺件或加装附件等情况，维修中队应在装上附件的《附件标识挂签》上注明安装的机号、位置、日期等，并在3个工作日内将《附件标识挂签》交到航材管理，航材附控部门在收到挂签后2个工作日内在航材管理系统中完成附件安装操作。', 9, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3762, 149, '5.1.3', '在异地更换附件后的信息传递和持续控制', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3763, 149, '5.1.3.1', '当分公司执管飞机在其它分公司故障，装机使用其它分公司器材时，提供器材的分公司将附件信息传递至飞机执管分公司，飞机执管分公司航材附控部门完成航材管理系统中的附件拆卸、安装工作，并进行控制。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3764, 149, '5.1.3.2', '当拆下件带回原执管分公司时：                         \n执管分公司附控部门按照《附件标识挂签》在航材管理系统中进行拆卸、安装或单拆、单装的操作，若是时控件，到期拆下注明维修类别，未到期注明到期时限。航材管理系统自动运行，计算出附件使用时间或循环等，为附件的送修、索赔、销售提供依据。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3765, 149, '5.1.3.3', '当拆下件留在提供可用附件维修分公司时：               \n提供可用附件维修分公司的航材与采购部天津分部附控部门将发生拆换器材的《附件标识挂签》，在1个工作日内传递给附件装上飞机的执管分公司。执管维修分公司航材与采购部天津分部附件控制部门根据收到的《附件标识挂签》，在航材管理系统中进行拆卸、安装或单拆、单装的操作，若是时控件，到期拆下注明维修类别，未到期注明到期时限。航材管理系统自动运行，计算出附件使用时间或循环等，为附件的送修、索赔、销售提供依据。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3766, 149, '5.1.4', '装机附件的检查', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3767, 149, '5.1.4.1', '当装机附件的件号、序号、装机位置等信息与航材管理系统记录不一致时，或在CAMP改版中附件控制部分有变更（包含条目的新增、件号新增，维修时限延长或缩短或工作类别的变化）时，航材部附控部门可根据实际业务制定并下发《装机附件检查表》（CCA-LD-025），下发至生产计划控制分部。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3768, 149, '5.1.4.2', '生产计划控制分部安排实施检查工作，并将完成的检查表原件退交航材部附控部门。', 6, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3769, 149, '5.1.4.3', '附控部门依据检查结果在航材管理系统中更新和完善附件控制记录。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3770, 149, '5.2', '出售或退租飞机的附件管理\n对于出售或退租的飞机，收到航空安全与质量管理部转发的《民用航空器注销登记函》后，航材部附件控制项目将在22个工作日内向国航信息管理部提交批量删除数据申请，在此期间分公司需要完成出售或退租飞机的所有装机附件安装拆卸工作。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3771, 149, '5.3', '时控件的控制管理', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3772, 149, '5.3.1', '航材附控部门严格按CAMP（或等效的工程部业务通告）规定的时限，在航材管理系统中对时控件进行控制。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3773, 149, '5.3.2', '当接到CAMP改版（或等效的工程部业务通告）时，航材附控部门须核实本分公司在航材管理系统中管理的附件与CAMP条目变更的一致性，防止超期或不一致项目出现，如有不一致信息，在3个工作日内将信息反馈给航材附件控制人员。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3774, 149, '5.3.3', '航材附控部门如发现CAMP内容与航材管理系统中的数据存在差异时，应在三个工作日内填写《维修方案修改申请表》（CCA-LD-042）报工程部和航材部附控项目。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3775, 149, '5.3.4', '装机时控件的控制管理  \r\n时控附件按机型、机号、章节、件号、序号管理，根据其拆换、维修记录实施动态监控，分公司附控部门每日在航材管理系统中对当天装机时控件信息进行核实，以确保该附件的时寿控制和使用履历的可追溯性，其中包括装机和拆换日期，拆换原因，飞机注册号及位置（包含在库位置），总使用时间（TT），大修后时间（TSO），检修后时间（TST）等。', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3776, 149, '5.3.5', '航材附控部门根据CAMP的规定，对已装机的时控件，在其到时限前，附控人员须提前600飞行小时或60天或300飞行循环，通过航材管理系统打印《附件拆换指令单》（CCO）。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3777, 149, '5.3.6', '《附件拆换指令单》（CCA-LD-023）一式四联，第四联附控部门自留备查，其它三联交航材计划部门确认有可用器材并签字，航材计划部门留存第三联，在一周内将第一、二联反馈附控部门，附控部门将第一、二联下发至分公司计划控制部门。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3778, 149, '5.3.7', '生产计划控制分部必须在指令单限定的时间内安排完成到时限附件的更换，并检查确认指令单签署完整后，自留第二联存档，将第一联在完工后3个工作日反馈给分公司航材附控部门。', 6, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3779, 149, '5.3.8', '如有特殊情况不能按期完成指令时，生产计划控制分部按《工程手册》中4.2.7的要求，办理延期手续，并通知航材管理。航材附控部门接到延期批准后，附控人员对延期批准文件涉及的附件严密监控，生产计划控制分部应在延期批准文件要求的时间内安排维修中队完成拆件并将签署完成的指令单反馈附控部门。附控部门根据反馈单闭合相关记录。', 6, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3780, 149, '5.4', '库存时控件的控制与管理', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3781, 149, '5.4.1', '根据CAMP的规定，对有库存时限的时控件，在其到时限前，航材附控部门须提前28天；对库存期限为30天的时控件，在其到时限前，航材附控部门须提前7天，均通过航材管理系统打印《器材移库通知单》（CCA-LD-024）下发到航材计划部门。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3782, 149, '5.4.2', '航材计划部门根据《器材移库通知单》的内容，如果是到期出库报废，执行01-05-011《器材的报废》。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3783, 149, '5.4.3', '如果是出库送修校验，航材计划部门依据备件及生产需求状况制定器材出库计划。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3784, 149, '5.4.4', '对于在库/在机累计时限的器材，航材计划部门在收到《器材移库通知单》的3个工作日内，通过航材管理系统打印《器材移库指令单》（CCA-LD-027）发至航材库房。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3785, 149, '5.4.5', '对于只计算在库时限的器材，至少在库存的时控件到期前2个工作日，将器材《器材移库指令单》发至航材库房。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3786, 149, '5.4.6', '航材库房人员接到《器材移库指令单》后，须在要求的时间内办理器材出库手续，防止超寿、超时限的附件被领出装机，并将器材移库信息反馈航材计划和附控部门。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3787, 149, '5.5', '附件使用记录的修正', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3788, 149, '5.5.1', '租出航材原则上应归还原件，特殊情况下由双方协商可进行交换或销售。航材租借控制员须确保归还的航材技术文件和适航证件、附件控制信息（使用证明等）满足相关管理规定。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3789, 149, '5.5.2', '租出归还件经检验合格后，附件控制人员依据航材适航证件、附件控制信息（使用证明等）在航材管理系统中修正附件的使用记录。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3790, 149, '5.5.3', '对于采购使用过的附件，航材计划员应在订货前确认供货商可以提供有助于最终确定附件适航性的履历信息，经检验合格后，附件控制人员依据航材适航证件、附件控制信息等在航材管理系统中维护附件的使用记录。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3791, 149, '5.6', '备用发动机/APU的附件控制。\r\n航材附控部门在接到所保管期间备发附件串件信息或附件恢复等信息后，在2个工作日内完成航材管理系统内附件拆装工作。', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3792, 149, '5.7', '发动机/APU梯次拆下。\r\n航材附控部门在接到梯次拆下的发动机/APU后，依据生产计划控制分部提供的发动机串件单，于2个工作日内在航材管理系统中更新维护梯次拆下的发动机/APU随发附件的装机履历。', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3793, 149, '5.8', '租赁发动机/APU与出租发动机/APU附件的管理', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3794, 149, '5.8.1', '租入发动机/APU附件的管理。\r\n租发使用分公司或协议维修单位负责完成租赁发动机的物理验收与技术文件的验收。验收完成后将技术文件中发动机/APU附件清单与发动机附件使用时间等文件交航材管理附控部门，并进行持续控制。航材管理附控部门要确保在租发使用期间时控件信息的准确，满足国航CAMP的要求。如发现有差异问题，航材管理附控部门将情况反馈航部材附控项目，航材部附控项目转航材部战略采购项目，由航材部战略采购项目统一协调。', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3795, 149, '5.8.2', '出租还回后发动机/APU附件的管理。\r\n出租发动机/APU还回后原则上都要送厂进行退租检查。航材管理附控部门在接到所保管期间备发附件串件信息或附件恢复等信息后，在2个工作日内完成航材管理系统内附件拆装工作。', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3796, 149, '5.9', '各机型CAMP中救生衣的控制                          \n装机救生衣的控制，在CAMP第一部分，由生产计划控制分部纳入定检控制，以保障装机救生衣时寿大于一年；库存救生衣的控制，由航材库房管理人员，每月查看系统，以保障库存救生衣时寿大于一年。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3797, 149, '5.10', '在位附件改装件号升级和序号变更', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3798, 149, '5.10.1', '每架飞机的EO完成后：                                \n如有在位改装引起装机附件、序号的变化，生产计划控制分部应在收到实施改装的工作单（明确写有改装前件号、序号及改装后件号、序号）后3个工作日内将复印件/扫描件反馈到航材管理附控部门。', 6, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3799, 149, '5.10.2', '航材管理附控部门在2个工作日内更正航材管理系统中在控装机附件相应的件号和序号信息；并将实施改装的工作单复印件进行存档，如有必要上传至航材管理系统中。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3800, 149, '5.11', '串件后的管理', NULL, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3801, 149, '5.11.1', '维修人员依据批准的《串件单》完成串件工作并将件、序号等串件必要信息填写完整，把《串件单》交还给生产计划控制分部或航材AOG部门；生产计划控制分部或航材AOG部门清点《串件单》的编号，将所有《串件单》单独归类，将《串件单》复印件及时交给MCC。', 9, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3802, 149, '5.11.2', '生产计划控制分部清点《串件单》的编号，每日发布串件单汇总单，将所有《串件单》复印件/扫描件及时交给航材与采购部天津分部。', 6, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3803, 149, '5.11.3', '附控部门依据串件单上提供的机号等信息，在2个工作日内将串件单在系统中完成串件操作。每周与生产计划控制分部发布的串件单汇总单进行核对，如未收到应及时索要。', 2, '2018-06-29 10:39:54', '2018-06-29 10:39:54', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3804, 150, '5.1', '工程指令/工程通告（EO/EB）的索赔', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3805, 150, '5.1.1', '航材与采购部天津分部索赔人员收到EO/EB后，建立索赔EO/EB的控制档案并对其进行跟踪、控制，由其专职索赔人员进行跟踪、处理和申办索赔。', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3806, 150, '5.1.2', '维修中队在执行检查性EO/EB中发现问题，应及时报告技术支援分部。', 9, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3807, 150, '5.1.3', '对需要修理、增订器材和施工工时超出EO/EB范围，而该飞机又在担保期内的情况，技术支援分部应及时填写《国航机群索赔信息通知单》（CCA-LD-021）通知航材与采购部天津分部。', 4, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3808, 150, '5.2', '新飞机的索赔\n在新飞机交付后，技术支援分部收集随新飞机带来的故障和缺陷，并及时交给分公司的专职索赔人员，由其申请办理索赔。', 4, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3809, 150, '5.3', 'C检的一次性补偿的索赔\n在接收到新引进的飞机后，索赔人员应向波音公司申请波音飞机在担保期内执行的每个C检的补偿。', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3810, 150, '5.4', '飞机故障的索赔\r\n新飞机的整机担保期限，4年期的有：737NG系列，在此期间发生的因产品自身质量问题的故障缺陷，并为此所进行纠正的工时和器材都应索赔，其返还率为100%。\r\n飞机服务寿命政策（SLP）的担保期为12年（指结构件），在4年（或3年）整机担保期内的索赔返还率为100%，超过整机担保期之后的12年之内仅为器材的索赔，其索赔返还率将逐年递减。\r\n根据飞机生产厂家的担保索赔政策，在规定的担保期内因设计、材料、制造工艺、技术规范和标准等导致的重大的、重复性的和主结构方面的，而索赔SB/SL中又没有提到的故障和缺陷，经改装和修理后都要向飞机生产厂家提出工时和器材的索赔。\r\n', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3811, 150, '5.4.1', '在飞机的检查、维修、大修和改装过程中，技术支援分部如发现故障所做的维修工作与担保/索赔有关，应将索赔的故障信息提供给航材与采购部天津分部索赔人员。', 4, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3812, 150, '5.4.2', '在飞机的航线检修和定检维修中，一旦发现较大的故障缺陷时（例如：随新飞机带来的故障缺陷、飞机外表面油漆明显的起皮、剥落和掉漆（一年内）；担保期内的起落架封圈更换、燃油箱漏油的修补、主结构件故障、材料和工艺缺陷导致的飞机结构腐蚀、复合材料裂纹、较大系统性和重复性故障及重大故障试飞的燃油和起降费等），技术支援分部应将故障信息、排故工时和器材及时报告航材与采购部天津分部索赔人员。', 4, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3813, 150, '5.4.3', '技术支援分部应对重大和重复性故障进行可靠性分析和评估，及时通报给飞机、发动机/附件生产厂家或驻厂代表，寻求技术支援，询问能否免费提供器材和工时索赔等。并将分析评估报告、与生产厂家来往的（电子）信函提供给航材与采购部天津分部索赔人员。', 4, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3814, 150, '5.5', '发动机的索赔', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3815, 150, '5.5.1', '在执管飞机装机发动机上执行的检查和改装，由航材与采购部天津分部的索赔人员申请办理索赔。在特殊情况下，可收集数据后报航材部索赔业务科一起申请办理索赔。', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3816, 150, '5.5.2', '发动机故障的索赔:\r\n在发动机检查和维修中发现的故障缺陷、各种报废件、空中停车、喘振、终止起飞、异地故障换发、非包容性等方面的故障，技术支援应填写《国航机群索赔信息通知单》（CCA-LD-021）交给航材与采购部天津分部索赔人员。', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3817, 150, '5.6', '航材的索赔', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3818, 150, '5.6.1', '航材采购的索赔', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3819, 150, '5.6.2', '航材送修的保修索赔', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3820, 150, '5.7', '特殊项目索赔', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3821, 150, '5.7.1', '协议项目索赔。\r\n航材与采购部天津分部对单个协议项目的索赔（例如：飞机轮胎和刹车毂、客舱和厨房设备，及BFE协议等的索赔）。', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3822, 150, '5.7.2', '经营性租赁索赔。\r\n航材与采购部天津分部开展对经营性租赁飞机担保项目的索赔，及配合有关部门开展对经营性租赁飞机、发动机和较大部件的大修基金的索赔。', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3823, 150, '5.7.3', '飞机、发动机和重大部件外送修理的索赔委托。 \r\n对于飞机C检、D检、发动机、APU及起落架等大件外送进行修理或翻修时，都有可能存在担保/索赔的项目，主谈协议部门负责人在签署送修和/或转包协议时，应将索赔委托的条款内容加入到送修协议中，索赔收益归国航。', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3824, 150, '5.7.4', '延期项目的索赔', NULL, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3825, 150, '5.7.4.1', '对于有时间限制的改装项目，由于某种原因要保留到下一次停场中执行修理，但下一次停场有可能要超过担保/索赔期，技术支援分部应提前向生产厂家提出书面报告，并将书面报告及其厂家回复通知索赔人员。', 4, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3826, 150, '5.7.4.2', '因某些特殊原因无法提前预见的功能性故障项目（确实属于部件本身的质量问题），即使该项目超出了担保期，技术支援分部也应向生产厂家提出说明，尽可能争取公司的利益。', 2, '2018-06-29 10:55:36', '2018-06-29 10:55:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3827, 151, '5.1', 'AOG缺料（包括DD）信息的获得：\r\n分公司生产计划控制分部MCC值班员或维修中队以书面（或电话）形式将飞机AOG缺料的详细相关信息（包括飞机号、地点、等级、缺料件号及替代件号、缺料数量、需求时间等）通知航材AOG值班员。', NULL, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3828, 151, '5.2', 'AOG缺料解决方案的制定和实施', NULL, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3829, 151, '5.2.1', 'AOG值班员根据AOG缺料信息，首先在国航集团内部协调、落实有无可用的资源，并评估运用集团资源解决缺料所需的时间是否满足生产需求。', 2, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3830, 151, '5.2.2', '当集团内部资源不能够经济、及时地解决AOG需求时，AOG值班员根据AOG缺料信息协调、落实从国航集团外部本着先近后远、快捷高效、先免费后付费的原则寻找解决AOG缺料的资源。确保航材能满足生产需求。', 2, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3831, 151, '5.2.3', 'AOG值班员负责监控AOG缺料的解决进度，在最终器材到位后，通知分公司MCC协调领用。', 2, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3832, 151, '5.2.4', 'AOG值班员负责在AOG缺料解决后，将缺料信息交航材相关部门，并对后续工作进行监控和处理。', 9, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3833, 151, '5.2.5', '对于紧急租赁的器材，AOG值班员将信息通知负责租赁的计划员，计划员对在装租借器材做好清查统计，监控所需器材到货计划，并根据实际到货情况，尽早发布《拆件单》TJ-LD-001给生产MCC，注明时限。', 2, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3834, 151, '5.3', 'DD的延期。\r\n因航材等原因，需对DD进行延期申请，航材与采购部天津分部向生产计划控制分部提出《保留故障延期申请单》延期申请。', NULL, '2018-06-29 11:01:03', '2018-06-29 11:01:03', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3835, 152, '5.1', 'EO的器材管理', NULL, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3836, 152, '5.1.1', '生产计划控制分部将EO/MRO挂在分公司内部网信息共享中，将Form No.: MM-T01《维修指令器材设备需求单》发航材与采购部天津分部。', 6, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3837, 152, '5.1.2', '航材与采购部天津分部应与分公司生产计划控制分部对接，根据维修生产计划完成EO/MRO所需器材的准备，并保证在生产计划日期器材到位，如果器材在计划执行日期无法到货的，必须在开工至少5个工作日前正式反馈生产，说明原因和预计到货日期。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3838, 152, '5.1.3', '航材计划员应根据改装器材包中包含高价件的信息，在SAP系统中建立改装包高价件器材物料BOM。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3839, 152, '5.2', 'CEO-S的器材管理', NULL, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3840, 152, '5.2.1', '航材与采购部天津分部接收航材部下发的CEO-S。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3841, 152, '5.2.2', '航材与采购部天津分部收到CEO-S后，按照CEO-S的要求进行控制。航材管理计划员负责依据CEO-S的要求在SAP系统中建立准确的改装合同，跟踪改装合同的进度。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3842, 152, '5.2.3', '对于无截止时限要求的CEO-S，航材计划员按照CEO-S的要求或结合器材故障送修完成器材的改装工作。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3843, 152, '5.2.4', '对于有截止时限要求的CEO-S，航材计划员应结合生产保障情况，梯次完成备件及装机器材的改装工作。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3844, 152, '5.2.5', '航材计划员接到CEO-S文件后需仔细阅读，了解器材改装具体要求。在器材改装开始执行后，航材计划员应通过SAP系统中的EOEB监控平台查询CEO-S报表，全程跟踪改装进度。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3845, 152, '5.2.6', '当航材计划员收到厂家建议执行的服务通告等改装要求时，应及时填写《改装申请单》（随附厂家提供的服务通告等改装文件）发给航材部计划与调配部门负责EO控制的主管计划员，由其发给工程部进行评估。工程部要在5个工作日内将评估意见通过《改装申请单》反馈航材部，由负责EO控制的主管计划员发给分公司航材计划员反馈厂家，同时发给航材部主管计划员。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3846, 152, '5.2.7', '在某些特殊情况下，厂家在未得到航材与采购部天津分部的批准而执行了某项改装工作，当器材返回后，航材计划员应及时填写《改装申请单》（随附厂家提供的服务通告等改装文件）发给航材部计划与调配部门负责EO控制的主管计划员，由其发给工程部门进行评估。工程部门要在5个工作日内将评估意见通过《改装申请单》反馈航材部，由航材部计划与调配部门负责EO控制的主管计划员发给分公司航材计划员处理差异，同时发给航材部主管计划员。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3847, 152, '5.2.8', '航材管理应严格执行CEO-S中的索赔条款。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3848, 152, '5.3', '禁装类MRO的器材管理', NULL, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3849, 152, '5.3.1', '航材与采购部天津分部根据SAP对器材发料/采购的提示，实施禁装器材的禁发、禁采管理。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3850, 152, '5.3.2', '特殊情况下，维修中队要求领用有禁装提示的器材时，需由技术支援分部的部门经理评估批准后，实施器材发料。', 4, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3851, 152, '5.3.3', '航材与采购部天津分部应确认分公司是否有涉及的禁装器材，并根据MRO指令要求对禁装器材做后续处置。', 2, '2018-06-29 11:08:32', '2018-06-29 11:08:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3852, 153, '5.1', '串件原则', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3853, 153, '5.1.1', '在库房缺少备件的情况下，为防止飞机处于紧急停场状态，可采用从飞机、发动机或其他部件串件方式解决。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3854, 153, '5.1.2', '主、次级飞行操纵系统的部件，水平安定面球形螺杆，主级飞行操纵作动器，发动机，起落架的对换应有额外限制条件且不能超出标准，并要得到相关技术人员的同意。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3855, 153, '5.1.3', '如对部件的互换性存有疑问，件号（包括）尾号及此件的改装情况都应在可用件串件前得到核实。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3856, 153, '5.1.4', '从任一飞机、备发、APU、起落架附件上串件到另一飞机、备发、APU、起落架附件上都应获得相关单位批准，并使用串件单，以保证对零件的时寿控制、AD指令和改装状态的跟踪。严禁对那些没有在维护手册中有描述的安装或拆卸的零件进行串件。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3857, 153, '5.1.5', '对于已确认因其它原因造成的故障件或不可用件必须严格禁止在飞机、发动机或其附件上进行串件或安装。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3858, 153, '5.1.6', '维修人员依据AMM、或支持的指令工卡完成航线串件工作后须在所涉及飞机的TLB或其他相关技术文件上记录，并注明件号、序号。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3859, 153, '5.1.7', '生产计划控制分部在下达串件指令时视情通知航材AOG部门，同时通知串件单位编写相应非例行工作单NR，以指令的形式，由MCC发出、回收。NR内容包括但不限于，所需串件的机号、件号、序号，技术依据，拆装步骤，测试标准等。\r\nMCC负责安排、落实、监控拆、串件工作。无论同机串件或不同飞机串件，按需由工程师编写NR，以指令的形式，由MCC发出、回收。NR内容包括但不限于，所需串件的机号、件号、序号，技术依据，拆装步骤，测试标准等。', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3860, 153, '5.1.8', '救生衣类器材剩余时限不足1年不允许串件。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3861, 153, '5.2', '串件单的管理', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3862, 153, '5.2.1', '串件单的使用范围。维修人员在对有序号器材进行串件时要填写《串件单》（CCA-LD-043R2）。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3863, 153, '5.2.2', '《串件单》的编号及领取。分公司对串件单进行统一编号，且保证此编号具有唯一性。维修人员从MCC领取《串件单》。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3864, 153, '5.2.3', '《串件单》的填写。维修人员填写《串件单》，须经MCC值班经理、车间值班经理批准（上述人员之一批准即可，紧急情况可口头请示后，补办手续）；如果航线、定检两个部门进行交叉串件，必须由MCC值班经理进行批准。维修人员负责串件单技术信息的准确性，批准人审核《串件单》填写的完整性。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3865, 153, '5.2.4', '《串件单》的反馈', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3866, 153, '5.2.4.1', '维修人员依据批准的《串件单》完成串件工作并将件、序号等串件必要信息填写完整，当日把《串件单》交到MCC。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3867, 153, '5.2.4.2', '生产计划控制分部次日将《串件单》交给航材与采购部天津分部。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3868, 153, '5.2.5', '《串件单》的记录和追踪 。航材与采购部天津分部须对《串件单》进行记录和跟踪。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3869, 153, '5.2.5.1', '航材与采购部天津分部附控人员依据《串件单》对SAP完成系统维护。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3870, 153, '5.2.5.2', '航材AOG人员或航材计划人员完成缺件准备,准备完成后将信息反馈生产计划控制分部MCC。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3871, 153, '5.2.5.3', '生产计划控制分部MCC对《串件单》进行记录，收到相应非例行工作单和航材到货反馈后安排后续工作。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3872, 153, '5.3', '同机串件', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3873, 153, '5.3.1', '同机串件不需要批准，维修人员在串件完成后，应到MCC填写《串件单》。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3874, 153, '5.3.2', '生产计划控制分部在一个工作日内将串件单传递给航材与采购部天津分部，附控人员依据串件单上的信息在航材管理系统中完成串件操作。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3875, 153, '5.4', '内部串件。凡是内部串件工作，按照本程序5.2.2-5.3.2执行。', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3876, 153, '5.5', '外部串件', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3877, 153, '5.5.1', '执管飞机需串件', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3878, 153, '5.5.1.1', '当库存、调拨、租赁、采购和本分公司停场飞机串件均不能满足飞机需求时，维修中队负责确认所需器材的件号、替代件号并填写无料单发航材。涉及外部串件工作时，不得自行决定。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3879, 153, '5.5.1.2', '航材与采购部天津分部向航材部AOG求援并书面提供所需器材的适用件号，AOG负责协调有关部门确定可拆、串飞机的批复。MCC负责与AMC进行串件前的沟通。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3880, 153, '5.5.1.3', '航材与采购部天津分部接到航材部AOG可以在分公司停厂飞机串件通知后，填写《串件申请单》报航材部AOG。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3881, 153, '5.5.1.4', '航材与采购部天津分部接到航材部AOG批准后《串件申请单》后，书面反馈MCC值班经理。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3882, 153, '5.5.1.5', 'MCC负责安排、落实、监控拆、串件工作。无论同机串件或不同飞机串件，按需由工程师编写NR，以指令的形式，由MCC发出、回收。NR内容包括但不限于，所需串件的机号、件号、序号，技术依据，拆装步骤，测试标准等。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3883, 153, '5.5.1.6', '航材与采购部天津分部负责附控信息跟踪，负责组织和跟踪器材的到货情况和还件情况，如涉及我分公司后续处理的器材，到货后要立即反馈MCC值班经理。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3884, 153, '5.5.1.7', 'MCC按需配合落实器材到货的后续恢复工作。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3885, 153, '5.5.1.8', '如需异地运输，航材与采购部天津分部负责组织运输。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3886, 153, '5.5.2', '非执管飞机需串件', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3887, 153, '5.5.2.1', '不得自行决定串件，须由执管方提出串件申请，并报AMC和AOG审批。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3888, 153, '5.5.2.2', 'MCC接收并组织执行AMC的串件指令。', 6, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3889, 153, '5.5.2.3', '各部门落实相关工作。', 10, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3890, 153, '5.5.3', '器材运输的通用规定\r\n1）本管理程序适用于航线运行飞机缺件及排故器材的运输。\r\n2）执管飞机外部串件时，必须通过航材与采购部完成器材寻找和运输工作。\r\n3）航材管与采购部天津分部在无法完成器材的寻找和运输工作时，必须通知航材与采购部AOG，由航材与采购部AOG利用有效资源协助完成器材的寻找和运输工作。\r\n4）作为器材的提供方时，航材与采购部天津分部负责准备好器材及可用证明并负责办理运输手续，且必须跟踪器材的发运信息并及时提供给需求方的航材与采购部和航材与采购部AOG。\r\n5）作为器材需求方时，航材与采购部天津分部负责全程监控器材的运输及完成接收工作，并将接收后的可用器材转交生产计划控制分部，生产计划控制分部完成器材的安装和飞机放行工作。\r\n6）垫付的费用，按照Ameco的相关管理规定处理。\r\n', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3891, 153, '5.6', '串件后的附件控制', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3892, 153, '5.6.1', '不同分公司飞机串件。\r\n由发生串件所在分公司的生产计划控制分部将维修中队或航材AOG部门填写的串件单交到附控部门，附控部门依据串件单上提供的机号等信息，在2个工作日内将串件单传递给另一相关飞机执管分公司的附控部门，由这两个分公司的附控部门共同在系统中完成串件操作。', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3893, 153, '5.6.2', '不同分公司在异地发生串件。 \r\n由发生串件所在分公司的生产计划控制分部或航材AOG部门将维修中队填写的串件单交到附控部门，附控部门依据串件单上提供的机号等信息，在2个工作日内将串件单分别传递给另二个相关飞机执管分公司的附控部门，由这三个分公司的附控部门共同在系统中完成串件操作。', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3894, 153, '5.6.3', '本分公司飞机在异地发生串件（含同机）。\r\n由发生串件所在分公司的生产计划控制分部或航材AOG部门将维修中队填写的串件单交到当地附控部门，附控部门依据串件单上提供的机号等信息，在2个工作日内将串件单传递给相关飞机执管分公司的附控部门，由执管分公司的附控部门在系统中完成串件操作。', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3895, 153, '5.7', '发动机串件管理', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3896, 153, '5.7.1', '分公司不得自行决定在库存备发上串件；航线缺件应首先通过领用库存备件、从停场飞机串件、分公司间备件调拨支援、待修/在修发动机可用器材调拨支援、租赁、器材订购等方式解决。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3897, 153, '5.7.2', '分公司在以上方式无法满足生产紧急需求需要从库存备发串件时，由航材与采购部天津分部填写《备用发动机/APU串件申请单》报告航材与采购部AOG，航材部AOG在核实库存备件、从停场飞机串件、分公司备件调拨支援、待修/在修发动机可用器材调拨支援、租赁、器材订购等方式无法满足生产紧急需求时，在《备用发动机/APU串件申请单》填写处理意见并签字后返还给分公司航材部门，航材与采购部天津分部填写《备用发动机/APU串件指令单》，维修车间根据《备用发动机/APU串件指令单》实施串件工作。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3898, 153, '5.7.3', '维修人员在实施库存备发串件工作时，应严格按照AMM手册进行航线可更换件的倒串，倒串器材后对开口的管路安装堵盖。如遇AMM手册中没有操作程序的非航线可更换件，分公司可委托具备操作能力的维修厂家进行倒串，但分公司质量检验人员必须在场，在执行完非航线可更换件串件后，由质量检验人员摘下该备发的可用挂签并妥善保管，并对被串件备发颁发不可用挂签。当分公司协调维修厂家恢复安装被串非航线可更换件时，分公司必须有质量检验人员在场，在恢复安装工作完成后，由质量检验人员摘下该备发的不可用挂签，并将保管的可用挂签重新挂到备发上。以上委托维修厂家倒串非航线可更换件工作完成后一个工作日内，技术支援将委托执行的工作内容以书面形式发给工程部发动机项目。', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3899, 153, '5.7.4', '航材部门库房管理人员在库存备发串件过程中必须核对被串件与《备用发动机/APU串件指令单》列出的项目相符并在《备用发动机/APU串件指令单》上签字确认，记录每一项备发串件的详细信息备案。维修人员在《备用发动机/APU串件指令单》上填写实际串件信息并签字确认,并按照5.2串件单程序，填写串件单，交MCC。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3900, 153, '5.7.5', '异地备发串件，在完成批准手续后,由需求分公司的生产计划控制分部与实施备发串件的分公司的生产计划控制分部联系安排串件生产工作。需求分公司的航材部门负责提供已批准的《备用发动机/APU串件申请单》到实施备发串件的分公司的航材部门，航材部门填写《备用发动机/APU串件指令单》，各分公司根据《备用发动机/APU串件指令单》实施串件工作。异地备发串件，由实施串件的分公司将被串器材及可用证明，交当地航材与采购部天津分部办理运输手续，其航材与采购部跟踪被串器材的发运信息并及时提供给需求分公司的航材与采购部天津分部。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3901, 153, '5.7.6', '串件过程中如果造成发动机损伤，维修中队必须采取处理措施，同时填写《发动机故障缺陷记录单》记录损伤原因和情况以及采取的处理措施并确认备发是否可用。如果备发不可用，则必须挂不可用挂签，并在1个工作日内将《发动机故障缺陷记录单》交技术支援分部由其发给工程部发动机项目。', 9, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3902, 153, '5.7.7', '备发串件完成后，航材与采购部天津分部接收生产计划控制方便反馈的《备用发动机/APU串件指令单》，核实维修中队填写的内容是否完整，包括实际串件件号、实际串件序号、装上飞机号、装上发动机号、串件原因描述、生产者签字及日期，并在1个工作日内反馈航材部AOG。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3903, 153, '5.7.8', '航材与采购部天津分部战略采购项目根据《备发最低装机附件标准》和备发保障率的要求，在2个工作日内下发《发动机缺件补装通知单》（CCA-LD-033）给各分公司，分公司航材与采购部天津分部负责按照航材计划、调配、采购工作程序准备、调拨或订购补充器材。对于消耗件缺件，由分公司航材与采购部天津分部负责订购补充。\r\n如果由本分公司人员恢复备发，补充器材到货后，航材与采购部天津分部反馈生产计划控制分部（附《备用发动机/APU串件指令单》）。航材与采购部天津分部接到生产计划控制分部备发恢复反馈后及时反馈航材与采购部天津分部战略采购部门；如果天津分公司只负责补充缺料，补充器材到货后航材与采购部应及时将信息反馈航材与采购部战略采购部门。\r\n', NULL, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3904, 153, '5.7.9', '备发完成缺件补充后，航材部门在2个工作日内将反馈信息填入《发动机缺件补装通知单》并发给航材部战略采购项目。', 2, '2018-06-29 13:02:07', '2018-06-29 13:02:07', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3905, 154, '5.1', '新引进（租赁）飞机\r\n对于新引进（租赁）的飞机，根据技术支援（出租方）提供的原始装机清册，于10个工作日内，汇总出符合SAP系统要求的附件清单，交航材部导入系统。并在航材部反馈导入数据后，进一步进行修正后继续提交航材部导入SAP。', NULL, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3906, 154, '5.2', '飞机出售或退租', NULL, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3907, 154, '5.2.1', '当接到飞机出售或退租通知后，按要求完成涉及飞机装机清册、发动机清单、APU清单、起落架清单和时控件清单的准备工作。', 4, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3908, 154, '5.2.2', '接收飞机即将出售或退租计划，在规定的时间内完成要求提供的各种报表，注明系统是否有合格证。', 4, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3909, 154, '5.2.3', '在要求的时间内提供更新后的附件清单。', 4, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3910, 154, '5.3', '拆装信息的接收与处理', NULL, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3911, 154, '5.3.1', '接收提发运提供的不可用件挂签后，进行交接、登记、存档，于1个工作日内完成系统拆装，大批量的3工作日内完成。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3912, 154, '5.3.2', '接收航材检验提供的可用件退料挂签后，于1个工作日内完成系统拆装，并及时交验收完成器材入库。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3913, 154, '5.3.3', '接收航材与采购部的异地拆换挂签或拆换信息后，于1个工作日完成系统拆装。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3914, 154, '5.3.4', '分公司飞机在异地执行C/D检或外站故障使用对方器材时，接收相关分公司传来的器材挂签，1个工作日内完成系统拆装。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3915, 154, '5.3.5', '接到生产计划控制分部提供的串件单后，于1个工作日内在系统完成飞机的串件工作，特殊情况除外。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3916, 154, '5.3.6', '当装机附件的件号、序号、装机信息不清楚时，及时下发《装机附件检查表》。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3917, 154, '5.3.7', '根据CAMP时控件、时寿件的拆、装信息，及时核对SAP与实际数据的一致性。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3918, 154, '5.3.8', '当接收的挂签下件件序号填写“不可见”或“不清楚”时，在“能确认位置信息的”查询系统“该位置”在控件序号，并填在挂签拆下部位；如无在控件序号“或不能确认位置信息的”，则在挂签拆下部分注明，并按流程将挂签返回提发运。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3919, 154, '5.4', '换发（APU）/起落架工作', NULL, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3920, 154, '5.4.1', '换发信息处理                                        \n换发（APU）后，根据生产计划控制分部提供的“发动机（APU）拆/装报告”（附缺件单、串件单），5个工作日内做好拆下发动机的发动机报表并反馈技术，同时完成系统拆装。航材与采购部或工程部门在发动机/APU调拨给航材与采购部天津基地2个工作日内，将包含时控件信息的发动机/APU出厂报表提交给航材与采购部天津分部附控人员，附控人员核对相关时控件SAP信息的准确性。', 4, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3921, 154, '5.4.2', '在外维修飞机的换件清单处理                         \n根据接收到的在外维修飞机的拆换件挂签2个工作日内完成系统拆装，大批量的5个工作日内完成。', 2, '2018-06-29 13:06:19', '2018-06-29 13:06:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3922, 155, '5.1', '发动机托架接收', NULL, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3923, 155, '5.1.1', '依据生产计划控制分部发布的换发工作计划确认在控发动机托架的状态和数量是否满足生产需求，如不满足及时报生产计划控制分部，并通知航材与采购部天津分部。', 5, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3924, 155, '5.1.2', '联系航材与采购部战略采购部门主管计划员书面提出需求申请。', 2, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3925, 155, '5.1.3', '按照航材与采购部战略采购部门主管计划员托架运输协调的结果安排托架运输工作，通知本部门提发运人员做好卸车准备，通知生产支援分部做好验收准备。', 2, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3926, 155, '5.1.4', '依据TJ-04-001《工具设备计划与采购程序》完成发动机托架验收工作。', 5, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3927, 155, '5.2', '发动机托架的修理', NULL, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3928, 155, '5.2.1', '由生产支援分部书面提出发动机托架损坏需要大修或修理的需求，报航材与采购部天津分部。', 5, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3929, 155, '5.2.2', '将生产支援分部的需求信息报航材与采购部战略采购部门，并保持联系，及时将采购部门的处理意见反馈生产支援分部。', 2, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3930, 155, '5.2.3', '依据航材与采购部天津分部达成的协议组织完成发动机托架的修理工作。', 5, '2018-06-29 13:08:17', '2018-06-29 13:08:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3931, 156, '5.1', '分公司职责', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3932, 156, '5.1.1', '负责建立分公司档案主档、分档目录清单，该清单需经分公司质量经理或其授权的责任人批准。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3933, 156, '5.1.2', '分公司负责将本单位档案主档、分档目录清单并报航空安全与质量管理部备案，如清单内容发生变化，应及时报航空安全与质量管理部备案。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3934, 156, '5.1.3', '航空安全与质量管理部门每年至少开展一次分公司单机档案与维修记录管理情况的自查，做好检查记录。对检查中发现的问题，航空安全质量分部部门应及时通知相关责任单位，采取有效整改措施，监督其按措施完成整改，做好记录。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3935, 156, '5.1.4', '档案保管部门对保存在其部门的单机档案与维修记录的完整性、有效性和正确性负责。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3936, 156, '5.2', '单机档案保管方式\r\n1）分公司的飞机单机档案实行集中管理、分散控制的原则。\r\n2）单机档案的保管采取航空安全与质量管理部主档、分公司分档存放的方法。\r\n3）《单机档案存档内容及责任部门清单》中所列资料内容，除明确规定航空安全与质量管理部主档所存资料外，分公司各执管部门按清单中规定内容保存，对其的有效性、完整性、和正确性负责。\r\n4）航空安全质量分部单机档案适航事务负责制定单机档案存档目录，单机档案存档目录应明确各存档内容、存档地点、责任部门和责任人。\r\n5）当《单机档案存档内容及责任部门清单》中所列资料内容发生变化时，目录清单应实时更新。\r\n6）如航空器发生重大事故/事件时，分公司各单机档案管理部门应根据调查组的要求对相关维修记录进行封存和启封。\r\n', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3937, 156, '5.3', '保管期限', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3938, 156, '5.3.1', '《单机档案存档内容及责任部门清单》中所列资料内容除航空器单机档案中的维修记录部分分别保存至其要求的期限外，其它所有信息应当保存至航空器出售或永久退役后1年。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3939, 156, '5.3.2', '对于我公司运行的租赁的航空器，除保存一套原始维修记录用于退租外，还应保存一套复印件，保存期限至飞机退租后1年。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3940, 156, '5.4', '维修记录的移交', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3941, 156, '5.4.1', '如飞机执管分公司发生了变更，则所有飞机相关单机档案原始文件均移交给接收单位，原执管分公司不需保存任何档案。交付单位与接收单位在单机档案交接时应签署备忘录，明确说明移交的文件。附件的原始合格证和发动机/APU的拆装记录可由原分公司继续保存，但需在交接备忘录中注明。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3942, 156, '5.4.2', '如发动机/APU责任分公司发生变更，相关的维修记录的交接参见TJ-10-016CCA《公司内部发动机移交管理程序》中的《发动机移交技术文件/维修记录交接清单》（CCA-ED-106R3）和TJ-10-022CCA《分公司内部APU移交管理程序》中的《APU技术文件交接清单》（CCA-ED-168）。', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3943, 156, '5.4.3', '如飞机发生了出售、退租、出租等情况，飞机执管分公司应将所有单机档案原始文件移交给接收方，并且保留一套移交的复印件或通过扫描方式保存的电子版文件。保存期限为交付后1年。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3944, 156, '5.4.4', '对于湿租飞机和发动机/APU，根据相关协议管理和保存维修记录。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3945, 156, '5.5', '单机档案的监督检查\n航空安全质量分部须对本单位单机档案管理情况每年至少进行一次自查，做好检查记录。\n对检查中发现的问题，航空安全质量分部应及时通知相关责任单位，采取有效整改措施，监督其按措施完成整改，做好记录。', 8, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3946, 156, '5.6', '单机档案的借阅管理\n按照01-07-003《维修记录管理程序》执行。', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3947, 156, '5.7', '维修记录的管理\n按照01-07-003《维修记录管理程序》执行。', NULL, '2018-06-29 13:17:52', '2018-06-29 13:17:52', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3948, 157, '5.1', '飞机、APU履历本和发动机履历\n履历本是单机档案的重要组成部分，用于记录飞机从制造出厂直至退役期间所有有意义的使用和维修方面的事实和事件。国航现用履历本分飞机履历本和APU履历本两种。发动机履历采用分档保存的方式，无实物履历本。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3949, 157, '5.1.1', '飞机履历本\r\n飞机履历本用于记录飞机在服役期间的主要维护工作和重要故障情况，它主要包括以下内容：\r\n1）飞行时间记录：包括月飞行小时、自开始使用时间累计、月起落次数（正常/连续）、自开始使用起落次数。\r\n2）定检检查工作记录：包括A检（含A检）以上所有定检工作的完成日期、内容、飞行小时、飞行起落。\r\n3）工程指令完成记录：包括已完成工程指令的工程指令号、文件依据、内容、完成日期。\r\n4）AD/CAD 指令完成记录：包括已完成AD/CAD 指令号、工程指令号、文件依据、内容、截止期限、完成日期。对于涉及重检的AD/CAD 指令，应填写相应首检完成记录和重检工作所对应的CAMP编号。\r\n5）AD/CAD 重复性检查记录：只需填写首次重检信息，包括AD/CAD 号、工程指令号、文件依据、内容、执行日期和备注。\r\n6）主要故障/事故记录：包括故障发生时间、地点、航班号、ATA 章节号、故障描述、措施。\r\n7）发动机更换记录：包括完成日期、机身时间、起落次数、发动机翻修时限、位置、更换原因、装上序号/拆下序号。\r\n8）APU 更换记录：包括完成日期、机身时间、起落次数、更换原因、装上序号 /拆下序号。\r\n', NULL, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3950, 157, '5.1.2', '发动机履历\r\n发动机履历记录了发动机在服役期间的主要维护工作和重要故障情况，发动机履历采用分档方式保存。其主要包含的内容及存放方式如下：\r\n1）发动机历史记录保存在发动机全寿命管理系统中：包括机号、发动机位置、装上记录、拆下记录。\r\n2）发动机飞行时间记录保存在发动机全寿命管理系统中：包括自开始使用累计飞行小时、自开始使用累计飞行循环。\r\n3）工程指令完成记录保存在TDMS系统（技术文档管理系统）中：包括发动机型号、发动机序号、工程指令号、文件依据、内容、完成日期。\r\n4）AD/CAD 指令完成记录保存在TDMS系统（技术文档管理系统）中：包括发动机型号、发动机序号、AD/CAD 号、工程指令号、文件依据、内容、截止期限、完成日期。\r\n5）发动机原始数据构型文件及维修工作记录电子版保存在发动机全寿命管理系统中：包括维修的完成日期、内容、飞行小时、飞行循环。\r\n6）主要故障/事故报告保存在发动机全寿命管理系统中：包括飞机型号、飞机注册号、发动机位置、发动机型号、发动机序号、故障发生日期、地点、航班号、发生阶段、后果、ATA 章节、故障描述、措施。\r\n', NULL, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3951, 157, '5.1.3', 'APU履历本\n1）APU 履历本用于记录APU 在服役期间的主要维护工作和重要故障情况，它主要包括以下内容：\n2）飞机APU 历史记录：包括机号、装上记录和拆下记录。\n3）飞行时间记录：包括当月飞行时间、自开始使用时间累计、当月使用循环、自开始循环次数。\n4）工程指令、CAD/AD 指令完成记录：包括工程指令号、AD/CAD 号、内容、截止日期、完成日期。\n5）翻修工作记录：包括完成日期、内容、参考资料。', 4, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3952, 157, '5.2', '履历本使用规定', NULL, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3953, 157, '5.2.1', '履历本是飞机和APU使用、维护和修理的主要记录文件，维修人员在使用中要据实填写，字迹要清楚，不得随意更改。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3954, 157, '5.2.2', '发动机履历采用分档管理，履历内容保存在TDMS系统（技术文档管理系统）和发动机全寿命管理系统（TJ-10-025CCA《发动机全寿命管理系统管理程序》）中。', 4, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3955, 157, '5.2.3', 'APU更换时按要求完成APU履历本的填写。', 9, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3956, 157, '5.2.4', '履历本中各项目的详细填写规范见履历本中栏目填写说明。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3957, 157, '5.2.5', '飞机履历本由活页构成，页码由各部分的编号和各部分的流水号组成。各有关部分填写完成后要及时插入，活页一经插入就不能取出。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3958, 157, '5.2.6', '飞行时间记录、定期检查工作记录应每月填写，工程指令、AD/CAD指令完成记录及时插入或填写。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3959, 157, '5.2.7', '当飞机外委修理时要保证履历本的各项记录填写到最新状态，并做好交接记录。', 6, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3960, 157, '5.2.8', 'APU 进车间检修或翻修后，所做的全部工作项目应由修理单位详细填写。', NULL, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3961, 157, '5.2.9', '履历本应当保存至飞机/ APU出售或永久性退役后一年。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3962, 157, '5.2.10', '当飞机执管责任转移时，相关分公司应保证履历本良好交接。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3963, 157, '5.2.11', '对于租用或借用的发动机/APU，如租用或借用时间不超过6个月（含），可仅具有总使用小时/循环记录和自上次翻修后的使用小时/循环记录、上一次翻修记录、上一次从运营的航空器上拆下的修理记录、AD/SB执行记录和自上次装机以来的发动机性能监控数据；如果租用或借用时间超过6个月，则应当具有全部的履历记录。', 2, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3964, 157, '5.3', '飞机记录本\n飞机记录本的管理按照07-11-003CCA《航线维修记录管理程序》执行。', NULL, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3965, 157, '5.4', '记录本和履历本的保管\n记录本和履历本的保管按TJ-07-001CCA《单机档案与维修记录管理程序》中有关单机档案保管的相应规定执行。', 8, '2018-06-29 13:57:48', '2018-06-29 13:57:48', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3966, 158, '5.1', '防空停组织机构。分公司防空停组织由防空停工作组和防空停检查组组成。防空停工作组组长由分公司主管技术或质量的高级副经理担任。防空停工作组下设防空停办公室，由技术支援分部高级工程师担任组长、发动机工程师、助理工程师担任组员。防空停工作组人员组成在每年的第一季度进行调整发布，防空停办公室、防空停检查组成员由防空停工作组讨论确定后一同发布。', NULL, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3967, 158, '5.2', '防空停日常管理工作\r\n1）密切与工程部沟通与交流，关注天津执管机队发动机的工作状况，分析造成空停的主要原因，提交天津执管机队发动机存在的问题。\r\n2）负责发动机日常监控、管理工作，包括故障管理，航线维护检查管理、性能监控管理。\r\n3）组织召开防空停会议，讨论分析发动机运行状况，针对性制定检查方案和计划，监控实施过程和结果。\r\n4）负责汇总防空停检查组检查结果和发现问题的后续控制。每月结合防空停工作会议，汇报检查及处理结果。\r\n5）负责上级检查单位防空停检查发现问题的整改落实工作。\r\n6）按照分公司程序01-RM-001《风险管理程序》、01-SA-011《系统评价程序》完成防空停风险管理工作。\r\n', NULL, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3968, 158, '5.3', '防空停专项检查', NULL, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3969, 158, '5.3.1', '1）负责制定年度防空停专项检查工作方案，方案应明确检查计划、检查形式、范围、标准及要求、检查人员组成，原则上每台发动机每年至少完成一次打开包皮检查。负责汇总防空停检查组检查结果和发现问题的后续控制。每月结合防空停工作会议，汇报检查及处理结果。\n2）防空停检查组根据防空停专项检查方案，做好检查前的各项准备工作，安排检查人员按计划对本单位执管机队的所有在翼发动机的维护状态进行检查。', 4, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3970, 158, '5.3.2', '3）对于在检查中发现的问题，可以当时纠正的由配合检查的中队填写地故单并完成排故工作。', 9, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3971, 158, '5.3.3', '4）对于无法当场纠正的问题进行汇总，按机号、发动机号报防空停工作组。', 9, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3972, 158, '5.3.4', '5）技术支援分部负责汇总年度防空停检查工作经验，制作PPT材料。', 4, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3973, 158, '5.4', '防空停检查培训。每年组织一次防空停培训工作，参加培训人员至少覆盖到本年度防空停检查组成员。', 1, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3974, 158, '5.5', '防空停会议\r\n1）技术支援分部每月组织召开防空停会议，确定防空停会议日期及主要议题，下发会议通知，对防空停工作进行阶段性总结。\r\n2）技术支援分部负责准备会议资料，编写相应的PPT，总结近期发动机运行情况，性能状况，本体状况，排故点评，防空停类EO完成情况，及检查结果分析。负责航线发动机维护及故障情况进行评述。\r\n3）各有关部门对发动机各种议题的讨论，及改进发动机维护工作的措施。\r\n4）技术支援分部编写发动机防空停会议纪要，上报工程部，下发。\r\n', NULL, '2018-06-29 14:00:33', '2018-06-29 14:00:33', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3975, 159, '5.1', '飞机放行的实施\r\n国航股份的飞机在维修作业完成之后，由被授权的飞机放行人员代表国航股份批准飞机投入使用。\r\n飞机放行人员从事飞机维修时必须随身携带维修人员执照和放行人员授权证明，或将其存放在其通常行使证件权限的工作区域内，随时接受检查。跟班放行时，跟班人员必须随身携带维修人员执照。', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3976, 159, '5.1.1', '飞机放行文件的签署', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3977, 159, '5.1.1.1', '航线放行\r\n在航线维修中，放行人员在检查相应工卡都已签署完毕，确认所有故障均已排除或按相关规定予以保留，以及所有勤务保证工作完成后，在飞机技术记录本（TLB）的相应栏目中签字，即构成飞机放行。', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3978, 159, '5.1.1.2', '定检放行\r\n国航股份的飞机在完成A检或者相当级别（含）以上的飞机定期检修工作及结合其完成的改装工作后，应当由授权的放行人员检查相应工卡（包括例行和非例行的）都已签署完毕，确认所有故障均已排除或按规定的程序予以保留，签发《航空器放行证书和维修符合性声明》（Form No.: QM-068），即构成飞机维修放行。飞机投入营运前，还应由授权的放行人员在飞机记录本上签署，以完成飞机放行。', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3979, 159, '5.1.1.3', '超差放行：\r\n1）可以办理保留故障的超差放行\r\n-按照保留故障管理程序执行。\r\n2）超出飞机适航性资料的超差放行（是指飞机带有故障或缺陷的情况下经工程评估可以放行的项目），包括：\r\n-如得到原厂家批准的，可以直接放行。\r\n-具有有效工程文件的，放行部门可以直接放行。\r\n-以前发生过类似故障，并有批准的文件，分公司技术部门和质量部门批准后放行。\r\n-无任何依据的，分公司质量部门协同技术部门决定是否批准放行，必要时报适航部门取得批准。\r\n-其余情况由分公司当日值班领导协调解决。\r\n3）涉及到超出国航股份《工程手册》等管理手册方面的超差放行，需经公司质量部批准，必要时报适航部门批准。\r\n', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3980, 159, '5.1.1.4', '其他情况下放行：\r\n1）	对于特殊维修作业，如果在航线维修时完成，则按航线放行执行；如果在定检维修时完成，则按定检放行执行。\r\n2）	外站特殊情况下的放行，由当地 MCC协调执管基地/Ameco的放行人员以传真的形式进行放行。\r\n3）	飞机备降在无相应维修协议的外站时，可执行由该航班的机长按照北京飞机维修工程有限公司质量部门的授权做无故障放行处理。如遇到因突发备降导致当地无法提供放行能力保障的特殊情况时，可由维修部AMC协调机长做无故障放行处理。\r\n4）	所有重要修理和改装的放行以签署《重要修理及改装记录》形式放行。\r\n5）	对于定检工作包拆分的情况，应对每个拆分单元分别签发《航空器放行证书和维修符合性声明》，并在证书中注明拆分信息。\r\n', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3981, 159, '5.1.1.5', '向机组提供飞机的放行文件\r\n向机组提供的飞机放行文件有TLB、CLB及相关DD单，均应放在驾驶舱和客舱规定的位置。', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3982, 159, '5.2', '被授权的飞机放行人员的权利\r\n1）被授权的放行人员可以对国航股份飞机在分公司所在航站和跟班到外站放行或到外站抢修后放行，也可以在特殊情况下以传真的形式放行。\r\n2）各外站的机务工程师可以在授权放行的机型范围内在外站对国航股份飞机进行飞机放行。 \r\n3）外站机务代理的被授权人员可以在授权放行范围内的外站对国航股份的飞机进行飞机放行。\r\n4）飞机放行人员有权拒绝任何不符合标准放行的要求。\r\n', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3983, 159, '5.3', '实施飞机放行的项目\r\n在下列维修作业完成后，必须实施飞机放行：\r\n1）计划性维修（航线及定检维修）；\r\n2）试飞；\r\n3）特殊维修作业；\r\n4）涉及必检项目的检查；\r\n5）重要修理和改装；\r\n6）执行适航指令或工程指令；\r\n7）执行批准的MEL 保留的项目；\r\n8）超差放行。\r\n', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3984, 159, '5.4', '对飞机放行的监管', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3985, 159, '5.4.1', '分公司质量部门有权对飞机放行进行检查监督，并对发现的问题作出处理决定。', 8, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3986, 159, '5.4.2', '分公司质量经理对飞机放行有否决权。', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3987, 159, '5.4.3', '在飞机放行方面存在的不同意见，公司质量经理有最终决定权。', NULL, '2018-06-29 14:26:46', '2018-06-29 14:26:46', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3988, 160, '5.1', '航空器电台执照的申请、办理及管理程序', NULL, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3989, 160, '5.1.1', '制定电台证办理计划\r\n1）每年年初，整理当年需办理电台证的飞机，依据：正式电台证每3年更换一次，临时证在3个月之内更换为正式证。\r\n2）	分组：将3个月内到期的飞机分为一组。\r\n', NULL, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
