INSERT INTO `tb_step_info` VALUES (3991, 160, '5.1.2.1', '在电台证到期3个月前下发业务通告，通知生产计划控制分部安排相关航空器无线电设备的校验工作。', 8, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3992, 160, '5.1.2.2', '生产计划控制分部负责下发工作单，安排完成航空器无线电设备校验工作，并将完成的工作单按要求的日期反馈航空安全质量分部。', 6, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3993, 160, '5.1.3', '电台申请表格的填写\r\n根据反馈的航空器无线电设备检查工作单填写《中国民用航空器电台执照申请表》、《民用航空器电台检查表（二）》。办理执照时交民航局无委办。', NULL, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (3994, 160, '5.1.4', '电台校验\r\n确定电台的校验时间和地点；配合民航无线电管理检查员完成对航空器的现场检查，填写《民用航空器电台检查表（二）》。', NULL, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
