INSERT INTO `tb_step_info` VALUES (4001, 160, '5.1.7.1', '生产计划控制分部在收到新的航空器电台执照后，负责制定工作计划，确保在新执照生效前飞机飞回基地（如飞机回不到基地，生产计划控制分部负责安排在合适的地点）将其装到飞机上；', 6, '2018-06-29 14:29:50', '2018-06-29 14:29:50', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
