INSERT INTO `tb_step_info` VALUES (4013, 161, '5.1', 'ELT更换及信息报告要求', NULL, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4014, 161, '5.1.1', '技术支援分部根据维修部ELT拆换工作单编制原则、AMM手册和工程部门提供的ELT工程文件，编制ELT拆换工作单。工作单需明确对各类ELT构型所采取的拆换步骤（包括AIM等的更换注意事项）、测试要求（如需）和编码信息反馈的要求，并附《飞机应急定位发射机（ELT）更换信息单》（CCA-QM-092R1）。', 4, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4015, 161, '5.1.2', '维修中队在任何情况下更换任何形式的ELT时，都必须按照ELT拆换工作单的要求填写《飞机应急定位发射机（ELT）更换信息单》，并将新的ELT编码与原编码进行比对，（原编码可依据5.3.1查阅）如发现不一致应进一步核实，确因排故等原因导致的不一致发生应在备注一中说明。将《飞机应急定位发射机（ELT）更换信息单》报MCC。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4016, 161, '5.1.3', 'MCC1个工作日内将《飞机应急定位发射机（ELT）更换信息单》报航空安全质量分部（传真或其它方式）。', 6, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4017, 161, '5.1.4', '航空安全质量分部在收到《飞机应急定位发射机（ELT）更换信息单》后，应对信息单所填内容进行核对，如正确无误则将《飞机应急定位发射机（ELT）更换信息单》报中国民航无线电管理业务平台。如有错误则将《飞机应急定位发射机（ELT）更换信息单》退回MCC，要求其纠正。', 8, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4018, 161, '5.2', '《飞机应急定位发射机（ELT）更换信息单》的填报', NULL, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4019, 161, '5.2.1', '《飞机应急定位发射机（ELT）更换信息单》的15位编码前二位必须是B3或33，表示中国代码。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4020, 161, '5.2.2', '维修中队填报《飞机应急定位发射机（ELT）更换信息单》时要字迹清晰、工整，同时将联系电话与传真号码填写完整。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4021, 161, '5.3', 'ELT编码信息档案的建立', NULL, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4022, 161, '5.3.1', '航空安全质量分部负责按照《飞机紧急定位发射机ELT信息汇总表》（CCA-QM-T01）形式建立所执管飞机的ELT编码信息档案，在ELT信息发生变化后要及时更新，经审核后在分公司内网发布，通知各维修中队和生产计划控制分部。', 8, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4023, 161, '5.3.2', '航材附控部门根据ELT附件挂签的更换信息及时更新SAP系统。', 2, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4024, 161, '5.4', 'ELT信息的确认与纠错', NULL, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4025, 161, '5.4.1', '航空安全质量分部依据MCC上报的ELT拆换信息，对ELT的编码信息、飞机注册号等符合性进行确认，如MCC反馈的ELT拆换信息有误，航空安全质量分部将用《飞机应急定位发射机（ELT）更换信息单》备注栏注明的方式通知MCC。', 8, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4026, 161, '5.4.2', 'MCC负责通知维修中队按照工程部制定的ELT编码标准、维修部编制工作单及原来厂家相关ELT维护手册按信息错误的类别进行纠正。', 6, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4027, 161, '5.4.3', '一般发生ELT信息错误有以下（但不限于）三类；\r\n1）信息填报错误。\r\n2）部件拆装错误。\r\n3）编码写入错误。\r\n', NULL, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4028, 161, '5.4.4', '对于信息填报错误，维修中队需按填报的《飞机应急定位发射机（ELT）更换信息单》与ELT上的铭牌重新核对即可纠正。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4029, 161, '5.4.5', '对于部件拆装错误，一般发生在带有存储器（DONGLE/AIM）的ELT，如存储器（DONGLE/AIM）被错误拆换，维修中队需按照工作单、相关维护手册进行纠正。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4030, 161, '5.4.6', '对于编码写入错误，应将ELT送修重新写入正确的编码。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4031, 161, '5.4.7', '在错误得到纠正后，维修中队需将纠正后的ELT相关信息按照5.1.2进行报告，确保正确无误。', 9, '2018-06-29 14:37:48', '2018-06-29 14:37:48', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4032, 162, '5.1', '年检计划\r\n航空安全质量分部应在每年1月15日前将本年度飞机年检计划报公司航空安全与质量管理部适航事务科。', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4033, 162, '5.2', '年检申请\r\n1）每年1月10日前与局方沟通决定飞机年检时间填写《航空器年度适航检查申请书》AAC-092（2/91）。\r\n2）填写的《航空器年度适航检查申请书》由质量经理批准。\r\n3）批准后的《航空器年度适航检查申请书》在单位名称处加盖分公司公章。\r\n4）将加盖分公司公章的《航空器年度适航检查申请书》上报航空安全与质量管理部适航事务分部、民航天津监管局。\r\n', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4034, 162, '5.3', '年检的实施', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4035, 162, '5.3.1', '自检', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4036, 162, '5.3.1.1', '布置形式、内容\r\n航空安全质量分部以文件形式布置飞机年检工作。内容包括：自查工作、完成时间、主要工作、注意事项、往年飞机年检发现问题复查等。', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4037, 162, '5.3.1.2', '飞机自检工作由生产计划控制分部负责组织实施。', 6, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4038, 162, '5.3.1.3', '维修中队按照生产计划控制分部下发的《年检例行工作单》完成航空器自检工作。', 9, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4039, 162, '5.3.1.4', '航空安全质量分部按规定填写《民用航空器持续适航检查记录单》（CCA-QM-088）（以下简称“记录单”），“记录单”由航空安全质量分部检验员和质量经理签署。“记录单”是航空器文件部分自检的有效记录，在填写时必须做到完整、准确，并对所填写的数据、事实及结论负责。由航空安全质量分部负责将“记录单”与《年检例行工作单》作为维修记录保存。', 8, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4040, 162, '5.3.2', '适航部门检查', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4041, 162, '5.3.2.1', '航空安全质量分部在完成飞机自检工作的基础上，准备好有关文件、资料，以备适航部门检查。', 8, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4042, 162, '5.3.2.2', '适航部门正式检查时，航空安全质量分部负责本单位执管机群年检的协调工作。', 8, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4043, 162, '5.3.3', '发现问题及纠正措施', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4044, 162, '5.3.3.1', '航空安全质量分部负责转发适航部门下发的审查/监查发现问题通知单》M0003（以下简称“通知单”）。针对“通知单”中列出的机务维修方面问题，航空安全质量分部要求相关责任单位逐条拟定纠正措施及完成日期，相关责任单位填写《改正措施报告》M2008C(2008/08/10)，由航空安全质量分部提交天津监管局。', 8, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4045, 162, '5.3.3.2', '航空安全质量分部将发现问题及《纠正措施报告》上报公司航空安全与质量管理部。', 8, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4046, 162, '5.4', '年检签署\n国航股份机群经过适航部门的年度检查合格后由华北地区适航管理部门在国航股份运行规范D部航空器维修 D0003航空器清单统一签署。', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4047, 162, '5.5', '年检总结\r\n航空安全质量分部对本年度年检中发现的问题按工程管理、质量管理、维修记录、飞机履历、保留故障、适航性资料等方面进行分类总结。', NULL, '2018-06-29 15:19:17', '2018-06-29 15:19:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4048, 163, '5.1', '涉及重检的适航指令的检查', NULL, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4049, 163, '5.1.1', '适航指令的监控工作主要基于计算机系统平台“技术文档管理系统（TDMS）”（以下简称“TDMS系统”）及SAP系统。', 8, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4050, 163, '5.1.2', '生产计划控制分部每月第4个工作日前将上一个日历月分公司执管飞机执行的重检类适航指令反馈航空安全质量分部。', 6, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4051, 163, '5.1.3', '航空安全质量分部每月对上一日历月执行的重检类适航指令进行检查，并填写《重检适航指令检查记录》（CCA-QM-102R2），检查结果于每月15日前通过航空安全管理系统报公司航空安全与质量管理部。', 8, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4052, 163, '5.1.4', '公司航空安全与质量管理部对各分公司的检查结果抽查，抽查比例10%以上，抽查结果在公司范围内通报。', NULL, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4053, 163, '5.1.5', '航空安全质量分部每年1月总结上一年度所执行重检类适航指令的检查监控情况，并形成年度报告。报告内容至少包括上一年度本单位执行重检适航指令的数量、执行情况、反馈情况、执行间隔是否满足要求、检查中发现的问题及整改情况等，报告需报分公司领导审批并通过机务航空安全管理系统报公司航空安全与质量管理部。', 8, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4054, 163, '5.2', '监控过程中发现的问题', NULL, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4055, 163, '5.2.1', '如监控过程中发现问题，航空安全质量分部通过航空安全系统向责任部门下发问题整改通知。', 8, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4056, 163, '5.2.2', '责任部门对所发现的问题要认真分析、确定应采取的纠正措施，在规定的期限内在航空安全管理系统中反馈预防与纠正措施。', 6, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4057, 163, '5.2.3', '航空安全质量分部负责对责任部门的预防与纠正措施进行审核，对于不能满足要求的措施驳回，由责任部门重新制定措施。', 8, '2018-06-29 15:36:49', '2018-06-29 15:36:49', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4058, 164, '5.1', '试飞前提\r\n下列情况下应进行飞机试飞： \r\n1）双发飞机，更换了两台新发动机或两台发动机都做过重大改装、重要修理但未经使用或重新安装了两台发动机；\r\n2）双发飞机，更换一台新发动机或安装上一台做过重大改装、重要修理但未经使用的发动机，同时另一台发动机属重新安装；\r\n3）机载设备经过调节，排除故障，更换或改装后，只能在飞行中检验其工作性能者；\r\n4）油封超过三个月的飞机；\r\n5）根据具体的维修项目要求；\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4059, 164, '5.2', '试飞的条件\r\n只有符合下述条件，飞机方可试飞： \r\n1）按照适航当局和制造厂的要求，飞机各结构部位和各系统均已达到可以飞行的要求并由航空安全质量分部签署试飞的放行文件后。\r\n2）允许有保留故障，但这些故障必须在MEL允许范围内并且不影响某个系统和整架飞机试飞的检查准确性。\r\n3）一般情况下试飞应在日出和日落时间内进行。但在特殊情况下，如不涉及机外的目视检查项目，并在航管部门批准和航空安全质量分部同意时也可以推迟到日落后完成。\r\n4）气象条件必须达到航管部门要求的标准。\r\n5）已具备一份与试飞目的相符的试飞方案。\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4060, 164, '5.3', '试飞方案的编写、批准、修改和分发\r\n国航737飞机大修后的试飞手册由工程部负责编写，工程部批准。在进行大修/非大修后的试飞时，天津分公司技术支援分部根据试飞的目的可采用大修后试飞手册中的相关部分编写具体的试飞方案，或与飞行部门商定试飞科目和方法。\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4061, 164, '5.3.1', '编写\r\n编写参考但不限于以下内容：飞行手册、飞机操作手册、飞机维护手册。试飞方案分地面检查部分和飞行检查部分。地面检查部分按飞机系统进行编写，对相关系统的工作性能进行检查。飞行检查部分按飞行阶段进行检查，分阶段对飞机各系统的工作品质进行检查。飞行检查部分主要进行地面检查无法实现，必须在飞行中进行验证的项目。', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4062, 164, '5.3.2', '批准\r\n对技术支援分部负责编写的试飞方案，系统工程师负责编写试飞方案，并对其正确性和可操作性负责。相应的分部经理负责试飞方案审核，对其准确性和完整性负责。在完成初稿后，将试飞方案交相应飞行部门征求意见，在得到意见反馈后，根据需要修改，定稿后的试飞方案由分公司主管技术支援分部的高级副总经理最终签署批准。', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4063, 164, '5.3.3', '分发\r\n批准后的试飞方案由技术支援分部下发相关生产计划控制分部。', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4064, 164, '5.4', '试飞程序的执行', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4065, 164, '5.4.1', '由生产计划控制分部按航管部门的要求提出试飞申请并将试飞方案交给飞行部门和航空安全质量分部。 \r\n试飞前由生产计划控制分部召集并主持飞行部门试飞机组、航空安全质量分部试飞检验员和相关人员参加的试飞准备会，会上由分公司相关人员介绍飞机状况并讨论试飞问题和要求，对参加试飞人员进行“安全须知”讲解或要求相关人员登机后阅读机上“安全须知”。\r\n飞行人员逐项完成试飞方案中的地面检查部分，生产计划控制分部协助。地面检查合格后，开始进行试飞方案中的飞行检查部分，由机组执行，分公司人员协助。\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4066, 164, '5.4.2', '航空安全质量分部检验员对试飞过程进行监督。', 8, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4067, 164, '5.4.3', '试飞结束后由生产计划控制分部召集并主持讲评会，机组人员向分公司介绍试飞情况并确定是否再次试飞。', 6, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4068, 164, '5.4.4', '如果对试飞方案的准确性和可操作性有异议，由技术支援分部将讲评会反映的问题由书面形式反馈到工程部。', 4, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4069, 164, '5.4.5', '试飞机组和分公司航空安全质量分部的试飞检验员签署试飞结论。\r\n最终签署的试飞方案由分公司按国航飞机单机档案管理的要求存档。\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4070, 164, '5.5', '外站试飞程序', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4071, 164, '5.5.1', '当天津分公司执管飞机在外站需要试飞，在试车前须由当地机务工程师或航线协议维修单位，负责将试飞的原因和目的报告Ameco运行管理部AMC/天津分公司MCC。\r\n在获得天津分公司批准后，由当地机务工程师或航线协议维修单位负责联系当地机场和空管部门，在征得对方同意后并在确保安全的情况下方可试飞。\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4072, 164, '5.5.2', '试飞方案由工程部或分公司技术支援分部编写，经相应人员批准。试飞过程中做好相关记录，并在试飞结束后由当地机务工程师或航线协议维修单位及时通报AMC和分公司MCC。', 4, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4073, 164, '5.5.3', '试飞记录由当地机务工程师或航线协议维修单位交MCC。MCC交航空安全质量分部存档。', 6, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4074, 164, '5.6', '外站试车程序', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4075, 164, '5.6.1', '当天津分公司执管飞机在外站需要试车，在试车前须由当地机务工程师或航线协议维修单位，负责将试车的原因和目的报告天津分公司MCC。', 6, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4076, 164, '5.6.2', '如需要，航线中队值班工程师依据AMM相关章节编写试车方案或发布相关试车工卡。', 9, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4077, 164, '5.6.3', '在获得批准后，由当地机务工程师或航线协议维修单位负责联系当地机场和空管部门，在征得同意后，按照试车相应规定进行试车。\r\n试车人员必须具备分公司授权或认可的该机型发动机试车资格，如无合格的试车人员，可由机组协助完成。\r\n机组协助地面试车时，不能进行高功率试车，只能进行慢车功率试车。\r\n天津分公司如确认地面试车工作在机组能力范围内，由其向Ameco运管部AMC申请由机组协助试车，在申请中分公司应准备试车工作中需要机务人员地面完成的相应工作单。\r\n运行管理部AMC通知相关外站的机务工程师或航线协议维修单位，由其联系机组进行试车工作，在通知中，应加入试车工作中需要机务人员地面完成的相应工作单。\r\n外站的机务工程师或航线协议维修单位负责按照需机务人员地面完成的相应工作单及飞机维护手册，安排实施与试车相关的地面工作。\r\n机组按照飞行操作手册的要求完成试车。\r\n试车完毕，由机组与外站机务工程师或航线协议维修单位共同完成相关记录，并由机务工程师或航线协议维修单位及时通报AMC。\r\n试车过程必须由机务工程师、机组或航线协议维修单位的由国航认可的人员在场。\r\n当地机务工程师、机组或航线协议维修单位将外站试车记录交MCC。\r\nMCC将外站试车记录交航空安全质量分部存档。\r\n', NULL, '2018-06-29 15:40:24', '2018-06-29 15:40:24', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4078, 165, '5.1', '工程文件编写', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4079, 165, '5.1.1', '本程序中的工程通告（EB）/维修提示（MT）/操作提示（OT）/维修方案改版单（CR）/ 器材准备通知单（MON）/发动机车间工作指令（PEO-S）/附件车间工作指令（CEO-S）由TDMS系统下发到技术支援分部，由相关工程师评估，以EMAIL电子版形式下发各部门，并在工程文件截止日期前确认收到。工程师按程序要求编写分公司技术文件，如果工程文件有反馈要求，由技术支援分部负责反馈。', 4, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4080, 165, '5.1.2', '文件类型－机型/发动机型号－章节－四位流水号－版次，如：EB/MT/OT/CR/MON/PEO-S/CEO-S-B737-21-2001-R00 \r\n手册更改指令（MRO）的编号原则为：文件类型－机型－手册名称－四位流水号－版次，如：MRO-B737- AFM -0001-R00。\r\n警告：MRO不允许有多机型的情况，对于涉及多个机型的手册修改，应针对相应机型下发多份MRO予以执行。\r\n', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4081, 165, '5.1.3', '文件编号中的部门代码和机型号的定义：\r\n章节：两位ATA章节代码，涉及多个章节的用UNIV标识；\r\n版次：R＋两位流水号，如R00；\r\n机型：采用四位编码，多机型以MULT标识，单一机型以对应的机型号标识。\r\n', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4082, 165, '5.2', '分公司工程文件的编写和审批流程\r\n文件编号中的部门代码和机型号的定义：\r\n章节：两位ATA章节代码，涉及多个章节的用UNIV标识；\r\n版次：R＋两位流水号，如R00；\r\n机型：采用四位编码，多机型以MULT标识，单一机型以对应的机型号标识。\r\n', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4083, 165, '5.3', '分公司工程文件的颁发要求', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4084, 165, '5.3.1', 'MT的颁发\r\n主送单位为各主要相关部门，抄送单位为其它各相关部门；对于与外站维修有关的MT，工程师在编写时，在发文单位标明相关外站。', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4085, 165, '5.3.2', '业务通告的颁发\r\n主送单位为各主要相关部门，抄送单位为其它各相关部门；对于与外站维修有关的业务通告，工程师在编写时，在发文单位标明相关外站。', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4086, 165, '5.4', '工程文件的执行与反馈\r\nMT/OT为指导或提示性技术文件，各涉及部门不需反馈具体执行结果，但必须通过EMAIL系统确认接收作为回执。', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4087, 165, '5.5', '手册类MRO的持续管理和执行反馈', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4088, 165, '5.5.1', 'MRO的持续管理：\r\n技术支援分部依据TDMS系统的MRO总控清单，核对更新各相关有效MRO清单。', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4089, 165, '5.5.2', 'MRO的执行和反馈流程', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4090, 165, '5.5.2.1', '技术支援分部负责在TDMS中接收MRO，并提交分部经理评估后下发相关部门。', 4, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4091, 165, '5.5.2.2', 'MRO为指导或提示性技术文件，各涉及部门不需反馈具体执行结果，但必须通过EMAIL系统在截止日期前接收作为回执。', 10, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4092, 165, '5.5.2.3', 'MRO反馈\r\n1）技术支援分部负责整理和汇总MRO的完成反馈信息，在MRO完成后规定工作日内将总的执行结果录入TDMS系统；\r\n2）技术支援分部负责对MRO反馈单进行存档。\r\n', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4093, 165, '5.6', '工程文件的存档', NULL, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4094, 165, '5.6.1', '所有的工程文件一经批准生效， 技术支援分部在负责发放的同时也负责对该文件的存档工作。', 4, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4095, 165, '5.6.2', '相关部门各自收到的工程文件由本部门自行保存。各类工程文件保存期限：保存至航空营运人终止相关机型的运行后1年为止。', 10, '2018-06-29 15:43:18', '2018-06-29 15:43:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4096, 166, '5.1', '两次称重期间的载重平衡数据管理\r\n目的是为了评估和控制在每连续的两次飞机称重期间，因为执行改装工作，累计结果对飞机载重平衡数据的影响程度。', NULL, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4097, 166, '5.2', '载重平衡数据的收集\n生产计划控制分部按TJ-11-007CCA《工程指令计划管理程序》要求，在涉及有载重平衡数据影响的工程指令完成后，将反馈单连同载重平衡数据反馈技术支援分部。', 6, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4098, 166, '5.3', '载重平衡数据的累计管理', NULL, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4099, 166, '5.3.1', '技术支援分部载重平衡数据管理人员依据工程指令上注明的载重平衡数据变化量，按附录表格《国航飞机载重平衡数据档案》（TJ-ED-017）记录并计算载重平衡数据的变化情况。', 4, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4100, 166, '5.3.2', '技术支援分部负责执管飞机载重平衡数据的累计管理，并定期对累计数据进行评估，当累计的重量累积改变量大于飞机最大着陆重量的±0.5%或重心变化超过MAC的±0.5%时，必须以书面形式通知工程部，再由工程部通知运行控制中心飞机载重平衡数据管理部门，由运行控制中心负责发布飞机的载重平衡数据修正。', 4, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4101, 166, '5.3.3', '当飞机完成称重或者运行控制中心颁发新的载重平衡基础数据后,技术支援分部载重平衡数据管理人员将其记录入该机《国航飞机载重平衡数据档案》，并以此为新的数据零点进行持续的动态控制。', 4, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4102, 166, '5.3.4', '技术支援分部载重平衡数据管理人员每年12月底之前填写《国航股份飞机载重平衡基本数据统计报告表》（CCA-ED-037）并报告公司工程部构型信息管理科。', 4, '2018-06-29 15:46:11', '2018-06-29 15:46:11', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4103, 167, '5.1', '飞机移交前准备\r\n在飞机交接前三个月，由Ameco工程部牵头成立飞机交接小组，小组成员应包括技术支援分部、航空安全质量分部、航材与采购部天津分部和生产计划控制分部的人员。', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4104, 167, '5.2', '飞机差异评估\r\n在飞机移交前，由交付方的工程部门、航材部门协助接收方对该飞机进行差异评估。', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4105, 167, '5.2.1', '飞机构型差异评估\r\n由交接双方技术支援分部评估该飞机与接收方现有机队构型上的差异，以及维护差异，由接收方培训部门制定差异培训方案，并在飞机交接前完成相关人员的培训。在飞机运行前，接收方应根据该飞机的构型和维护差异以及该机型的CAMP，修改相应的飞机维护工卡，将该飞机融入到接收方的维修管理体系中。', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4106, 167, '5.2.2', '部件差异评估', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4107, 167, '5.2.2.1', '由交接双方技术支援分部、航材与采购部天津分部评估该飞机与接收方现有机队装机部件的差异。', 4, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4108, 167, '5.2.2.2', '由双方航材与采购部门协商差异部件的备件调拨方案，以保障该飞机移交后的正常运行。', 2, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4109, 167, '5.3', '飞机的技术交接', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4110, 167, '5.3.1', '维修记录和技术文件的交接\r\n飞机交接时，交付方应向接收方至少提供以下技术文件和维修记录：', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4111, 167, '5.3.1.1', '适航证件类：\r\n1）	现行有效的适航证复印件\r\n2）	现行有效的国籍登记证复印件\r\n3）	现行有效的电台执照复印件\r\n4）	噪音合格证\r\n5）	飞机制造厂所在国出口适航证\r\n6）	以前历次颁发的出口适航证（如有）\r\n7）	该型号飞机的型号合格证（包括型号合格证数据单）\r\n8）	发动机型号合格证（包括型号合格证数据单）\r\n9）	客舱材料阻燃证明\r\n10）原始出厂卫生证明（如有）\r\n11）其它原始出厂证件\r\n12）历史适航证件（如有）\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4112, 167, '5.3.1.2', '飞机维修记录文件：\r\n1）	飞机技术记录本\r\n2）	飞机履历本\r\n3）	飞机《单机档案》\r\n4）	定检工卡\r\n5）	所有适航指令完成工作单\r\n6）	所有服务通告完成工作单\r\n7）	所有改装完成工作单（包括EO和批准）\r\n8）	所有CEO完成工作单\r\n9）	所有结构修理完成工作单（包括修理方案和批准）\r\n10）最近一次的秤重报告及秤重设备清单\r\n11）校罗盘报告（如有）\r\n12）RVSM、ETOPS认证报告（如有）\r\n13）最近一次的试飞报告\r\n14）其他非例行工作完成工作单\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4113, 167, '5.3.1.3', '飞机构型状态技术文件：\r\n1）	经批准的客舱LOPA\r\n2）	应急设备布局图\r\n3）	松散设备清单\r\n4）	主要机载设备清单\r\n5）	机载软件清单\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4114, 167, '5.3.1.4', '飞机原始出厂资料：\r\n1）	制造厂出厂AD状态报告\r\n2）	制造厂出厂SB状态报告\r\n3）	制造厂出厂修理/改装报告（如有）\r\n4）	飞机详细规范\r\n5）	飞机原始装机清册\r\n6）重要再加工记录（SRL）（如有）\r\n7）其他飞机出厂时提供的技术文件\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4115, 167, '5.3.1.5', '部件维修记录文件：\r\n1）	飞机装机部件清册\r\n2）	时控件/寿命件清单\r\n3）	所有装机部件挂签（按需）\r\n4）	自制件清单及相关文件\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4116, 167, '5.3.1.6', '发动机维修记录文件：\r\n1）	发动机OEM厂履历本（若有，且只适用于TR700和RB211）\r\n2）	最新的发动机寿命件清单\r\n3）	时控件状态清单\r\n4）	特殊维护项目和未完成的非例行维护项目（若有）\r\n5）	需要监控（或保留）的故障或缺陷信息（若有）\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4117, 167, '5.3.1.7', 'APU维修记录文件：\r\n1）	APU适航挂签\r\n2）	APU履历本\r\n3）	最近一次的APU大修报告\r\n4）	APU LLP部件清单和状态（如有）\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4118, 167, '5.3.1.8', '起落架维修记录文件：\r\n1）	起落架适航挂签（如需）\r\n2）	最近一次起落架大修报告（如有）\r\n3）	起落架LLP部件清单\r\n4）	起落架原始出厂技术文件\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4119, 167, '5.3.1.9.1', '飞机基本数据和维修状态文件：\r\n1）飞机数据表，包括飞机、发动机、APU、起落架当前的总使用小时/循环数据及大修后使用小时/循环,飞机当前的维修状态。\r\n2）适航指令执行状态。\r\n3）服务通告执行状态。\r\n4）其他非例行维护项目当前的状态。\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4120, 167, '5.3.1.9.2', '5）时控件/寿命件状态。', 2, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4121, 167, '5.3.1.9.3', '6）飞机改装状态/STC清单。\r\n7）历史定检记录和当前定检状态。\r\n8）飞机自制造厂出厂后每日的小时/循环记录。\r\n9）飞机目前遗留的保留项目清单。\r\n10）所有非例行检查项目、特殊要求项目、结构修理重检项目的状态清单。\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4122, 167, '5.3.1.9.4', '11）飞机事故报告（如有）\r\n12）重要事件快报（如有）\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4123, 167, '5.3.1.10', '手册/资料：\r\n1）最新版飞行手册、使用手册、快检手册、重量平衡手册。\r\n2）各种维护类手册\r\n3）有关的部件维护手册（如座椅、厨房CMM）\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4124, 167, '5.3.2', '资料接收\r\n由技术支援分部牵头对5.3.1节中的资料进行清点验收，并对其进行分类保存，由工程部牵头向接收方飞行部门提供该飞机的飞行手册、使用手册、构型差异、操作差异等有关信息，向运行部门提供该飞机的载重平衡手册、最近的秤重报告，向客舱服务部门提供客舱布局图（LOPA）、应急设备布局图、餐车规格等信息。\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4125, 167, '5.3.3', '飞机的交接', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4126, 167, '5.3.3.1', '由接收方技术支援分部制定飞机检查单，按检查单对飞机进行全面检查，同时按松散设备清单清点松散设备。', 4, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4127, 167, '5.3.3.2', '由接收方的航空安全质量分部对交付方执行的交付检（航后或定检）进行监督和检查。', 8, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4128, 167, '5.4', '未尽事宜的处理\r\n飞机移交过程中遗留的问题，由交接双方及维修部签订备忘录，需后续解决的问题，由接收方持续监控解决。', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4129, 167, '5.5', '后续工作', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4130, 167, '5.5.1', '技术支援分部负责对通告、维护提示等技术文件的控制；\r\n需重复检查的结构修理项目的持续控制；\r\n其他非例行重复性检查项目。\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4131, 167, '5.5.2', '生产计划控制分部负责将接收的飞机状态和持续控制项目过渡到其维修管理体系中；\r\n重复性检查的控制；\r\n飞机目前维修状态的延续和执行。\r\n', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4132, 167, '5.5.3', '时控件/时寿件的持续控制', 2, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4133, 167, '5.6', '后续支援\r\n为保证飞机的顺利运行，交付方在飞机交付后的一段时间，技术支援分部负责为接收方提供必要的支援，包括工程指导、排故指导和维护指导。', NULL, '2018-06-29 15:52:20', '2018-06-29 15:52:20', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4134, 168, '5.1', '在接到工程部发布拆换指令后的2个工作日内制定分公司换发工作指令，下发生产计划控制分部。', 4, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4135, 168, '5.2', '生产计划控制分部接到发动机更换指令后，制定换发停场计划，确定换发地点，并立即书面通知航材与采购部天津分部换发停场计划和地点。若因某些特殊原因无法按期完成发动机拆换指令，则向运行管理部提交《发动机拆换延期申请单》（CCA-ED-095R1）', 6, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4136, 168, '5.3', '航材与采购部天津分部在接到换发停场计划，换发地点相关信息后，在5个工作日内将发动机运至指定的换发地点。航材与采购部天津分部负责将发动机运抵信息书面通知生产计划控制分部与控制。', 2, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4137, 168, '5.4', '维修中队组织具体实施发动机的拆换工作，协调、处理拆换中遇到的问题，在拆换中若造成拆下发动机损伤（航线无法修复的缺陷），需立即反馈给技术支援分部。', 9, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4138, 168, '5.5', '技术支援分部立即反馈工程部发动机项目。', 4, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4139, 168, '5.6', '维修中队完成拆发工作后的1个工作日内，将完整的换发技术文件反馈生产计划控制分部。', 9, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4140, 168, '5.7', '生产计划控制分部控制在接到文件后的1个工作日内将完整的文件及未关闭的发动机故障保留（DD）清单，反馈技术支援分部及航材与采购部天津分部（串件/缺件）。并反馈Ameco相关部门CAMP71-CA-108/ CAD2011-B737-11的完成情况。', 6, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4141, 168, '5.8', '航材与采购部天津分部在接到换发文件后的2个工作日内，向技术支援分部提供拆下发动机《发动机附件/QEC清单》（CCA-ED-096）。如果由于航材原因不能完成，请立即以书面形式反馈航材与采购部及Email通知分公司技术支援分部。', 2, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4142, 168, '5.9', '对于拆下后拟送修的发动机：', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4143, 168, '5.9.1', '维修中队完成技术支援分部下发的《发动机拆换指令单》（CCA-ED-045）规定的工作，在拆换中若造成拆下发动机损伤（航线无法修复的缺陷），则必须在拆发报告中注明。\r\n维修中队完成缺件检查、外观检查和全面孔探，并挂不可用签及发动机油封期签（油封期签上必须标明油封到期日期）后退库，通知航材与采购部天津分部。\r\n', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4144, 168, '5.9.2', '航材与采购部天津分部通知航材与采购部。', 2, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4145, 168, '5.9.3', '在发动机换下5个工作日内，技术支援分部汇总完整准确的技术文件交工程部，包括：\n1） 《发动机拆发报告》CCA-ED-047（技术支援分部）\n2） 《未关闭的发动机故障保留（DD）清单》（生产计划控制分部）\n3） 《发动机临时修理项目清单》（技术支援分部）\n4） 《发动机时寿件工作时间表》CCA-ED-050（技术支援分部）\n5） 《发动机孔探报告》（航空质量质量分部）\n6） 《发动机附件/QEC清单》CCA-ED-096（航材与采购部天津分部）\n7） 《发动机缺件单》CCA-ED-048R2（维修中队）', 4, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4146, 168, '5.10', '对于梯次换发拆下的发动机：', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4147, 168, '5.10.1', '维修中队完成技术支援分部下发的《发动机更换指令单》规定的工作。\r\n维修中队按照技术支援分部下发的《发动机更换指令单》完成所有规定的工作后。挂可用挂签及发动机油封期签（油封期签上必须标明油封到期日期），退库并通知航材与采购部天津分部。\r\n若未完成《发动机更换指令单》规定的工作，则立即报告技术支援分部。\r\n', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4148, 168, '5.10.2', '航材与采购部天津分部通知航材与采购部。', 2, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4149, 168, '5.10.3', '在发动机换下5个工作日内，技术支援分部接到反馈文件后的1个工作日内向工程部提交《梯次换发拆发报告》（CCA-ED-097）和《发动机缺件单》（CCA-ED-048R2）。', 4, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4150, 168, '5.11', '发动机的非计划拆换', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4151, 168, '5.11.1', '技术支援分部将拆换要求通知工程部相关发动机项目，确定拟装机使用的备发的序号，下发《发动机拆换指令单》，如备发需要验收同时下发《发动机验收指令单》，并立即通知生产计划控制分部和航材与采购部天津分部。', 4, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4152, 168, '5.11.2', '航材与采购部天津分部按要求将发动机运至目的地。', 2, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4153, 168, '5.11.3.1', '维修中队按验收检程序要求执行验收检工作后，按照《发动机更换指令》要求实施换发工作。在拆换中若造成拆下发动机损伤（航线无法修复的缺陷），需立即反馈给技术支援分部。', 9, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4154, 168, '5.11.3.2', '技术支援分部反馈工程部。', 4, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4155, 168, '5.11.4', '发动机拆下后，维修中队完成缺件检查、外观检查和全面孔探，并挂不可用签及发动机油封期签（油封期签上必须标明油封到期日期）后退库，并通知航材与采购部天津分部。\r\n对于由于发动机损伤等原因不能进行油封的发动机，要在油封签上注明不能油封的原因。\r\n', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4156, 168, '5.11.5', '航材与采购部天津分部通知航材与采购部。', 2, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4157, 168, '5.11.6', '在发动机换下5个工作日内，技术支援分部汇总完整准确的技术文件交工程部，包括：\r\n1）《发动机拆发报告》CCA-ED-047（技术支援分部）\r\n2）《未关闭的发动机故障保留（DD）清单》（生产计划控制分部）\r\n3）《发动机临时修理项目清单》（技术支援分部）\r\n4）《发动机时寿件工作时间表》CCA-ED-050（技术支援分部）\r\n5）《发动机孔探报告》（航空质量质量分部）\r\n6）《发动机附件/QEC清单》CCA-ED-096（航材与采购部天津分部）\r\n7）《发动机缺件单》CCA-ED-048R2（维修中队）\r\n', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4158, 168, '5.12', '发动机缺件单要求', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4159, 168, '5.12.1', '在换发过程中，如果需要从拆下的发动机上串件，必须将串件信息填入拆下发动机的缺件单，换发工作完成后要形成拆下发动机完整的《发动机缺件单》CCA-ED-048R1（缺件单必须包括发动机上除消耗器材以外的所有缺件）。', 9, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4160, 168, '5.12.2', '拆下发动机的技术文件保管\r\n由技术支援分部和/航空安全质量分部负责保管所拆下发动机的所有技术文件和维修记录。当发动机梯次拆下或完成修理，技术支援分部接收到工程部颁发的《发动机移交通知单》后，按照TJ-10-016CCA《分公司内部发动机移交管理程序》的要求进行技术文件移交。', NULL, '2018-06-29 15:56:35', '2018-06-29 15:56:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4161, 169, '5.1', '发动机技术文件验收', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4162, 169, '5.1.1', '对于修后发动机或新购买发动机：', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4163, 169, '5.1.1.1', '对于修后发动机，由技术支援分部接收工程部颁发《发动机验收检通知单》（CCA-ED-139）及发动机的送修工作包（包括在修理过程中调整或修改的部分）。按照《验收通知单》要求和最新版的EB-MULT-72-1003《发动机验收检查工作指南》的内容编写天津分公司发动机验收《工程文件处理单》（TJ-ED-003）,并在1个工作日内下发生产计划控制分部。', 4, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4164, 169, '5.1.1.2', '对于新购买发动机，技术支援分部接收由工程部颁发的《发动机技术验收通知单》，按照《验收通知单》要求和最新版的EB-MULT-72-1003《发动机验收检查工作指南》的内容编写天津分公司发动机验收《工程文件处理单》, 并在1个工作日内下发生产计划控制分部。', 4, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4165, 169, '5.1.2', '生产计划与控制分部安排验收工作。', 6, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4166, 169, '5.1.3', '航材与采购部天津分部负责接收航材部提供的发动机相关合同条款（若需要），并负责安排将发动机运至指定的地点。', 2, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4167, 169, '5.1.4', '天津分公司各相关部门在接到发动机后的5个工作日内安排完成对以下适航性放行文件、维修文件完成检查：', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4168, 169, '5.1.4.1', '对于新发和修后发动机，检查发动机适航放行文件。', 8, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4169, 169, '5.1.4.2', '对于修后发动机，对随发附件在SAP系统中完成验收，检查随发执行过台架测试级别（BENCH TEST）以上修理的附件的适航放行文件，检查合格后将相应的附件适航放行文件上传到SAP系统。', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4170, 169, '5.1.4.3', '对于修后发动机，检查发动机时寿件技术文件。要求本次进厂修理中更换过的时寿件必须有可追溯到零寿命的记录和满足适航要求的合格证。\r\n对于修后发动机，检查发动机适航指令/服务通告的完成状态，要符合发动机送修工作包的要求。\r\n对于修后发动机，检查发动机单元体及主要零、附件的倒件和串件状态。发动机本体及附件的修理工作要满足发动机送修工作包的要求。\r\n', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4171, 169, '5.1.5', '对于租赁发动机：\r\n天津分公司在接收到发动机后的5个工作日内对随发的技术文件的完整性进行核查。（适航文件）', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4172, 169, '5.1.6', '发动机物理验收检查：', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4173, 169, '5.1.6.1', '天津分公司在接到发动机后的5个工作日内按照天津分公司发动机验收《工程文件处理单》内容完成发动机物理验收检查工作。\r\n维修中队在完成发动机技术文件验收和物理验收后的1个工作日内填写《发动机技术验收报告》（CCA-ED-052）反馈生产计划控制分部。\r\n', NULL, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4174, 169, '5.1.6.2', '生产计划控制分部负责回收验收技术文件并反馈技术支援分部。', 6, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4175, 169, '5.1.6.3', '技术支援分部在接到所有验收工作的反馈后，确保所有反馈的文件资料填写完整规范，并按照《验收通知单》要求在1个工作日内反馈给工程部《发动机技术验收报告》及相关技术文件。并将验收文件存档。', 4, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4176, 169, '5.1.6.4', '如验收中发现问题，维修中队填写在《发动机技术验收报告》内。', 9, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4177, 169, '5.1.6.5', '技术支援分部负责联系处理相关问题，反馈工程部。', 4, '2018-07-02 08:10:19', '2018-07-02 08:10:19', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4178, 171, '5.1', '生产计划控制分部根据TJ-10-031CCA《持续适航维修方案管理程序》负责组织和安排飞机腐蚀预防和控制方案的实施：\r\n对于腐蚀预防与控制方案生效时超过首次检查门槛值的飞机，生效日期起一个月内制定一个执行检查任务的计划，在经主管民航地区管理局批准后按照计划完成检查任务。\r\n对于腐蚀预防与控制方案生效时正在分公司维修或存储状态的飞机，如超过首次检查的门槛值，应当在投入运行之前完成所有累积的腐蚀检查任务。\r\n由于计划的意外而造成不能在检查间隔内执行重复检查任务时，重复检查的间隔不能超过原间隔的10%。\r\n对于新引进使用过的飞机，除非原飞机的维修方案符合本程序的要求并有记录表明已执行了其中规定的腐蚀检查任务，在投入运行前应当完成所有已到期的腐蚀检查任务。\r\n', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4179, 171, '5.2', '维修中队根据生产计划控制分部的安排，实施飞机腐蚀预防和控制工作，一旦发现腐蚀，完成对腐蚀的打磨处理，请航空安全质量分部判定腐蚀等级。在工作单/卡中记录每项腐蚀检查任务的检查结果和处理措施。\r\n注意：Ⅲ级腐蚀需有飞机制造厂人员参加评定，然后确认。\r\n', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4180, 171, '5.3', '如果没有发现腐蚀或重复检查时仅发现有Ⅰ级腐蚀，无需上报。维修人员将检查和处理结果，通过生产计划控制分部反馈给技术支援分部，以便作为确定延长防腐工作重检间隔的依据。', 9, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4181, 171, '5.4', '检查中发现有以下腐蚀情况出现时的处理：\r\n1）	在首次检查时，任何Ⅰ，Ⅱ，Ⅲ级的腐蚀\r\n2）	任何检查时发现蔓延腐蚀\r\n3）	任何重复检查时发现II，III级腐蚀\r\n4）	在执行非腐蚀预防与控制大纲规定的维修任务时发现腐蚀\r\n注意：Ⅲ级腐蚀的确定需有飞机制造厂人员参加评定，然后确认。\r\n', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4182, 171, '5.4.1', '维修人员应记录检查结果，记录应包括腐蚀位置及打磨腐蚀后的损伤面积和深度，填写结构损伤报告表，并连同相应草图或照片报告生产计划控制分部。', 9, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4183, 171, '5.4.2', '生产计划控制分部反馈技术支援分部。', 6, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4184, 171, '5.4.3', '技术支援分部应填写：《飞机防腐和控制大纲检查报告表》。然后必须在发现腐蚀后72小时内，将《飞机防腐和控制大纲检查报告表》向相应的民航地区管理局和工程部报告。', 4, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4185, 171, '5.4.4', '结构工程师应审查相应部位的腐蚀控制程序，在30天内，结构工程师按需制定措施把此类腐蚀控制在Ⅰ级腐蚀以下，上报工程部认可，并报航空安全质量分部。', 4, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4186, 171, '5.4.5', '如果确定为Ⅲ级腐蚀，结构工程师必须在3天之内制定出适用同型号其他飞机的普查方案，经各级工程部门认可后报适航部门批准。普查方案获批准后由结构工程师发生产计划控制分部在该机型飞机上实施。\r\n注：《飞机防腐和控制大纲检查报告表》中控制号一栏填写方式：机号-年-月-日-流水号，如：2531-2008-01-02-001。\r\n', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4187, 171, '5.5', '飞机腐蚀的上报：', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4188, 171, '5.5.1', '如果腐蚀情况符合5.4的要求，由结构工程师填写《飞机防腐和控制大纲检查报告表》报告工程部构型信息管理科同时转发航空安全质量分部。', 4, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4189, 171, '5.5.2', '航空安全质量分部向民航天津监管局上报，同时按需抄报航空安全质量部。', 8, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4190, 171, '5.6', '关于《飞机防腐和控制大纲检查报告表》，报告内容至少应包括下述内容：\r\n1）机型、飞机注册号、日历时间、飞行小时/循环、检查日期\r\n2）任务号、检查方法\r\n3）腐蚀损伤级别/类别\r\n4）腐蚀损伤级别的具体部位\r\n5）腐蚀损伤的原因	\r\n6）腐蚀损伤的修理情况描述\r\n', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4191, 171, '5.7', '根据《飞机防腐和控制大纲检查报告表》，填写更新《飞机腐蚀记录清单》。', 4, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4192, 171, '5.8', '制定每架飞机的腐蚀记录单：', NULL, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4193, 171, '5.8.1', '技术支援分部负责建立所执管每架飞机的专门腐蚀损伤记录单《飞机腐蚀记录清单》。腐蚀损伤记录单中应当至少记录任何发现的腐蚀损伤的检查日期、任务号、腐蚀损伤的级别、原因分析、修理情况和为完善腐蚀预防与控制方案所作的必要的修订（当需要时）。', 4, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4194, 171, '5.8.2', '腐蚀损伤记录的填写：维修中队应在每次定检工作中负责填写《飞机腐蚀记录清单》，如果该次工作没有发现腐蚀，请在表格内反馈未发现腐蚀；如果在航线或非定检停场工作中发现腐蚀，也应该填写此《飞机腐蚀记录清单》。', 9, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4195, 171, '5.8.3', '如果该工作属于外委，则应该由外委单位填写。', 6, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4196, 171, '5.8.4', '技术支援分部根据维修中队填写的腐蚀损伤记录单更新该架飞机的总的清单，然后反馈航空安全质量分部。', 4, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4197, 171, '5.8.5', '航空安全质量分部将总清单与飞机单机档案一同保存。', 8, '2018-07-02 08:13:59', '2018-07-02 08:13:59', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4198, 172, '5.1', '信息收集和报告的间隔\r\n在Ⅱ类运行批准第一年（2014年）内每月21 日前，和在Ⅱ类运行批准一年后（2015及以后）每年8 月30 日前向工程部提交经批准Ⅱ类运行的机载设备的总拆换次数。\r\n技术支援负责电子系统的工程师依据EB-B737-UNIV-2001-R00，在符合5.1间隔和日期前一天从分公司故障信息网站检索与Ⅱ类运行要求有关的机载设备非定期更换的信息，并按机型分开汇总填表《Ⅱ类运行机载设备非定期更换报表》（CCA-ED-249）。\r\n', NULL, '2018-07-02 08:15:18', '2018-07-02 08:15:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4199, 172, '5.2', '信息报告\r\n报告表格通过邮件，或其他方式反馈至北京飞机维修工程有限公司工程部有关负责人，并电话联系对方确认收到。\r\n', NULL, '2018-07-02 08:15:18', '2018-07-02 08:15:18', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4213, 174, '5.1', '综合管理分部负责依据《关于波音飞机DVD光盘转换的业务通告》国航股份工技工程业务〔2013〕99号文件配置软件转换需要的计算机硬件和软件，交付技术支援分部使用，并负责该计算机日常的维护及升级工作。', 7, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4214, 174, '5.2', '航材与采购部天津分部负责提供EO使用的DVD光盘软件，保存转换好的按波音要求的件号的3.5寸软盘到库房。', 2, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4215, 174, '5.3', '生产计划控制分部将EO转换DVD软件的转换需求通知技术支援分部，并在确认软盘已经转换完毕后安排执行该EO。', 6, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4216, 174, '5.4', '技术支援分部负责依据TJ-10-013CCA《机载软件管理流程》：《关于波音飞机DVD光盘转换的业务通告》国航股份工技工程业务〔2013〕99号文件，并按波音的软件操作要求将：\r\n1）	新飞机提供的DVD光盘转换为3.5寸软盘，并配上飞机。\r\n2）	接到生产计划控制分部的EO转换需求后在10个工作日内将所需的软盘准备好，按指定的件号提供给航材与采购部天津分部存放到库房中。\r\n', NULL, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4217, 174, '5.5', '具有软件装载能力的航线可更换件的管理', NULL, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4218, 174, '5.5.1', '对于在送修、订货或改装中涉及到相应软件更新要求的航线可更换件，航材与采购部天津分部接生产计划控制分部下发的器材准备通知单。', 2, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4219, 174, '5.5.2', '航材与采购部天津分部送航线可更换件到有软件装载能力的相关修理站修理或改装。', 2, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4220, 174, '5.5.3', '对于涉及完成软件装载的航线可更换件，航材与采购部天津分部应要求相关修理站在此类航线可更换件的文件上标注相关软件信息，以便一线生产单位在领用时核实相关信息。软件信息一般包括以下四方面内容：\r\n1）本航线可更换件中已装载的所有软件的件号\r\n2）本航线可更换件中已装载的所有软件的序号\r\n3）本航线可更换件中已装载的所有软件的版本号\r\n4）质检人员的签章\r\n', NULL, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4221, 174, '5.5.4', '航材与采购部天津分部在租借此类航线可更换件时，应向对方明确此航线可更换件上国航所用的软件件号，以便对方正常租借。', 2, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4222, 174, '5.5.5', '检验人员在接收此类航线可更换件时，如出厂文件上标明有该器材的软件信息，应核对出厂文件中本程序5.5.3 中所要求的四方面内容是否完整，对于上述内容有缺失的此类器材不应直接入库。', 8, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4223, 174, '5.5.6', '维修中队在领用具有软件装载能力的航线可更换件时，必须首先检查相应的软件构型；若发现该航线可更换件的软件构型与《机载软件构型清单》上该航线可更换件所对应的软件构型不一致时，可重新安装正确的软件后使用该航线可更换件；对于不具备软件装载条件的航线可换件，按不可用件退回航材与采购部天津分部。', 9, '2018-07-02 08:18:38', '2018-07-02 08:18:38', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4224, 175, '5.1', '发动机引进\r\n对于随飞机引进的发动机，由接机小组中的分公司的接机代表将随机的发动机文件移交给技术支援分部存档，技术支援分部发动机工程师从中提取发动机本体时寿件初始数据。对于新引进备发（含租赁发动机），由工程部将本体时寿件初始数据提供给天津分公司技术支援分部。\r\n天津分公司技术支援分部在收到发动机本体时寿件初始数据后建立所属机队相关发动机时寿件数据控制档案。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4225, 175, '5.2', '发动机在翼使用期间的数据维护 。技术支援分部根据生产统计数据，每月定期更新在翼发动机时寿件使用数据（小时/循环）及剩余循环。', 4, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4226, 175, '5.3', '发动机时寿件寿命限制值的更新\r\n天津分公司技术支援分部在收到工程部下发的CAMP发动机时寿件控制条目临时修正版后的3个工作日内，按要求及时修正寿命限制数据，确保数据的准确性，并在修改后的3个工作日内书面反馈工程部。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4227, 175, '5.4', '发动机因时寿件到寿更换\r\n在翼发动机时寿件到寿前200循环（训练飞机发动机在时寿件到寿前800循环），分公司技术支援分部以书面形式通报工程部。\r\n在发动机时寿件预计到寿的两周前，工程部颁发《发动机拆换指令单》（CCA-ED-045）。分公司按照TJ-10-007CCA《发动机拆换操作程序》完成发动机更换工作。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4228, 175, '5.5', '发动机更换后的时寿件数据维护\r\n技术支援分部在发动机更换后3个工作日内须完成各推力级下的使用小时/循环数据累计和确认，计算剩余寿命（循环），完成《发动机时寿件工作时间表2》（CCA-ED-102）并通报工程部。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4229, 175, '5.6', '发动机修后出厂的时寿件数据审核\r\n技术支援分部在收到备发修理报告后，在装机前审核时寿件数据，确认其准确性、适航性；若发现问题，及时反馈工程部。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4230, 175, '5.7', '发动机移交工作中的时寿件数据管理\r\n参照TJ-10-016CCA《公司内部发动机移交管理程序》要求执行。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4231, 175, '5.8', '发动机时寿件控制工作情况的监督、检查\r\n航空安全质量分部按照监督的程序检查发动机时寿件控制情况。\r\n', NULL, '2018-07-02 08:20:35', '2018-07-02 08:20:35', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4232, 176, '5.1', '发动机重大非正常事件发生后，相关中队要立即通知MCC值班经理。', 9, '2018-07-02 08:21:32', '2018-07-02 08:21:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4233, 176, '5.2.1', '1）MCC值班经理立即将搜集到的相关信息通知领导及相关部门。', 6, '2018-07-02 08:21:32', '2018-07-02 08:21:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4234, 176, '5.2.2', '2）技术支援分部立即通知工程部相关发动机项目；同时若有必要，对QAR或DFDR数据进行译码。', 4, '2018-07-02 08:21:32', '2018-07-02 08:21:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4235, 176, '5.3.1', '1）或对发动机进行孔探检查。', 8, '2018-07-02 08:21:32', '2018-07-02 08:21:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4236, 176, '5.3.2', '2）在事件发生后的1个工作日内提交《发动机重大非正常事件报告表》（CCA-ED-103）及译码数据（若译码）。', 4, '2018-07-02 08:21:32', '2018-07-02 08:21:32', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4237, 176, '5.3.3', '3）按照工程部给出的处理意见完成后续工作，视情颁发《发动机重大非正常事件工程简报》（CCA-ED-104）。', 4, '2018-07-02 08:21:32', '2018-07-02 08:21:32', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4238, 177, '5.1', '本程序适用以下两种情况：\r\n发动机梯次调配使用；\r\n发动机进行修理后的首次装机调配使用。\r\n如发动机随飞机调整至/出天津分公司使用时，则按程序TJ-10-006CCA《国航股份内部飞机移交程序》执行。\r\n', NULL, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4239, 177, '5.2', '对于拆下准备送修的发动机', NULL, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4240, 177, '5.2.1', '对于拆下准备送修的发动机，发动机拆下后天津分公司生产计划控制分部5个工作日内，在TDMS/SAP系统中维护航线执行的发动机EO状态。', 6, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4241, 177, '5.2.2', '发动机修理后按照《发动机移交通知单》（CCA-ED-105R1）要求在SAP系统中完成移交。', 4, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4242, 177, '5.2.3', '发动机完成修理后，分公司收到工程部的《发动机移交通知单》（CCA-ED-105R1）和/或《发动机技术验收通知单》（CCA-ED-139）（见TJ-10-008CCA 《发动机技术验收程序》）。', 4, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4243, 177, '5.2.4', '生产计划控制分部必须在TDMS/SAP系统中确认该发的EO状态。', 6, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4244, 177, '5.3', '对于梯次调配使用的发动机', NULL, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4245, 177, '5.3.1', '对于梯次调出的发动机，技术支援分部在收到工程部颁发《发动机移交通知单》后，移交生产计划控制分部。', 4, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4246, 177, '5.3.2', '发动机拆下后生产计划控制分部在2个工作日内在TDMS/SAP系统中对移交发动机已完成的EO进行维护。', 6, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4247, 177, '5.3.3', '技术支援分部在SAP系统中将发动机的责任单位移交给接收方。并按照《发动机移交技术文件/维修记录交接清单》（CCA-ED-106R3）的要求在2个工作日内准备好交接文件及交接清单, 提交给接收方；《发动机移交技术文件/维修记录交接清单》上必须有相关人员签署并对所交付文件的准确性和全面性负责。', 4, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4248, 177, '5.3.4', '航材与采购部天津分部在收到《发动机移交通知单》后，协调发动机运输等工作。', 2, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4249, 177, '5.3.5', '对于梯次调入的发动机，接收工程部颁发《发动机移交通知单》、交付方提供的交接技术文件和维修记录后，对其准确性和全面性进行核实。', 4, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4250, 177, '5.3.5.1', '生产计划控制分部在TDMS/SAP系统中确认该发的EO状态。', 6, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4251, 177, '5.3.5.2', '技术支援分部在SAP系统中确认接收发动机。对于有问题的项目及时与交付方联系解决。', 4, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4252, 177, '5.3.5.3', '分公司在接到发动机后，执行如下检查，若发现问题及时报告工程部相关发动机项目：', NULL, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4253, 177, '5.3.5.3.1', '1）打开包装前检查外观是否有运输过程损伤。', 2, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4254, 177, '5.3.5.3.2', '2）检查发动机上有特殊要求附件是否安装在运输位置。\r\n3）检查发动机是否缺件，核对缺件清单。\r\n4）检查发动机油封挂签，确认油封期和滑油牌号。\r\n5）检查发动机上各种警告标识是否符合要求。\r\n', NULL, '2018-07-02 08:23:17', '2018-07-02 08:23:17', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4255, 178, '5.1', '完成回收统计', NULL, '2018-07-02 08:24:36', '2018-07-02 08:24:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4256, 178, '5.1.1', '维修中队按照工卡完成工作', 3, '2018-07-02 08:24:36', '2018-07-02 08:24:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4257, 178, '5.1.2', '生产计划控制分部对前一工作日完成所涉及重检适航指令的工作进行统计。', 6, '2018-07-02 08:24:36', '2018-07-02 08:24:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4258, 178, '5.2', '生产计划控制分部通过AEDMS系统对涉及重检适航指令进行反馈流程。\r\n在“构型数据库”下的“工程信息监控”里，可以接近“AD 执行数据导入”界面。\r\n路径为：构型数据库－－工程信息监控－－适航指令－－适航指令单机信息导入－－适航指令执行信息Excel文件上载。\r\n', NULL, '2018-07-02 08:24:36', '2018-07-02 08:24:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4259, 178, '5.3', '各要点数据的填写:\r\n填写适航指令的类型，只能选择FAA、CAAC、EASA。\r\n填写适航指令的编号，如AD2009-01-01，CAD2009-MULT-45/CAD2009-A320-16，EAD2009-0112。\r\n填写工程文件的类型，可以选择目前TDMS 系统支持的所有类型工程文件。\r\n填写工程文件的编号，此处编号应按照TDMS 系统里的文件编号来填写。\r\n填写机型，只需要写出飞机的类型，不需要具体构型，例如B737，A320，A319 等。\r\n填写飞机机号，其格式为B-XXXX。\r\n填写单机完成此份AD 的时间，其格式为XXXX-XX-XX。\r\n填写单机完成此份AD 时的飞行小时，其格式为XXXX.XX。\r\n填写单机完成此份AD 时的飞行循环，其格式为XXXX。\r\n填写单机此份AD 的状态，状态应为C（完成）、R（重检）、O（未完成）、SS（被取代）。\r\n', NULL, '2018-07-02 08:24:36', '2018-07-02 08:24:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4260, 178, '5.4', '反馈：\r\n生产计划控制分部如有需反馈时，在每天15:00前通过SAP系统向工程部反馈重检适航指令完成情况。\r\n', NULL, '2018-07-02 08:24:36', '2018-07-02 08:24:36', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4261, 179, '5.1', '随飞机引入发动机或引入备发：\r\n技术支援分部确认相应发动机已完成注册。接收发动机基本信息，包括：发动机序号（ESN）、发动机型号、执管分公司、预计交付日期。\r\n技术支援分部在接收到新交付的飞机/备发后，在2个工作日内,增加“发动机总概”，详见注1；录入方法详见软件操作说明2。\r\n', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4262, 179, '5.2', '更新在翼发动机数据：', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4263, 179, '5.2.1', '每月更新“发动机总概”数据：\r\n技术支援分部，在每月的前5个工作日内，定期更新自己执管发动机的“发动机总概”到上个月末最后一天的数据，详见注1；录入方法详见软件操作说明2。\r\n', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4264, 179, '5.2.2', '内窥更新：\r\n航空安全质量分部每次内窥后，在2个工作日内更新内窥记录。\r\n若内窥工作是在本基地之外地点完成，则要求在内窥完成后3个工作日内更新内窥记录。对于内窥正常的记录只要求录入文字信息，详见注2；对于内窥异常的记录，除录入文字信息外，必须上传两张典型图片；如内窥超标，必须在内窥后立即录入文字信息，及典型图片（不做数量限制）；录入方法详见软件操作说明2。\r\n', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4265, 179, '5.3', '换发数据更新：', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4266, 179, '5.3.1', '变更备发执管：\n换发飞机的执管分公司收到备发及《发动机移交通知单》，在确认接收发动机后，如发现发动机执管单位没有变更，则在1个工作日内通知工程部变更备发执管，包括：发动机序号（ESN）、换发飞机的执管分公司、预计换发执行日期。', 4, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4267, 179, '5.3.2', '更新“发动机总概”及录入发动机拆换记录：\r\n分公司在每次换发后，技术支援在2个工作日内更新拆下和装上发动机的“发动机总概”，详见注1；以及录入发动机拆换记录，详见注3；录入方法详见软件操作说明2。\r\n', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4268, 179, '5.3.3', '执管分公司完成更新“发动机总概”及录入发动机拆换记录工作，包括：发动机序号（ESN）。\r\n注1：发动机总概：机型，机号，发动机型号，ESN，位置，安装日期，自开始小时，自开始循次，（上次翻修时的小时，上次翻修时的循次，上次修理时的小时，上次修理时的循次），寿命件循次/小时，截止日期，△EGT，EGTM，最高温度。\r\n注2：发动机内窥文字信息：机号、机型、ESN、位置、检查日期、工作者、工作类别、报告编号、预警、检查情况、排除、部位。\r\n注3：发动机拆换记录：机号、位置、发动机型号、ESN、装上序号、自修理小时、自修理循次、自开始小时、自开始循次、拆下日期、拆换地点、拆换原因、计划或非计划、状态、原安装日期。\r\n', NULL, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4269, 179, '5.4', '飞机执管分公司变更：\n待接收飞机的执管分公司收到飞机后，2个工作日内技术支援分部通知工程部变更执管，包括：发动机序号（ESN）、接收飞机的执管分公司。', 4, '2018-07-02 08:27:09', '2018-07-02 08:27:09', 0, '01', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4270, 180, '5.1', '紧急MRO/MEL的下发', NULL, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4271, 180, '5.1.1', '技术支援分部资料管理在接到签发的MRO、MEL或其它手册改版后即对其改版期限进行认定。（在手册修正的每页右上角上有加盖紧急资料修正印章）。在TDMS系统进行接收。', 4, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4272, 180, '5.1.2', '为保障紧急MRO/MEL等的及时实施，各手册适用单位接收的是白色A4纸打印的相关内容，各手册适用单位都以此作为紧急MRO/MEL的临时完成方式完成资料的更新，并做好更新记录。其标准格式的正式纸张版或光盘将由工程部资料管理项目制作完成后发给各手册适用单位，各单位在接到正式修订后应完成正式的更新。', 4, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4273, 180, '5.2', '紧急MRO/MEL的实施', NULL, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4274, 180, '5.2.1', '机上资料的修正：\r\n技术支援分部在接收到紧急MRO、MEL改版通知单或技术资料分发清单后：\r\n', NULL, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4275, 180, '5.2.1.1', '首先根据飞机的排班情况，制定手册修订时间计划，在手册修正完成期限内完成。', 4, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4276, 180, '5.2.1.2', '在完成期限内，对排班计划返回天津的飞机，由资料员按要求完成。', 4, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4277, 180, '5.2.1.3', '在完成期限内，对排班计划未返回天津的飞机，必须在外站修订时，技术支援分部负责与外站人员沟通，通过邮寄或航班携带方式保证其按时修订。', 4, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4278, 180, '5.2.1.4', '在完成期限内，无法完成的工作，由技术支援分部通知工程部（下发紧急MRO/MEL相关部门），协调各个分公司配合完成。', 4, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4279, 180, '5.2.2', '部门持有的技术资料（非机上资料）的修正：\r\n技术支援分部负责在要求更新时限内完成手册的修订。手册修订可以使用工程部正式下发的修订内容或其打印件、传真件，但应在接收到工程部正式下发的修订内容后，第一时间内，用正式修订内容替换打印件或传真件。\r\n', NULL, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4280, 180, '5.3', '紧急情况下手册修订的记录和反馈\r\n技术支援分部资料管理应在手册修订完成的同时填写手册内或光盘包的手册修订记录（如适用）。并不晚于手册修订要求完成期限的后一个日历日向工程部反馈手册修订完成情况。 \r\n注：如采用打印件或传真件实施手册修订，修订时间以打印件或传真件的修订完成时间为准。后续以正式修订内容替换打印件或传真件无需再次填写修订记录。\r\n', NULL, '2018-07-02 08:28:27', '2018-07-02 08:28:27', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4281, 181, '5.1', 'APU引进', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4282, 181, '5.1.1', '对于随飞机引进的APU，技术支援分部从飞机资料中提取APU本体时寿件初始资料。对于新引进备发（含租赁APU），技术支援分部从随发资料中提取APU本体时寿件初始数据。', 4, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4283, 181, '5.1.2', '技术支援分部在收到APU本体时寿件初始数据（从履历本/修理报告中提取）后的5个工作日内建立所属机队相关APU时寿件数据控制档案。', 4, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4284, 181, '5.2', 'APU在翼使用期间的数据维护：', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4285, 181, '5.2.1', '技术支援分部每月定期更新在翼APU时寿件使用数据（小时/循环）及剩余循环，并每月定期将更新后的APU使用数据电子版报工程部各型APU主管工程师。', 4, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4286, 181, '5.2.2', 'APU时寿件寿命限制值的更新：\r\n技术支援分部在收到工程部下发的CAMP APU时寿件控制条目临时修正版后的5个工作日内，按要求及时修正寿命限制数据，确保数据的适航性，并在修改后的5个工作日内书面反馈工程部。\r\n', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4287, 181, '5.2.3', 'APU因时寿件到寿更换：\r\n在翼APU时寿件到寿前200循环，技术支援分部以书面形式通报工程部，以便提前作好换发计划及备发调配工作。\r\n', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4288, 181, '5.2.4', 'APU更换后的时寿件数据维护：\r\n技术支援分部在APU更换后5个工作日内，按要求完成《APU时寿件工作时间表》（CCA-ED-154），并书面通报工程部。\r\n技术支援分部在APU更换后5个工作日内，依据随发技术资料（修理报告所附时寿件数据或其它分公司移交的APU时寿件数据），建立新装APU时寿件数据控制档案。\r\n', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4289, 181, '5.3', 'APU修后出厂的时寿件数据审核\r\n技术支援在收到备用APU修理报告后，在3个工作日内审核时寿件数据，确认其准确性、适航性；若发现问题，及时反馈工程部。\r\n', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4290, 181, '5.4', 'APU移交工作中的时寿件数据管理\r\n参照《分公司内部APU移交管理程序》（TJ-10-022CCA）要求执行。\r\n', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4291, 181, '5.5', 'APU时寿件控制工作情况的监督、检查\r\n航空安全质量分部检查分公司的APU时寿件控制情况。\r\n', NULL, '2018-07-02 08:30:28', '2018-07-02 08:30:28', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4292, 182, '5.1', 'APU的计划拆换', NULL, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4293, 182, '5.1.1', '技术支援分部在收到公司工程部发布的《APU拆换指令单》后，立即按照指令单要求编写拆换指令，将拆换指令、《梯次换发报告》、《APU拆发报告》、《APU缺件单》打包发给生产计划控制分部。', 4, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4294, 182, '5.1.2', '生产计划控制分部在收到拆换指令之后，在3个工作日内确定换发时间及地点，书面通知航材与采购部天津分部换发时间和地点。若因某些特殊原因无法按期完成APU拆换指令，则由分公司向运行管理部提交《APU拆换延期申请单》。', 6, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4295, 182, '5.1.3', '航空安全质量分部完成APU验收后立即书面通知技术支援分部和生产计划控制分部。', 8, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4296, 182, '5.1.4', '维修中队负责具体实施APU的拆换工作，协调、处理拆换中遇到的问题，在拆换中若造成拆下APU损伤（航线无法修复的缺陷），需立即反馈给技术支援分部。', 9, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4297, 182, '5.1.5', '航空安全质量分部对APU拆换工作进行监督。', 8, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tb_step_info` VALUES (4298, 182, '5.1.6', '技术支援分部汇总拆换问题反馈公司工程部。', 4, '2018-07-02 08:34:05', '2018-07-02 08:34:05', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
