INSERT INTO `tb_step_info` VALUES (4339, 183, '5.2.2.7', '打开包装前外观检查是否有运输过程损伤。\r\n检查APU是否缺件，核对缺件清单。\r\n检查APU油封挂签，确认油封期和滑油牌号。\r\n检查APU上各种警告标识是否符合要求。\r\n若发现问题及时报告技术支援分部。\r\n', NULL, '2018-07-02 08:35:44', '2018-07-02 08:35:44', 0, '00', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
